INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'Giới thiệu','Tổ chức sự kiện là chương trình không thể thiếu trong mỗi giúp cho doanh nghiệp một mặt tạo quảng bá thương hiệu & sản phẩm, mặt khác lại tạo một cơ hội giao lưu gặp doanh nghiệp với các cơ quan truyền thông,cơ quan công quyền ... tăng cường quan hệ có lợi cho doanh nghiệp đó.\r\n\r\nTổ chức sự kiện là chương trình không thể thiếu trong mỗi giúp cho doanh nghiệp một mặt tạo quảng bá thương hiệu & sản phẩm, mặt khác lại tạo một cơ hội giao lưu gặp doanh nghiệp với các cơ quan truyền thông,cơ quan công quyền ... tăng cường quan hệ có lợi cho doanh nghiệp đó.','<p>Công ty Luật Ưu Việt hân hạnh đón quý vị ghé thăm website của chúng tôi. Hy vọng quý vị sẽ tìm thấy những thông tin hữu ích từ trang web này, qua đó có thể tham khảo, áp dụng để tự bảo vệ quyền và lợi ích hợp pháp của mình trong cuộc sống. &nbsp;<br />\r\nCông ty luật ưu việt là một tổ chức hành nghề luật sư - chuyên thực hiện dịch vụ tư vấn pháp luật cho các khách hàng là doanh nghiệp, doanh nhân, cá nhân ... - trong và ngoài nước.</p>\r\n','/media/images/61/intro/ava/','960_1418028037_80854856405b5e6d.jpg','','','',[now],[now]);
