INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories279,'Lên kế hoạch cho buổi tiệc Halloween','Tiệc Halloween là một trong những sự kiện vui nhất trong năm. Bạn được phép làm mấy trò sành điệu, ồn ào và cực kỳ vui vẻ. Để đảm bảo rằng cuộc liên hoan ngày 31 tháng 10 của bạn sẽ thật tuyệt vời, dưới đây là vài lựa chọn về thực phẩm và chủ đề hoàn hảo.','<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Tiệc&nbsp;<a href=\"http://khamphahalloween.blogspot.com/2014/09/le-hoi-halloween.html\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0);\">Halloween</a>&nbsp;là một trong những sự kiện vui nhất trong năm. Bạn được phép làm mấy trò sành điệu, ồn ào và cực kỳ vui vẻ. Để đảm bảo rằng cuộc liên hoan ngày 31 tháng 10 của bạn sẽ thật tuyệt vời, dưới đây là vài lựa chọn về thực phẩm và chủ đề hoàn hảo.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Take stock – Xác định vốn.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Xác định chủ đề thích hợp cho bữa tiệc của bạn sẽ dễ dàng hơn nếu bạn có những yếu tố cơ bản như: các yếu tố bên, tuổi tác của khách hàng, và ngân sách. Bữa tiệc của bạn là dành cho những đứa trẻ thích chơi trò “trick-or-treat”, vì nguyên nhân gây quỹ hay cho một nhóm bạn cùng chung sở thích? Có một sân chơi chung hay một khu vườn rộng sẵn sàng để trang trí? Tốt nhất, hãy xác định những thứ bạn đã có và xây dựng kế hoạch dựa trên chúng.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Những ý tưởng về chủ đề</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Những ý tưởng cổ điển bao giờ cũng tuyệt vời bởi vì có rất nhiều người đều có sẵn đồ cho những ý tưởng đó và rất dễ để trang trí dựa theo nó. Những sinh vật đáng sợ như Frankenstein, thây ma, quỷ, dơi và người sói có một loạt các truyền thuyết mà bạn có thể xây dựng xung quanh.Bạn có thể có những hoạt động dựa như một ngôi nhà bị ma ám hay một lễ hội kinh dị, đơn giản và phong phú hơn, lấy ý tưởng từ một bộ phim hoặc câu chuyện kinh dị nào đó, kiểu như là bữa tiệc trà với Ngài Mũ Điên chẳng hạn, sẽ rất là thú vị đây!</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Những ý tưởng hiện đại, sáng tạo cũng rất phổ biến bởi vì luôn có một nhóm cụ thể thích chúng. Nếu bạn có một nhóm bạn vẫn xem True Blood với nhau hàng tuần, hãy xây dựng một bữa tiệc xoay quanh bộ phim. Where The Wild Things Are, Chạng Vạng hay Harry Potter cũng đều có thể trở thành những bữa tiệc Halloween hiện đại và vui nhộn.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Nếu như muốn giữ cho mọi thứ thật đơn giản?</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Đôi khi một số bữa tiệc Halloween có thể dễ dàng trở nên mất kiểm soát và không phải ai cũng thích những hoạt động hóa trang (nhưng họ vẫn muốn có một buổi tiệc Halloween vui vẻ), vậy thì hãy bỏ qua phần chủ đề, khách khứa của bạn có thể mặc đồ thoải mái và nhận được sự đối xử ngọt ngào trong một đêm hội ma quái, tại sao không?</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Một điều chú ý là dù bữa tiệc của bạn có hay không có chủ đề, hãy đảm bảo rằng bạn có đủ kẹo cho những đứa trẻ và cư xử thận thiện với những người hàng xóm, không phải ai cũng mở tiệc vào Halloween.</p>\r\n','len-ke-hoach-cho-buoi-tiec-halloween',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','299_1439461077_73155cc6ed50bb17.jpg',[now], [now],[user_id],0,'','',[now]);set @news388 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories229,'Dịch vụ cho thuê người yêu \"điên rồ\" nhưng đắt khách','Cho thuê người yêu đang trở thành một xu hướng lạ của một bộ phận không nhỏ những người độc thân và cô đơn thành thị.','<p>Cho thuê người yêu đang trở thành một xu hướng lạ của một bộ phận không nhỏ những người độc thân và cô đơn thành thị.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/2015-05-08-131953.jpg\" /></p>\r\n\r\n<p>Noel - một trong những thời khắc đẹp, mà giới trẻ thường chọn&nbsp;để thể hiện tình cảm với nhau. Ảnh minh họa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Là một trong những dịch vụ độc đáo trong dịp Giáng sinh năm nay, cho thuê người yêu đang trở thành một xu hướng lạ của một bộ&nbsp;phận không nhỏ những người độc thân và cô đơn thành thị.</p>\r\n\r\n<p>Dịch vụ \"điên rồ”</p>\r\n\r\n<p>Trong căn phòng của công ty có cái tên rất “uy tín” – Tín Thành, chúng tôi được nghe câu chuyện về một ý tưởng “điên rồ”- cho thuê&nbsp;người yêu. Sẽ là bình thường nếu như dịch vụ này không xuất hiện ở Việt Nam, mà ở một quốc gia phương Tây xa xôi nào đó. Bởi lẽ,&nbsp;với người Việt ta vốn trọng tình, rất ngại đụng chạm đến chuyện thuê mướn, đặc biệt trong chuyện tình cảm nam nữ. Nhưng với nhận định rằng nhịp sống xã hội ngày nay ngày càng gấp gáp, khiến nhiều người không còn thời gian để quen biết, gặp gỡ,&nbsp;hẹn hò ... tìm cho mình một người yêu, hay một người bạn cùng đi chơi trong những dịp Noel hay Lễ tình nhân ... Tín Thành quyết định&nbsp;“tung ra” sự “điên rồ” không có “đối thủ cạnh tranh” ấy. Dịch vụ “người đóng thế” hay còn gọi nôm là “cho thuê người yêu” được khai sinh cách đây 1 năm và nay đã trở thành thân thuộc với&nbsp;nhiều đối tượng khách hàng. Bản chất của dịch vụ này là một cuộc gặp gỡ, hẹn hò giữa khách hàng là người cần có bạn trai hoặc bạn&nbsp;gái đi chơi cùng mình trong dịp lễ, Tết... và người kia là một cộng tác viên của công ty Tín Thành. Khi mới ra đời, dịch vụ cho thuê người yêu đã tạo ra những băn khoăn cho nhiều người về sự trong sáng của dịch vụ, song tới nay,&nbsp;đây đã trở thành một trong&nbsp;những dịch vụ của cuộc sống hiện đại. Nhất là khi, ngày nay số người quá bận bịu với công việc đến mức&nbsp;chưa tìm thấy người yêu của mình ngày càng tăng. Do vậy, đây được đánh giá là dịch vụ cho những người trẻ thành đạt và bận rộn. 1 - 2 triệu đồng là chi phí của khách hàng cho một cuộc hẹn hò kéo dài khoảng 3 giờ để có được 1 người đóng thế vai người yêu đi&nbsp;chơi trong dịp Giáng sinh. Mặc dù có chi phí không nhỏ, nhưng năm ngoái hiện tượng “cháy dịch vụ” đã xảy ra. “Dịp Noel năm nay, đã&nbsp;có hơn 30 khách hàng đặt hàng”, anh Nguyễn Chiến Thắng, Phó tổng giám đốc &nbsp;công ty Tín Thành cho biết. “Điên rồ” nhưng trong sáng và hút khách. Gọi là cho thuê người yêu, nhưng trong hợp đồng của khách hàng được quy định rất rõ các hành vi khách hàng không được làm, như:&nbsp;tự ý xâm phạm thân thể, tinh thần, cầm tay, ôm hôn ... “người yêu”. Nói về cơ chế đảm bảo an toàn cho khách hàng và cộng tác viên của dịch vụ, anh&nbsp;Nguyễn Chiến Thắng&nbsp;cho biết: “Cả hai bên trong&nbsp;cuộc hẹn hò này đều được đảm bảo an toàn cả bằng văn bản pháp lý là hợp đồng và cả trên thực tế. Theo đó, cả hai bên đều thống&nbsp;nhất và ký kết các điều khoản đảm bảo lợi ích ở trong hợp đồng từ trước. Về phía cộng tác viên, công ty còn có nhân viên bảo vệ “bí&nbsp;mật” trong suốt quá trình “hẹn hò” diễn ra. Điều này tạo tâm lý thoải mái cho cả người sử dụng dịch vụ lẫn cộng tác viên, chủ yếu là&nbsp;các bạn sinh viên. Đồng thời tạo nên tính chất trong sáng, nhân văn của dịch vụ”. Theo anh Thắng, nhiều người cho rằng, dịch vụ cho thuê người yêu sẽ không thể phát triển được ở Việt Nam do nhiều yếu tố chưa phù&nbsp;hợp với thuần phong mỹ tục. Nhưng thực tế&nbsp;đã minh chứng tính khả thi của dịch vụ này. Năm 2013 là năm “mở hàng” với thành tích&nbsp;“cháy dịch vụ”, báo hiệu một tương lai tươi sáng của dịch vụ “điên rồ” này. Còn năm nay, dịp Giáng sinh đã cận kề mà đã có hàng chục&nbsp;khách hàng đăng ký dịch vụ.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/2015-05-08-132212.jpg\" /></p>\r\n\r\n<p>Quà tặng - một trong những thứ không thể thiếu trong dịp Noel&nbsp;của các bạn trẻ. Ảnh chỉ mang tính minh họa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tình yêu thật nảy nở từ “tình giả”</p>\r\n\r\n<p>Dịch vụ “cho thuê người yêu”, “tìm bạn đời cho doanh nhân”, “café hẹn hò”... của Tín Thành đã trở thành “bà mối” cho khá nhiều cặp&nbsp;tình nhân. Câu chuyện tình yêu của bạn Trịnh Thanh Huyền (một cộng tác viên thường xuyên của dịch vụ “người đóng thế’ và “café&nbsp;hẹn hò”) là một ví dụ thú vị. Vốn là sinh viên năm cuối ĐH Kinh tế quốc dân và chưa có người yêu, Huyền tham gia làm cộng tác viên cho Vinamost với mục đích&nbsp;rất “sinh viên”- kiếm thêm thu nhập. Nhưng cũng chính ở đây, Huyền có được nhiều thứ khác quan trọng hơn. Đó là các mối quan hệ&nbsp;thân tình với các doanh nhân, một người anh “kết nghĩa” và quan trọng nhất có lẽ vẫn là tìm được “nửa kia” của mình. Huyền kể,&nbsp;Cô và “nửa kia” của mình quen nhau trong một bữa tiệc của công ty, hẹn hò nhau bằng một hợp đồng và yêu nhau sau nhiều lần&nbsp;“hẹn hò” ngoài hợp đồng khác. Và như thế “tình giả” đã ươm mầm cho tình yêu đích thực của đôi bạn trẻ. Đó chỉ là một trong nhiều câu chuyện của các đôi tình nhân được làm mối và se duyên bởi dịch vụ “điên rồ”này. Theo lời anh Nguyễn Chiến Thắng, rất nhiều người đã tìm được bạn đời của mình thông qua một dịch vụ khác của công ty.</p>\r\n\r\n<p>Tín Thành là công ty đầu tiên ở Việt Nam cung cấp dịch vụ cho thuê bạn trai, bạn gái, trợ lý, thư ký… Cộng tác&nbsp;viên cho dịch vụ cho thuê người yêu phải đạt các tiêu chuẩn: Nam cao từ 1,7m đến 1,9m, nặng 55kg đến 85kg;&nbsp;Nữ cao từ 1,60m đến 1,85m; Là sinh viên của một trường trung cấp, cao đẳng, đại học nào đó. Khách có nhu cầu thuê phải ký hợp đồng với những điều khoản ràng buộc rạch ròi như: không nắm tay hoặc&nbsp;khoác vai “đối tác”, không có những lời mời khiếm nhã ... Hợp đồng còn ghi rõ địa điểm, thời gian khách hàng và&nbsp;“đối tác” gặp gỡ để công ty tiện giám sát. Nếu khách hàng vi phạm những điều khoản (thân mật quá trớn, có lời&nbsp;mời khiếm nhã...) thì “đối tác” có quyền bỏ về, công ty không thanh toán lại tiền. Với dịch vụ cho thuê người&nbsp;yêu, Noel năm nay, Tín Thành giảm 20% giá cho khách hàng bình thường, giảm 30% giá cho sinh viên.</p>\r\n','dich-vu-cho-thue-nguoi-yeu-dien-ro-nhung-dat-khach',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','447_1439609058_51755ceb0e21afd8.jpg',[now], [now],[user_id],0,'','',[now]);set @news418 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories230,'Chuyện với Ca sĩ tên tuổi hát đám cưới','Tự hào là đơn vị hàng đầu trong lĩnh vực tổ chức sự kiện, biểu diễn nghệ thuật, phát triển thương hiệu, quảng cáo,...','<p>Vào dịp lễ tình nhân 2011, Hồ Ngọc Hà cùng nhóm V.music đã kết hợp cùng nhau trong MV \"Ngày hạnh phúc\" đầy ý nghĩa với những cặp đôi đang yêu. Ca khúc là một sáng tác của nhạc sĩ Nguyễn Hồng Thuận, nói về những tâm sự, cảm xúc của đôi tình nhân trong ngày lễ trọng đại.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/web/cuoi-gia-1.png\" /></p>\r\n\r\n<p>Ngoài vẻ đẹp \"nao lòng\" của Hồ Ngọc Hà trong trang phục cô dâu, MV còn thu hút khán giả bởi những cảnh đám cưới lãng mạn, dễ thương của dàn hot boy - hot girl xinh đẹp. Còn với nhóm V.Music, đây cũng là một trong những ca khúc thành công nhất của họ trong những ngày tháng còn hoạt động.</p>\r\n\r\n<p>\"Ngày hạnh phúc\" MV - Hồ Ngọc Hà ft. V.Music</p>\r\n\r\n<p>Nắm lấy tay anh - Tuấn Hưng</p>\r\n\r\n<p>Trong số những bản hit từng làm nên tên tuổi của Tuấn Hưng, \"Nắm lấy tay anh\" là một ca khúc đặc biệt khi đánh dấu cho cuộc hôn nhân hạnh phúc của anh và vợ. Được sáng tác bởi người bạn thân Tú Dưa, nội dung ca khúc và MV \"Nắm lấy tay anh\" như một lời cầu hôn đầy ngọt ngào của Tuấn Hưng dành cho cô vợ 9x, với hi vọng cả hai sẽ cùng chung sống và yêu thương nhau đến cuối đời.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/web/cuoi-gia-2.png\" /></p>\r\n\r\n<p>Ngay trong những ngày đầu ra mắt, bản Pop ngọt ngào, lãng mạn đã chiếm trọn cảm tình của người hâm mộ, giúp Tuấn Hưng thu về các giải thưởng và hàng chục triệu lượt xem trên kênh Youtube. Đây xứng đáng là một trong những bản tình ca đẹp nhất Vpop trong năm 2014.</p>\r\n\r\n<p>\"Nắm lấy tay anh\" MV - Tuấn Hưng</p>\r\n\r\n<p>Mơ một hạnh phúc - Đông Nhi &amp; Ông Cao Thắng</p>\r\n\r\n<p>Là cặp đôi đẹp nhất nhì showbiz Việt, Đông Nhi và Ông Cao Thắng khiến fan hâm mộ xúc động khi cùng nhau thể hiện ca khúc \"Mơ một hạnh phúc\", đồng thời dàn dựng một đám cưới đẹp như mơ khi ghi hình MV cho chương trình \"Gala nhạc Việt - Những giấc mơ trở về\". Trong tà áo cưới lộng lẫy, Đông Nhi trở nên dịu dàng, đằm thắm hơn so với phong cách gợi cảm thường thấy, sánh đôi cùng \"chú rể\" Ông Cao Thắng cực kì bảnh bao, cả hai mang đến cho người xem không khí của một lễ cưới đầy hạnh phúc.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/web/cuoi-gia-3.png\" /></p>\r\n\r\n<p>\"Mơ một hạnh phúc\" từng được ca sĩ Hiền Thục thể hiện thành công trước đó, tuy nhiên, khi cover lại ca khúc này, Đông Nhi và Ông Cao Thắng đã mang đến cho bản ballad một tinh thần, cảm xúc hoàn toàn mới, khiến người nghe cảm thấy nhẹ nhàng, ấm áp thay vì cô đơn, buồn bã.</p>\r\n\r\n<p>Happy Ending - Thủy Tiên</p>\r\n\r\n<p>Trước ngày cưới chính thức, \"cặp đôi vàng\" của showbiz Việt Thủy Tiên - Công Vinh tung MV \"Happy Ending\" như một món quà của cả hai gửi đến khán giả.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/web/cuoi-gia-4.png\" /></p>\r\n\r\n<p>Trong MV này, bên cạnh vẻ gợi cảm, xinh đẹp của Thủy Tiên, các fan còn thích thú khi lần đầu thấy Công Vinh trổ tài vũ đạo khá dễ thương. Với sản phẩm đầy ý nghĩa, Thủy Tiên đã quyết định chi \"mạnh tay\" cho phần MV với bối cảnh được dàn dựng y như một đám cưới thật. Không chỉ vậy, MV còn quy tụ hơn 100 diễn viên quần chúng trong trang phục trắng, cùng 300 bóng đèn và dải lụa giúp cho MV thêm phần \"lung linh\". Với mức đầu tư \"khủng\", cùng sự quan tâm của mọi người dành cho cặp \"Vic - Beck Việt\", \"Happy Ending\" xứng đáng là MV cưới hoành tráng nhất của Vpop.</p>\r\n\r\n<p>Cùng sánh đôi - Hồ Vĩnh Khoa</p>\r\n\r\n<p>Tuy không có quá nhiều hoạt động trong âm nhạc, nhưng \"hot boy nổi loạn\" Hồ Vĩnh Khoa vẫn luôn dành sự đầu tư kĩ lưỡng, chỉn chu cho từng sản phẩm của mình mỗi khi ra mắt khán giả. MV \"Cùng sánh đôi\" được nam ca sĩ tung ra vào mùa cưới năm 2014, nhanh chóng nhận được nhiều phản hồi tốt nhờ phần hình ảnh được thực hiện mới lạ, cùng cảnh quay đám cưới dễ thương của anh và diễn viên Ninh Dương Lan Ngọc.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/web/cuoi-gia-5.png\" /></p>\r\n\r\n<p>Không chọn một ca khúc cưới lãng mạn, ngọt ngào, \"Cùng sánh đôi\" là bản Pop-dance có tiết tấu vui tươi, tạo nên không khí trẻ trung, sôi động. Để thực hiện cảnh cảnh đám cưới \"đẹp như mơ\", Hồ Vĩnh Khoa và dàn diễn viên đã phải nhảy múa, cười nói trong nhiều giờ liên tục, ngoài ra, anh phải sử dụng hơn 100 bóng đèn để trang trí cho phần hậu cảnh, giúp hình ảnh của MV thêm phần lung linh, bắt mắt.</p>\r\n','chuyen-voi-ca-si-ten-tuoi-hat-dam-cuoi',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','170_1439608919_29155ceb05750305.png',[now], [now],[user_id],0,'','',[now]);set @news417 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories228,'CLB doanh nhân độc thân ngay tại Việt Nam','Một công ty với nhiều dịch vụ mới lạ: cho thuê người yêu, thuê chú rể, tổ chức sự kiện sock ... gần đây mới mở CLB doanh nhân độc thân với mục đích tìm vợ cho các sếp bận rộn.','<p><strong><span style=\"font-size:14px;\">Một công ty với nhiều dịch vụ mới lạ: cho thuê người yêu, thuê chú rể, tổ chức sự kiện sock ... gần đây mới mở CLB doanh nhân độc thân với mục đích tìm vợ cho các sếp bận&nbsp;rộn.</span></strong></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://tinthanhevents.com.vn/apt-upload/image/data/user/2015-05-08-115154.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:14px;\">Dịch vụ “tìm vợ cho doanh nhân” đang được Công ty Tín Thành ở Hà Nội triển khai và&nbsp;dự kiến khai trương trong tháng 5 tới. Phó tổng giám đốc Công ty Tín Thành – Ông&nbsp;Nguyễn Chiến Thắng cho biết ý tưởng kinh doanh dịch vụ tìm vợ cho sếp xuất phát từ&nbsp;thực tế có quá nhiều người do mải mê với công việc mà \"quên mất\" việc lấy vợ. Họ mải&nbsp;mê với các chuyến công tác, họp hành, nên có quá ít thời gian để tìm hiểu, hoặc dành cho&nbsp;các buổi hẹn hò.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">“Do đó, chúng tôi muốn mở ra một câu lạc bộ dành riêng cho những người cô đơn và&nbsp;đang có nhu cầu tìm một nửa. Tại đây, họ có thể gặp gỡ giao lưu với những cô gái còn&nbsp;khá trẻ, xinh xắn có học thức và là khách mời của Tín Thành để có thể xây dựng một mối&nbsp;quan hệ nghiêm túc”, Ông Thắng cho biết mỗi doanh nhân tham gia sẽ phải đóng một mức phí ban đầu đó là Phí duy trì Thành viên câu lạc bộ. Khi đã là thành viên của&nbsp;câu lạc, họ&nbsp;có thể tham gia các chương trình giao lưu,&nbsp;gặp gỡ với những khách mời là người nổi tiếng, ca sĩ, hoa khôi của các trường đại học,&nbsp;cao đẳng.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ngoài ra, tối thiểu mỗi tháng một lần, câu lạc bộ có các buổi sinh hoạt theo chủ đề để các&nbsp;nam doanh nhân giao lưu với những sinh viên trẻ của các trường đại học, cao đẳng hay&nbsp;tiếp viên hàng không, những nữ nhân viên trẻ chưa lập gia đình. Qua các buổi tiếp xúc,&nbsp;trao đổi, các sếp trẻ có thể tìm hiểu và lựa chọn cho mình những người phù hợp với lứa&nbsp;tuổi, tính cách … để tiến tới hôn nhân.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">“Trên thế giới đã có nhiều dịch vụ như vậy và họ hoạt động rất công khai, lành mạnh. Tín&nbsp;Thành cũng muốn đem ý tưởng này thực hiện tại Việt Nam và chúng tôi biết rất nhiều&nbsp;người đang mải mê với sự nghiệp mà quên mất rằng đã đến lúc họ cần một mái ấm, cần&nbsp;phải tìm cho mình nửa còn lại”, Ông Thắng nói.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Hiện Tín Thành đã tuyển được khoảng 600 cộng tác viên là nữ sinh viên các trường đại&nbsp;học, cao đẳng. Trong đó có 400 cộng tác viên thường xuyên ở Hà Nội, còn lại là tại TP HCM. Trước mắt, công ty sẽ triển khai dịch vụ ở Hà Nội, sau đó sẽ mở rộng vào khu vực phía&nbsp;Nam. “Chúng tôi sẽ xây dựng website gồm cả tiếng Việt và tiếng Anh. Dịch vụ của&nbsp;chúng tôi không chỉ dành riêng cho các doanh nhân trong nước mà còn muốn người nước&nbsp;ngoài - những người làm việc tại Việt Nam và muốn lấy vợ là người Việt”, Ông Thắng cho&nbsp;biết thêm.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Hiện đã có&nbsp;hơn 300&nbsp;khách hàng là chủ doanh nghiệp đăng ký tham gia Câu lạc bộ \"Tìm bạn&nbsp;cho doanh nhân\".</span></p>\r\n','clb-doanh-nhan-doc-than-ngay-tai-viet-nam',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','826_1439608199_7655cead87a2fcd.jpg',[now], [now],[user_id],0,'','',[now]);set @news416 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories279,'7 Bí quyết cần chú ý khi tổ chức sự kiện','Thử tưởng tượng công việc tổ chức sự kiện như trò chơi ghép hình, muốn có một bức tranh đẹp, chúng ta cần phải xác định được vị trí chính xác của các mảnh ghép, bức tranh càng lớn chúng ta cần càng nhiều mảnh ghép, cần càng nhiều sự chính xác, tỉ mỉ, tập trung cao độ để có thể đặt từng mảnh ghép vào vị trí duy nhất mà nó phải được đặt','<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">Thử tưởng tượng công việc tổ chức sự kiện như trò chơi ghép hình, muốn có một bức tranh đẹp, chúng ta cần phải xác định được vị trí chính xác của các mảnh ghép, bức tranh càng lớn chúng ta cần càng nhiều mảnh ghép, cần càng nhiều sự chính xác, tỉ mỉ, tập trung cao độ để có thể đặt từng mảnh ghép vào vị trí duy nhất mà nó phải được đặt. Vậy thì người tổ chức sự kiện sẽ là một nghệ nhân, họ cũng sẽ phải tạo ra một tác phẩm, điểm khác nhau duy nhất là tác phẩm đó là sự kết hợp hoàn hảo của nhiều người, nhiều công cụ, nhiều yếu tố, nhiều rủi ro và áp lực của người tổ chức sự kiện trước, trong và sau sự kiện đều ở mức cao, đặc biệt là trong sự kiện, mọi sự cố đều có thể ập đến bất cứ lúc nào.Người chịu trách nhiệm quản lí phải luôn sẵn sàng tinh thần và dự trù các giải pháp để giải quyết những tình huống bất ngờ nhất. Nếu không làm tốt công tác đó, một sự kiện hoàn toàn có thể trở thành thảm họa và phản tác dụng ngay lập tức.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">&nbsp;<img alt=\"7 Bí quyết cần chú ý khi tổ chức sự kiện \" height=\"321\" src=\"http://www.topevent.vn/upload/7-Bi-quyet-can-chu-y-khi-to-chuc-su-kien1.jpg\" width=\"500\" /></p>\r\n\r\n<h2 style=\"font-size: 15px; padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;\"><em><strong><span style=\"font-size: 12px;\">7 Bí quyết cần chú ý giúp bạn thành công khi tổ chức sự kiện</span></strong></em></h2>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">Một số điều sau đây sẽ giúp bạn thành công trong công tác Tổ chức sự kiện :</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;1.&nbsp;<strong>Xác định đúng đối tượng khách hàng mục tiêu</strong>. Mức độ thành công của một sự kiện được đánh giá thông qua số lượng và giá trị của những khách hàng mà sự kiện đó thu hút được, kể cả những khách hàng tiềm năng.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">VD : Thử tượng tượng một sự kiện về công nghệ thông tin lại toàn những quý bà, quý cô đang muốn tìm nơi mua sắm, bạn sẽ thấy kì cục đến mức nào? Phải biết khách hàng của bạn là ai để tạo ra một chủ đề phù hợp và xuyên suốt sự kiện.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;2.&nbsp;<strong>Tạo ý nghĩa cho sự kiện</strong>. Đừng vội vàng tung ngay sản phẩm hay dịch vụ. Hãy từ tốn hướng dẫn họ, cung cấp mọi thông tin cần thiết thực sự có ý nghĩa và giá trị trước khi thực hiện hoạt động bán hàng.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;3.&nbsp;<strong>Sự kiện phải gây được ấn tượng</strong>. Trong điều kiện ngân sách cho phép, đừng ngại tạo sự độc đáo, thu hút cho sự kiện. Tầm ảnh hưởng của sự kiện càng rộng, càng có ích cho doanh nghiệp bạn.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;4.&nbsp;<strong>Sử dụng một công ty tổ chức sự kiện chuyên nghiệp</strong>. Vẫn biết tự các doanh nghiệp cũng có thể tự lực tổ chức, tuy nhiên, nếu thấy không ổn về vấn đề nào đó, hoặc không đo lường được hiệu quả truyền thông của sự kiện, hãy nhờ đến một công ty uy tín để có thể quản lí qui trình của sự kiện và có khả năng lặp lại bao nhiêu lần chào hàng sản phấm đến khách hàng một cách có hiệu quả.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"7 Bí quyết cần chú ý khi tổ chức sự kiện 2\" height=\"328\" src=\"http://www.topevent.vn/upload/7-Bi-quyet-can-chu-y-khi-to-chuc-su-kien2.jpg\" width=\"500\" /></p>\r\n\r\n<h3 style=\"font-size: 15px; padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;\"><span style=\"font-size: 12px;\">Cần chú ý đến những điểm quan trọng khi tổ chức sự kiện</span></h3>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;5.&nbsp;<strong>Quảng bá cho sự kiện</strong>. Doanh nghiệp bạn đầu tư chi phí khủng, thuê địa điểm thuộc hạng sang, lên kế hoạch chương trình vô cùng chi tiết, trang hoàng nơi tổ chức sự kiện thật lộng lẫy nhưng không ai biết đến, điều này thật khủng khiếp. Luôn ghi nhớ là sự nhận diện cao, đúng lúc, đúng nơi, đúng đối tượng, sự kiện càng thành công và đạt được mục tiêu ban đầu bạn đề ra.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;6.&nbsp;<strong>Đừng quên liên tục tạo lập các mối quan hệ xung quanh</strong>. Mục đích chính của tổ chức sự kiện là bạn có thể tạo mối quan hệ với những người được mời một cách trực tiếp. Hãy để cho họ cảm thấy được chào đón và trân trọng và khả năng họ trở thành những khách hàng tiềm năng trong tương lai là rất cao.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">&nbsp;7.&nbsp;<strong>Giữ gìn mối quan hệ tốt đẹp sau sự kiện</strong>. Một email cảm ơn sự có mặt của khách mời có lẽ là sự lựa chọn đúng đắn trong trường hợp này. Đồng thời hãy nhớ hỏi ý kiến của họ về sự kiện, ý kiến của họ sẽ là những lời khuyên hữu ích cho kinh nghiệm tổ chức sự kiện của bạn.</p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">Công ty Tổ chức sự kiện Thành Đạt – dịch vụ tổ chức sự kiện hàng đầu Việt Nam<br />\r\nKhi bạn cần, đừng ngại ngần =&gt; Hãy nhấc điện thoại và alô ngay cho Tổ chức sự kiện Thành Đạt để được hỗ trợ, tư vấn tận tình và thực sự cảm nhận sự Tận tâm và Dịch vụ tổ chức sự kiện số 1 của Thành Đạt</p>\r\n','7-bi-quyet-can-chu-y-khi-to-chuc-su-kien',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','236_1439548954_67455cdc61ac9150.jpg',[now], [now],[user_id],0,'','',[now]);set @news415 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories279,'Kinh nghiệm và lưu ý khi làm việc với PG và khách hàng trong tổ chức sự kiện','Cung cấp nhân sự - PG - PB khi làm việc với khách hàng cũng thường xảy ra nhiều sự cố không mong muốn. Để đảm bảo công việc suôn sẻ và lấy được niềm tin của khách hàng người tổ chức sự kiện nên lưu ý và làm việc có kế hoạch.<br />\r\n','<p>Cùng nhau điểm qua một số vấn đề cần lưu ý khi làm việc với các bạn PG - PB</p>\r\n\r\n<p>Điều đầu tiên: Người tổ chức sự kiện cần luôn có một danh sách PG - PB sẵn sàng cho công việc. Nguồn nhân lực này là các bạn trẻ sinh viên năng động, trẻ trung vì vậy Người tổ chức sự kiện sẽ dễ dàng có được danh sách nhân sự luôn có sẵn và được cập nhật thường xuyên. Với xu hướng của giới trẻ ngày nay (chụp ảnh, lưu giữ, chia sẻ) và sự phát triển của internet (mạng xã hội, Blog, forum,...) bạn sẽ không phải khó khăn hay tốn công sức mới có được profile của các bạn PG - PB.</p>\r\n\r\n<p>Khi bạn có được profile, hãy tự tay làm thành một bộ Profile nhân sự cho chính Cty bạn trong việc lưu trữ đồng thời cũng là một&nbsp; công cụ hữu ích giúp bạn tiết kiệm thời gian với đối tác.</p>\r\n\r\n<p>Khi làm việc với đối tác nếu không có sẵn Profile nhân sự, bạn sẽ khó trình bày thuyết phục và nếu khách hàng yêu cầu gặp mặt để tuyển chọn (đây là yêu cầu thường xuyên và hầu như đều có xuất hiện khi chuẩn bị cho một sự kiện cần dùng đến PG - PB). Việc bạn có sẵn bộ Profile nhân sự thiết kế theo phong cách riêng của Cty mình sẽ là một điểm cộng cho bạn trong mắt khách hàng. Hơn nữa, việc này giúp bạn và khách hàng tiết kiệm được rất nhiều thời gian để đạt được kết quả cuối cùng trong việc chọn người.&nbsp;</p>\r\n\r\n<p>Training nhân sự (nếu có): những sự kiện đặc biệt và cần phải có sự training công việc trước khi làm, bạn sẽ phải sắp xếp cho PG - PB làm việc với khách hàng, bạn cần lưu ý một số điểm sau:</p>\r\n\r\n<p>Thời gian: Tập trung các bạn PG - PB luôn sớm hơn giờ hẹn với khách hàng từ 30 - 40p để tránh tình trạng khách hàng phải chờ đợi, khó chịu ảnh hưởng đến hình ảnh công ty.<br />\r\nKhi khách hàng hướng dẫn công việc và các yêu cầu cho PG - PB bạn phải có mặt ở đó, nắm được yêu cầu của khách hàng đối với các bạn PG - PB vì người tổ chức sự kiện sẽ phải theo sát các hoạt động của PG - PB đảm bảo hoạt động theo đúng yêu cầu của khách hàng. Tránh trường hợp khi có chuyện ngoài ý muốn xảy ra: khách hàng đổ lỗi cho PG - PB, còn PG-PB lại đổ lỗi do khách hàng không nói yêu cầu đó từ đầu, vì vậy làm việc 03 bên luôn là cách tốt nhất để đảm bảo bạn sẽ không rơi vào tình trạng khó xử.<br />\r\n&nbsp;Làm việc với PG - PB: Quy định về thời gian làm việc, xử phạt ra sao, khen thưởng thế nào một cách rõ ràng cho các bạn trước khi làm việc - tránh tình trạng đi làm trễ giờ, nghỉ không xin phép........</p>\r\n','kinh-nghiem-va-luu-y-khi-lam-viec-voi-pg-va-khach-hang-trong-to-chuc-su-kien',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','47_1439460977_3955cc6e7114b6c.jpg',[now], [now],[user_id],0,'','',[now]);set @news387 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories230,'Quảng cáo Internet CPM: Giải pháp tối ưu cho DN','Dựa trên nền tảng công nghệ phát triển, ngày càng có nhiều hình thức quảng cáo trực tuyến mới được ra đời với nhiều tính năng và ưu điểm nổi bật. Hình thức quảng cáo CPM, giải pháp tối ưu và xu hướng tất yếu cho quảng cáo trực tuyến tại Việt Nam.','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','quang-cao-internet-cpm-giai-phap-toi-uu-cho-dn',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','811_1439459849_61055cc6a09018fb.jpg',[now], [now],[user_id],0,'','',[now]);set @news386 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories230,'Premier Village Đà Nẵng resort- Tuyệt tác bên bờ Biển Đông','Tọa lạc ngay trên bãi biển được Tạp chí Forbes bầu chọn là một trong những bãi biển đẹp nhất hành tinh, Premier Village Đà Nẵng Resort quản lý bởi Accor mang tới không gian nghỉ dưỡng hoàn hảo không chỉ bởi sự sang trọng mà còn cả sự riêng tư đầm ấm của mỗi gia đình. ','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','premier-village-da-nang-resort--tuyet-tac-ben-bo-bien-dong',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','336_1439458352_21155cc643032fa3.jpg',[now], [now],[user_id],0,'','',[now]);set @news385 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories230,'\"Búp sen hồng\" khám phá Bà Nà Hills','Hơn 1.300 đại biểu của Liên hoan Búp Sen Hồng lần thứ 21 vừa có chuyến tham quan thú vị tại Bà Nà Hills. ','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','bup-sen-hong-kham-pha-ba-na-hills',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','801_1439458092_5355cc632cad50d.jpg',[now], [now],[user_id],0,'','',[now]);set @news384 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories230,'Cơ hội lớn cho hàng Việt chinh phục thị trường Nga','Tham gia Hội chợ-Bán hàng “Hàng Việt Nam chất lượng cao Moscow 2015”, là cơ hội, thời cơ thuận lợi nhất cho các doanh nghiệp (DN) Việt Nam đẩy mạnh xuất khẩu và thâm nhập sâu vào thị trường Nga.','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','co-hoi-lon-cho-hang-viet-chinh-phuc-thi-truong-nga',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','227_1439457448_87455cc60a8ab7cb.jpg',[now], [now],[user_id],0,'','',[now]);set @news383 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories229,'Triển lãm hàng Thái, sức nóng chưa hề giảm với người tiêu dùng Việt','Tại triển lãm hàng Thái Lan lầm thứ 15 ở Hà Nội, thay vì chỉ đến ngắm cho biết, hầu hết khách hàng đều ra về với một túi hàng trên tay, nhiều người chi cả triệu để mua đủ thứ sản phẩm từ các đồ gia dụng, bột giặt,... cho đến thực phẩm, mỹ phẩm, đồ trang sức.','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','trien-lam-hang-thai-suc-nong-chua-he-giam-voi-nguoi-tieu-dung-viet',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','215_1439457202_18755cc5fb2679e5.jpg',[now], [now],[user_id],0,'','',[now]);set @news382 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories229,'Triển lãm quốc tế Thực phẩm & Đồ uống lần thứ 19','Triển lãm Quốc tế Thực phẩm và Đồ uống Việt Nam  được tổ chức thường niên, và trở thành một hoạt động xúc tiến thương  mại quan trọng của ngành đồ uống Việt Nam và của khu vực. Được sự chỉ đạo và hỗ trợ của Ủy ban Nhân dân TP Hồ Chí Minh, Sở Công thương TP Hồ Chí Minh, Hội Lương thực thực phẩm TP Hồ Chí Minh phối hợp cùng đơn vị thực hiện là Công ty CP Quảng cáo và Hội chợ Thương mại Vinexad, Hiệp hội Bia Rượu Nước Giải Khát Việt Nam tổ chức Triển lãm Quốc tế về Thực phẩm và Đồ uống và Triển lãm','<p><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/2014_TP_02.jpg\" style=\"width: 600px; height: 395px;\" /></p>\r\n\r\n<div align=\"justify\" style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\">ượng, Triển lãm đã và đang nhận được sự đánh giá cao của Thành phố, các Sở Ban ngành, các doanh nghiệp và khách tham quan,… đảm bảo mục tiêu mở rộng kinh doanh, phát triển thị trường, đẩy mạnh xuất khẩu đối với các công ty trong nước và tìm kiếm đại lý phân phối tại Việt Nam đối với các công ty nước ngoài.</font></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\">&nbsp;</div>\r\n\r\n<div align=\"justify\" style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\">&nbsp;&nbsp;&nbsp;&nbsp;</font></span><br />\r\n<span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\">&nbsp;Đặc biệt trong năm 2014, Triển lãm có sự tham gia của 10 công ty đến từ Đài Loan trưng bày và giới thiệu các sản phẩm đặc trưng của Đài Loan như bánh truyền thống, Gà Đen Truyền thống Đài Loan, Bánh bích qui, nước tương….Bên cạnh đó là các công ty cung cấp nguyên liệu, phụ gia sử dụng trong chế biến thực phẩm như Công ty Miyako ( Nhật Bản); Công ty Tianjin Chufa, Công ty Jining, Công ty Micro Tech (Trung Quốc)….<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; Bên cạnh lĩnh vực Thực phẩm chế biến và Đồ uống, triển lãm năm nay còn thu hút sự tham gia của nhiều công ty trưng bày và giới thiệu các thiết bị công nghệ tiên tiến nhất trong lĩnh vực chế biến, bao bì đóng gói, nhãn mác thực phẩm và đồ uống. Trong đó, phải kể đến sự tham gia của các hãng nổi tiếng như: Nishihara, Topack, Toyo Jidoky – Nhật Bản ( Phân phối là công ty Á Châu); Máy đóng gói Thực phẩm của công ty Thương mại Kiên Cường; Hãng Geoge Food (phân phối là công ty Kim Điền Phong); &nbsp;Máy đóng nắp chai, lon của công ty Yeudong (Trung Quốc), máy chế biến thịt, máy cắt, máy nghiền của hãng Shenyang Jixiang (Trung Quốc)…<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; Triển lãm Quốc tế Thực phẩm và Đồ uống luôn nhận được sự hỗ trợ của các bộ ngành cơ quan hữu quan cho các doanh nghiệp trong nước và quốc tế, tăng cường hợp tác, xúc tiến thương mại, trao đổi, tiếp cận với sản phẩm và công nghệ của thế giới, đồng thời phát triển các thương hiệu trong nước, góp phần cho sự phát triển toàn diện về lượng và chất của ngành chế biến thực phẩm, đồ uống, bao bì đóng gói của Việt Nam, cũng như đẩy mạnh xuất khẩu.</font></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal; text-align: center;\"><span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/2014_TP_01.jpg\" style=\"width: 600px; height: 395px;\" /></font></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal; text-align: justify;\">\r\n<div align=\"justify\" style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\">&nbsp;Đặc biệt trong năm 2014, Triển lãm có sự tham gia của 10 công ty đến từ Đài Loan trưng bày và giới thiệu các sản phẩm đặc trưng của Đài Loan như bánh truyền thống, Gà Đen Truyền thống Đài Loan, Bánh bích qui, nước tương….Bên cạnh đó là các công ty cung cấp nguyên liệu, phụ gia sử dụng trong chế biến thực phẩm như Công ty Miyako ( Nhật Bản); Công ty Tianjin Chufa, Công ty Jining, Công ty Micro Tech (Trung Quốc)….</font></span></div>\r\n\r\n<div align=\"justify\" style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\">&nbsp;</div>\r\n\r\n<div align=\"justify\" style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\">&nbsp;&nbsp;&nbsp;&nbsp; Bên cạnh lĩnh vực Thực phẩm chế biến và Đồ uống, triển lãm năm nay còn thu hút sự tham gia của nhiều công ty trưng bày và giới thiệu các thiết bị công nghệ tiên tiến nhất trong lĩnh vực chế biến, bao bì đóng gói, nhãn mác thực phẩm và đồ uống. Trong đó, phải kể đến sự tham gia của các hãng nổi tiếng như: Nishihara, Topack, Toyo Jidoky – Nhật Bản ( Phân phối là công ty Á Châu); Máy đóng gói Thực phẩm của công ty Thương mại Kiên Cường; Hãng Geoge Food (phân phối là công ty Kim Điền Phong); &nbsp;Máy đóng nắp chai, lon của công ty Yeudong (Trung Quốc), máy chế biến thịt, máy cắt, máy nghiền của hãng Shenyang Jixiang (Trung Quốc)…</font></span></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\">&nbsp;</div>\r\n\r\n<div align=\"justify\" style=\"margin: 0px; padding: 0px; outline: none; color: rgb(68, 68, 68); font-family: tahoma; font-size: 12px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; outline: none; font-family: Tahoma;\"><font size=\"2\" style=\"margin: 0px; padding: 0px; outline: none;\">&nbsp;&nbsp;&nbsp;&nbsp; Triển lãm Quốc tế Thực phẩm và Đồ uống luôn nhận được sự hỗ trợ của các bộ ngành cơ quan hữu quan cho các doanh nghiệp trong nước và quốc tế, tăng cường hợp tác, xúc tiến thương mại, trao đổi, tiếp cận với sản phẩm và công nghệ của thế giới, đồng thời phát triển các thương hiệu trong nước, góp phần cho sự phát triển toàn diện về lượng và chất của ngành chế biến thực phẩm, đồ uống, bao bì đóng gói của Việt Nam, cũng như đẩy mạnh xuất khẩu.</font></span></div>\r\n</div>\r\n','trien-lam-quoc-te-thuc-pham--do-uong-lan-thu-19',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','245_1439456777_50055cc5e09be3c9.jpg',[now], [now],[user_id],0,'','',[now]);set @news381 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories229,'Triển lãm Giao dịch Thương mại Thái Lan tại TP.HCM','Tuần lễ Thái Lan – Triển lãm Giao dịch Thương mại Thái Lan, một sự kiện hàng năm nằm trong Chương trình Xúc tiến Thương mại được tổ chức tại Hà Nội và Thành phố Hồ Chí Minh bởi Cục Xúc tiến Thương Mại, Bộ Thương  mại Thái Lan – Văn phòng thương vụ Thái. Sự kiện này là một trong những hoạt động giao lưu hợp tác giữa Thái Lan và Việt Nam nhằm củng cố mối quan hệ, mở rộng thị trường kinh doanh và xúc tiến thương mại đầu tư bao gồm thiết lập mạng lưới kinh doanh giữa hai quốc gia.','<p style=\"text-align: center;\"><br />\r\nTuần lễ Thái Lan – Triển lãm Giao dịch Thương mại Thái Lan, một sự kiện hàng năm nằm trong Chương trình Xúc tiến Thương mại được tổ chức tại Hà Nội và Thành phố Hồ Chí Minh bởi Cục Xúc tiến Thương Mại, Bộ Thương &nbsp;mại Thái Lan – Văn phòng thương vụ Thái. Sự kiện này là một trong những hoạt động giao lưu hợp tác giữa Thái Lan và Việt Nam nhằm củng cố mối quan hệ, mở rộng thị trường kinh doanh và xúc tiến thương mại đầu tư bao gồm thiết lập mạng lưới kinh doanh giữa hai quốc gia.<br />\r\nNăm nay, tại thành phố Hồ Chí Minh, Cục Xúc tiến Thương Mại, Bộ Thương &nbsp;mại Thái Lan – Văn phòng thương vụ Thái tổ chức “Tuần lễ Thái Lan 2015” từ ngày 8 đến ngày 12 tháng 7 năm 2015, với hai hoạt động chính:<br />\r\n- 08/07/2015 (9h00-12h00)<br />\r\nChương trình kết nối giao thương tại khách sạn Rex, 141 Nguyễn Huệ, Q.1, TP. Hồ Chí Minh<br />\r\n- 09-12/07/2015 (9h00-22h00)<br />\r\nTuần lễ Thái Lan 2015 tại Trung tâm Triển lãm &amp; Hội nghị Tân Bình (TBECC)<br />\r\nVới &nbsp;300 công ty (Nhà sản xuất Thái Lan, nhà cung cấp, nhà xuất khẩu và các đại lý, các nhà nhập khẩu và phân phối Việt Nam) và quy mô 350 gian hàng tham gia Tuần lễ Thái Lan 2015. Khách tham quan sẽ được tiếp cận với rất nhiều sản phẩm Thái Lan từ các thương hiệu uy tín chất lượng cao như: hàng tiêu dùng (thực phẩm, trái cây và đồ uống); mỹ phẩm, spa, chăm sóc sức khỏe và sắc đẹp; linh phụ kiện ô tô; sản phẩm điện/điện tử; đồ dùng trong nhà và đồ trang trí (nội thất, quà tặng, sản phẩm trang trí, dệt may, da giày, dụng cụ nhà bếp, văn phòng phẩm và thiết bị văn phòng); nhà hàng, giáo dục, du lịch.<br />\r\nĐồng thời, chúng tôi còn &nbsp;tổ chức các hoạt động văn hóa văn nghệ như: trưng bày ẩm thực Thái, các màn trình diễn ẩm thực, thời trang và bốc thăm may mắn.<br />\r\nNhân dịp này, chúng tôi xin chân thành cảm ơn tất cả các phóng viên, nhà báo đã tích cực hỗ trợ góp phần đăng tải các thông tin về Tuần lễ Thái Lan – Triển lãm giao dịch Thương mại Thái Lan tại TP Hồ Chí Minh đến các doanh nghiệp và người dân tại TP. Hồ Chí Minh.&nbsp;<br />\r\n<img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/UploadFile_000000382.jpg\" style=\"width: 1280px; height: 853px;\" /></p>\r\n','trien-lam-giao-dich-thuong-mai-thai-lan-tai-tphcm',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','874_1439456513_55955cc5d016d83f.jpg',[now], [now],[user_id],0,'','',[now]);set @news380 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories229,'Đường hoa xuân Bạch Đằng - Đà Nẵng 2015',' Đường hoa xuân Bạch Đằng - Đà Nẵng 2015 với tổng đầu tư 5 tỷ đồng bao gồm 6 khu vực, trong đó có tiểu cảnh \'Alice ở xứ sở thần tiên\' dành riêng cho trẻ em.','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','duong-hoa-xuan-bach-dang---da-nang-2015',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','846_1439456085_53455cc5b556766a.jpg',[now], [now],[user_id],0,'','',[now]);set @news379 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories228,'Chương trình giao lưu nghệ thuật đặc biệt “Ngày hội non sông” - Mừng Đảng, mừng Xuân, mừng Hiến pháp','(TG) Chương trình “Ngày hội non sông” do Viện Nghiên cứu lập pháp thuộc Ủy ban Thường vụ Quốc hội chỉ đạo Tạp chí Nghiên cứu lập pháp phối hợp với Đài truyền hình Việt Nam, Công ty Sự kiện Truyền thông Việt Nam, Công ty Phú An và các  đơn vị liên quan tổ chức là hoạt động giao lưu - nghệ thuật đặc biệt để chào mừng thành công của kỳ họp thứ 6, Quốc hội khóa XIII đã thông qua bản Hiến pháp sửa đổi. Chương trình được truyền hình trực tiếp trên kênh VTV1 – Đài truyền hình Việt Nam từ Nhà hát lớn Hà','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','chuong-trinh-giao-luu-nghe-thuat-dac-biet-ngay-hoi-non-song---mung-dang-mung-xuan-mung-hien-phap',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','519_1439458910_10355cc665e722e9.jpg',[now], [now],[user_id],0,'','',[now]);set @news378 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories228,'Gay cấn đêm nhạc dân gian Sao Mai 2015','Sự đồng đều của các thí sinh một lần nữa đã gây khó khăn cho ban giám khảo trong đêm chung kết Sao Mai 2015 dòng dân gian khi phải chọn ra 4 trong 13 thí sinh dự đêm chung kết xếp hạng.<br />\r\n','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','gay-can-dem-nhac-dan-gian-sao-mai-2015',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','819_1439455338_17255cc586a39ec3.jpg',[now], [now],[user_id],0,'','',[now]);set @news377 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories228,'Chương trình nghệ thuật \"Linh thiêng Việt Nam\" lần thứ III năm 2015: Nghìn thu còn tỏa sáng','Nhân kỷ niệm 68 năm Ngày Thương binh - Liệt sĩ, vào lúc 20 giờ 30 tối 25/7, tại Nhà hát Lớn Hà Nội đã diễn ra Chương trình “Linh thiêng Việt Nam” lần III năm 2015. Đây là chương trình giao lưu nghệ thuật thường niên, để cùng cả nước tôn vinh và tri ân các liệt sĩ, gia đình có công, thương bệnh binh… những người đã anh dũng hy sinh, hiến dâng một phần xương máu của mình cho thống nhất non sông. Chương trình được phát trực tiếp trên kênh VTV2. Chương trình do Báo Lao động & Xã hội phối hợp cùng Cô','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%; width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; vertical-align: middle; max-width: 100%;\" /></span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 22px; text-align: justify;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<br style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\" />\r\n<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','chuong-trinh-nghe-thuat-linh-thieng-viet-nam-lan-thu-iii-nam-2015-nghin-thu-con-toa-sang',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','482_1439455163_35555cc57bb3ab72.jpg',[now], [now],[user_id],0,'','',[now]);set @news376 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories228,'FLC khởi công dự án tháp đôi 265 Cầu Giấy','Ngày 8/8, Tập đoàn FLC đã chính thức khởi công dự án tháp đôi FLC Twin Towers tại địa chỉ 265 Cầu Giấy. Đến dự lễ khởi công có ông Huỳnh Phong Tranh – Ủy viên BCH TW Đảng, Tổng Thanh tra Chính phủ; ông Tô Quang Thu – Phó Chủ nhiệm Ủy ban Kiểm tra Trung ương; ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Tp. Hà Nội, ông Nguyễn Văn Sửu, Phó Chủ tịch UBND Tp. Hà Nội cùng nhiều đồng chí lãnh đạo các cơ quan ban ngành ở Trung ương, thành phố Hà Nội, tỉnh Vĩnh Phúc, Thanh Hóa, các cơ quan thông tấn, báo chí','<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Buổi lễ cũng rất vinh dự đón nhận lẵng hoa chúc mừng của ông Phạm Quang Nghị, Ủy viên Bộ Chính trị, Bí thư Thành ủy Hà Nội; ông Ngô Văn Dụ, Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra TW Đảng; ông Uông Chu Lưu, Ủy viên BCH TW Đảng, Phó Chủ tịch Quốc hội, ông Hà Hùng Cường, Ủy viên BCH TW Đảng, Bộ trưởng Bộ Tư pháp.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1524.JPG\" style=\"width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Huỳnh Phong Tranh, Tổng Thanh tra Chính phủ và ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn FLC.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Khởi công dự án trong vòng chưa đầy 1 tháng kể từ khi trở thành chủ đầu tư, Tập đoàn FLC tiếp tục cho thấy phong cách làm việc thần tốc vốn đã làm nên thương hiệu nổi tiếng của mình.</span><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Phát biểu tại Lễ khởi công, ông Trịnh Văn Quyết, Chủ tịch HĐQT Tập đoàn đã trân trọng cảm ơn sự giúp đỡ quý báu của các cấp chính quyền thành phố Hà Nội và quận Cầu Giấy, yếu tố quan trọng giúp cho Tập đoàn có thể nhanh chóng bắt tay vào triển khai Dự án và mong rằng sẽ tiếp tục nhận được sự ủng hộ, tạo điều kiện trong thời gian tới để giúp Tập đoàn FLC có thể hoàn thành Dự án theo đúng tiến độ đề ra.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_9502.JPG\" style=\"width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Có hơn 1.400 khách mời là các đối tác, nhà đầu tư, khách hàng của Tập đoàn đã đến dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Cũng tại buổi lễ này, ông Quyết cho biết, với mục tiêu đem đến môi trường sống và làm việc thực sự đẳng cấp, dự án được thiết kế với đầy đủ các hạng mục dịch vụ, tiện ích sống như: Trung tâm thương mại, bệnh viện, trường học quốc tế, bể bơi bốn mùa, sân đỗ trực thăng…</span><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">“Sau khi hoàn thành, FLC Twin Towers sẽ là một trong 3 tòa tháp cao nhất của thủ đô Hà Nội, cung cấp cho thị trường 66.484 m2 sàn chung cư, 35.960 m2 sàn văn phòng và 25.000 m2 sàn thương mại. Và đây cũng sẽ là trụ sở làm việc mới của hơn 3.000 cán bộ, công nhân viên đang công tác tại Tập đoàn FLC. Thiết kế sáng tạo của các kiến trúc sư người Anh Quốc và Canada đã phủ xanh hơn 70% diện tích của Tháp đôi, với nhiều khu vườn sinh thái, không gian mở… tôi tin tưởng rằng, FLC Twin Towers sẽ không chỉ đem lại không gian sống và làm việc đẳng cấp, mà còn tạo nên một điểm nhấn kiến trúc nổi bật, làm đẹp cho khu vực Cầu Giấy nói riêng, Hà Nội nói chung”, ông Quyết nói.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Các đại biểu tham dự Lễ khởi công.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1666.JPG\" style=\"width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu tham dự Lễ khởi công.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Cùng quan điểm này, phát biểu tại buổi Lễ, ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội nói: “Với thiết kế hiện nay, Dự án FLC Twin Towers sẽ là một trong 3 tháp cao nhất Hà Nội. Tôi đề nghị chủ đầu tư cùng với các nhà thiết kế nỗ lực để FLC Twin Towers tạo ra một điểm nhấn cho khu vực phía Tây Hà Nội. Chủ đầu tư cũng cần tập trung nguồn lực về con người, tài chính để triển khai dự án vì với khối lượng công việc như trên, để hoàn thành trong thời gian 3 năm là không hề nhỏ. Nhân đây, tôi cũng giao nhiệm vụ cho các đồng chí lãnh đạo sở ban ngành và chính quyền địa phương cần tiếp tục hỗ trợ để chủ đầu tư hoàn thành đúng tiến độ thi công, đưa sản phẩm đến tay người tiêu dùng một cách thiết thực và hiệu quả nhất”.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Ngọc Tuấn, Phó Chủ tịch UBND Thành phố Hà Nội phát biểu tại buổi lễ.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1835.JPG\" style=\"width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; line-height: 21px; text-align: justify; font-size: 12px;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Ông Tuấn cũng nhấn mạnh thêm, việc Tập đoàn FLC trở thành chủ đầu tư và bắt tay ngay vào triển khai dự án không chỉ giúp làm hồi sinh bản thân dự án này, đáp ứng nhu cầu của thị trường, giải quyết yêu cầu đô thị hóa của Thủ đô mà còn mang nhiều ý nghĩa kinh tế - xã hội tích cực khác như tận dụng được tài nguyên đất, tạo công ăn việc làm, tạo giá trị gia tăng, kích thích thị trường bất động sản nói riêng và nền kinh tế nói chung.</span><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Tại buổi lễ, ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam khẳng định, lễ khởi công Dự án là một sự kiện đáng chú ý của thị trường bất động sản Hà Nội. Theo ông Nam, trong bối cảnh thị trường bất động sản đang hồi phục mạnh mẽ, và với vị trí đắc địa, dự án chắc chắn sẽ nhận được sự quan tâm lớn từ thị trường.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/IMG_8776.jpg\" style=\"width: 628px; height: 348px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Ông Nguyễn Trần Nam, nguyên Thứ trưởng Bộ Xây dựng, Chủ tịch Hiệp hội Bất động sản Việt Nam.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Tháp đôi FLC Twin Towers là dự án mới nhất gia nhập danh mục “hot” của Tập đoàn FLC. Nhờ sở hữu một trong những vị trí đẹp nhất tại Thủ đô Hà Nội, dự án tháp đôi 265 Cầu Giấy vốn đã được khách hàng và nhà đầu tư săn đón ngay từ khi được chấp thuận đầu tư hồi năm 2011. Tuy nhiên, dự án đã không được triển khai thi công từ đó đến nay, khiến nó dường như bị lãng quên.</span><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Cho đến tháng 7 vừa qua, khi có thông tin Tập đoàn FLC trở thành chủ đầu tư mới, dự án đã ngay lập tức trở lại như một điểm nóng của thị trường. Niềm tin rằng dự án sẽ nhanh chóng được khởi công chưa bao giờ vững chắc như bây giờ, bởi Tập đoàn FLC vốn có tiếng là hành động “ngay và luôn”. Tiếng tăm của FLC đã được kiểm chứng qua hàng loạt dự án mà Tập đoàn đã và đang triển khai như FLC Landmark Tower, FLC Complex Phạm Hùng, FLC Garden City, FLC Star Tower hay FLC Samson Beach &amp; Golf Resort…</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/ZVN_1906.JPG\" style=\"width: 628px; height: 348px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Các đại biểu thực hiện nghi lễ Khởi công dự án Tháp đôi FLC Twin Towers.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">“Trong suốt một tháng qua, chúng tôi liên tục nhận được điện thoại và tiếp đón hàng nghìn lượt khách hàng và nhà đầu tư hỏi về kế hoạch khởi công hay mở bán Dự án”, bà Nguyễn Xuân Hương, Trưởng phòng Kinh doanh bất động sản Tập đoàn FLC cho biết.</span><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Vị trí 265 Cầu Giấy mà tháp đôi FLC Twin Towers tọa lạc là cực kỳ thuận tiện khi nằm rất gần bến tàu điện trên cao, chỉ cách chưa đầy 1 km để đến các bệnh viện, trường học lớn, trụ sở mới của các cơ quan hành chính, tiện đường di chuyển ra sân bay quốc tế Nội Bài, cạnh các công viên lớn của Thủ đô như công viên Thủ Lệ, công viên Nghĩa Đô, và công viên Cầu Giấy…</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\"><img alt=\"\" src=\"http://w3ni182.web3nhat.net/data/media/137/files/Phoi%20canh%20FLC%20Twin%20Towers.jpg\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 12px; line-height: 21px; text-align: justify;\">Phối cảnh dự án tháp đôi FLC Twin Towers – 265 Cầu Giấy.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">Với vị trí đắc địa, thiết kế hiện đại, tiện ích đồng bộ và đặc biệt là tiến độ triển khai nhanh dưới bàn tay của FLC, dự án FLC Twin Towers hứa hẹn sẽ góp phần vào sự bùng nổ của thị trường bất động sản phía Tây Hà Nội trong thời gian tới.</span><br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<br style=\"margin: 0px; padding: 0px; color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: arial, Helvetica; font-size: 14px; line-height: 21px; text-align: justify;\">“Không chỉ sẽ là trụ sở mới của Tập đoàn, FLC Twin Towers còn chính là sản phẩm đúc kết kinh nghiệm và tâm huyết của Tập thể cán bộ nhân viên Tập đoàn trong nhiều năm hoạt động đầu tư phát triển các dự án bất động sản. Chúng tôi cam kết sẽ ưu tiên và tập trung nguồn lực để triển khai và hoàn thành Dự án đúng tiến độ, an toàn vớichất lượng cao nhất”, ông Trịnh Văn Quyết phát biểu tại buổi lễ.</span></p>\r\n','flc-khoi-cong-du-an-thap-doi-265-cau-giay',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','928_1439453588_43855cc5194a8ef1.jpg',[now], [now],[user_id],0,'','',[now]);set @news375 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories279,'Cho thuê thiết bị âm thanh ánh sáng sân  khấu   ','Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...','<p><span style=\"font-family: Roboto, sans-serif; font-size: 12.8999996185303px; line-height: 21.0085716247559px; text-align: justify; background-color: rgb(244, 247, 249);\">Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...Chuyên cho thuê âm thanh ánh sáng , cung cấp các thiết bị âm thanh ...</span></p>\r\n','cho-thue-thiet-bi-am-thanh-anh-sang-san--khau',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','833_1439437921_40855cc1461cf8bc.jpg',[now], [now],[user_id],0,'','',[now]);set @news374 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories226,'Bể bơi tầng 1 nhà R3 khu đô thị Royal City','Ngày 1/12/2013, Cụm bể bơi bốn mùa trong nhà lớn nhất Việt Nam tại Khu đô thị Times City đã chính thức khai trương với quy mô kỷ lục lên đến 4.000 m2. “Thiên đường bơi lội trong nhà” này không nhằm mục đích kinh doanh mà là món quà tri ân của chủ đầu tư (Tập đoàn Vingroup) dành tặng hoàn toàn miễn phí cho cư dân khu căn hộ cao cấp Vinhomes - Times City ','<p><span style=\"color: rgb(48, 48, 48); font-family: Arial, Tahoma, Helvetica, sans-serif; line-height: normal; text-align: center;\">Bể bơi tầng 1 nhà R3 khu đô thị Royal City</span></p>\r\n','be-boi-tang-1-nha-r3-khu-do-thi-royal-city',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','677_1439459402_75955cc684af041e.jpg',[now], [now],[user_id],0,'','',[now]);set @news360 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Individual, Consulting & Planning','Vừa qua Công ty cổ phần đầu tư và công nghệ HVC đã ký hợp đồng với công ty TNHH Vinpearl về việc cung cấp lắp đặt công nghệ thiết bị 150 bể bơi biệt thự, 2 cụm bể bơi resort 500m3, 1 cụm bể bơi trung tâm 2500m3','<p style=\"text-align: justify;\">Thời gian thực hiện rất ngắn cho cả dự án là 3 tháng. Hoàn thành dự án này HVC sẽ lập kỷ lục là công ty cung cấp lắp đặt thiết bị bể bơi nhanh nhất tại Việt Nam. Thông thường việc nhập khẩu thiết bị cho dự án đã mất khoảng 2 tháng như vậy HVC chỉ còn 1 tháng để hoàn thành việc lắp đặt.</p>\r\n\r\n<p style=\"text-align: justify;\">Dự án Vinpearl resort and Villas được đầu tư tại khu vực Vũng Me đảo Hòn Tre - TP Nha Trang - Khánh Hòa. Với quy mô 900 phòng bao gồm khách sạn và biệt thự nằm ngay sát bờ biển nơi đây hứa hẹn sẽ là thiên đường của du lịch nghỉ dưỡng. Được đầu tư theo tiêu chuẩn 5 sao nên chủ đầu tư đặc biệt chú trọng về kiến trúc, chất lượng xây dựng và chất lượng trang thiết bị.</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/vinpearl%20villas%201.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Dự án bao gồm 1 khu khách sạn lớn và 150 biệt thự nghỉ dưỡng</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/vinpearl%20villas%202.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Mỗi biệt thự sẽ bao gồm 1 bể bơi được đầu tư theo tiêu chuẩn 5 sao</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/vinpearl%20villas%203.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Ngoài ra còn khu bể bơi lớn tại trung tâm khách sạn và 2 bể bơi resort</p>\r\n','individual-consulting--planning',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','180_1439438741_76355cc17950df52.png',[now], [now],[user_id],1,'','',[now]);set @news361 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Decoration and Set up: Event Planner','Tiếp theo chuỗi khai trương khách sạn, khu biệt thự, thủy cung, vui chơi trẻ em... của khu nghỉ dưỡng đẳng cấp Vinpearl Phú Quốc. Ngày 15/11/2014 HVC kết hợp cùng Công ty Vinpearl Phú Quốc tiếp tục khai trương công viên nước Vinpearl Phú Quốc','<p style=\"text-align: justify;\">Công viên nước Vinpearl Phú Quốc được đánh giá là một trong những công viên nước quy mô nhất Việt Nam về số lượng trò chơi nước, nhất là các trò chơi cảm giác mạnh. Việc khai trương công viên nước Vinpearl Phú Quốc sẽ góp phần lớn tạo ra sức hút cho khu du lịch Vinpearl Phú Quốc. Hoàn thành công trình công viên nước Vinpearl Phú Quốc trong một thời gian rất ngắn khoảng 5 tháng HVC đã chứng tỏ là công ty số 1 Việt Nam về việc cung cấp thiết bị bể bơi và thiết bị công viên nước hiện nay. Dưới đây là một số hình ảnh sơ bộ về công viên nước Vinpearl Phú Quốc trong ngày khai trương:</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%2012.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Một góc của công viên nước Vinpearl Phú Quốc</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%201.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi dành cho trẻ em mô hình nhà Hawaii</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%202.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Dòng sông lười uốn lượn bao quanh công viên nước</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%204.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%203.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Bể tạo sóng với nhiều chế độ sóng khác nhau</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%205.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò trơi trượt lướt ván nhẹ nhàng</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%206.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%207.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi trượt phao gia đình cho 4 người 1 lượt chơi</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%208.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%209.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trờ chơi hố trượt lốc xoáy với cảm giác mạnh</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%2010.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi trượt tốc độ và trượt kín Aqualop</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvppq%2011.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi tạo cảm giác mạnh nhất là hố đen vũ trụ</p>\r\n','decoration-and-set-up-event-planner',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','462_1439438749_8855cc179da71b7.png',[now], [now],[user_id],1,'','',[now]);set @news362 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Ancohol','Dự án thủy cung đầu tiên HVC thi công tại dự án khu đô thị Times City Hà Nội đã thành công tốt đẹp.  Tiếp tục phát triển lĩnh vực này HVC đã ký hợp đồng thứ 2 cung cấp và lắp đặt toàn bộ thiết bị xử lý nước Thủy Cung tại Phú Quốc với quy mô tương tự như tại dự án Times City.','<p style=\"text-align: justify;\">Thủy cung Phú Quốc được chia ra làm hai khu riêng biệt với khu thủy cung nước ngọt và thủy cung nước mặn, do vậy thiết bị dùng cho thủy cung cũng tương đối đặc chủng, có loại thiết bị phải vận chuyển rời không thể vận chuyển bằng container. Công nghệ xử lý nước thủy cung cũng được HVC lựa chọn kỹ càng để phù hợp với điều kiện khí hậu tại Việt Nam và giảm giá thành đầu tư. Toàn bộ công nghệ thiết bị HVC sử dụng cho thủy cung Phú Quốc được nhập khẩu từ Tây Ban Nha, Maylaysia và Canada.</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/thuy%20cung.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Thủy cung tại đảo Phú Quốc có quy mô tương tự như thủy cung Times City</em></p>\r\n\r\n<p style=\"text-align: justify;\">Với việc thi công Thủy cung thứ 2 tại Việt Nam HVC đã chứng tỏ năng lực là công ty đứng đầu Việt Nam về xử lý nước giải trí cao cấp. Hoàn thành xong dự án này HVC tin tưởng có đủ năng lực để bước vào cạnh tranh trong thị trường thiết kế và xây dựng thủy cung tại khu vực Đông Nam Á. Đây cũng chính là ước muốn cháy bỏng của lãnh đạo công ty HVC trong việc xây dựng công ty cổ phần đầu tư và công nghệ HVC vươn tầm với ra khỏi lãnh thổ quốc gia Việt Nam.</p>\r\n','ancohol',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','636_1439782581_36355d156b56e5ed.png',[now], [now],[user_id],1,'','',[now]);set @news363 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Music & Entertainment','Sáng ngày 19/10/2014, trung tâm thương mại (TTTM) Vincom Center Hạ Long đã chính thức tổ chức lễ khai trương tại Khu Cột Đồng Hồ, Phường Bạch Đằng, TP. Hạ Long. Đây là công trình trung tâm thương mại đầu tiên theo tiêu chí tất cả trong một của tỉnh Quảng Ninh. Điều đặc biết nhất là sự xuất hiện của sân trượt băng nghệ thuật do HVC đã tham gia cung cấp và lắp đặt.','<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" height=\"334\" src=\"http://www.vingroup.net/Uploads/0_Tintuchoatdong/Khai%20truong%20vc%20HL%202014/L%E1%BB%85%20c%E1%BA%AFt%20b%C4%83ng%20khai%20tr%C6%B0%C6%A1ng%20TTTM%20Vincom%20Center%20H%E1%BA%A1%20Long..JPG\" width=\"496\" /></p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: justify;\">Được thiết kế theo phong cách Hoàng Gia Châu Âu sang trọng, TTTM Vincom Center Hạ Long bao gồm 4 tầng nổi và 1 tầng hầm với tổng diện tích sàn lên tới hơn 37.000m2, bốn mặt tiếp giáp đường lớn và nằm trong khu đông dân cư nhất của Thành phố. Với vị trí đắc địa và uy tín của chủ đầu tư, chỉ sau một thời gian ngắn chào thuê, hơn 95% diện tích mặt sàn của TTTM đã được lấp đầy với hàng trăm thương hiệu nổi tiếng, từ thời trang, phụ kiện đến đồ gia dụng, nội thất, ẩm thực...</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" height=\"330\" src=\"http://www.vingroup.net/Uploads/0_Tintuchoatdong/Khai%20truong%20vc%20HL%202014/KhaitruongVHL13.jpg\" width=\"495\" /></p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: justify;\">Đến với Vincom Center Hạ Long, khách hàng có thể thỏa sức mua sắm cùng các nhãn hiệu thời trang như Giordano, Lee, G2000, Elise, Eash, Easy Spirit, Ak Club, Eash, Kelly Bùi, Valentino Creations, CK, Tissot, PNJ, Narsis, Lare Boss, Rayban, BATA, Narsis, PNJ, TNG, Blue Exchange, GenViet Jeans… Ngoài ra, <strong>siêu thị tiêu dùng Saigon Co.opmart</strong> có quy mô trên 3.500m2 với hàng ngàn sản phẩm đa dạng, hấp dẫn sẽ đáp ứng mọi nhu cầu tiêu dùng thiết yếu. Sức hút của <strong>Thế giới Ẩm thực </strong>tại Vincom Center Hạ Long nằm ở hàng chục nhà hàng nổi tiếng như Master Beef, Gogi House, Thái BBQ, Lotteria, Food To Go, Kodo Café &amp; Fastfood,… với đầy đủ hương vị từ đồ truyền thống của Việt Nam, Trung Quốc, Hàn Quốc, Thái Lan… cho tới Châu Âu, Châu Úc…; hoặc đơn giản là những món bánh ngọt, đồ uống, kem tươi và snack thơm ngon mà giới trẻ yêu thích.</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" height=\"334\" src=\"http://www.vingroup.net/Uploads/0_Tintuchoatdong/Khai%20truong%20vc%20HL%202014/KhaitruongVHL10.jpg\" width=\"500\" /></p>\r\n\r\n<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" src=\"http://m.f25.img.vnecdn.net/2014/10/19/DSC-2294-JPG-9819-1413705030.jpg\" /></p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: justify;\">Đặc biệt, lần đầu tiên tại thành phố biển, người dân và khách du lịch sẽ có cơ hội trải nghiệm cảm giác lướt nhẹ trên sàn trượt băng tự nhiên <strong>Vinpealland Ice Rink Hạ Long</strong> với quy mô 1.086m2; Đây là sân trượt băng được đánh giá đạt tiêu chuẩn và hiện đại nhất Việt Nam hiện nay. Sân trượt băng được thiết kế và sử dụng công nghệ Canada đã được HVC chuyển giao cho chủ đầu tư. Với thời gian thi công nhanh kỷ lục trong 6 tháng so với thông thường là 1 năm đã cho thấy khả năng vượt trội của HVC trong việc giữ uy tín về tiến độ với chủ đầu tư.</p>\r\n','music--entertainment',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','132_1439438769_36455cc17b1ba8c1.png',[now], [now],[user_id],1,'','',[now]);set @news364 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories226,'Bể bơi trong nhà lớn nhất Việt Nam tại Khu đô thị Times City','Ngày 1/12/2013, Cụm bể bơi bốn mùa trong nhà lớn nhất Việt Nam tại Khu đô thị Times City đã chính thức khai trương với quy mô kỷ lục lên đến 4.000 m2. “Thiên đường bơi lội trong nhà” này không nhằm mục đích kinh doanh mà là món quà tri ân của chủ đầu tư (Tập đoàn Vingroup) dành tặng hoàn toàn miễn phí cho cư dân khu căn hộ cao cấp Vinhomes - Times City ','<p>Bể bơi trong nhà lớn nhất Việt Nam tại Khu đô thị Times City<br />\r\n&nbsp;</p>\r\n','be-boi-trong-nha-lon-nhat-viet-nam-tai-khu-do-thi-times-city',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','938_1439459781_89055cc69c5340c5.jpg',[now], [now],[user_id],0,'','',[now]);set @news365 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories226,'Bể bơi HVC đầu tư xây dựng tại huyện Khoái Châu tỉnh Hưng Yên','Cụm bể bơi bốn mùa tọa lạc tại tầng hầm B2, tòa nhà T18, khu đô thị Times City (458 Minh Khai, Hà Nội), với ba bể bơi được phân chia theo các cấp độ: người lớn, thiếu niên và trẻ em. Trong đó, tâm điểm là bể bơi người lớn, có diện tích lên đến gần 1.400m2, sở hữu chiều dài đáng mơ ước với mọi tín đồ đam mê đường đua xanh: 56m, vượt qua tiêu chuẩn 50m của bể thi đấu Olympic. ','<p>Bể bơi HVC đầu tư xây dựng tại huyện Khoái Châu tỉnh Hưng Yên</p>\r\n','be-boi-hvc-dau-tu-xay-dung-tai-huyen-khoai-chau-tinh-hung-yen',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','43_1439459547_97555cc68db6ac9b.jpg',[now], [now],[user_id],0,'','',[now]);set @news366 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories226,'32 bể bơi biệt thự nghỉ dưỡng khu resort Vinpearl Phú Quốc','32 bể bơi biệt thự nghỉ dưỡng khu resort Vinpearl Phú Quốc. Để đảm bảo hoạt động cả bốn mùa trong năm, toàn bộ cụm bể bơi được làm ấm bởi hệ thống lưu dẫn, tuần hoàn và gia nhiệt hiện đại. Cùng với nhiệt độ nước, không gian bể bơi cũng được sưởi ấm vào mùa đông, đảm bảo phục vụ các tín đồ bơi lội trong mọi tình huống thời tiết.<br />\r\n','<p>32 bể bơi biệt thự nghỉ dưỡng khu resort Vinpearl Phú Quốc<br />\r\n&nbsp;</p>\r\n','32-be-boi-biet-thu-nghi-duong-khu-resort-vinpearl-phu-quoc',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','36_1439459650_2755cc6942cfea5.jpg',[now], [now],[user_id],0,'','',[now]);set @news367 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories226,'Bể bơi 5500m3 trung tâm khách sạn Vinpearl Phú Quốc','Bể bơi 5500m3 trung tâm khách sạn Vinpearl Phú Quốc.Trên phần diện tích còn lại là bể bơi thiếu niên và bể bơi trẻ em với chiều sâu lần lượt là 0,9m và 0,5m được kết nối liên hoàn với nhau, tạo nên không gian vui chơi, thư giãn thực sự cho các công dân tương lai của Times City. ','<p>Bể bơi 5500m3 trung tâm khách sạn Vinpearl Phú Quốc</p>\r\n','be-boi-5500m3-trung-tam-khach-san-vinpearl-phu-quoc',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','755_1439459585_89955cc6901270cd.jpg',[now], [now],[user_id],0,'','',[now]);set @news368 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Catering: Event Planner','Món quà nào cũng có thể phai nhạt theo thời gian, chỉ có cảm xúc còn đọng lại trong tim mãi mãi... SinhNhat.Vn cho đi những khoảnh khắc sinh nhật tuyệt vời - Đó chính là món quà quí giá mà SinhNhat.Vn trao tặng bạn và người thân của bạn!','<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Với slogan \"Sinh Nhật Như Mơ Hạnh Phúc Bất Ngờ!\" Công ty CHÚC MỪNG SINH NHẬT mong ước mang đến cho khách hàng không chỉ là một bữa tiệc vui mà trên hết là giá trị tinh thần, đáp ứng được nhu cầu quan tâm, chăm sóc, nâng niu người thân trong ngày sinh nhật.</span></span><span style=\"font-family: Arial, sans-serif;\">&nbsp;</span><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Như chúng ta cũng biết, ai cũng có một ngày sinh, và sinh nhật là dịp đặc biệt - rất riêng của mỗi người, nhiều người tin rằng khi mình có được một ngày sinh nhật vui vẻ - hạnh phúc thì sẽ may mắn trong những ngày tháng tiếp theo... Hiểu được điều này CÔNG TY CHÚC MỪNG SINH NHẬT đã xem đây là sứ mệnh của mình, sẽ cố gắng tận tụy để mỗi sinh nhật là một kỷ niệm đẹp giữa khách hàng và công ty!</span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\">Tổ chức sinh nhật cho bé Mai Phương 9 tuổi tại Park Hyatt Sài Gòn chủ đề Queen Frozen: Sân khấu dựng 3D núi tuyết hùng vĩ nơi trú ẩn của nàng công chúa Elsa trong bộ phim bé yêu thích \"Nữ Hoàng Băng Giá\", trang trí bàn tiệc cho bé đẹp lung linh với hoa tươi và các phụ kiện trang trí xinh xắn...</span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><img alt=\"trang-tri-tiec-sinh-nhat-nu-hoang-bang-gia-queen-frozen-decor-birthday-to-chuc-sinh-nhat-be-sang-trong\" height=\"333\" src=\"http://sinhnhat.vn/userfiles/trang-tri-tiec-sinh-nhat-nu-hoang-bang-gia-queen-frozen-decor-birthday-to-chuc-sinh-nhat-be-sang-trong.jpg\" width=\"500\" /><img alt=\"to-chuc-trang-tri-tiec-sinh-nhat-be-dep-sang-trong-queen-frozen-birthday-theme-flower-decor.\" height=\"166\" src=\"http://sinhnhat.vn/userfiles/to-chuc-trang-tri-tiec-sinh-nhat-be-dep-sang-trong-queen-frozen-birthday-theme-flower-decor.jpg\" width=\"245\" /><img alt=\"to-chuc-trang-tri-tiec-sinh-nhat-be-dep-sang-trong-queen-frozen-birthday-theme-flower\" height=\"166\" src=\"http://sinhnhat.vn/userfiles/to-chuc-trang-tri-tiec-sinh-nhat-be-dep-sang-trong-queen-frozen-birthday-theme-flower(1).jpg\" width=\"252\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\">MC thực hiện nghi thức sinh nhật trang trọng, đầm ấm và ý nghĩa... Ngày sinh nhật là kỉ niệm đẹp cho bé và gia đình.&nbsp;</span></span><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\">Đặc biệt, chương trình có 2 khách mời là Ca sĩ Hiền Thục và ngôi sao nhí - bé Bảo Trân giải nhất Doremi 2012 tham dự và ca hát cùng bé Mai Phương</span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><img alt=\"to-chuc-sinh-nhat-be-gai-queen-frozen-birthday\" height=\"333\" src=\"http://sinhnhat.vn/userfiles/to-chuc-sinh-nhat-be-gai-queen-frozen-birthday(1).jpg\" width=\"500\" /></span></span><br />\r\n<img alt=\"sinh-nhat-ca-si-hien-thuc-hat-tang-sinh-nhat-be\" src=\"http://sinhnhat.vn/userfiles/sinh-nhat-ca-si-hien-thuc-hat-tang-sinh-nhat-be.jpg\" /><img alt=\"to-chuc-sinh-nhat-cho-be-ca-si-nhi\" height=\"253\" src=\"http://sinhnhat.vn/userfiles/to-chuc-sinh-nhat.jpg\" width=\"309\" /><img alt=\"to-chuc-sinh-nhat-be-sang-trong-dep-y-nghia-ca-si-hay\" height=\"253\" src=\"http://sinhnhat.vn/userfiles/to-chuc-sinh-nhat-be-sang-trong-dep-y-nghia-ca-si-hay(1).jpg\" width=\"190\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">SinhNhat.vn vinh dự được tổ chức thôi nôi cho bé gái Abbie - công chúa nhỏ của đại gia đình Tập đoàn Thành Thành Công và Ngân hàng Sacombank, cả nhà đã có những giấy phút ấm áp, hạnh phúc bên nhau chào mừng con gái yêu tròn 1 tuổi:</span></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><img alt=\"sinh-nhat-to-chuc-thoi-noi-sinh-nhat-1-tuoi-be-yeu\" height=\"193\" src=\"http://sinhnhat.vn/userfiles/sinh-nhat-to-chuc-thoi-noi-sinh-nhat-1-tuoi-be-yeu.jpg\" width=\"259\" /></span></span></span><span style=\"color: rgb(0, 0, 128);\"><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-vip-doanh-nhan-sang-trong-nha-giau-co\" height=\"193\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-vip-doanh-nhan-sang-trong-nha-giau-co(1).jpg\" width=\"266\" /></span><br />\r\n<img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-danh-cho-vip-doanh-nhan-noi-tieng\" height=\"218\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-danh-cho-vip-doanh-nhan-noi-tieng.jpg\" width=\"298\" /><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-danh-cho-vip-doanh-nhan-noi-tieng\" height=\"218\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-be-trai-be-gai-dich-vu-cao-cap-vip-doanh-nhan-sang-trong-nha-giau.jpg\" width=\"228\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Ông Đặng Văn Thành -Chủ tịch HĐQT ngân hàng Sacombank (ông ngoại của bé Abbie) và cậu Đặng Hồng Anh Chủ tịch Sacomreal rất vui trong ngày sinh nhật 1 tuổi của cháu cưng.&nbsp;</span></span></span><br />\r\n<span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Các anh chị của bé cũng đã có những kỉ niệm đáng nhớ trong ngày thôi nôi em, cùng chú Hề thử tài làm xiếc và tham gia các trò chơi vui nhộn:</span></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><em><strong><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-danh-cho-vip-doanh-nhan-sang-trong-nha-giau-nhat-vn\" height=\"186\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-yeu-dich-vu-cao-cap-danh-cho-vip-doanh-nhan-sang-trong-nha-giau-nhat-vn.jpg\" width=\"278\" /></span></span></strong></em></span><img alt=\"to-chuc-thoi-noi-sinh-nhat-dich-vu-cao-cap-danh-cho-vip-doanh-nhan\" height=\"186\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-dich-vu-cao-cap-danh-cho-vip-doanh-nhan.jpg\" width=\"245\" /><br />\r\n<img alt=\"to-chuc-sinh-nhat-thoi-noi-bieu-dien-xiec-chu-he-vui-cho-be-yeu\" height=\"180\" src=\"http://sinhnhat.vn/userfiles/to-chuc-sinh-nhat-thoi-noi-bieu-dien-xiec-chu-he-vui-cho-be-yeu.jpg\" width=\"266\" /><img alt=\"sinh-nhat-thoi-noi-chu-he-to-chuc-tro-choi-cho-be-yeu\" height=\"180\" src=\"http://sinhnhat.vn/userfiles/sinh-nhat-thoi-noi-chu-he-to-chuc-tro-choi-cho-be-yeu.jpg\" width=\"257\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\">Cty CHÚC MỪNG SINH NHẬT chuyên tổ chức tiệc sinh nhật từ 1 -&gt;100 tuổi... Website www.SinhNhat.vn ra đời từ năm 2007, đến nay, trải qua nhiều năm chuyên nghiệp SinhNhat.vn luôn dẫn đầu thị trường,&nbsp;</span></span><span style=\"font-size: small;\"><span style=\"font-family: Arial;\">luôn đạt danh hiệu uy tín, sáng tạo hàng đầu và phong cách nhất!&nbsp;</span></span>C<span style=\"font-size: small;\"><span style=\"font-family: Arial;\">húng tôi tự hào mang đến cho khách hàng dịch vụ chất lượng cao, khách hàng luôn ưu tiên sử dụng dịch vụ của chúng tôi đồng thời duy trì mối quan hệ tốt, giới thiệu bạn bè và gia đình đến với SinhNhat.vn.</span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Vợ chồng doanh nhân (</span></span></span><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Cty trang trí nội thất Lạc Phát) đã giao cho SinhNhat.vn tổ chức thực hiện trọn gói buổi tiệc thôi nôi con trai yêu Thiên Phúc:</span></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><img alt=\"thoi-noi-to-chuc-sinh-nhat-1-tuoi-cho-be-yeu\" height=\"334\" src=\"http://sinhnhat.vn/userfiles/thoi-noi-to-chuc-sinh-nhat-1-tuoi-cho-be-yeu.jpg\" width=\"500\" /></span></span></span><img alt=\"thoi-noi-sinh-nhat-1-tuoi-be-yeu-trang-tri-ban-anh-chup-hinh-to-chuc-tiec-thoi-noi-sinh-nhat-y-nghia\" height=\"167\" src=\"http://sinhnhat.vn/userfiles/thoi-noi-sinh-nhat-1-tuoi-be-yeu-trang-tri-ban-anh-chup-hinh-to-chuc-tiec-thoi-noi-sinh-nhat-y-nghia.jpg\" width=\"248\" /><img alt=\"thoi-noi-sinh-nhat-1-tuoi-be-yeu-trang-tri-ban-hinh-anh-to-chuc-tiec-thoi-noi-sinh-nhat-y-nghia\" height=\"166\" src=\"http://sinhnhat.vn/userfiles/thoi-noi-sinh-nhat-1-tuoi-be-yeu-trang-tri-ban-hinh-anh-to-chuc-tiec-thoi-noi-sinh-nhat-y-nghia.jpg\" width=\"251\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Cuộc sống hiện đại với bao bề bộn lo toan, có những lúc bạn không có thời gian để làm điều gì đó cho con cưng, cho chồng yêu hay cả cho bản thân mình… Hãy để chúng tôi chia sẻ cùng bạn, giúp bạn làm cho cuộc sống trở nên đẹp và thi vị hơn. Khi đến với SinhNhat.Vn bạn có thể chọn được rất nhiều kiểu tổ chức tiệc sinh nhật hay, độc đáo và ý nghĩa phù hợp với nhiều đối tượng, độ tuổi khác nhau. Bạn cũng không cần mất nhiều thời gian vì SinhNhat.vn đứng ra lo trọn gói từ việc giúp bạn chọn nhà hàng, lên ý tưởng chương trình và thiết kế, trang trí, chụp hình cho bé v.v… Bé yêu và bạn xứng đáng được tận hưởng dịch vụ chu đáo nhất từ SinhNhat.Vn.</span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Mừng bé Suri 1 tuổi ba mẹ đặt SinhNhat.vn trang trí bàn ảnh bé, khu vực chụp ảnh thật đẹp, cổng chào, sân khấu và phòng tiệc cũng được trang trí bong bóng rất sinh động bắt mắt...</span></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><img alt=\"trang-tri-to-chuc-thoi-noi-sinh-nhat-be-yeu\" height=\"332\" src=\"http://sinhnhat.vn/userfiles/trang-tri-to-chuc-thoi-noi-sinh-nhat-be-yeu.JPG\" width=\"500\" /><img alt=\"trang-tri-thoi-noi-sinh-nhat-be-tinker-bell\" height=\"167\" src=\"http://sinhnhat.vn/userfiles/trang-tri-thoi-noi-sinh-nhat-be-tinker-bell.jpg\" width=\"249\" /><img alt=\"trang-tri-thoi-noi-sinh-nhat-cho-be-tinker-bell-6-9-tuoi\" height=\"167\" src=\"http://sinhnhat.vn/userfiles/trang-tri-thoi-noi-sinh-nhat-cho-be-tinker-bell-6-9-tuoi.jpg\" width=\"250\" /><img alt=\"to-chuc-thoi-noi-be-gai\" height=\"350\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-be-gai.JPG\" width=\"500\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 128);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Trong ngày sinh nhật đầu tiên của mình bé Jason Đặng thích thú tham gia trò chơi \"Dự đoán nghề nghiệp tương lai của em\" do Chú hề Happy của SinhNhat.vn tổ chức:</span></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-doc-dao\" height=\"332\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-doc-dao.jpg\" width=\"500\" /><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-doc-dao-hay\" height=\"172\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-doc-dao-hay.jpg\" width=\"259\" /><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-doc-dao-hay-nhat\" height=\"172\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-doc-dao-hay-nhat.jpg\" width=\"242\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">SinhNhat.vn tổ chức thực hiện&nbsp;trọn gói cho tiệc thôi nôi - sinh nhật cho bé: Từ chụp hình thiết kế album kỉ niệm đến trang trí bày trí - trang trí phòng tiệc và tổ chức trọn vẹn chương trình giải trí giúp buổi tiệc diễn ra ấm áp, sang trọng làm hài lòng gia chủ và khách mời. Khâu trang trí luôn được chuẩn bị tỉ mỉ chu đáo theo chủ đề, màu sắc đã thống nhất với khách. SinhNhat.vn trang trí tiệc với nhiều phong cách: ấn tượng, lãng mạn, sang trọng, độc đáo đồng thời sử dụng nhiều nguyên vật liệu trang trí đa dạng như trang trí bong bóng, hoa tươi, hoa giấy (hoa thủ công)...</span></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><span style=\"color: rgb(0, 0, 128);\"><img alt=\"trang-tri-san-khau-tiec-thoi-noi-sinh-nhat-be\" height=\"360\" src=\"http://sinhnhat.vn/userfiles/trang-tri-san-khau-tiec-thoi-noi-sinh-nhat-be(1).jpg\" width=\"503\" /></span><img alt=\"trang-tri-bong-bong-sinh-nhat-dep-re\" height=\"179\" src=\"http://sinhnhat.vn/userfiles/trang-tri-bong-bong-sinh-nhat-dep-re(1).jpg\" width=\"235\" /><img alt=\"trang-tri-san-khau-tiec-thoi-noi-sinh-nhat-be-dep\" height=\"179\" src=\"http://sinhnhat.vn/userfiles/trang-tri-san-khau-tiec-thoi-noi-sinh-nhat-be-dep(1).jpg\" width=\"266\" /><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><img alt=\"to-chuc-sinh-nhat-thoi-noi-trang-tri-san-khau-backdrop-to-chuc-chuong-trinh\" height=\"248\" src=\"http://sinhnhat.vn/userfiles/sinh-nhat-thoi-noi-trang-tri-san-khau-backdrop-to-chuc-chuong-trinh.jpg\" width=\"502\" /></span></span><br />\r\n<span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><img alt=\"to-chuc-thoi-noi-sinh-nhat-trang-tri-ban-hinh-anh-ki-niem-cho-be-1-9-tuoi\" height=\"335\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-trang-tri-ban-hinh-anh-ki-niem-cho-be-1-9-tuoi.jpg\" width=\"502\" /></span></span><br />\r\n<img alt=\"banh_kem_sinh_nhat_de_thuong_doc_dao_dep_nhat\" height=\"348\" src=\"http://sinhnhat.vn/userfiles/banh_kem_sinh_nhat_de_thuong.jpg\" width=\"236\" /><img alt=\"to_chuc_thoi_noi_sinh_nhat_cho_be_yeu\" height=\"348\" src=\"http://sinhnhat.vn/userfiles/banh_kem_sinh_nhat.jpg\" width=\"266\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Trong nhiều năm liền SinhNhat.vn luôn được gia đình các doanh nhân sang trọng chọn để giao trách nhiệm tổ chức tiệc sinh nhật cho các thành viên trong gia đình, các khách hàng thân thiết trong hơn 5 năm qua có thể kể đến như gia đình Chủ tịch HĐQT Tập đoàn Masan, Vinamilk, Cafe Trung Nguyên... Và gia đình các nghệ sĩ nổi tiếng như MC Quyền Linh, diễn viên Mai Thu Huyền, Ca sĩ Thanh Thảo...</span></span>&nbsp;</p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><img alt=\"ca-si-dam-vinh-hung-thanh-thao-sinh-nhat-thoi-noi-be-jacky\" height=\"351\" src=\"http://sinhnhat.vn/userfiles/sinh_nhat_dam_vinh_hung_thanh_thao_ca_si_thoi_noi_jacky_.jpg\" width=\"513\" /></span></span><span style=\"font-size: small;\"><span style=\"color: rgb(0, 0, 128);\"><strong><span style=\"font-family: Arial, sans-serif;\"><img alt=\"to_chuc_thoi_noi_cho_be_trai\" height=\"175\" src=\"http://sinhnhat.vn/userfiles/to_chuc_thoi_noi_cho_be_yeu.jpg\" width=\"246\" /><img alt=\"to_chuc_thoi_noi_cho_be_gai\" height=\"175\" src=\"http://sinhnhat.vn/userfiles/to_chuc_thoi_noi_cho_be_.jpg\" width=\"265\" /></span></strong></span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">Không chỉ trang trí đẹp SinhNhat.vn còn cung cấp rất nhiều tiết mục hấp dẫn phục vụ cho chương trình tiệc thôi nôi, sinh nhật trẻ con, sinh nhật người lớn, lễ hội sự kiện...</span></span></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><img alt=\"tiet-muc-bieu-dien-xiec-thieu-nhi-chuyen-nghiep-tiec-sinh-nhat\" height=\"272\" src=\"http://sinhnhat.vn/userfiles/tiet-muc-bieu-dien-xiec-thieu-nhi-chuyen-nghiep-tiec-sinh-nhat.jpg\" width=\"185\" /><img alt=\"tiet-muc-bieu-dien-xiec-thieu-nhi-chuyen-nghiep-tiec-sinh-nhat.\" height=\"272\" src=\"http://sinhnhat.vn/userfiles/tiet-muc-bieu-dien-xiec-thieu-nhi-sinh-nhat.jpg\" width=\"315\" /><img alt=\"to-chuc-sinh-nhat-cho-be-ca-mua-nhac-thieu-nhi-ban-nhac\" height=\"323\" src=\"http://sinhnhat.vn/userfiles/to-chuc-sinh-nhat-cho-be-ca-mua-nhac-thieu-nhi-ban-nhac.jpg\" width=\"500\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><strong><span style=\"color: rgb(0, 51, 102);\"><span style=\"font-size: small;\">Bánh kem, bánh cup cake do SinhNhat.vn cung cấp được thiết kế đôc quyền theo chủ đề tiệc sinh nhật bé, chất lượng thơm ngon cao cấp:</span></span></strong></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><img alt=\"banh-kem-ngon-dep-danh-cho-thoi-noi-sinh-nhat-be-gai-be-trai-cao-cap-vip\" height=\"283\" src=\"http://sinhnhat.vn/userfiles/banh-kem-ngon-dep-danh-cho-thoi-noi-sinh-nhat-be-gai-be-trai-cao-cap-vip.jpg\" width=\"196\" /><img alt=\"banh-kem-cong-chua-doc-dao-danh-cho-thoi-noi-sinh-nhat-be-gai-trai-cao-cap\" height=\"283\" src=\"http://sinhnhat.vn/userfiles/banh-kem-cong-chua-doc-dao-danh-cho-thoi-noi-sinh-nhat-be-gai-trai-cao-cap.jpg\" width=\"310\" /><br />\r\n<strong><span style=\"color: rgb(0, 51, 102);\"><span style=\"font-size: small;\"><img alt=\"to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-banh-kem-dep-ngon\" height=\"367\" src=\"http://sinhnhat.vn/userfiles/to-chuc-thoi-noi-sinh-nhat-cho-be-trai-y-nghia-an-tuong-banh-kem-dep-ngon.jpg\" width=\"262\" /></span></span></strong><strong><span style=\"color: rgb(0, 51, 102);\"><span style=\"font-size: small;\"><img alt=\"banh-kem-sinh-nhat-be-trai-1-tuoi-dep-an-tuong-y-nghia\" height=\"367\" src=\"http://sinhnhat.vn/userfiles/banh-kem-sinh-nhat-be-trai-1-tuoi-dep-an-tuong-y-nghia.jpg\" width=\"244\" /></span></span></strong></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: justify;\"><strong><span style=\"color: rgb(0, 51, 102);\"><span style=\"font-size: small;\">Đặc biệt, SinhNhat.vn chuyên chụp hình cho bé lưu giữ lại khoảnh khắc tuyệt vời khi bé lên một tuổi, ghi lại những cột mốc phát triển của bé từng năm theo ngày sinh nhật, chúng tôi đến tận nhà chụp để có thể ghi lại hình ảnh tự nhiên và đẹp nhất của bé cùng gia đình</span>:</span></strong></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><strong><span style=\"color: rgb(0, 51, 102);\"><img alt=\"chup-hinh-cho-be-sinh-nhat-1-tuoi-thoi-noi\" height=\"252\" src=\"http://sinhnhat.vn/userfiles/chup-hinh-cho-be-sinh-nhat-1-tuoi-thoi-noi.jpg\" width=\"500\" /></span></strong><br />\r\n<img alt=\"chup-hinh-cho-be-ki-niem-sinh-nhat-1-tuoi-thoi-noi\" height=\"377\" src=\"http://sinhnhat.vn/userfiles/chup-hinh-cho-be-ki-niem-sinh-nhat-1-tuoi-thoi-noi.jpg\" width=\"500\" /></p>\r\n\r\n<p style=\"font-family: tahoma; font-size: 11px; line-height: normal; text-align: center;\"><img alt=\"chup-hinh-cho-be-ki-niem-sinh-nhat-1-tuoi-be-gai\" height=\"377\" src=\"http://sinhnhat.vn/userfiles/chup-hinh-cho-be-ki-niem-sinh-nhat-1-tuoi-be-gai.jpg\" width=\"249\" /><img alt=\"chup-hinh-cho-be-ki-niem-sinh-nhat-1-tuoi-be-yeu\" height=\"378\" src=\"http://sinhnhat.vn/userfiles/chup-hinh-cho-be-ki-niem-sinh-nhat-1-tuoi-be-yeu.jpg\" width=\"254\" /></p>\r\n','catering-event-planner',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','271_1439438776_27955cc17b8c18d8.jpg',[now], [now],[user_id],1,'DSPL','',[now]);set @news369 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Break Down/Clean up: Event Planner	','Quy trình bổ nhiệm cán bộ của ta tiếng là chặt chẽ, được làm qua rất nhiều bước nhưng xem ra \"con voi vẫn chui lọt lỗ kim\". Những quyết định bổ nhiệm đầy vụ lợi, sự cả nể, rồi những cái \"tặc lưỡi\" của tập thể đã vô hình trung tạo điều kiện cho tham nhũng, tiêu cực, chạy chọt.','<p>PV <a href=\"http://www.doisongphapluat.com/tag/b%C3%A1o.html\">báo</a> <a href=\"http://www.doisongphapluat.com/doi-song/\">Đời sống</a> và <a href=\"http://www.doisongphapluat.com/phap-luat/\">Pháp luật</a> đã có cuộc trao đổi với PGS. TS Lê Bỉnh, Giảng viên cao cấp khoa Triết học - Học viện Chính trị Khu vực 1 (nguyên Chủ nhiệm bộ môn Duy vật lịch sử, khoa Triết học - Học viện Chính trị Quân sự) để đem đến cái nhìn sâu sắc hơn về hiện tượng này.</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\" style=\"line-height:27px;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"line-height:27px !important;\">\r\n			<p><em><img alt=\"“Chạy đủ thứ” và chuyện tư lợi trong bổ nhiệm cán bộ - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x670x/357/2014/12/6/le-binh.jpg\" style=\"line-height:27px !important;border:1px solid rgb(102, 102, 102) !important;height:auto !important;\" title=\"“Chạy đủ thứ” và chuyện tư lợi trong bổ nhiệm cán bộ - Ảnh 1\" width=\"500\" /></em></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"line-height:17px !important;\">\r\n			<h3><em>Ông Lê Bỉnh.</em></h3>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Bổ nhiệm \"ồ ạt\" vì... tư lợi</strong></p>\r\n\r\n<p><em>Ngoài lùm xùm liên quan đến việc bổ nhiệm 60 cán bộ trước khi về hưu của nguyên Tổng Thanh tra Chính phủ Trần Văn Truyền, dư luận không khỏi lo ngại tình trạng trên cũng xảy ra ở ngành này, ngành kia. PGS nghĩ sao về hiện tượng, lãnh đạo trước khi \"hạ cánh\" đã ký bổ nhiệm hàng loạt cán bộ và có dấu hiệu không minh bạch?</em></p>\r\n\r\n<p>Vừa rồi Thanh tra Chính phủ cũng đã xác nhận nguyên Tổng Thanh tra Chính phủ Trần Văn Truyền trước khi nghỉ hưu có ký quyết định <a href=\"http://www.doisongphapluat.com/xa-hoi/xung-quanh-viec-lanh-dao-bo-nhiem-can-bo-o-at-truoc-khi-nghi-huu-a25936.html\">bổ nhiệm cán bộ</a> chưa đủ năng lực. Thanh tra Chính phủ cũng khẳng định, nhiều cán bộ được bổ nhiệm thiếu tiêu chuẩn, điều kiện và đã phải xử lý bằng cách cho đi đào tạo; hiện nay vẫn đang trong quá trình rà soát, phân công lại. Thế nên, việc bổ nhiệm sai quy trình của cán bộ tiền nhiệm sẽ để lại nhiều hậu quả với đội ngũ lãnh đạo kế cận. Đó là chưa kể những cán bộ không đủ năng lực sẽ làm méo mó nền công vụ nước nhà, ảnh hưởng tiêu cực đến sự phát triển bền vững của chế độ công quyền.</p>\r\n\r\n<p>Trước đây, những người trước khi về hưu cũng có trường hợp bổ nhiệm cán bộ. Nhưng những trường hợp cán bộ được bổ nhiệm này là cần thiết, khách quan, dân chủ nên không có dư âm gì. Gần đây cùng xu thế \"chạy chức\", \"chạy quyền\", lợi dụng sơ hở pháp luật đã xuất hiện tư tưởng tư lợi trong vấn đề đề bạt cán bộ, nên một số lãnh đạo trước khi về tranh thủ bổ nhiệm nhiều cán bộ sai quy định. Trong việc của ông Truyền, các cơ quan chức năng cần phải khẩn trương vào cuộc xác minh rõ có \"chạy chức\", \"chạy quyền\" hay không? Nếu có vi phạm thì phải xử lý nghiêm, không có \"vùng cấm\" cho bất kỳ cán bộ nào cả.</p>\r\n\r\n<p><em>Việc các quan chức tại vị liên tục \"nhét\" con cháu mình vào những vị trí quan trọng để khi về hưu có thể \"hạ cánh an toàn\" được ví như một hình thức \"tham nhũng tương lai\" và thể hiện lối tư duy \"giữ gôn\", PGS nghĩ sao về nhận định này?</em></p>\r\n\r\n<p>Theo tôi, khái niệm \"giữ gôn\" hay \"tham nhũng tương lai\" cần phải bàn thêm, nhưng với những quan chức trước khi về hưu vẫn bổ nhiệm những cán bộ không đúng tiêu chuẩn là lạm dụng quyền lực. Mà cái cốt lõi, lạm dụng quyền lực cũng chính là một hình thức tham nhũng. Anh không gương mẫu thực hiện nghiêm túc các quy định của pháp luật về quy trình bổ nhiệm cán bộ để có căn cứ theo nhu cầu thực tiễn, mà anh cứ bổ nhiệm sai, vì \"cái gì đó\", ồ ạt... đa số là vì vụ lợi thôi. Nếu không vì vụ lợi, anh chẳng bổ nhiệm cán bộ ồ ạt để làm gì. Đặc biệt, việc bổ nhiệm những người có \"dây mơ rễ má\" khiến dư luận càng nghi ngờ.</p>\r\n\r\n<p>Những nghi vấn về việc \"chạy chức\", \"chạy quyền\", chi tiền để được bổ nhiệm cũng từ đó mà ra. Câu chuyện của nguyên Tổng Thanh tra Chính phủ Trần Văn Truyền bổ nhiệm mấy chục cán bộ trước khi về hưu cũng khiến dư luận đặt câu hỏi. Không thể cứ bổ nhiệm xong rồi \"hạ cánh an toàn\" được. Điều đó cũng cho thấy, có một số trường hợp lạm dụng chức quyền, tranh thủ thời gian để chụp giật cơ hội tham nhũng. Thực tế đặt ra yêu cầu cần phải có cơ chế giám sát chặt chẽ đối với những người đứng đầu có quyền hạn trong bổ nhiệm đề bạt cán bộ, làm sao để họ muốn lạm dụng quyền lực (mà thực chất là tham nhũng) cũng không lạm dụng được.</p>\r\n\r\n<p><strong>\"Bốn ệ\" và trách nhiệm người đứng đầu</strong></p>\r\n\r\n<p><em>Vậy việc bổ nhiệm sai, bổ nhiệm thừa cán bộ là do cơ chế chính sách của ta chưa chặt hay do khâu tổ chức thực hiện, thưa PGS?</em></p>\r\n\r\n<p>Việc bổ nhiệm cán bộ ồ ạt không đủ tiêu chuẩn cho thấy, thực tế hiện nay phát sinh khái niệm \"nợ tiêu chuẩn\". Điều đó là vi phạm pháp luật, vi phạm quy định bổ nhiệm cán bộ của Đảng ta. Theo đúng quy định, tiêu chí bổ nhiệm, đề bạt cán bộ phải rõ ràng, minh bạch, đúng tiêu chuẩn chứ không cán bộ nào được \"nợ tiêu chuẩn\". Về nguyên tắc, khi không đạt tiêu chuẩn thì không bổ nhiệm. Việc cho \"nợ tiêu chuẩn\" là do cán bộ quan chức lạm dụng quyền lực, tự ý cho nợ để rồi lại nảy sinh tình trạng bổ sung tiêu chuẩn bằng việc chạy chọt, đút lót. Như vậy là do khâu tổ chức thực hiện sai chứ không phải chủ trương chính sách sai.</p>\r\n\r\n<p><em>PGS nghĩ sao trước nhiều ý kiến cho rằng, việc bổ nhiệm cán bộ hiện nay theo tiêu chí \"bốn ệ\" (hậu duệ, tiền tệ, quan hệ, trí tuệ) dẫn đến tình trạng cán bộ ngồi nhầm chỗ, không đảm bảo được công việc?</em></p>\r\n\r\n<p>Điều đó thật đáng suy ngẫm. Tất nhiên, đó chỉ là dư luận xã hội. Một vấn đề mà xã hội, cử tri lo ngại và nhiều ĐBQH đã phản ánh là việc bổ nhiệm, đề bạt, luân chuyển cán bộ; tuyển dụng công chức, viên chức biểu hiện nhiều tiêu cực. Tình trạng chạy chức, chạy quyền, chạy việc ngày càng lộ diện rõ hơn. Nếu như bộ ngành nào, địa phương nào cũng bổ nhiệm cán bộ theo tiêu chí ưu tiên \"bốn ệ\" như dư luận <a href=\"http://www.doisongphapluat.com/xa-hoi/\">Xã hội</a> đã đề cập thì đội ngũ cán bộ làm được việc, có năng lực, đủ tâm, đủ tầm sẽ bị thui chột. Do đó phải có giải pháp đồng bộ, trước hết, chăm lo xây dựng Đảng thật sự trong sạch vững mạnh, nâng cao năng lực lãnh đạo và sức chiến đấu của Đảng; chăm lo xây dựng đội ngũ cán bộ trước hết là đội ngũ cán bộ chủ chốt.</p>\r\n\r\n<p><em>Để ngăn ngừa việc lạm dụng quyền hạn, ký quyết định bổ nhiệm cán bộ thiếu khách quan, sai quy định như vậy cần những cơ chế giám sát gì, thưa PGS?</em></p>\r\n\r\n<p>Việc phân cấp, phân quyền đã được thực hiện khá tốt trong những năm gần đây, tuy vậy, đối với công tác bổ nhiệm, ngoài đảm bảo đúng quy trình, thủ tục, tiêu chuẩn, điều kiện để bổ nhiệm, phải đảm bảo tính công khai, dân chủ, công bằng. Tất cả phải quy trách nhiệm cho người đứng đầu. Nếu làm tốt, nhất quán, chắc chắn sẽ phòng chống được tiêu cực trong bổ nhiệm, đề bạt cán bộ cũng như thi tuyển công chức, viên chức. Phải thực sự thực hiện tốt nguyên tắc tập trung dân chủ, phát huy vai trò của cấp ủy Đảng và trách nhiệm của người đứng đầu; đặc biệt cần khắc phục thói độc đoán chuyên quyền, gia trưởng của người đứng đầu.</p>\r\n\r\n<p><em>Trân trọng cảm ơn PGS!</em></p>\r\n','break-downclean-up-event-planner',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','499_1439438794_31055cc17cab2b65.png',[now], [now],[user_id],1,'DSPL','',[now]);set @news371 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Ban nhạc Jazz - Flamenco','Công ty tổ chức sự kiện Hoàng Gia chuyên cung cấp thiết bị sự kiện - Dàn dựng - Thi công - Trang trí - Tổ chức sự kiện. Cung cấp cho thuê thiết bị sự kiện: Nhà bạt, âm thanh, ánh sáng, sân khấu, bàn ghế, trang trí hoa các loại.','<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><span style=\"font-size:14px;\">Rồng Việt là một doanh nghiệp lâu năm trong lĩnh vực&nbsp;<a href=\"http://sukienrongviet.vn/\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0);\">tổ chức sự kiện</a>&nbsp;đặc biệt là tổ chức chương trình ca nhac, biểu diễn nghệ thuật.Chúng tôi luôn luôn khai thác và sáng tạo các loại hình nghệ thuật đặc sắc, mới mẻ để chinh phục người tiêu dùng bằng nhiều hình thức khác nhau. Qúy khách muốn tổ chức chương trình ca nhạc, biểu diễn nghệ thuật phục vụ khai trương khánh thành, khởi động động thổ, hội nghị hội thảo, lễ ra mắt giới thiệu sản phẩm, tổ chức lễ rút thăm trúng thưởng, …thì Hãy đến với Rồng Việt. Chúng tôi sẽ thiết kế phù hợp theo từng chủ đề theo yêu cầu của khách hàng, nội dung chương trình xuyên suốt mang tính chủ đạo cao và đặc biệt là chi phí rất hợp lý.</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"http://sukienrongviet.vn/media/images/to-chuc-bieu-dien-nghe-thuat%202.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 450px; height: 250px;\" /></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><span style=\"font-size:14px;\">Rồng Việt có đội ngũ&nbsp;<a href=\"http://sukienrongviet.vn/dich-vu-chinh/nhan-su-su-kien.html\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0);\">nhân viên tổ chức event</a>&nbsp;nhiều năm kinh nghiệm dàn dựng các chương trình nghệ thuật chuyên nghiệp với các đạo diễn, biên đạo múa, ca nhạc sĩ, nghệ sĩ ngôi sao hàng đầu Việt Nam.</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size:14px;\"><input alt=\"\" src=\"http://sukienrongviet.vn/media/files/dich-vu-to-chuc-chuong-trinh-ca-nhac-bieu-dien-nghe-thuat.jpg\" style=\"margin: 0px; padding: 0px; width: 400px; height: 300px;\" type=\"image\" /></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size:14px;\"><input alt=\"\" src=\"http://sukienrongviet.vn/media/files/dich-vu-to-chuc-chuong-trinh-ca-nhac-bieu-dien-nghe-thuat-1.jpg\" style=\"margin: 0px; padding: 0px; width: 400px; height: 220px;\" type=\"image\" /></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><span style=\"font-size:14px;\">Chúng tôi tổ chức chương trình ca nhạc, biểu diễn nghệ thuật cùng với các dịch vụ đi kèm :</span></p>\r\n\r\n<ul style=\"margin: 0px 0px 0px 25px; padding-right: 0px; padding-left: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">\r\n	<li style=\"margin: 0px; padding: 0px; list-style: outside;\"><span style=\"font-size:14px;\"><a href=\"http://sukienrongviet.vn/dich-vu-chinh/thiet-bi-su-kien.html\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0);\">Cho thuê thiết bị,</a>&nbsp;thiết kế lắp đặt sân khấu, giàn không gian, bàn ghế</span></li>\r\n	<li style=\"margin: 0px; padding: 0px; list-style: outside;\"><span style=\"font-size:14px;\">Lắp đặt hệ thống âm thanh, ánh sáng hiện đại</span></li>\r\n	<li style=\"margin: 0px; padding: 0px; list-style: outside;\"><span style=\"font-size:14px;\">Cung cấp đội ngũ nhân sự phục vụ cho chương trình nhạc</span></li>\r\n</ul>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"http://sukienrongviet.vn/media/images/to-chuc-bieu-dien-nghe-thuat.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 350px; height: 233px;\" /></span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><span style=\"font-size:14px;\"><span style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0);\">Các bạn có thể tham khảo thêm hình ảnh&nbsp;từ các&nbsp;</span><a href=\"http://sukienrongviet.vn/su-kien-da-to-chuc.html\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 0, 0);\">sự kiện&nbsp;đã tổ chức</a><span style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0);\">&nbsp;thành công của Rồng Việt.</span></span></p>\r\n','ban-nhac-jazz---flamenco',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','88_1439536659_19755cd961327397.png',[now], [now],[user_id],0,'DSPL','',[now]);set @news372 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories225,'Nguyên Giám đốc Sở Xây dựng Cà Mau bị kỷ luật vì \"ưu ái\" vợ mới','Nguyên Giám đốc Sở Xây dựng Cà Mau Nguyễn Quốc Định vừa bị xử lý kỷ luật cảnh cáo về mặt Đảng và chính quyền.','<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: justify;\">Trung thu là tết tình thân,tết của mọi nhà,Rồng Việt với kinh nghiệm lâu năm khâu tổ chức hân hạnh đem đến cho bạn một buổi tối trung thu mới mẻ,sáng tạo và đầy hấp dẫn.Chúng tôi đảm nhận tư vấn kịch bản,lên kế hoạch tổ chức,cung cấp nhân sự,thiết bị sự kiện phục vụ cho buổi tiệc trung thu của quí khách.Với sự chuyên nghiệp,năng động chúng tôi tự tin đáp ứng được mọi yêu cầu của buổi tiệc với bất cứ quy mô,bất cứ phong cách nào.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><img alt=\"\" src=\"http://sukienrongviet.vn/media/images/loi-chuc-tet-trung-thu-hay-va-y-nghia-nhat_1.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 502px; height: 376px;\" /></p>\r\n\r\n<h3 style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;\"><strong style=\"margin: 0px; padding: 0px;\">Chúng tôi chuyên cung cấp :</strong></h3>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">- Lên ý tưởng,kịch bản cho chương trình trung thu.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">- Cung cấp lân,rồng,trống hội phục vụ cho buổi lễ trung thu.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">- MC dẫn chương trình,chị Hằng,chú Cuội.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">- Thiết bị phục vụ trang trí buổi lễ trung thu.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">- Nhân sự phục vụ buổi lễ trung thu.</p>\r\n\r\n<h3 style=\"margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;\"><strong style=\"margin: 0px; padding: 0px;\">Một số gói dịch vụ chính :</strong></h3>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><strong style=\"margin: 0px; padding: 0px;\">1.</strong><strong style=\"margin: 0px; padding: 0px;\">HỘI TRĂNG RẰM &nbsp;: 57,000,000 VNĐ</strong></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Bao gồm :</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Sân khấu : 30m2</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Âm thanh , ánh sáng : Biểu diễn nghệ thuật</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Phướn dọc</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Backdrop nội dung chương trình</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Trang trí Sân khấu</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chị Hằng , Chú Cuội</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ ảo Thuật</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Xiếc Thú</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Múa Lân</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Trống Hội</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Bóng bay nghệ thuật</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chương trình Gameshow</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><strong style=\"margin: 0px; padding: 0px;\">2.</strong><strong style=\"margin: 0px; padding: 0px;\">VẦNG TRĂNG YÊU THƯƠNG : 15,000,000 VNĐ</strong></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">Bao gồm :</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Âm thanh , ánh sáng.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Backdrop Nội dung chương trình</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chị Hằng</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chú Cuội</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Múa lân</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Múa Trống</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><strong style=\"margin: 0px; padding: 0px;\">3.&nbsp;</strong><strong style=\"margin: 0px; padding: 0px;\">TUỔI CỦA TRĂNG : 8,000,000 VNĐ</strong></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Âm thanh</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chị Hằng</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Lân</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Trống</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\"><strong style=\"margin: 0px; padding: 0px;\">4.&nbsp;</strong><strong style=\"margin: 0px; padding: 0px;\">DỊCH VỤ TỰ CHỌN</strong></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chị Hằng : 1,500,000 VNĐ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Chú Cuội : 1,500,000 VNĐ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Lân : 800,000 VNĐ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Trống &nbsp;: 800,000 VNĐ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Xiếc Thú : 2,500,000 VNĐ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ ảo Thuật : 2,500,000 VNĐ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif;\">+ Bơm bóng nghệ thuật :1,000,000 VNĐ/ h.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><img alt=\"\" src=\"http://sukienrongviet.vn/media/images/mua%20lan.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 500px; height: 375px;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><img alt=\"\" src=\"http://sukienrongviet.vn/media/images/mc-chi-hang.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 500px; height: 304px;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-size: 12px; line-height: 18px; font-family: Arial, Helvetica, sans-serif; text-align: center;\"><img alt=\"\" src=\"http://sukienrongviet.vn/media/images/kd1_1.jpg\" style=\"margin: 0px; padding: 0px; border: none; width: 500px; height: 375px;\" /></p>\r\n','nguyen-giam-doc-so-xay-dung-ca-mau-bi-ky-luat-vi-uu-ai-vo-moi',1,'','',[site_id],[user_id],'/media/images/137/news/ava/','505_1439536761_46555cd9679b8054.png',[now], [now],[user_id],0,'DSPL','Văn Dũng',[now]);set @news373 = LAST_INSERT_ID();
