INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product691,'','<p>Công ty cổ phần đầu tư và công nghệ &nbsp;HVC là đại diện phân phối và cung cấp thiết bị bể bơi của hãng<strong>Astralpool - Tây Ban Nha</strong>&nbsp;tại Việt Nam. Đây là hãng thiết bị sản xuất thiết bị bể bơi lớn nhất thế giới hiện nay. Thiết bị bể bơi của hãng Astralpool đa dạng và đồng bộ cung cấp đầy đủ mọi thiết bị cho một bể bơi cao cấp. Astralpool đã có mặt tại thị trường Việt Nam trên 20 năm nên hiện tại Astralpool là thương hiệu phổ biến nhất được nhiều khách hàng biết đến.</p>\r\n\r\n<p>Với lợi thế là đại lý lớn nhất phân phối thiết bị của hãng tại Việt Nam và nhập khẩu trực tiếp thiết bị nên HVC luôn cạnh tranh về giá thành sản phẩm. Ngoài ra chế độ bảo hành của HVC cũng áp dụng đúng theo cam kết của nhà sản xuất với thời gian của một số thiết bị lên đến 5 năm.</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"line-height:normal;height:267px;\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiết bị bể bơi astral\" border=\"0\" height=\"100\" src=\"http://hvcgroup.net/images/stories/astral%20binh%20loc.jpg\" title=\"thiết bị bể bơi astral\" width=\"140\" /></p>\r\n\r\n			<p>Bình lọc cát</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiet bi be boi astral\" border=\"0\" height=\"100\" src=\"http://hvcgroup.net/images/stories/astral%20catridge.jpg\" title=\"thiet bi be boi astral\" width=\"142\" /></p>\r\n\r\n			<p>Bình lọc lỗi giấy</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiet bi be boi\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20bom.jpg\" title=\"thiet bi be boi\" /></p>\r\n\r\n			<p>Bơm dùng cho bình lọc</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiết bị bể bơi\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20boi%20nguoc%20dong.jpg\" title=\"thiết bị bể bơi\" /></p>\r\n\r\n			<p>Bộ tạo sóng ngược dòng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiet bi\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20inlet.jpg\" title=\"thiet bi\" /></p>\r\n\r\n			<p>Đầu cấp nước vào bể</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"be boi astral\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20skimmer.jpg\" title=\"be boi astral\" /></p>\r\n\r\n			<p>Tấm thu nước mặt bể</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiết bị\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20lat%20ranh.jpg\" title=\"thiết bị\" /></p>\r\n\r\n			<p>Tấm lát rãnh xung quanh</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"bể bơi astral\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20thang.jpg\" title=\"bể bơi astral\" /></p>\r\n\r\n			<p>Thang lên xuống bể bơi</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"bể bơi\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20dieu%20khien%20hoa%20chat.jpg\" title=\"bể bơi\" /></p>\r\n\r\n			<p>Bộ điều khiển hóa chất</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20ve%20sinh.jpg\" /></p>\r\n\r\n			<p>Bộ dụng cụ làm vệ sinh</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiet bi be boi\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20den.jpg\" title=\"thiet bi be boi\" /></p>\r\n\r\n			<p>Đèn halogel chiếu sáng</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiet bi be boi astral\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20valve.jpg\" title=\"thiet bi be boi astral\" /></p>\r\n\r\n			<p>Hệ thống van điều khiển</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:160px;\">\r\n			<p><img alt=\"thiết bị bể bơi\" border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20mosaic.jpg\" title=\"thiết bị bể bơi\" /></p>\r\n\r\n			<p>Gạch mosaic ốp lát bể</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20cover.jpg\" /></p>\r\n\r\n			<p>Tấm phủ giữ nhiệt vệ sinh</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20phao.jpg\" /></p>\r\n\r\n			<p>Hệ thống phục vụ thi đấu</p>\r\n			</td>\r\n			<td style=\"width:160px;\">\r\n			<p><img border=\"0\" src=\"http://hvcgroup.net/images/stories/astral%20spa.jpg\" /></p>\r\n\r\n			<p>Bể&nbsp; matsa jacuzzi</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Để&nbsp; biết nhiều hơn thông tin về các sản phẩm&nbsp; bể bơi Astralpool, xin Quý khách hàng vui lòng liên hệ với công ty&nbsp;</p>\r\n','','','','','',[site_id]);
