INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories332,'Tự khắc phục nhanh cho tủ lạnh Bosch','Tủ lạnh Bosch khá phổ biến trong mỗi gia đình hiện đại. Thiết bị gì không riêng gì tủ lạnh sau một thời gian sử dụng đều xảy ra những hư hỏng không thể tránh. Để hiểu biết hơn về cách vận hành hay cách sửa chữa tủ lạnh khi xảy ra lỗi chúng tôi khuyên bạn nên tham khảo bài viết dưới đây','<h3>Tủ lạnh không sáng đèn</h3>\r\n\r\n<p><em>Nguyên nhân:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Công tắc đèn bị kẹt</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bóng đèn bị hỏng</p>\r\n\r\n<p><em>Cách khắc phục:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thay bóng đèn mới</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kiểm tra xem có thể thao tác công tắc dễ dàng không</p>\r\n\r\n<p><strong>Thực phẩm bị đông quá cứng</strong></p>\r\n\r\n<p>Dùng một vật cùn để nạy thực phẩm đông lạnh. Không được dùng các vật sắc bén hoặc dao vì sẽ làm hư hỏng đường ống dẫn chất làm lạnh hoặc các bề mặt bằng nhựa</p>\r\n\r\n<p><strong>Tủ lạnh không lạnh</strong></p>\r\n\r\n<p><em>Nguyên nhân:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chưa bật công tắc bật/tắt nguồn</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cúp điện hoặc cầu chì bịhư; cắm phích cắm chưa đúng</p>\r\n\r\n<p><em>Cách khắc phục:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nhấn nút bật/tắt nguồn</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kiểm tra xem nguồn điện và cầu chì có đang bật hay không</p>\r\n\r\n<p><strong>Đèn hiện thị nhiệt độ nhấp nháy</strong></p>\r\n\r\n<p><em>Nguyên nhân:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mở cửa thường xuyên.</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Các lỗ thông gió bị bịt kín</p>\r\n\r\n<p><em>Cách khắc phục:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Loại bỏ các chướng ngại vật</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đặt quá nhiều thực phẩm ngăn đá Không mở cửa tủ lạnh khi không cần thiết</p>\r\n\r\n<p><strong>Tủ lạnh quá lạnh</strong></p>\r\n\r\n<p><em>Nguyên nhân:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nhiệt độ cài quá thấp</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nếu đông lạnh một lượng lớn thực phẩm tươi, nhiệt độ trong ngăn lạnh sẽtạm thời giảm vì bộ phận làm lạnh vận hành lâu</p>\r\n\r\n<p><em>Cách khắc phục:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Không lưu trữ quá dung tích đông lạnh tối đa</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tăng nhiệt độ</p>\r\n\r\n<p><strong>Thời gian làm lạnh lâu hơn</strong></p>\r\n\r\n<p><em>Nguyên nhân:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đông lạnh lượng thực phẩm tươi&nbsp;quá nhiều</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lỗ thông khí bị che khuất.</p>\r\n\r\n<p><em>Cách khắc phục:</em></p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Không nên lưu trữvượt quá dung tích đông lạnh tối đa</p>\r\n\r\n<p style=\"margin-left:4.05pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Loại bỏcác chướng ngại vật</p>\r\n','tu-khac-phuc-nhanh-cho-tu-lanh-bosch',1,'','',[site_id],[user_id],'/media/images/144/news/ava/','764_1442195522_56655f628420b8c1.png',[now], [now],[user_id],0,'','',[now]);set @news588 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories320,'10 nguyên tắc an toàn với lò nướng Bosch','Việc sử dụng lò nướng điện Bosch hiện nay ngày càng phổ biện, đặc biệt những tháng cuối năm nay (năm 2013). Không giống như các loại bếp gas hay lò vi sóng, nguy cơ gây cháy nổ, gây mất an toàn cao, tuy nhiên lò nướng điện Bosch cũng tiềm ẩn một sự không an toàn.','<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">Các nguyên tắc an toàn khi sử dụng lò nướng Bosch <o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">Nếu gia đình bạn đang sở hữu một chiếc&nbsp;lò nướng điện âm tủ Bosch, thì hãy tuân thủ những nguyên tắc an toàn&nbsp;sau đây – vì sự an toàn của bạn và của cả gia đình:<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">1. Không bao giờ rời khỏi lò khi lò đang hoạt động, đặc biệt khi sử dụng thiết bị đựng bằng những vật liệu dễ bắt lửa. Những vật liệu này có thể đốt thành than và dễ bốc cháy. NGUY CƠ CHÁY!<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">2. Nếu thấy khói hay lửa, hãy đóng kín cửa lò để dập tắt lửa. Tắt lò và rút dây cắm ra khỏi ổ điện.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">3. Trẻ em chỉ có thể sử dụng lò khi có sự giám sát của người lớn hoặc chúng đã được hướng dẫn sử dụng lò an toàn và nhấn mạnh các nguy hiểm xảy ra nếu sử dụng sai quy cách.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">4. Trẻ em không nên sử dụng lò khi không có sự giám sát của người lớn trong trường hợp mức sử dụng mức công suất lớn.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">5. Nếu cửa hoặc các đệm cửa bị hỏng thì không được cho lò làm việc chừng nào chưa được người có chuyên môn sửa chữa xong<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">6. Lò nướng có thể sử dụng được ngay sau khi kỹ thuật viên đã sửa chữa.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">7. Cần chú ý khi nướng bằng vỉ nướng. Không cho trẻ em chạm vào thiết bị này.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">8. Trước khi cho đĩa hoặc thiết bị đựng thức ăn trong lò, hãy kiểm tra độ an toàn của chúng với lò. Chỉ sử dụng các dụng cụ an toàn và thích hợp trong lò nướng.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">9. Không để dây cáp nguồn tiếp xúc với đồ vật ẩm ướt và sắc bén hoặc khi lò còn nóng.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 5.75pt; line-height: 12.65pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-size: 9pt; font-family: Tahoma, sans-serif;\">10. Không được che khuất các lỗ thông hơi của lò.<o:p></o:p></span></p>\r\n','10-nguyen-tac-an-toan-voi-lo-nuong-bosch',1,'','',[site_id],[user_id],'/media/images/144/news/ava/','283_1442195423_40955f627df1fc36.png',[now], [now],[user_id],0,'','',[now]);set @news587 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories331,'Sửa lò vi sóng tại Hà Nội','Quy trình sửa chữa Trung tâm dịch vụ thiết bị nhà bếp Châu Âu – Suabep.vn','<p>Thứ nhất:</p>\r\n\r\n<p>Khi khách hàng gọi đến văn phòng Suabep.vn, Chậm nhất sau 2h chúng tôi sẽ cử nhân viên kỹ thuật đến nhà khách hàng để kiểm tra.</p>\r\n\r\n<p>Thứ hai:</p>\r\n\r\n<p>Sau khi kỹ thuật đến và kiểm tra lò vi sóng của khách hàng sẽ báo bệnh của lò vi sóng và báo giá link kiện cho khách hàng.</p>\r\n\r\n<p>Thứ ba:</p>\r\n\r\n<p>Sau khi đã báo giá sửa chữa, giá link kiện cho khách hàng nhân viên kỹ thuật sẽ tiến hành sửa chữa.</p>\r\n\r\n<p>Trong trường hợp quý khách hàng không nhất chí với báo giá sửa chữa, báo giá link kiện chúng tôi cam kết không thu của khách hàng bất kỳ một chi phí nào của khách hàng (chi phí kiểm tra bếp, chi phí đi lại...) Để đảm bảo quyển lợi cho khách hàng.</p>\r\n\r\n<p>Thứ tư:</p>\r\n\r\n<p>Khi nhân viên kỹ thuật tiến hành sửa chữa cho khách hàng song. Sau đó sẽ viết phiếu thu kiêm phiếu bảo hành nghi đầy đủ thông tin khách hàng và phải có dấu của trung tâm. Đồng thời gi rõ thời gian bảo hành.</p>\r\n\r\n<p>Chúng tôi cam kết đối với những thiết bị, link kiện mà chúng tôi đã sửa chữa, thay thế trong thời gian còn bảo hành (có phiếu bảo hành của trung tâm) nếu có hỏng hóc chúng tôi không thu thêm bất kỳ một khoản chi phí nào của khách hàng.</p>\r\n\r\n<p>Đối với nhân viên kỹ thuật khi đến nhà khách hàng bắt buộc phải tiến hành theo đúng quy định trên của trung tâm. Nếu nhân viên kỹ thuật không thực hiện đúng Quý khách hàng xin vui lòng liên hệ với trung tâm&nbsp; theo&nbsp;SĐT:<img height=\"32\" src=\"file:///C:/Users/T420/AppData/Local/Temp/msohtmlclip1/01/clip_image001.gif\" width=\"32\" />0988 364 056 chúng tôi sẽ sử lý nghiêm khắc đối với nhân viên kỹ thuật. Để đảm bảo quyền lợi của khách hàng chúng tôi mong nhận được sự phản hồi của khách hàng nếu sự sai sót của nhân viên kỹ thuật.</p>\r\n\r\n<p>Xin chân thành cảm ơn sự tin tưởng của quý khách hàng!</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng điện không vào.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng đèn vẫn sáng nhưng thức ăn không nóng.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng bị đánh lửa trong lò vi sóng.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng bị hỏng mạch cao tần.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng bị vỡ đĩa.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng bị mất sóng.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng không quay đĩa.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng không hoạt động.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng bị chập cháy.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng bị lỗi mạch, bị hỏng mạch đồng hồ.</p>\r\n\r\n<p style=\"margin-left:8.65pt;\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lò vi sóng hỏng biến áp nguồn.</p>\r\n\r\n<p>Dịch vụ của chúng tôi sẽ đem đến sự hài lòng và tin tưởng cho quý khách hàng!</p>\r\n','sua-lo-vi-song-tai-ha-noi',1,'','',[site_id],[user_id],'/media/images/144/news/ava/','811_1442195282_61055f6275232ab0.jpg',[now], [now],[user_id],0,'','',[now]);set @news586 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories331,'Cách bảo quản sử dụng lò vi sóng','1. Những hướng dẫn chung<br />\r\n<br />\r\n- Những dụng cụ nấu nướng của lò vi sóng chỉ được dùng riêng cho việc nấu, rã đông hoặc hâm nóng trong lò vi sóng.<br />\r\n<br />\r\n- Không dùng giấy bạc để bao bọc những hộp đựng thức ăn khi hâm nóng trong lò vi sóng﻿﻿<br />\r\n<br />\r\n- Không nên sử dụng những dụng cụ bằng kim loại trong lò vi sóng vì chúng sẽ ngăn cản và làm chuyển hướng sóng điện từ trong lò.<br />\r\n<br />\r\n- Các dụng cụ nấu nướng sẽ nóng lên khi bạn dùng chúng để hâm nóng thức ăn. Do vậy, nên đeo găng tay trước khi chạm vào chúng.<br />\r\n<br />\r\n','<p>- Bạn nên biết chính xác thời gian cần thiết để nấu một món ăn trong lò vi sóng vì món ăn có thể bị thay đổi mùi vị nếu được nấu quá chín trong lò.</p>\r\n\r\n<p>- Cho muối vào giai đoạn cuối của quá trình nấu nướng thay vì cho ngay vào lúc đầu như cách nấu thông thường.</p>\r\n\r\n<p>- Các dụng cụ nấu nướng phải có kích thước tương ứng với ngăn chứa bên trong của lò vi sóng.</p>\r\n\r\n<p>- Thời gian chờ của lò cũng rất quan trọng vì trong suốt khoảng thời gian này, thức ăn sẽ chín thêm nhờ hơi nóng bên trong.</p>\r\n\r\n<p>- Không phải món ăn nào cũng thích hợp với lò vi sóng. Do vậy, bạn cần tìm hiểu kỹ để tránh nấu những món ăn không phù hợp.</p>\r\n\r\n<p>- Nếu dùng lò vi sóng để nấu món trứng, hãy chọc thủng lòng đỏ trứng trước khi cho món ăn vào lò để trứng có thể chín đều.</p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">2. Cách đặt một số loại thức ăn trong khi nấu﻿﻿<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">Vị trí đặt món ăn trong lò vi sóng có thể giúp quá trình nấu diễn ra nhanh hơn. Sau đây là một số nguyên tắc của việc đặt thức ăn bên trong lòng lò vi sóng:<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Bạn nên đặt những thực phẩm có hình tròn ở phần bên ngoài của mâm quay (hình tròn) bên trong lò vi sóng với những khoảng cách đều nhau.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Nên đảo lại thức ăn sau khoảng một nửa thời gian nấu để mọi thứ được chín đều.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Đối với các loại rau như bông cải, bạn hãy cho chúng vào dưới đáy của vật đựng (như thố, tô…).<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">3. Sử dụng lò vi sóng để rã đông thực phẩm﻿﻿<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">Bạn nên kiểm tra một số vấn đề sau trước khi bắt đầu rã đông:<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Cần biết chắc trọng lượng và hình dáng của thực phẩm.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Nên tách rời các thực phẩm nhỏ và cắt nhỏ những thực phẩm lớn.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Đảm bảo thời gian rã đông riêng biệt đối với những phần thịt quá lớn.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Đối với thịt băm, bạn hãy tách nhỏ chúng trước khi rã đông.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Lột bỏ hết lớp bao nhựa đựng thịt trước khi cho chúng vào rã đông.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">4. Sử dụng lò vi sóng để hâm nóng thức ăn<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">Để hâm nóng thức ăn, hãy lưu ý một số vấn đề sau:<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Ngoại trừ những vật đựng bằng thủy tinh được dành riêng cho lò vi sóng, chỉ nên sử dụng những loại thố, tô, đĩa… bằng nhựa có thể dùng cho lò vi sóng để hâm nóng thức ăn.<o:p></o:p></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 7.5pt; line-height: 16.5pt; box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"font-family: Tahoma, sans-serif;\">- Cần đậy thật chặt những vật đựng thức ăn khi cho chúng vào lò.<o:p></o:p></span></p>\r\n','cach-bao-quan-su-dung-lo-vi-song',1,'','',[site_id],[user_id],'/media/images/144/news/ava/','136_1442195079_88955f6268800094.png',[now], [now],[user_id],0,'','',[now]);set @news585 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories330,'Sửa bếp từ Bosch chuyên nghiệp, cung cấp linh kiện, mạch điện chính hãng.','- Sửa chữa tại nhà, không mang về, linh kiện chính hãng, 100% kỹ sư Bách Khoa giỏi đảm nhiệm. Nhận sửa những pan bệnh khó cho các bạn thợ với giá ưu đãi.<br />\r\n<br />\r\n- Tự hào là đại lý bảo hành ủy quyền của nhiều hãng bếp từ có thương hiệu như: Frico, Kitchmate, Chefs, Munchen, Giovani, Caso, Rommelsbacher, Indufix, Steba, Erolla, Aeg, Bosch, Teka, Siemens, Baumatic…<br />\r\n<br />\r\n- Đến với công ty Toàn Linh bạn sẽ được trả lời về tình trạng bếp từ nhà bạn lỗi gì, nguyên nhân gây hư hỏng, nếu là lỗi nhẹ hoặc lỗi do','<p>CHUYÊN NGHIỆP HƠN VỚI QUY TRÌNH SỬA BẾP TỪ THEO 5 BƯỚC.</p>\r\n\r\n<p>– Sửa bếp từ theo quy trình 5 bước giúp Quý khách hàng kiểm soát được quá trình thợ sửa chữa bếp từ và giúp thợ giỏi sửa chữa bếp từ tốt hơn, tránh bỏ sót giai đoạn không kém phần quan trọng như: Bảo dưỡng vệ sinh bụi bẩn ở bếp từ (mặt kính, quạt gió, mạch điện). Kiểm tra cách nắp đặt bếp từ có đúng quy định không (nguồn điện, ổ cắm, dây dẫn điện, dán keo chống nước). Hướng dẫn cho khách hàng sử dụng bếp từ đúng cách, an toàn, đạt hiệu suất cao trong việc nấu nướng, tiết kiệm điện. Hướng dẫn chọn mua, sử dụng và bảo quản nồi từ…</p>\r\n\r\n<p>SAU ĐÂY KỸ THUẬT VIÊN TOÀN LINH&nbsp;XIN GIỚI THIỆU QUY TRÌNH 5 BƯỚC SỬA BẾP TỪ HIỆU QUẢ.</p>\r\n\r\n<p>– Bước 1: Khi khách hàng gọi điện đến tổng đài sửa bếp từ: chúng tôi sẽ tiếp nhận thông tin từ khách hàng (tên bếp từ, tình trạng hỏng, địa chỉ nhà, số điện thoại liên lạc khi cần thiết…), sau khoảng 30 phút chúng tôi sẽ cử nhân viên kỹ thuật đến kiểm tra bếp từ, bếp hồng ngoại của gia đình.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>– Bước 2: Sau khi kỹ thuật đến và kiểm tra bếp của khách hàng sẽ báo bệnh của bếp và nguyên nhân gây hỏng, đưa ra phương án sửa chữa đối với lỗi đã xác định và báo giá sửa chữa cho khách hàng duyệt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>– Bước 3: Sau khi đã báo giá sửa chữa, nếu khách hàng đồng ý nhân viên kỹ thuật sẽ tiến hành sửa chữa (thay thế linh kiện chính hãng, vệ sinh bụi bẩn ở mạch điện và quạt gió, kiêm tra dây dẫn và các giắc cắm trên mạch điện).</p>\r\n\r\n<p>Trong trường hợp khách hàng không nhất chí với báo giá sửa chữa, chúng tôi cam kết không thu của khách hàng bất kỳ một chi phí nào của khách hàng (chi phí kiểm tra bếp, chi phí đi lại…) để đảm bảo quyển lợi cho khách hàng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>– Bước 4: Sửa bếp từ xong sẽ tiến hành lắp đặt lại vị trí cũ và chạy thử để kiểm tra: nguồn điện, dòng điện tiêu thụ, tốc độ nấu, cảm biến báo nhiệt, dây dẫn, ổ cắm… có đạt tiêu chuẩn không.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>– Bước 5: Nếu đạt yêu cầu nhân viên kỹ thuật sẽ báo cáo để khách hàng nghiệm thu, viết phiếu thu kiêm phiếu bảo hành nghi đầy đủ thông tin khách hàng, tình trạng hỏng, nội dung sửa, giá tiền và thời gian bảo hành.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chúng tôi cam kết đối với những thiết bị, linh kiện mà chúng tôi đã sửa chữa, thay thế trong thời gian còn bảo hành (có phiếu bảo hành của công ty) nếu có hỏng hóc chúng tôi không thu thêm bất kỳ một khoản chi phí nào của khách hàng.</p>\r\n\r\n<p>Xin chân thành cảm ơn sự tin tưởng của quý khách hàng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Các lỗi bếp từ Bosch thường hay gặp phải như:</p>\r\n\r\n<p>- Bếp không nhận nồi.</p>\r\n\r\n<p>- Bếp điện vào chập chờn.</p>\r\n\r\n<p>- Bếp đun nhưng không nóng hoặc nóng chậm.</p>\r\n\r\n<p>- Bếp đun được vài phút thì tự tắt.</p>\r\n\r\n<p>- Bếp bật không nên nguồn.</p>\r\n\r\n<p>- Bếp đang đun xì khói, đánh lửa, có mùi khét.</p>\r\n\r\n<p>- Bếp đang đun tự tắt và báo lỗi.</p>\r\n\r\n<p>- Bếp đang đun tự tắt nguồn, chập điện, nhảy aptomat.</p>\r\n\r\n<p>- Bếp không điều khiển được.</p>\r\n\r\n<p>- Bếp bị vỡ mặt kính</p>\r\n\r\n<p>- Bếp báo lỗi: E0, E1, E2, E3, E4, E5, E6, E7, E8, E9…</p>\r\n\r\n<p>NHỮNG ĐIỀU CẦN CHÚ Ý KHI SỬ DỤNG BẾP TỪ ĐỨC.</p>\r\n\r\n<p>Bếp từ nội địa Đức được thiết kế chạy ở nguồn điện 230V-50Hz ổn định, điều kiện khí hậu lạnh và khô nên rất tốt cho các thiết bị điện tử. Còn ở Việt Nam có khí hậu nóng và ẩm, nguồn điện 220V-50Hz không ổn định, có nhiều côn trùng gây hại như rán và kiến, không tốt cho các thiết bị điện tử. Bếp từ Đức được thiết kế theo tiêu chuẩn Châu Âu, khi về Việt Nam do chưa được “nhiệt đới hóa” nên nếu không biết cách sử dụng thị bếp cũng rất nhanh hỏng.</p>\r\n\r\n<p>Để khắc phục nhược điểm này, kỹ thuật viên sửa bếp từ chúng tôi khuyên người sử dụng những điều sau:<br />\r\n- Bếp từ Đức có công suất lớn nên khi cấp điện cho bếp phải dùng dây có tiết diện lớn (tối thiểu phải bằng tiết diện dây của bếp). Không nên dùng phích điện, công tắc, ổ cắm nối dài cho bếp mà phải dùng aptomat từ 30A đến 40A để cấp điện cho bếp.<br />\r\n- Nguồn điện ở nước ta thấp hơn ở Đức và không ổn định nên không được bất bếp ở chế độ cực đại như chế độ Boot, Power, không bật bếp ở nấc cao nhất mà hãy bật dưới một hoặc hai nấc, đề phòng quá tải khi điện yếu sẽ làm hỏng bếp.</p>\r\n\r\n<p>- Không được tắt bếp bằng cách ngắt điện của bếp mà phải tắt bằng phím nguồn trên mặt bếp, rồi đợi cho quạt gió quạt nguội bếp và dừng hẳn mới được rút điện ra.</p>\r\n\r\n<p>- Trong quá trình sử dụng nên cấp điện thường xuyên cho bếp để phòng chống ẩm cho mạch điện, tránh được hư hỏng do bếp bị sốc điện và mô ve đánh lửa mỗi khi rút điện ra hoặc cắm điện vào.</p>\r\n\r\n<p>- Chú ý vệ sinh bụi bẩn ở khe quạt thông gió để việc lưu thông không khí làm mát bếp được tốt hơn, khi không dùng bếp thì không nên đặt nồi trên bếp phòng khi mèo, chuột… chạy qua sẽ tự khởi động bếp, gây hư hại cho bếp và dụng cụ nấu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cung cấp linh kiện, bo mạch nguồn, bo mạch điều khiển, mâm từ, quạt gió chính hãng, chất lượng cao, bảo đảm uy tín cho các bạn thợ.</p>\r\n\r\n<p>Nhận sửa các pan bệnh khó cho các bạn thợ với giá ưu đãi.&nbsp;</p>\r\n','sua-bep-tu-bosch-chuyen-nghiep-cung-cap-linh-kien-mach-dien-chinh-hang',1,'','',[site_id],[user_id],'/media/images/144/news/ava/','712_1442194168_14155f622f834311.png',[now], [now],[user_id],1,'','',[now]);set @news584 = LAST_INSERT_ID();
