INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by) VALUES (null,'Thiết kế Web bán hàng','<p><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Website giới thiệu sản phầm, bán hàng chiếm trên 50% các mô hình website đang hoạt động. &nbsp;Web bán hàng phân cấp thành nhiều mức độ phức tạp, tùy thuộc vào mô hình hoạt động, khả năng đầu tư và kế hoạch dài hạn của Doanh nghiệp.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"http://WEBNHAT.VN.com/data/media/1/images/2014/website-ban-hang.jpg\" style=\"width: 670px; height: 326px;\" /></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Xác định đặc điểm hàng hóa để lựa chọn tính năng hợp lý. Dưới đây là một số tính năng cần xem xét trước khi áp dụng cho website của bạn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Thiết kế web bán hàng chuẩn SEO là giải pháp giúp quý khách không những có được một website bán hàng chuyên nghiệp, giá cả hợp lý mà còn có thể giúp quý khách tự tối ưu từ khóa lên trang nhất Google.<br />\r\nChúng tôi hiểu rằng bạn gặp rất nhiều khó khăn trong việc lựa chọn đối tác thiết kế web giá rẻ , vì thế hãy chọn Công ty Đại Thống vì bạn sẽ được sở hữu một website chuẩn SEO chuyên nghiệp và chỉ thanh toán khi hài lòng, công cụ quản trị cực kỳ dễ dùng để thêm mới hay sửa đổi nội dung cho website</span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Với hơn 10 năm kinh nghiệm trong lĩnh vực thiết kế web bán hàng, chúng tôi đã phát triển một mã nguồn riêng (Không dùng mã nguồn mở) nên có thể đáp ứng mọi yêu cầu thiết kế web đặc biệt. Và qua khảo sát nhu cấu thiết kế web bán hàng chúng tôi đã tổng hợp lại các chức năng mà một website bán hàng chuyên nghiệp cần phải có:</span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Quản lý&nbsp;danh mục sản phẩm (không giới hạn cấp danh mục)<br />\r\nQuản lý&nbsp;sản phẩm (Tên, ảnh minh họa, chi tiết, giá niêm yết, giá bán tại cửa hàng...)<br />\r\nQuản lý&nbsp;bài viết giới thiệu, hướng dẫn mua hàng, tin tức<br />\r\nQuản lý giá bán chuyên nghiệp và có thể cập nhật giá bán hàng loạt để tiết kiệm thời gian&nbsp;<br />\r\nCho phép đặt hàng trực tuyến và thông báo có đơn đặt hàng mới qua Email</span></span><br />\r\n<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Có thể tự viết Title, Keywords, Description để tối ưu hóa website với công cụ tìm kiếm (Google)&nbsp;<br />\r\nTìm kiếm sản phẩm<br />\r\nHỗ trợ trực tuyến<br />\r\nThống kê truy cập</span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Xin quý khách lưu ý: Nếu quý khách cần thiết kế các chức năng đặc biệt để phục vụ nhu cầu kinh doanh riêng biệt xin liên hệ để được tư vấn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Bảo hành &amp; bảo trì mã nguồn: <strong>Vĩnh viễn</strong><br />\r\nThời gian thiết kế: 7 ngày<br />\r\nHỗ trợ và hướng dẫn quản trị website: 24/7<br />\r\nHotline: <strong></strong> (Mr. Dũng)</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n',[site_id],[user_id],'thiet-ke-web-ban-hang',[now],[now],[user_id]);
