INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'THƯ NGỎ','\"Gia sư\" không đơn giản chỉ là một người thầy truyền đạt kiến thức cho con em bạn mà họ còn là một người bạn, một tấm gương về đạo đức và nhân cách sống trong suốt quá trình học tập để con bạn có thể học hỏi. Do đó, người thầy ngoài việc phải đảm bảo về trình độ chuyên môn, kỹ năng sư phạm để giúp học trò tìm thấy \"cảm hứng\" học tập còn phải có nhân cách đạo đức tốt, lối sống lành mạnh...\r\nĐảm bảo Quý phụ huynh khi đã nhận Gia sư từ chúng tôi sẽ không khi nào phải thay đổi gia sư khác, vì nếu điều đó xảy ra sẽ làm xáo trộn rất nhiều đến tâm lý, quá trình học tập của các em học sinh và cuộc sống của gia đình. Đó là điều mà chúng tôi không mong muốn.','<h3>CÁC HOẠT ĐỘNG CỦA TRUNG TÂM</h3>\r\n\r\n<h3>1. Gia sư bậc tiểu học tại Hà Nội:</h3>\r\n\r\n<div>§&nbsp;&nbsp;Trumg tâm gia sư nhận các lớp mầm non – tiểu học cơ sở.</div>\r\n\r\n<div>§&nbsp;&nbsp;Gia sư lớp 1 – 2: Luyện phát âm, ghép vần, tô chữ, học toán nhanh, luyện chữ, luyện nói: Ngọng, lắp …</div>\r\n\r\n<div>§&nbsp;&nbsp;Gia sư lớp 3 – 4 – 5: Luyện chữ đẹp, hướng dẫn làm văn, học toán nhanh, tư duy làm văn – toán, học tiếng anh tiểu học …</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<h3>2. Gia sư bậc trung học cơ sở tại Hà Nội:</h3>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Gia sư toán&nbsp;các lớp từ 6 – 7 – 8 – 9: theo chương trình sách giáo khoa và chương trình nâng cao – ôn thi vào lớp 10.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Gia sư văn các lớp từ 6 – 7 – 8 – 9: theo chương trình sách giáo khoa và chương trình nâng cao – ôn thi vào lớp 10.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Gia sư các môn khác: Văn, Sử, Địa, Sinh, Anh, Lý, Hóa …</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><a href=\"http://3.bp.blogspot.com/-qyI7l6rWkAQ/VWxBWTQ-RnI/AAAAAAAAABc/7mHbRnkIv5c/s1600/ti%25E1%25BA%25BFng%2Banh.jpg\"><img border=\"0\" src=\"http://3.bp.blogspot.com/-qyI7l6rWkAQ/VWxBWTQ-RnI/AAAAAAAAABc/7mHbRnkIv5c/s640/ti%25E1%25BA%25BFng%2Banh.jpg\" /></a></div>\r\n\r\n<div>\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>3.&nbsp;Gia sư bậc trung học phổ thông tại Hà Nội:</h3>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Trung tâm gia sư&nbsp;dạy kèm, luyện thi Đại học – Cao đẳng Khối A: với các môn Toán – Lý – Hóa bắt đầu hệ thống từ lớp 10 – 11 – 12 theo chương trình cơ bản SGK và các chương trình nâng cao – ôn thi đại học.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Trung tâm gia sư&nbsp;luyện thi Đại học – Cao đẳng Khối B: Với các môn Toán – Hóa – Sinh bắt đầu hệ thống từ lớp 10 – 11 – 12 theo chương trình cơ bản SGK và các chương trình nâng cao – ôn thi đại học tại Hà Nội.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Trung tâm gia sư&nbsp;luyện thi Đại học – Cao đẳng Khối C: Với các môn:&nbsp;Văn – Sử – Địa&nbsp;bắt đầu hệ thống từ lớp 10 – 11 – 12 theo chương trình cơ bản SGK và các chương trình nâng cao – ôn thi đại học.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Trung tâm gia sư&nbsp;dạy kèm, luyện thi Đại học – Cao đẳng Khối D: Với các môn: Toán – Văn – Anh bắt đầu hệ thống từ lớp 10 – 11 – 12 theo chương trình cơ bản SGK và các chương trình nâng cao – ôn thi đại học.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Gia sư các môn khác:&nbsp;Năng khiếu: Nhạc, Thể thao, Vẽ, …</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><a href=\"http://4.bp.blogspot.com/-85d-A7eJVPw/VWxBiImsTjI/AAAAAAAAABk/H01N4cryIUg/s1600/gi%25C3%25A2su.jpg\"><img border=\"0\" src=\"http://4.bp.blogspot.com/-85d-A7eJVPw/VWxBiImsTjI/AAAAAAAAABk/H01N4cryIUg/s640/gi%25C3%25A2su.jpg\" /></a></div>\r\n\r\n<div>\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>4. Gia sư cho người đi làm tại Hà Nội:</h3>\r\n&nbsp;\r\n\r\n<div>- Ngoại Ngữ tại Hà Nội:</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Tiếng anh: Tiếng anh giao tiếp cơ bản, ngữ pháp tiếng anh … chương trình học theo nhóm, học 1 – 1 …</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Tiếng Trung: Tiếng Trung giao tiếp cơ bản, ngữ pháp tiếng Trung … chương trình học theo nhóm, học 1 – 1 …</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Tiếng Nga: Tiếng Nga giao tiếp cơ bản, ngữ pháp tiếng Nga… chương trình học theo nhóm, học 1 – 1 …</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Tiếng Pháp: Tiếng Pháp giao tiếp cơ bản, ngữ pháp tiếng Pháp … chương trình học theo nhóm, học 1 – 1 …</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Tiếng Nhật: Tiếng Nhật giao tiếp cơ bản, ngữ pháp tiếng Nhật … chương trình học theo nhóm, học 1 – 1 …</div>\r\n\r\n<div>- Gia sư tin học văn phòng tại Hà Nội:</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Đào tạo tin học văn phòng cấp tốc – các chương trình nâng cao Word và Excel.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Đào tạo Kế toán cấp tốc.</div>\r\n\r\n<div>§&nbsp;&nbsp;&nbsp;Đào tạo quản trị hệ thống website.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<h3>5. Chương trình luyện thi:</h3>\r\n\r\n<div>§&nbsp;&nbsp;Các chương trình&nbsp;luyện thi vào lớp 10&nbsp;giành cho các trường chuyên và không chuyên.</div>\r\n\r\n<div>§&nbsp;&nbsp;Các chương trình luyện thi Đai học theo từng chủ để – từ dễ tới khó.</div>\r\n\r\n<div>§&nbsp;&nbsp;Luyện thi Toeic</div>\r\n\r\n<div>§&nbsp;&nbsp;Luyện thi Toefl</div>\r\n\r\n<div>§&nbsp;&nbsp;Luyện thi Ielts</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;<a href=\"http://2.bp.blogspot.com/-VQ-aD9A5Niw/VVHFwgtyGhI/AAAAAAAAABY/eTBt_D7g2bE/s1600/hinh.jpg\"><img border=\"0\" src=\"http://2.bp.blogspot.com/-VQ-aD9A5Niw/VVHFwgtyGhI/AAAAAAAAABY/eTBt_D7g2bE/s640/hinh.jpg\" /></a></div>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n','/media/images/111/intro/ava/','947_1433035940_292556a64a4dcc35.jpg','','','',[now],[now]);
