INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories451,'Viet Nam to take smart grid to save power','Applied since 2005 in many developed countries, the smart power grid uses information and communications technology to gather and act on information, such as information about the behaviors of suppliers and consumers, in an automated fashion to improve the efficiency, reliability, economics, and sustainability of the production and distribution of electricity.','<p>Applied since 2005 in many developed countries, the smart power grid uses information and communications technology to gather and act on information, such as information about the behaviors of suppliers and consumers, in an automated fashion to improve the efficiency, reliability, economics, and sustainability of the production and distribution of electricity.</p>\r\n\r\n<p>Deputy PM Hoang Trung Hai has instructed the Ministry of Industry and Trade to make a scheme on developing smart grid in Viet Nam with specific stages, targets and criteria. In the short term, relevant legal documents and policies will be put forth.</p>\r\n','viet-nam-to-take-smart-grid-to-save-power',1,'','',[site_id],[user_id],'/media/images/166/news/ava/','155_1444103417_538561344f916ada.jpg',[now], [now],[user_id],0,'','',[now]);set @news710 = LAST_INSERT_ID();
