INSERT INTO news_categories (cat_id, site_id, user_id, cat_parent, cat_name, `alias`, cat_order, cat_description, cat_countchild, image_path, image_name, `status`, created_time, modified_time, showinhome, meta_keywords, meta_description) VALUES (null,[site_id],[user_id],0,'Tin Tức Công Ty','tin-tuc-cong-ty',13,'tin tức công ty',0,'/media/images/101/category/ava/','6011404530146_news.jpg',1,[now], [now],1,'','');
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'100% mẫu nước ở Trạm cấp nước Mỹ Đình II chứa chất gây ung thư','Ngày 7/7, Sở Y tế Hà Nội công bố 100% mẫu nước được lấy tại Trạm cấp nước Mỹ Đình II đều có hàm lượng thạch tín vượt ngưỡng cho phép. Sở Y tế cũng yêu cầu đóng cửa Trạm cấp nước này để khắc phục tình trạng trên.','<p style=\"text-align: justify;\">Trước đó, trong 2 ngày mùng 2 và 5.7, Trung tâm Y tế dự phòng (Sở Y tế Hà Nội) và Viện kiểm nghiệm ATVSTP quốc gia đã tiến hành lấy 14 mẫu nước (bao gồm nước thô, nước qua xử lý tại Trạm, nước lấy ở hộ dân) để xét nghiệm hàm lượng thạch tín.</p>\r\n\r\n<p style=\"text-align: justify;\">Đến nay, trong 13 mẫu được kiểm tra thì cả 13 mẫu đều có hàm lượng thạch tín vượt ngưỡng cho phép. Chiếu theo Quy chuẩn kỹ thuật quốc gia về chất lượng nước ăn uống QCVN 01:2009/BYT do Bộ Y tế ban hành năm 2009, giới hạn hàm lượng thạch tín cho phép trong nước chỉ là 0,01mg/l thì 100% các mẫu nước này đều không an toàn về hàm lượng thạch tín.</p>\r\n\r\n<p style=\"text-align: justify;\">Cụ thể, hàm lượng thạch tín dao động từ 0,018mg/l đến 0,079mg/l. Như vậy, các mẫu nước ở Trạm cấp nước Mỹ Đình II có thạch tín vượt ngưỡng từ 2 - gần 8 lần.</p>\r\n\r\n<p style=\"text-align: justify;\">Trước tình trạng này, Sở Y tế Hà Nội đề nghị Sở Xây dựng yêu cầu Công ty HUDS dừng hoạt động cấp nước của Trạm cấp nước Mỹ Đình II. Trạm cấp nước Mỹ Đình II chỉ được phép tiếp tục hoạt động khi chất lượng nước cung cấp đảm bảo theo đúng qui định. Trong thời gian dừng hoạt động, Công ty HUDS có trách nhiệm đảm bảo việc cung cấp nước đầy đủ để người dân sử dụng.</p>\r\n\r\n<p style=\"text-align: justify;\">Trước đó, từ ngày 27-30/6 Bộ Y tế đã tổ chức kiểm tra, giám sát chất lượng nước tại 16 nhà máy nước và 07 trạm cấp nước trên địa bàn Hà Nội, trạm cấp nước khu đô thị Nam Đô; chất lượng nước cấp sau đồng hồ tại một số hộ gia đình tại 06 quận nội thành với tổng số mẫu là 196 mẫu (Trong đó 30 mẫu lấy tại các nhà máy, trạm cấp nước và 166 mẫu lấy tại các hộ gia đình). Kết quả xét nghiệm mẫu nước cho thấy có một số mẫu nước không đạt các chỉ tiêu chất lượng theo QCVN 01:2009/BYT.<br />\r\n&nbsp;<br />\r\nKết quả xét nghiệm tại 16 nhà máy nước và 07 trạm cấp nước đánh giá 107 chỉ tiêu (theo QCVN 01/2009) có 05/107 chỉ tiêu không đạt: Chỉ tiêu Asen có 1/20 cơ sở cấp nước là trạm cấp nước Mỹ Đình II có nồng độ cao hơn 1,82 lần ngưỡng cho phép (xét nghiệm thực tế 0,0182 mg/L); Chỉ tiêu Clo dư (20/20 cơ sở cấp nước có nồng độ cao hơn ngưỡng cho phép và 150/155 mẫu lấy tại hộ gia đình thấp hơn ngưỡng cho phép) (mức dao động từ 0.02 - 1,33 mg/L); Chỉ tiêu Amoni (từ 0,02 - 7,89 mg/L) (có 7/20 cơ sở cấp nước và 15/155 mẫu tại hộ gia đình và pecmanganat (2,56 - 9,60 mg/L) (có 12/20 &nbsp;mẫu tại cơ sở cấp nước và 40/155 mẫu tại hộ gia đình). Hai chỉ tiêu không đạt này chủ yếu ở khu vực Quận Hoàng Mai); Mangan (0,361 mg/L) (có 1/20 cơ sở cấp nước cao hơn nồng độ cho phép, tại nhà máy nước Sơn Tây, cơ sở 1).<br />\r\nKết quả xét nghiệm tại Khu đô thị Nam Đô: Nước cấp từ công ty kinh doanh nước sạch cung cấp đến bể chứa của khu đô thị: 02 chỉ tiêu Clo dư và pecmanganat không đạt QCVN 01:2009/BYT. Nước từ bể đến các hộ gia đình: Tại các bể mái có sự tồn tại của vi sinh vật (coliform và coliform chịu nhiệt) ở mức độ thấp (dưới 50CFU/100ml) và không đạt chỉ tiêu nitrit. Tại hộ gia đình có 4/15 mẫu nước không đạt chỉ tiêu nitrit và 15/15 không đạt chỉ tiêu pecmanganat và clo dư.</p>\r\n\r\n<p style=\"text-align: justify;\">Theo Bộ Y tế, việc ăn nước nhiễm Asen lâu ngày có thể gây nhiều bệnh lý cho cơ thể, như xuất hiện mảng sừng dày trên da, rối loạn tiêu hóa, xơ gan, tăng huyết áp… Asen cũng là một trong những chất độc có thể gây ung thư.</p>\r\n\r\n<p style=\"text-align: justify;\">Tú Anh</p>\r\n','100-mau-nuoc-o-tram-cap-nuoc-my-dinh-ii-chua-chat-gay-ung-thu',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','8281404794700_nuoc-ban-7714-94b37.jpg',[now], [now],[user_id],1,'','',[now]);set @news21 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'Dân Mỹ Đình không biết nước sinh hoạt nhiễm độc tố Asen','Trong 10 hộ gia đình được hỏi, chỉ có 1 hộ duy nhất trả lời có biết thông tin, nhưng gia đình vẫn sử dụng nước bình thường.','<p style=\"text-align: justify;\">Chiều 3/7, một ngày sau khi Bộ Y tế có kiến nghị dừng hoạt động Trạm cấp nước Mỹ Đình II (thuộc Công ty TNHH một thành viên dịch vụ nhà ở và khu đô thị – HUD) - nơi cung cấp nước cho toàn bộ các hộ dân sống trong Khu đô thị Mỹ Đình II do nước sinh hoạt cung cấp cho người dân không đảm bảo an toàn, có nồng độ asen trong nước cao gấp gần 4 lần mức cho phép, phóng viên có mặt tại Khu đô thị Mỹ Đình II để tìm hiểu ý kiến của người dân.<br />\r\n<a href=\"http://eurotec.vn/data/media/101/images/Nuoc sach.jpg\"><img alt=\"\" src=\"http://eurotec.vn/data/media/101/images/Nuoc%20sach.jpg\" style=\"width: 450px; height: 675px;\" /></a><br />\r\n<strong>Bác Bẩy cho chúng tôi xem nguồn nước nhà bác đang sử dụng</strong><br />\r\nQua hỏi thăm khoảng 10 hộ gia đình sống trong khu đô thị, bao gồm cả khu chung cư và khu nhà biệt thự, chỉ có duy nhất 1 hộ gia đình sống ở tòa nhà CT4 trả lời có biết thông tin (xem qua TV), còn lại không ai trong số những người được hỏi hay biết thông tin nguồn nước cung cấp cho gia đình mình bị nhiễm asen.<br />\r\n&nbsp;<br />\r\nCác hộ gia đình đều cho biết họ vẫn sử dụng nước bình thường và không thấy có thông báo tạm dừng cấp nước cũng như không nhận thấy những dấu hiệu bất thường trong nguồn nước.<br />\r\n&nbsp;<br />\r\nTrao đổi với nhân viên Xí nghiệp 2 (thuộc Công ty TNHH một thành viên dịch vụ nhà ở và khu đô thị - HUD) làm nhiệm vụ bảo vệ tại các tòa nhà chung cư trong khu đô thị Mỹ Đình II họ cũng không biết gì về thông tin này. Các nhân viên bảo vệ cũng không cho phép phóng viên lên gặp người dân trên các tòa chung cư khi chưa được phép của lãnh đạo Xí nghiệp.<br />\r\n&nbsp;<br />\r\nDò hỏi các vị khách trong một quán nước gần tòa nhà CT4, phóng viên may mắn gặp được bác Bẩy – tổ trưởng dân phố ở tòa nhà CT4 và được ông cho biết, ông cũng không có thông tin gì về việc nước nhiễm asen ở khu đô thị Mỹ Đình II. Bản thân gia đình ông cũng không nhận thấy những dấu hiệu đặc biệt gì trong nước sử dụng. Tận mắt chứng kiến nguồn nước ở nhà bác Bẩy, chúng tôi cũng không thấy có dấu hiệu gì đặc biệt. Nước trong, không mùi, để lắng không thấy có cặn.<br />\r\n&nbsp;<br />\r\nBác Bẩy cho biết, năm 2013, nhiều người dân sống trong tòa nhà của ông phát hiện trong nước sinh hoạt ở nhà mình có màu đỏ, đục, có mùi tanh, hôi… Sự việc đã được đại diện người dân thông báo cho Công ty TNHH một thành viên dịch vụ nhà ở và khu đô thị (HUD) và yêu cầu được sử dụng nước sạch Sông Đà thay vì nguồn nước giếng khoan mà họ đang sử dụng.<br />\r\n&nbsp;<br />\r\nVới lý do, trong quá trình xây dựng, HUD đã đầu tư khá nhiều tiền để xây dựng trạm cấp nước đồng bộ cho toàn bộ khu đô thị Mỹ Đình II, nên việc người dân muốn chuyển sang dùng nước sạch Sông Đà sẽ gây lãng phí cho công trình mà HUD đã đầu tư. HUD cũng hứa với người dân trong khu đô thị xử lý việc nước nhiễm asen bằng cách đầu tư thêm hệ thống lọc cho Trạm cấp nước Mỹ Đình II, sau đó mẫu nước đã được gửi đi xét nghiệm ở Viện Khoa học công nghệ Việt Nam địa chỉ 18 Hoàng Quốc Việt và nhận được chứng nhận an toàn, nồng độ asen trong mức cho phép.<br />\r\n<img alt=\"\" src=\"http://eurotec.vn/data/media/101/images/Nuoc%20sach1.jpg\" style=\"width: 450px; height: 300px;\" /><br />\r\n<strong>Đa phần các hộ gia đình đều lắp thêm hệ thống lọc nước trong nhà mình</strong><br />\r\nVụ việc sau đó lắng xuống, tuy nhiên người dân ở đây vẫn không tin tưởng hoàn toàn vào chất lượng nước, đa phần các gia đình đều lắp riêng hệ thống lọc nước, hay loại máy khử độc nước trong nhà mình, có nhà mua riêng nước Lavie để phục vụ ăn uống.<br />\r\n&nbsp;<br />\r\nGia đình bác Bẩy cũng như gia đình bác Viển đã sống trong khu đô thị Mỹ Đình II được 9-10 năm. Từ khi dọn vào đây sinh sống, các gia đình cũng “chung thân” sử dụng nguồn nước giếng khoan do Trạm cấp nước Mỹ Đình II xử lý và cung cấp cho các gia đình.<br />\r\n&nbsp;<br />\r\nBác Bẩy cùng nhiều người dân sống ở tòa nhà CT4 cho biết, chiều 2/7 có thấy một đoàn cán bộ của Viện Vệ sinh dịch tễ xuống làm việc với lãnh đạo Xí nghiệp 2 tuy nhiên họ không biết thêm thông tin gì về cuộc làm việc này.<br />\r\n<strong>&nbsp;Theo Thanh Hà (Vov.vn)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n','dan-my-dinh-khong-biet-nuoc-sinh-hoat-nhiem-doc-to-asen',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','341404448520_Nuoc sach.jpg',[now], [now],[user_id],1,'','',[now]);set @news18 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'Kinh hoàng với kết quả kiểm tra nước sinh hoạt tại Hà Nội','Kết quả xét nghiệm 196 mẫu nước trên địa bàn TP Hà Nội do Bộ Y Tế công bố 2/7 đã tỏ rõ mối lo ngại về chất lượng nước ăn uống mà người dân thủ đô đang hứng chịu.\r\n','<p style=\"text-align: justify;\"><strong>Hàng loạt mẫu nước ở các trạm cấp không đạt chuẩn</strong></p>\r\n\r\n<p style=\"text-align: justify;\">Thực hiện theo ý kiến chỉ đạo của Phó Thủ tướng Vũ Đức Đam, Bộ Y tế đã thành lập 2 Đoàn kiểm tra công tác quản lý nhà nước về chất lượng nước sinh hoạt tại thành phố Hà Nội và thành phố Hồ Chí Minh.</p>\r\n\r\n<p style=\"text-align: justify;\">Trong 3 ngày (27-30/6), Bộ Y tế đã kiểm tra chất lượng nước tại 16 nhà máy nước và 7 trạm cấp nước trên địa bàn Hà Nội, trạm cấp nước khu đô thị Nam Đô và tại một số hộ gia đình tại 6 quận nội thành; đồng thời lấy 196 mẫu xét nghiệm.</p>\r\n\r\n<p><img alt=\"\" src=\"http://eurotec.vn/data/media/101/images/Nuoc-sinh-hoat.jpg\" style=\"width: 450px; height: 245px;\" /><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Kết quả tại 16 nhà máy nước và 7 trạm cấp nước có 5 /107 tiêu chí không đạt ngưỡng theo QCVN01/2009-BYT về chất lượng nước ăn uống là: Clo dư, Amoni, Pecmanganat, Asen, Mangan.</p>\r\n\r\n<p style=\"text-align: justify;\">Tất cả 20/20 mẫu tại 20 cơ sở cấp nước được lấy đều có nồng độ Clo dư cao hơn ngưỡng cho phép. Trong khi 150 trên 155 mẫu lấy tại hộ gia đình, clo dư lại thấp hơn ngưỡng. Có trong 7/20 mẫu tại cơ sở cấp nước và 15/155 mẫu tại hộ gia đình không đạt chỉ tiêu Amoni.</p>\r\n\r\n<p style=\"text-align: justify;\">Tỷ lệ không đạt này với chỉ tiêu Pecmanganat tương tự lần lượt là 12/20 và 40/155, chủ yếu ở khu vực Quận Hoàng Mai. Về chỉ tiêu Mangan có 1/20 cơ sở cấp nước cao hơn nồng độ cho phép tại nhà máy nước Sơn Tây, cơ sở 1. Trạm cấp nước Mỹ Đình II không đạt chỉ tiêu về asen khi có nồng độ cao gần gấp 2 lần ngưỡng cho phép.</p>\r\n\r\n<p style=\"text-align: justify;\">Tại buổi làm việc của Bộ Y tế với UBND TP Hà Nội sáng 2/7, Thứ trưởng Nguyễn Thanh Long đã đề nghị trạm cấp nước Mỹ Đình (thuộc Công ty TNHH một thành viên dịch vụ nhà ở và khu đô thị) phải dừng hoạt động cấp nước cho đến khi đảm bảo nồng độ Asen trong nước cấp phù hợp QCVN01/2009-BYT.</p>\r\n\r\n<p><img alt=\"\" src=\"http://eurotec.vn/data/media/101/images/Nuoc-sinh-hoat1.jpg\" style=\"width: 450px; height: 338px;\" /><br />\r\n<em><strong>Thứ trưởng Nguyễn Thành Long trong buổi kiểm tra tình hình cấp nước tại &nbsp;chung cư Nam Đô ngày 2/7/2014</strong></em></p>\r\n\r\n<p style=\"text-align: justify;\">Cũng trong sáng 2/7, đoàn Bộ Y tế do Thứ trưởng Nguyễn Thanh Long dẫn đầu cũng đến kiểm tra chất lượng nước sinh họat tại tổ hợp Nam Đô 609 Trương Định. Đây là một trong những điểm cấp nước sinh hoạt được Bộ Y Tế lưu tâm sau hàng loạt những phản ánh về chất lượng nước sinh hoạt của người dân không được đảm bảo được đăng tải.</p>\r\n\r\n<p style=\"text-align: justify;\">Tại đây, 16 mẫu nước đã được Viện Sức khỏe nghề nghiệp và Môi trường trực thuộc Bộ Y Tế niêm phong mang đi xét nghiệm từ ngày 27/6.</p>\r\n\r\n<p style=\"text-align: justify;\">Kết quả xét nghiệm nước tại bể chứa của khu đô thị này cho thấy chất lượng nước cũng không đạt cả hai chỉ tiêu về clo dư và pecmanganat. 4/15 mẫu nước được lấy tại các hộ gia đinh không đạt chỉ tiêu nitrit và 15/15 không đạt chỉ tiêu pecmanganat và clo dư. Tại các bể mái có sự tồn tại của vi sinh vật ở mức độ thấp.</p>\r\n\r\n<p style=\"text-align: justify;\"><strong>Tạp chất vượt ngưỡng ít cũng độc như thuốc sâu</strong></p>\r\n\r\n<p style=\"text-align: justify;\">Nghiên cứu của các nhà khoa học cho thấy, Clo dư trong nước có thể phản ứng với các yếu tố khác để hình thành độc tố gọi là trihalomethanes (THMs). THMs có liên quan đến một loạt các chứng bệnh ảnh hưởng tới sức khỏe con người như hen suyễn và eczema, ung thư bàng quang và bệnh tim. Ngoài ra, theo một vài nghiên cứu cho biết clo dư trong nước máy cao có thể làm gia tăng tỉ lệ khuyết tật và sinh non ở phụ nữ mang thai.</p>\r\n\r\n<p style=\"text-align: justify;\">Nước có hàm lượng Clo thấp dưới tiêu chuẩn (0,3mg/lít) dễ bị nhiễm vi sinh có thể gây đau bụng, tiêu chảy…Ngược lại, nước có hàm lượng clo vượt quá tiêu chuẩn (0,5mg/lít) có thể gây ngộ độc.</p>\r\n\r\n<p><img alt=\"\" src=\"http://eurotec.vn/data/media/101/images/Nuoc-sinh-hoat2.jpg\" style=\"width: 450px; height: 263px;\" /><br />\r\n<em><strong>Nước nhiễm bẩn khiến người dân Nam Đô bức xúc gửi đơn khắp nơi cầu cứu</strong></em></p>\r\n\r\n<p style=\"text-align: justify;\">Hàm lượng Amoni vượt ngưỡng là dấu hiệu cho thấy nguồn nước bị ô nhiễm bởi chất thải có nguồn gốc động vật và có thể chứa các vi khuẩn gây bệnh. Bản thân amoni không độc, nhưng trong quá trình khai thác, xử lý và lưu trữ nước, nó có thể chuyển hóa thành nitrit và nitrat.</p>\r\n\r\n<p style=\"text-align: justify;\">Với Nitrit (NO2- ) là ion vô cơ chứa nitơ (N) tự phát sinh trong môi trường và dễ dàng đi vào các nguồn nước, đất… gây ảnh hưởng đến sức khỏe con người và động, thực vật khi hàm lượng vượt quá giới hạn cho phép (QCVN).</p>\r\n\r\n<p style=\"text-align: justify;\">Nitrit rất độc, với hàm lượng 0,01 mg/l đã có thể gây độc hại cho sức khỏe con người. Nitrit có tác dụng oxy hóa hemoglobin (huyết sắc tố) chứa trong hồng cầu, biến hemoglobin thành methemoglobin (trẻ em mắc chứng bệnh này thường xanh xao và dễ bị đe dọa đến mạng sống, đặc biệt là trẻ dưới 6 tháng tuổi).&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Ngoài ra, NO2- &nbsp;trong cơ thể dễ tác dụng với các amin tạo thành nitrosamine là 1 hợp chất tiền ung thư. Hàm lượng nitrosamin cao khiến cơ thể không kịp đào thải, tích lũy lâu ngày trong gan gây ra hiện tượng nhiễm độc, ung thư gan.</p>\r\n\r\n<p style=\"text-align: justify;\">Về chỉ số Pecmanganat, là biểu hiện lượng các chất hữu cơ có trong nước và để xác định mức độ ô nhiễm, nhiều nước hiện không còn dùng chỉ số này.</p>\r\n\r\n<p style=\"text-align: justify;\">Riêng về Asen mà Trạm cấp nước Mỹ Đình hiện đang vượt chuẩn là một chất cực độc khi vượt ngưỡng cho phép. Đây là một chất độc có thể gây ung thư da và phổi . Mặt khác Asen ảnh hưởng đến thực vật như một chất cản trở trao đổi chấtlàm giảm mạnh năng suất , đặc biệt trong môi trường thiếu photpho.</p>\r\n\r\n<p>Theo PGS. TS Trần Hồng Côn, Giảng viên khoa Hóa học, Trường ĐH Khoa học Tự nhiên, ĐHQG Hà Nội: \"Những chỉ tiêu bắt buộc trong nước ăn uống đều phải nằm trong khoảng cho phép mới an toàn với người sử dụng.Những chất này dù lượng nhỏ, nhưng rất độc, giống như độ độc hại của thuốc trừ sâu”.</p>\r\n\r\n<p><strong>Theo Hồng Anh (Soha.vn/Trí thức trẻ)</strong></p>\r\n','kinh-hoang-voi-ket-qua-kiem-tra-nuoc-sinh-hoat-tai-ha-noi',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','761404447956_Nuoc-sinh-hoat1.jpg',[now], [now],[user_id],1,'','',[now]);set @news17 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'Cảnh báo suy kiệt nguồn nước tại Việt Nam','Do đất nước đang trong quá trình đẩy mạnh công nghiệp hóa, hiện đại hóa, nên nhu cầu sử dụng nước cho phát triển kinh tế, phục vụ dân sinh ngày càng lớn khiến tình trạng ô nhiễm, suy thoái, cạn kiệt nguồn nước diễn ra phổ biến và nghiêm trọng.\r\n ','<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">Theo nhận xét của GS.TS Ngô Đình Tuấn, Trường Đại học Thủy lợi: Việc khai thác, sử dụng tài nguyên nước chưa hợp lý và thiếu bền vững đã và đang gây suy giảm tài nguyên nước, trong khi hiệu quả sử dụng nước còn thấp, tình trạng lãng phí trong sử dụng nước còn phổ biến trong phạm vi cả nước. Chẳng hạn như nước được dùng cho sản xuất nông nghiệp nhiều nhất là ở Đồng bằng sông Cửu Long và lưu vực sông Hồng, chiếm tới 70% lượng nước sử dụng. Tuy vậy, diện tích thực tưới thấp hơn nhiều so với diện tích thiết kế (chỉ đạt 68% tổng diện tích được tưới), chứng tỏ hiệu quả sử dụng nước cho nông nghiệp chưa cao.<br />\r\n&nbsp;<br />\r\nNước còn được sử dụng nhiều cho công nghiệp. Theo một nghiên cứu mới đây, nhóm sông có tỷ lệ dùng nước cho công nghiệp cao nhất là lưu vực sông Hồng-Thái Bình, chiếm gần 1/2 tổng lượng nước sử dụng cho ngành công nghiệp cả nước. Trong đó 25% sử dụng nước công nghiệp diễn ra ở lưu vực sông Đồng Nai; 7% ở nhóm sông Đông Nam Bộ và 10% ở lưu vực Cửu Long. Đặc biệt, tỷ lệ sử dụng nước dưới đất cho công nghiệp rất lớn, riêng TP Hồ Chí Minh có đến 57% doanh nghiệp sử dụng nước dưới đất.<br />\r\n&nbsp;<br />\r\nDự báo đến năm 2015, khối lượng nước sử dụng trong công nghiệp sẽ tăng gấp đôi so với năm 2006, mức độ tăng sẽ chủ yếu diễn ra ở lưu vực sông vốn đã là một cơ sở công nghiệp lớn là các lưu vực sông Hồng-Thái Bình, Đồng Nai, nhóm sông Đông Nam Bộ, Cửu Long và Vu Gia-Thu Bồn.<br />\r\n&nbsp;<br />\r\nThứ trưởng Bộ Tài nguyên và Môi trường Nguyễn Thái Lai cho rằng: Luật Tài nguyên nước ban hành năm 1998 là một bước tiến quan trọng trong công tác quản lý tài nguyên nước ở nước ta. Tuy vậy, qua hơn 12 năm thực hiện, Luật cũng đã bộc lộ nhiều nhược điểm, bất cập nên Bộ đã và đang tập trung sửa đổi, bổ sung Luật ...<br />\r\n&nbsp;<br />\r\nMột trong những vấn đề quan trọng nhất trong quản lý tài nguyên nước là quản lý lưu vực sông để điều hòa, phân bổ nguồn nước, phối hợp điều tiết nguồn nước trên sông sử dụng tổng hợp đa mục tiêu, đáp ứng yêu cầu phòng chống lũ, cấp nước cho hạ du và các mục đích khác. Dự thảo Luật quy định cụ thể các nội dung về quản lý lưu vực sông, phân loại lưu vực sông, tổ chức bộ máy quản lý, điều phối hoạt động có liên quan đến tài nguyên nước trong lưu vực sông; phòng chống ô nhiễm, suy thoái, cạn kiệt và ứng phó, khắc phục sự cố ô nhiễm nguồn nước và gắn trách nhiệm, thẩm quyền quản lý nhà nước về lưu vực sông. Đồng thời điều chỉnh, bổ sung các quy định về bảo vệ nước dưới đất, bảo vệ nguồn nước sinh hoạt; kiểm soát chặt chẽ việc cấp phép các hoạt động xả nước thải...<br />\r\n&nbsp;<br />\r\nThanh Tùng<br />\r\nTheo &nbsp;cand.com.vn</span></span></p>\r\n','canh-bao-suy-kiet-nguon-nuoc-tai-viet-nam',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','2131404210807_thuyloi2186-450.jpg',[now], [now],[user_id],1,'','',[now]);set @news16 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'Những tạp chất trong nước với sức khoẻ con người','Xét nghiệm nước là công việc cần làm để tìm ra những vấn đề của nguồn nước. Dựa trên kết quả xét nghiệm ta có thể dễ dàng chọn lựa công nghệ và thiết bị để xử lý nước, những dây chuyền lọc nước tốt nhất và phù hợp nhất cho nguồn nước, và cần một hệ thống xử lý nước chuyên nghiệp, hiện đại.','<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>XỬ LÝ NƯỚC SINH HOẠT TỪ ĐƠN GIẢN ĐẾN PHỨC TẠP</strong><br />\r\nNước có thể nhiễm một hay nhiều chỉ tiêu từ nhẹ đến gây ung thư và nếu bạn có mẫu nước khó xử lý hãy gọi điện thoại, gửi E-mail cho chúng tôi để được tư vấn miễn phí.<br />\r\nXin sơ lược cách đọc các chỉ số trong bảng xét nghiệm như sau:<br />\r\n&nbsp;<strong>1. Mùi vị</strong><br />\r\n* Nước giếng ngầm: mùi trứng thối là do có khí H2S, kết quả của quá trình phân hủy các chất hữu cơ trong lòng đất và hòa tan vào mạch nước ngầm. Mùi tanh của sắt và mangan.<br />\r\n* Nước mặt (sông, suối, ao hồ): mùi tanh của tảo là do sự xuất hiện của các loại tảo và vi sinh vật. Trong trường hợp này nước thường có màu xanh.<br />\r\n* Nước máy: mùi hóa chất khử trùng (clo) còn dư lại trong nước.<br />\r\nMùi vị khác lạ sẽ gây cảm giác khó chịu khi dùng nước. Tuỳ theo loại mùi vị mà có cách xử lý phù hợp như dùng hóa chất diệt tảo trong ao hồ, keo tụ lắng lọc, hấp phụ bằng than hoạt tính,…<br />\r\n<strong>2. Màu</strong><br />\r\n* Màu vàng của hợp chất sắt và mangan.<br />\r\n* Màu xanh của tảo, hợp chất hữu cơ.<br />\r\nNước có độ màu cao thường gây khó chịu về mặt cảm quan. Với các quy trình xử lý như sục khí ozôn, clo hóa sơ bộ, keo tụ, lắng lọc có thể làm giảm độ màu của nước. Cần lưu ý, khi nguồn nước có màu do hợp chất hữu cơ, việc sử dụng Clo có thể tạo ra chất mới là trihalomethane có khả năng gây ung thư.<br />\r\n3. pH<br />\r\nNguồn nước có pH &gt; 7 thường chứa nhiều ion nhóm carbonate và bicarbonate (do chảy qua nhiều tầng đất đá). Nguồn nước có pH &lt; 7 thường chứa nhiều ion gốc axit. Bằng chứng dễ thấy nhất liên quan giữa độ pH và sức khỏe của người sử dụng là nó làm hỏng men răng pH của nước có liên quan đến tính ăn mòn thiết bị, đường ống dẫn nước và dụng cụ chứa nước. Đặc biệt, trong môi trường pH thấp, khả năng khử trùng của Clo sẽ mạnh<br />\r\nhơn. Tuy nhiên, khi pH &gt; 8,5 nếu trong nước có hợp chất hữu cơ thì việc khử trùng bằng Clo dễ tạo thành hợp chất trihalomethane gây ung thư.<br />\r\nTheo tiêu chuẩn, pH của nước sử dụng cho sinh hoạt là 6,0 – 8,5 và của nước uống là<br />\r\n6,5 – 8,5.<br />\r\n<strong>4. Độ đục</strong><br />\r\nĐộ đục là đại lượng đo hàm lượng chất lơ lửng trong nước, thường do sự hiện diện của chất keo, sét, tảo và vi sinh vật.<br />\r\nNước đục gây cảm giác khó chịu cho người dùng và có khả năng nhiễm vi sinh. Tiêu chuẩn nước sạch quy định độ đục nhỏ hơn 5NTU, nhưng giới hạn tối đa của nước uống chỉ là 2 NTU. Các quy trình xử lý như keo tụ, lắng, lọc góp phần làm giảm độ đục của nước.<br />\r\n<strong>5. Độ kiềm</strong><br />\r\nĐộ kiềm của nước là do các ion bicarbonate, carbonate và hydroxide tạo nên. Trong thành phần hóa học của nước, độ kiềm có liên quan đến các chỉ tiêu khác như pH, độ cứng và tổng hàm lượng khoáng. Việc xác định độ kiềm của nước giúp cho việc định lượng hóa chất trong quá trình keo tụ, làm mềm nước cũng như xử lý chống ăn mòn.<br />\r\nHiện nay, không có bằng chứng cụ thể nào liên quan giữa độ kiềm và sức khỏe của người sử dụng. Thông thường, nước dùng cho ăn uống nên có độ kiềm thấp hơn 100 mg/l.<br />\r\n<strong>6. Độ cứng</strong><br />\r\nĐộ cứng là đại lượng đo tổng các cation đa hóa trị có trong nước, nhiều nhất là ion canxi và magiê. Nước mặt thường không có độ cứng cao như nước ngầm. Tùy theo độ cứng của nước người ta chia thành các loại sau:<br />\r\nĐộ cứng từ 0 – 50mg/l -&gt; Nước mềm<br />\r\nĐộ cứng từ 50 – 150mg/l -&gt; Nước hơi cứng<br />\r\nĐộ cứng từ 150 – 300mg/l -&gt; Nước cứng<br />\r\nĐộ cứng &gt; 300mg/l -&gt; Nước rất cứng<br />\r\nNước cứng thường cần nhiều xà phòng hơn để tạo bọt, hoặc gây hiện tượng đóng cặn trắng trong thiết bị đun, ống dẫn nước nóng, thiết bị giải nhiệt hay lò hơi. Ngược lại, nước cứng thường không gây hiện tượng ăn mòn đường ống và thiết bị.Theo tiêu chuẩn nước sạch, độ cứng được quy định nhỏ hơn 350 mg/l. Đối với nước ăn uống, độ cứng nhỏ hơn 300 mg/l. Tuy nhiên, khi độ cứng vượt quá 50 mg/l, trong các thiết bị đun nấu đã xuất hiện cặn trắng. Trong thành phần của độ cứng, canxi và magiê là 2 yếu tố quan trọng thường được bổ sung cho cơ thể qua đường thức ăn. Tuy nhiên, những người có nguy cơ mắc bệnh sỏi thận cần hạn chế việc hấp thụ canxi và magiê ở hàm lượng cao.<br />\r\nCó thể khử độ cứng bằng phương pháp trao đổi ion.<br />\r\n&nbsp;<br />\r\n<strong>7. Tổng chất rắn hòa tan (TDS)</strong><br />\r\nTDS là đại lượng đo tổng chất rắn hòa tan có trong nước, hay còn gọi là tổng chất khoáng. Tiêu chuẩn nước sạch quy định TDS nhỏ hơn 1.000 mg/l. Tiêu chuẩn nước uống quy định TDS nhỏ hơn 500 mg/l.<br />\r\n<strong>8. Độ oxy hóa (Chất hữu cơ)</strong><br />\r\nĐộ oxy hóa được dùng để đánh giá mức độ ô nhiễm của nguồn nước. Có 2 phương pháp xác định độ oxy hóa tùy theo hóa chất sử dụng là phương pháp KMnO4 và K2CrO7. Tiêu chuẩn nước sạch quy định độ oxy hóa theo KMnO44) nhỏ hơn 2 mg/l. nhỏ hơn 4 mg/l. Tiêu chuẩn nước uống quy định độ oxy hóa (theo KMnO<br />\r\n&nbsp;<br />\r\n<strong>9. Nhôm</strong><br />\r\nNhôm là thành phần chính trong các loại đá khoáng, đất sét. Nhôm được dùng trong các ngành công nghiệp sản xuất chất bán dẫn, thuốc nhuộm, sơn và đặc biệt là hóa chất keo tụ trong xử lý nước. Nước khai thác từ vùng đất nhiễm phèn thường có độ pH thấp và hàm lượng nhôm cao.<br />\r\nNhôm không gây rối loạn cơ chế trao đổi chất, tuy nhiên có liên quan đến các bệnh Alzheimei và gia tăng quá trình lão hóa. Tiêu chuẩn nước uống quy định hàm lượng nhôm nhỏ hơn 0,2 mg/l.<br />\r\n<strong>10. Sắt</strong><br />\r\nDo ion sắt hai dễ bị oxy hóa thành hydroxyt sắt ba, tự kết tủa và lắng nên sắt ít tồn tại trong nguồn nước mặt. Đối với nước ngầm, trong điều kiện thiếu khí, sắt thường tồn tại ở dạng ion Fe2+ và hoà tan trong nước. Khi được làm thoáng, sắt hai sẽ chuyển hóa thành sắt ba, xuất hiện kết tủa hydroxyt sắt ba có màu vàng, dễ lắng. Trong trường hợp nguồn nước có nhiều chất hữu cơ, sắt có thể tồn tại ở dạng keo (phức hữu cơ) rất khó xử<br />\r\nlý. Ngoài ra, nước có độ pH thấp sẽ gây hiện tượng ăn mòn đường ống và dụng cụ chứa, làm tăng hàm lượng sắt trong nước.<br />\r\nSắt không gây độc hại cho cơ thể. Khi hàm lượng sắt cao sẽ làm cho nước có vị tanh,<br />\r\nmàu vàng, độ đục và độ màu tăng nên khó sử dụng. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng sắt nhỏ hơn 0,5 mg/l.<br />\r\n<strong>11. Mangan</strong><br />\r\nMangan thường tồn tại trong nước cùng với sắt nhưng với hàm lượng ít hơn. Khi trong nước có mangan thường tạo lớp cặn màu đen đóng bám vào thành và đáy bồn chứa.<br />\r\nMangan có độc tính rất thấp và không gây ung thư. Ở hàm lượng cao hơn 0,15 mg/l có thể tạo ra vị khó chịu, làm hoen ố quần áo. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng mangan nhỏ hơn 0,5 mg/l.<br />\r\n<strong>12. Asen (thạch tín)</strong><br />\r\nDo thấm qua nhiều tầng địa chất khác nhau, nước ngầm thường chứa asen nhiều hơn nước mặt. Ngoài ra asen có mặt trong nguồn nước khi bị nhiễm nước thải công nghiệp, thuốc trừ sâu.<br />\r\nKhi bị nhiễm asen, có khả năng gây ung thư da và phổi. Tiêu chuẩn nước sạch quy định asen nhỏ hơn 0,05 mg/l. Tiêu chuẩn nước uống quy định asen nhỏ hơn 0,01 mg/l.<br />\r\n<strong>13. Cadimi</strong><br />\r\nDo thấm qua nhiều tầng địa chất khác nhau, nước ngầm thường chứa hàm lượng cadimi nhiều hơn nước mặt. Ngoài ra Cadimi còn thấy trong nguồn nước bị nhiễm nước thải công nghiệp khai thác mỏ, nước rỉ bãi rác. Cadimi có thể xuất hiện trong đường ống thép tráng kẽm nếu xảy ra hiện tượng ăn mòn.<br />\r\nCadimi có tác động xấu đến thận. Khi bị nhiễm độc cao có khả năng gây ói mữa. Tiêu chuẩn nước uống quy định Cadimi nhỏ hơn 0,003 mg/l.<br />\r\n<strong>14. Crôm</strong><br />\r\n&nbsp;Crôm có mặt trong nguồn nước khi bị nhiễm nước thải công nghiệp khai thác mỏ, xi mạ, thuộc da, thuốc nhuộm, sản xuất giấy và gốm sứ.<br />\r\nCrôm hóa trị 6 có độc tính mạnh hơn Crôm hóa trị 3 và tác động xấu đến các bộ phận cơ thể như gan, thận, cơ quan hô hấp. Nhiễm độc Crôm cấp tính có thể gây xuất huyết, viêm da, u nhọt. Crôm được xếp vào chất độc nhóm 1 (có khả năng gây ung thư cho người và vật nuôi). Tiêu chuẩn nước uống quy định crôm nhỏ hơn 0,05 mg/l.<br />\r\n<strong>15. Đồng</strong><br />\r\nĐồng hiện diện trong nước do hiện tượng ăn mòn trên đường ống và các dụng cụ thiết bị làm bằng đồng hoặc đồng thau. Các loại hóa chất diệt tảo được sử dụng rộng rãi trên ao hồ cũng làm tăng hàm lượng đồng trong nguồn nước. Nước thải từ nhà máy luyện kim, xi mạ, thuộc da, sản xuất thuốc trừ sâu, diệt cỏ hay phim ảnh cũng góp phần làm tăng lượng đồng trong nguồn nước.<br />\r\nĐồng không tích lũy trong cơ thể nhiều đến mức gây độc. Ở hàm lượng 1 – 2 mg/l đã làm cho nước có vị khó chịu, và không thể uống được khi nồng độ cao từ 5 – 8 mg/l. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng đồng nhỏ hơn 2 mg/l.<br />\r\n<strong>16. Chì</strong><br />\r\nTrong nguồn nước thiên nhiên chỉ phát hiện hàm lượng chì 0,4 – 0,8 mg/l. Tuy nhiên do ô nhiễm nước thải công nghiệp hoặc hiện tượng ăn mòn đường ống nên có thể phát hiện chì trong nước uống ở mức độ cao hơn.<br />\r\nKhi hàm lượng chì trong máu cao có thể gây tổn thương não, rối loạn tiêu hóa, yếu cơ, phá hủy hồng cầu. Chì có thể tích lũy trong cơ thể đến mức cao và gây độc. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng chì nhỏ hơn 0,01 mg/l<br />\r\n<strong>17. Kẽm</strong><br />\r\nKẽm ít khi có trong nước, ngoại trừ bị ô nhiễm từ nguồn nước thải của các khu khai thác quặng. Chưa phát hiện kẽm gây độc cho cơ thể người, nhưng ở hàm lượng &gt; 5 mg/l đã làm cho nước có màu trắng sữa. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng kẽm &lt; 3mg/l.<br />\r\n<strong>18. Niken</strong><br />\r\n&nbsp;Niken ít khi hiện diện trong nước, ngoại trừ bị ô nhiễm từ nguồn nước thải của ngành điện tử, gốm sứ, ắc quy, sản xuất thép.<br />\r\nNiken có độc tính thấp và không tích lũy trong các mô. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng niken nhỏ hơn 0,02mg/l.<br />\r\n<strong>19. Thủy ngân</strong><br />\r\n&nbsp;Thủy ngân hiếm khi tồn tại trong nước. Tuy nhiên các muối thủy ngân được dùng trong công nghệ khai khoáng có khả năng làm ô nhiễm nguồn nước. Khi nhiễm độc thủy ngân các cơ quan như thận và hệ thần kinh sẽ bị rối loạn. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng thủy ngân nhỏ hơn 0,001 mg/l.<br />\r\n<strong>20. Molybden</strong><br />\r\n&nbsp;Molybden ít khi có mặt trong nước. Molybden thường có trong nước thải ngành điện, hóa dầu, thủy tinh, gốm sứ và thuốc nhuộm. Molybden dễ hấp thụ theo đường tiêu hóa và tấn công các cơ quan như gan, thận. Tiêu chuẩn nước uống quy định molybden nhỏ hơn 0,07 mg/l.<br />\r\n<strong>21. Clorua</strong><br />\r\nNguồn nước có hàm lượng clorua cao thường do hiện tượng thẩm thấu từ nước biển hoặc do ô nhiễm từ các lọai nước thải như mạ kẽm, khai thác dầu, sản xuất giấy, sản xuất nước từ quy trình làm mềm.<br />\r\nClorua không gây hại cho sức khỏe. Giới hạn tối đa của clorua được lựa chọn theo hàm lượng natri trong nước, khi kết hợp với clorua sẽ gây vị mặn khó uống. Tiêu chuẩn nước sạch quy định Clorua nhỏ hơn 300 mg/l. Tiêu chuẩn nước uống quy định Clorua nhỏ hơn 250 mg/l.<br />\r\n<strong>22. Amôni – Nitrit - Nitrat</strong><br />\r\nCác dạng thường gặp trong nước của hợp chất nitơ là amôni, nitrit, nitrat, là kết quả của quá trình phân hủy các chất hữu cơ hoặc do ô nhiễm từ nước thải. Trong nhóm này, amôni là chất gây độc nhiều nhất cho cá và các loài thủy sinh. Nitrit được hình thành từ phản ứng phân hủy nitơ hữu cơ và amôni và với sự tham gia của vi khuẩn. Sau đó nitrit sẽ được oxy hóa thành nitrat. Ngoài ra, nitrat còn có mặt trong nguồn nước là do nước thải từ các ngành hóa chất, từ đồng ruộng có sử dụng phân hóa học, nước rỉ bãi rác, nước mưa chảy tràn. Sự có mặt hợp chất nitơ trong thành phần hóa học của nước cho thấy dấu hiệu ô nhiễm nguồn nước.<br />\r\n<strong>23. Sunfat</strong><br />\r\nSunfat thường có mặt trong nước là do quá trình oxy hóa các chất hữu cơ có chứa sunfua hoặc do ô nhiễm từ nguồn nước thải ngành dệt nhuộm, thuộc da, luyện kim, sản xuất giấy. Nước nhiễm phèn thường chứa hàm lượng sunfat cao. Ở nồng độ sunfat 200mg/l nước có vị chát, hàm lượng cao hơn có thể gây bệnh tiêu chảy. Tiêu chuẩn nước uống quy định sunfat nhỏ hơn 250 mg/l.<br />\r\n<strong>24. Florua</strong><br />\r\nNước mặt thường có hàm lượng flo thấp khoảng 0,2 mg/l. Đối với nước ngầm, khi chảy qua các tầng đá vôi, dolomit, đất sét, hàm lượng flo trong nước có thể cao đến 8 – 9 mg/l.<br />\r\nKết quả nghiên cứu cho thấy khi hàm lượng flo đạt 2 mg/l đã làm đen răng. Nếu sử dụng thường xuyên nguồn nước có hàm lượng Flo cao hơn 4 mg/l có thể làm mục xương. Flo không có biểu hiện gây ung thư. Tiêu chuẩn nước uống quy định hàm lượng flo trong khoảng 0,7 – 1,5 mg/l.<br />\r\n<strong>25. Xyanua</strong><br />\r\n&nbsp;Xyanua có mặt trong nguồn nước do ô nhiễm từ các loại nước thải ngành nhựa, xi mạ, luyện kim, hóa chất, sợi tổng hợp. Xyanua rất độc, thường tấn công các cơ quan như phổi, da, đường tiêu hóa. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng xuanua nhỏ hơn 0,07 mg/l.<br />\r\n<strong>26. Coliform</strong><br />\r\n&nbsp;Vi khuẩn Coliform (phổ biến là Escherichia Coli) thường có trong hệ tiêu hóa của người. Sự phát hiện vi khuẩn Coli cho thấy nguồn nước đã có dấu hiệu ô nhiễm. Tiêu chuẩn nước uống và nước sạch đều quy định hàm lượng E. Coliform bằng 0. Riêng Coliform tổng số trong nước sạch được cho phép 50 vi khuẩn / 100 ml.<br />\r\n(trích Handbook of Drinking Water Quality – Standards &amp; Control)<br />\r\n<strong>1. MỘT SỐ BIỆN PHÁP XỬ LÝ NƯỚC ĐƠN GIẢN TỰ LÀM</strong><br />\r\nĐối với nước nhiễm phèn(sắt):<br />\r\nĐối với nước nhiễm phèn, ta xử lý ô nhiễm bằng vôi sống. Lấy 100g vôi sống cho vào 1000l nước, sau đó để nước lắng xuống, gạn lấy nước trong. Nước nhiều sắt thường có màu vàng, mùi tanh. Cách đơn giản để làm sạch nước nhiễm sắt là đổ nước vào thùng, khoắng lên nhiều lần rồi để lắng, chắt lấy nước trong. Có thể dùng phèn chua để xử lý nước nhiễm phèn sắt. Phèn chua giã nhỏ (nửa thìa cho 25 lít nước) đổ vào thùng quấy nhiều lần để sắt và phèn kết tủa lắng dần xuống đáy. Ngoài ra có thể xử lý bằng phương pháp sục khí, qua giàn mưa và bồn lắng, lọc để khử<br />\r\nsắt. Làm giàn mưa bằng ống nhựa, khoan 150 - 200 lỗ có đường kính từ 1,5mm đến 2mm tùy theo công suất máy bơm đang sử dụng. Dưới cùng của bể lọc là lớp sỏi dày khoảng 1 gang, trên lớp sỏi là lớp cát dày khoảng 2,5 - 3 gang. Phương pháp này có thể kết hợp xử lý được một số chất khác với hàm lượng thấp như: Hydrogen sulfite H2S, Amoniac, Asen.<br />\r\n2. Xử lý Hydrogen sulfite H2S:<br />\r\nNước chứa H2S thường không gây tác hại cho sức khoẻ, nhưng nó làm cho nước có mùi và vị của trứng thối. Nước cấp có chứa hàm lượng H2S thấp khoảng 1,0 ppm đã có đặc tính ăn mòn, làm xỉn màu các đố dùng bằng bạc hay đồng, làm cho quần áo và đồ gốm có vết đen. Mua máy bơm sục khi hồ cá về sục khí vào hồ nước kết hợp dùng than củi, than hoạt tính lọc nước. H2S được hấp phụ trên bề mặt của các hạt than. Chúng ta phải định kỳ thay các hạt than trong bể lọc (tùy thuộc vào khả năng hấp phụ của than và hàm lượng H2S trong nước).<br />\r\n3. Xử lý nước cứng:<br />\r\nNước cứng là thuật ngữ dùng để chỉ nước có chứa hàm lượng lớn các ion như Ca2+, Mg2+; loại nước này thường ảnh hưởng đến tuổi thọ các thiết bị sử dụng nước hằng ngày. Các cách xử lý đơn giản:<br />\r\nCách 1: Đun sôi nước sẽ làm các ion này kết tủa.<br />\r\nCách 2: Dùng thiết bị có ngăn chứa các hạt lọc cation. Theo quá trình trao đổi ion các hạt cation mang điện dương sẽ đổI cho các hợp chất mang điện tích dương. Do đó nước sẽ mềm hoặc loạI bỏ được các kim loạI nặng như : Fe, Cu, Zn… để hoàn nguyên hạt nhựa cation hãy dùng nước muốI nồng độ 10% để rửa ngược vật liệu<br />\r\n4. Khử trùng nước sinh họat:<br />\r\nĐể đạt tiêu chuẩn của Bộ Y tế, nước sinh hoạt bắt buộc phải được khử trùng. Phương pháp khả thi rẻ tiền là dùng nước Javen (hypochlorit natri ). Hãy nhớ rằng không được pha quá nồng độ dư lượng chlorine 0,3mg/l. Cũng có thể khử trùng nước bằng ozone hay tia cực tím, nhưng không phù hợp với việc nước sau khử trùng phải tiếp tục lưu chuyển trong đường ống và bể chứa.<br />\r\n2. XỬ LÝ NƯỚC SINH HOẠT PHỨC TẠP<br />\r\n&nbsp;Mỗi nguồn nước có thể nhiễm một hay nhiều tạp chất làm nước không đạt tiêu chuẩn. Để xử lý triệt để các tạp chất gây hại cho người ta cần có kết quả xét nghiệm mẫu nước. Chúng tôi đưa ra vài phương pháp lọc nước phổ biến Chúng ta biết rằng có rất nhiều phương pháp lọc khác nhau như:<br />\r\na.Xây hồ lọc (không áp) : Ở đây cũng có nhiều cách lọc như lọc từ trên xuống, lọc ngược từ dưới lên, lọc ngang. Phương pháp này chiếm diện tích rất lớn để đạt được công suất lọc 1m3/h, quá trình làm vệ sinh rất khó khăn.<br />\r\nb.Lọc áp lực : Thiết kế lọc từ dưới lên, xem ra phương pháp lọc này cũng không tiện lợi. Vì chúng ta dùng một lực nén tứ dưới lên, lúc đó các vật liệu lọc không thể liên kết với nhau tạo thành một lớp lọc bền vững mà chúng tôi luôn ở trạng thái động, chính vì lẽ đó hệ thống lọc không đạt hiệu quả cao.<br />\r\nTương tự cũng có rất nhiều phương pháp lọc khác: lọc khung bản, lọc đĩa, lọc ly tâm… Nhưng xem ra chúng cũng không đạt được mục đích tối ưu, nên chúng tôi đã quyết định chọn phương pháp lọc cột đa lớp trên xuống là hiệu quả và đã có lắp cho rất nhiều đơn vị cùng ngành nói chung và các ngành khác nói riêng.<br />\r\nLọc áp lực trên xuống: tại sao ở đây chúng tôi sử dụng phương pháp lọc áp lực trên xuống, vì lọc ở đây có tác dụng giữ lại các tủa bông và tủa nụ do quá trình oxy hóa từ đầu nguồn tạo ra, ngăn chặn các cặn vật lý do lưu chuyển trên đường ống có kích thước lớn hơn 50 micron nhằm đưa nước đạt độ trong gần cho phép, đồng thời bộ phận này giúp các bộ phận sau hoạt động tốt hơn. Hệ thống này được thiết kế lọc áp lực có dòng chảy từ trên xuống và thiết kế hệ thống van xả rửa ngược từ dưới lên, rất tiện lợi cho người sử dụng.<br />\r\nSau khi chọn được phương pháp tối ưu để thiết kế cột lọc ta tiến hành chọn vật liệu để xử lý nguồn nước bị nhiễm:<br />\r\n- Nhiễm phèn sắt: nước ngầm chảy trong lòng đất khi nhiễm sắt có mùi tanh bùn, nước trong không màu. Sau khi được bơm lên hồ chứa gặp không khí sắt kết tủa, nước có màu vàng, váng và có mùi tanh bùn. Hiểu được nguyên lý trên chúng ta sẽ tiến hành kết hợp các bước sau:<br />\r\nLàm thoáng bằng giàn mưa là cho sắt tiếp xúc không khí có chứa oxy để oxy hoá Fe2 thành Fe3, tạo Fe(OH)3 kết tủa lắng xuống. Bơm nước từ giếng lên cao 3-4 m (càng cao càng tốt) cho qua giàn phun (có thể là ống nhựa đục nhiều lỗ hoặc chậu nhựa đục lỗ cũng đc).chứa nước vào hồ để lắng phèn<br />\r\nPhương pháp làm thoáng cung cấp Ô xy, làm Fe2+ thnh Fe3+, sau đó Fe3+ thủy phn<br />\r\nthnh Fe(OH)3 ít tan, lắng lại v lọc thơ.<br />\r\nPhương trình:<br />\r\n4Fe2+ + O2 + 10H2O = 4Fe(OH)3 + 8H+<br />\r\nLọc áp lực: nước sau khi làm thoáng bơm qua cột xử lý có chứa sỏi đá và chất khử phèn cực mạnh của USA ( cát mangan, đa brim hoặc FILOX). nước sau khi qua cột lọc phèn này hoàn toàn loại bỏ hết phèn. Tùy theo đặc trưng ngành nghề mà có biện pháp xử lý thích hợp: nước lò hơi ngoài các tiêu chuẩn cơ bản còn yêu cầu làm mềm nước, tương tự nước dùng cho xi mạ yêu cầu thêm độ dẫn điện, TDS.............chúng tôi chỉ khái quát vài phương pháp lọc nước cơ bản xử lý làm mềm nước<br />\r\nThiết bị trao đổi ion là các chất vật liệu hạt không hòa tan có trong cấu trúc phân tử các gốc Axit hay Bazơ có thể thay thế được mà không làm thay đổi tính chất vật lý của chúng và cũng không làm biến mất hoặc hòa tan các Ion dương hay âm cố định trên các góc này đẩy Ion cùng dấu có trong dung dịch lỏng. Đó là sự trao đổiIon, cho phép thay đổi thành phần Ion của chất lỏng cần xử lý mà không thay đổi số lượng tải toàn bộ có trong chất lỏng trước khi trao đổi. , thông thường chúng tôi sử dụng Resin sản phẩm của hãng Dow của Mỹ sản xuất có dung tích trao đổi 2Eq.<br />\r\nTheo đó ở cột Cation, khi qua cột này sẽ giữ lại tất cả các Ion kim loại phân ly trong nước như: Ca, Mg, Fe,… và đưa vào nước góc Na+ do quá trình hoàn nguyên nhựa bằng muối ăn (NaCl). Chúng diễn ra theo phương trình sau :<br />\r\nR – Na +[<br />\r\nMg2+<br />\r\nR – [<br />\r\nMg<br />\r\n+ 2Na+<br />\r\nCa2+<br />\r\nCa<br />\r\n·<br />\r\nkhử mui` (Activated Carbon).<br />\r\nThiết bị này có tác dụng giữ tất cả các mùi lại trong nước gây khó chịu khi sử dụng như: NH3, H2S, Clo dư… Nhờ sử dụng than hoạt tính (Activated Carbon). Thực nghiệm chỉ ra rằng than hoạt tính là chất hấp thụ có phổ rất rộng. Phần lớn các phân tử hữu cơ sự cố định trên bề mặt của chúng, các phân tử phân cực lớn và có cấu tạo hàng với khối lượng mol nhỏ ( rượu đơn, axit hữu cơ bậc nhất) rất khó giữ lại. Các phân tử mol cao bị than hoạt tính hấp thụ khá tốt. Ngoài tính chất hấp thụ than hoạt tính còn giúp các vi khuẩn có khả năng phân hủy một phần của pha hấp thụ. Như vậy, một phần của chất tiếp tục tái sinh và có thể giải phóng các vị trí để tiếp tục cố định các phần tử mới.<br />\r\nSau thời gian sử dụng chúng có thể bảo hòa, lúc đó ta tiến hành tái sinh than hoạt tính, có rất nhiều cách như: tái sinh bằng hơi, tái sinh bằng nhiệt, tái sinh bằng hóa chất rất tiện ích. Chi phí tái sinh không đáng kể, ở đây chúng tôi sử dụng than hoạt tính do VN sản xuất có chất lượng tương đương Mỹ</span></span></p>\r\n','nhung-tap-chat-trong-nuoc-voi-suc-khoe-con-nguoi',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','4241404210880_images.jpg',[now], [now],[user_id],0,'','',[now]);set @news15 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'Công nghệ thẩm thấy ngược là gì?','Cho đến nay, thẩm thấu ngược vẫn được coi là là công nghệ cao cấp nhất trong ngành lọc nước, chỉ có nước mới \"chui lọt\" qua được. Chính vì thế, nó còn có tên gọi là Hyberfiltratio','<p style=\"text-align: justify;\">Cho đến nay, thẩm thấu ngược vẫn được coi là là công nghệ cao cấp nhất trong ngành lọc nước, chỉ có nước mới \"chui lọt\" qua được. Chính vì thế, nó còn có tên gọi là Hyberfiltration.</p>\r\n\r\n<p style=\"text-align: justify;\">Công nghệ thẩm thấu ngược là công nghệ lọc nước sử dụng màng siêu lọc, khe lọc 0.0001micron (lọc đến kích thước ion, nguyên tử) để sản xuất nước siêu tinh khiết, ứng dụng trong các lĩnh vực sau:<br />\r\nSản xuất nước tinh khiết đóng chai<br />\r\nTái sử dụng (lọc lại) nước tiểu của các nhà du hành vũ trụ thành nước uống trên các trạm vũ trụ<br />\r\nChạy thân nhân tạo<br />\r\nLọc nước biển thành nước ngọt…<br />\r\nLọc thẩm thấu ngược là công nghệ phát minh tại Mỹ, được xác định là công nghệ lọc nước tiên tiến nhất thế giới hiện nay.</p>\r\n\r\n<p style=\"text-align: justify;\">Thẩm thấu ngược là gì</p>\r\n\r\n<p style=\"text-align: justify;\">Diễn giải một cách nôm na, đó là một quy trình ngược lại của thẩm thấu.<br />\r\nThẩm thấu là một hiện tượng tự nhiên. Nước bao giờ cũng chuyển dịch từ nơi có nồng độ muối/ khoáng thấp đến nơi có nồng độ cao hơn. Quá trình diễn ra cho đến khi nồng độ muối khoáng từ 2 nơi này cân bằng.<br />\r\nĐể làm điều ngược lại (thẩm thấu ngược), người ta dùng một áp lực đủ để đẩy ngược nước từ nơi có hàm lượng muối/ khoáng cao “thấm” qua một loại màng đặc biệt để đến nơi không có hoặc có ít muối/ khoáng hơn.<br />\r\nMàng RO<br />\r\nLà một màng mỏng làm từ vật liệu Cellulose Acetate, Polyamide hoặc màng TFC có những lỗ nhỏ tới 0.001 micron. Tất cả các màng này đều chịu áp suất cao nhưng khả năng chịu pH và chlorine không giống nhau.<br />\r\nQuá trình<br />\r\nVới tốc độ và áp lực cực lớn, dòng nước chảy liên tục trên bề mặt của màng RO. Một phần trong số những phân tử nước “chui” qua được những lỗ lọc. Các tạp chất bị dòng nước cuốn trôi và “thải” bỏ ra ngoài. Với cách thức này, bề mặt của màng RO liên tục được rửa sạch và có tuổi thọ tới 2 – 5 năm.</p>\r\n','cong-nghe-tham-thay-nguoc-la-gi',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','7191404210950_mang RO.jpg',[now], [now],[user_id],1,'','',[now]);set @news14 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories14,'So sánh giữa công nghệ lọc nước NANO và RO (NANO vs RO)','So sánh giữa công nghệ lọc nước NANO và RO (NANO vs RO)\r\nRất nhiều người thắc mắc công nghệ lọc nước Nano là gì và nó có ưu việt gì so với công nghệ lọc nước RO hay không. Vì vậy, chúng tôi giới thiệu với các bạn bài viết phân biệt sự khác nhau giữa công nghệ lọc nước nano và công nghệ lọc nước RO theo cách dễ hiểu nhất.','<p style=\"text-align: justify;\">Rất nhiều người thắc mắc công nghệ lọc nước Nano là gì và nó có ưu việt gì so với công nghệ lọc nước RO hay không. Vì vậy, chúng tôi giới thiệu với các bạn bài viết phân biệt sự khác nhau giữa công nghệ lọc nước nano và công nghệ lọc nước RO theo cách dễ hiểu nhất.Để cho tiện theo dõi, chúng tôi xin tóm tắt bài viết như sau:</p>\r\n\r\n<p style=\"text-align: justify;\">1. Mức độ lọc:<br />\r\n• Công nghệ RO (thẩm thấu ngược) là công nghệ lọc nước tiên tiến và triệt để nhất hiện nay. Vì các khe hở màng lọc RO có kích cỡ 0,001 micromet, giống như cơ chế hoạt động của thận người sẽ cho ra sản phẩm nước hoàn toàn nguyên chất.<br />\r\n• Công nghệ Nano có các khe hở to hơn. Như vậy sẽ không loại bỏ hoàn toàn hàm lượng chất khoáng. Tất nhiên chất đó sẽ có loại có lợi hoàn toàn hay không thì chưa có câu trả lời vì còn phụ thuộc vào nước đầu vào.<br />\r\n2. Yếu điểm:<br />\r\n• Công nghệ lọc RO sẽ cho ra nước tinh khiết, đồng thời cũng loại bỏ hoàn toàn khoáng chất có trong nước.<br />\r\n• Công nghệ Nano không qua hệ thống xử lý thô sẽ dễ gây tắc màng; những chất khoáng còn lại trong nước có thể sẽ không có lợi cho cơ thể (?!); giá thành cao.<br />\r\n3. Cách khắc phục.<br />\r\n• Công nghệ mới của RO của Ecoro đã có thêm chức năng bổ sung khoáng chất có lợi cho cơ thể.<br />\r\n• Với công nghệ lọc Nano, người sử dụng phải xác định trước nguồn nước để dùng Nano hiệu quả hơn.<br />\r\nTa thấy, công nghệ lọc nước Nano còn kém công nghệ lọc nước RO ở mức độ nguyên chất<br />\r\nNội dung bài viết:Theo Báo cáo “Nước cho mọi người-Nước cho sự sống” của Chương trình đánh giá Nước Thế giới của UNESCO, mỗi ngày có hơn 6000 người chết do các bệnh liên quan đến nước, gồm tiêu chảy, nhiễm giun và các bệnh lây nhiễm khác.<br />\r\nNgoài ra, các chất ô nhiễm hữu cơ từ chất thải công nghiệp từ các nhà máy giấy, dệt, da, xưởng đúc, lọc, hóa dầu là nguyên nhân chính gây bệnh tật hiểm nghèo, có thể gây ra các loại bệnh mới trên thế giới, nơi không có đủ các quy định cần thiết để bảo vệ con nguời trước các dòng thải công nghiệp.Đã có rất nhiều giải pháp kỹ thuật được đưa ra để giải quyết vấn đề trên.<br />\r\nMột trong những công nghệ tiên tiến nhất trong lĩnh vực xử lý nước trên thế giới trong những thập niên gần đây là công nghệ lọc nước theo nguyên lý thẩm thấu ngược (Reverse Osmosis) với các mắt lọc kích cỡ 0,001 micromet, giống như cơ chế hoạt động của thận người có thể lọc tất cả các vi khuẩn đọc hại cũng như các tạp chất trong nước(và người ta còn gọi màng lọc RO là thận nhân tạo). Do nhà khoa học ORIRAJIN người Mỹ phát minh năm 1950.<br />\r\nBan đầu công nghệ này chủ yếu ứng dụng trong mục đích lọc nước phục vụ con người du hành vũ trụ (tuần hoàn hệ thống nước trong tầu con thoi, khoa học vũ trụ). Sau này nó được ứng dụng vào nhiều lĩnh vực khác, như trong y tế là chạy thận nhân tạo, lọc nước tinh khiết cho phòng thí nghiệm, cho công nghiệp sản xuất vi mạch điện tử (nước siêu tinh khiết). Đặc biệt các vùng đất nhiễm mặn hay không có nước ngọt, các tầu đi biển dài ngày không thể thiếu sản phẩm máy lọc nước công nghệ thẩm thấu ngược để lọc nước mặn thành nước ngọt và hiện nay là phục vụ nước uống hàng ngày như sản phẩm nước uống tinh khiết đóng chai quen thuộc.<br />\r\nCông nghệ lọc nước RO đã và đang lọc bất kỳ nguồn nước nào ở bất kỳ đâu thành nước tinh khiết, giúp ngăn ngừa bệnh tật và nhiễm độc cho hàng triệu người trên khắp thế giớiTuy nhiên sự phổ cập công nghệ lọc nước thẩm thấu ngược hay còn gọi là máy RO còn nhiều trở ngại do một phần là giá thành, một phân là do nhận thức của người dân, nhưng thực sự khi so sánh gia thành với những giá trị hữu ích mà nó mang lại cho con người thì rất lớn. ví dụ như đầu tư một máy RO chính hiệu.<br />\r\n&nbsp;Đài Loan giá 3 triệu VND (giá trị sử dụng máy khoảng 10 năm và 6 -12 tháng thì thay lõi lọc) phục vụ cho mục đích nước ăn uống trong gia đình thì giá trị của nó mạng lại là rất to lớn, phòng ngừa bệnh tật qua con đường ăn uống hàng ngày, mà ta vẫn hàng ngày đang chủ quan, không quan tâm đến nguồn nước mình đang sử dụng.Hiện nay theo trào lưu phát triển công nghệ mới, công nghệ Nano cũng đang được ứng dụng vào trong lĩnh vực xử lý nước. Các nhà khoa học đề xuất công nghệ nanô làm giải pháp để giải quyết vấn đề nước sạch.Các nhà nghiên cứu đã khảo sát các hạt silic được phủ một lớp nanô vật liệu hoạt tính từ hyđrôcácbon có các mấu neo bằng silic. Lớp phủ này được tạo thành qua một quá trình hóa học tự kết hợp, do vậy không cần các thành phần khác mà chỉ cần kích thích các thành phần để tạo ra các hạt hoạt tính. Các hạt hoạt tính này còn được gọi là silic gia công bề mặt (SES), sau đó được thử nghiệm nhằm minh họa khả năng có thể loại bỏ các phân tử sinh học, các mầm bệnh dạng virut như virút Polio, các vi khuẩn như Escherichia coli và Cryptosporidium parvum là các mầm bệnh lây qua đường nước. Các kết quả thu được cho thấy rõ ràng các loại chất hữu cơ được loại bỏ một cách hiệu quả ở khoảng giá trị pH của nước sạch thông qua quá trình kích thích các hạt được phủ vật liệu hoạt tính trong nước bị ô nhiễm trong thời gian 1 giờ và lọc qua lớp bột. Quy trình lọc diễn ra bởi lực hút tĩnh điện nay, các nhà nghiên cứu đã đưa ra một phương án sử dụng các ống nano cacbon để thay thế cho các vật liệu thông thường trong các hệ thống tinh lọc nước.Công nghệ nano có thể là câu trả lời để đảm bảo một nguồn cung ứng nước uống an toàn (?) giá thành rẻ ???, cho những khu vực bị ảnh hưởng bởi hạn hán thường xuyên hay ở những nơi nguồn nước bị ô nhiễm tràn lan. Trong công trình nghiên cứu được công bố trên nhiều thông tin đại chúng hiện nay. Hiện nay tại VN đã có sản phẩm lọc nước công nghệ Nano do Nga sản suất, mặc dù sản phẩm chưa ứng dụng nhiều nhưng cũng đã gây nhiều sự chú ý của người dân vì nó là công nghệ mới mà nhất là có cái chữ công nghệ Nano và được các nhà khoa học tổ chức nhiều hội thảo (thực chất là PR). Thực chất thì máy lọc Nano là sử dụng các mắt lọc kích cỡ nanomet hình ống để loại bỏ các thành phần hóa học khác có kích cỡ lớn hơn nước.Tuy nhiên về mặt công nghệ các nhà khoa học còn đang ca ngợi tính chất vượt trội của công nghệ Nano trong xử lý nước là không lọc hết hoàn toàn (tinh khiết như máy RO) mà vẫn để lại một hàm lượng chất khoáng có lợi cho cơ thể. Điều đó có thể ? Không thể trả lời chính xác được vì còn phụ thuộc vào yếu tố nguồn nước đầu vào. Nếu giả sử có 2 nguồn nước dầu vào nhiễm các chất độc hại khác nhau hoặc tổng lượng chất rắn hòa tan trong nước khác nhau (TDS) thì sao? Thì nguồn nước đầu ra các hệ số về các chất độc hại còn lại cũng sẽ thay đổi tùy theo giá trị đầu vào. Vì vậy nó vẫn cần một sự kết hợp các quá trình xử lý lọc thô, tinh lọc rồi siêu lọc xong rồi mới có thể là màng lọc Nano vì với kích cỡ mắt lọc nanomet đem sử dụng vào các nguồn nước tại ở VN hiện nay (kể cả nước máy thành phố) thì nguy cơ bị tắc màng lọc ngay lặp tức là rõ ràng vì màng lọc Nano không thể rửa lại được, (không có cơ chế tự rửa như màng RO) và hiện tại giá thành thay thế màng lọc nano là rất cao. Nói chung, để lựa chọn giải pháp xử lý nguồn nước nào cho phù hợp thì cần phụ thuộc chất lượng của nguồn nước ấy đang ở mức độ nào.</p>\r\n','so-sanh-giua-cong-nghe-loc-nuoc-nano-va-ro-nano-vs-ro',1,'','',[site_id],[user_id],'/media/images/101/news/ava/','8011404210983_May-loc-nuoc-nano.jpg',[now], [now],[user_id],0,'','',[now]);set @news13 = LAST_INSERT_ID();
