INSERT INTO product (id, `name`, `code`, price, price_market, price_discount, price_discount_percent, include_vat, unit, quantity, `status`, position, product_sortdesc, product_desc, avatar_path, avatar_name, avatar_id, site_id, create_user, update_user, created_time, update_time, meta_keywords, meta_description, meta_title, list_product_relate, product_category_id, ishot, `alias`) VALUES (null,'Màng RO Nhập khẩu từ USA','EC-100',,,,,0,'',,1,1,'','<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp; MÀNG LỌC RO viết tắt từ hai chữ REVERSE OSMOSIS.&nbsp; Cho đến nay, thẩm thấu ngược vẫn được coi là là công nghệ cao cấp nhất trong ngành lọc nước, chỉ có nước mới \"chui lọt\" qua được. Chính vì thế, nó còn có tên gọi là Hyberfiltration.<br />\r\n- Hiện tượng thẩm thấu: là một hiện tượng tự nhiên. Nước bao giờ cũng chuyển dịch từ nơi có nồng độ muối/ khoáng thấp đến nơi có nồng độ cao hơn. Quá trình diễn ra cho đến khi nồng độ muối khoáng từ 2 nơi này cân bằng<br />\r\n<strong>- Thẩm thấu ngược RO:</strong><br />\r\nNguyên lý hoạt động: Ngược lại với các cơ chế lọc thẩm thấu thông thường, nhờ lực hấp dẫn của trái đất để tạo ra sự thẩm thấu của các phân tử nước qua các mao mạch của lõi lọc. Với tốc độ và áp lực cực lớn, dòng nước chảy liên tục trên bề mặt của màng RO. Một phần trong số những phân tử nước “chui” qua được những lỗ lọc. Các tạp chất bị dòng nước cuốn trôi và “thải” bỏ ra ngoài. Với cách thức này, bề mặt của màng RO liên tục được rửa sạch và có tuổi thọ tới 5 năm.</p>\r\n','/media/images/products/101/4/','8301403853122_mang-ro_usa.jpg',0,[site_id],[user_id],[user_id],[now],[now],'','','','',@product_categories113,1,'mang-ro-nhap-khau-tu-usa');
