INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,0,'Đại hội Công đoàn năm 2014','Ban chấp hành Công đoàn mới gửi tặng đóa hoa rực rỡ tới bà Đặng Thị Minh Tâm – đại diện Ban chấp hành Công đoàn nhiệm kỳ 2012 – 2014, thể hiện sự trân trọng những đóng góp xây dựng Công đoàn trong suốt thời gian qua.','<p>Đại hội Công đoàn tập trung vào hai nội dung chính:</p>\r\n\r\n<p>+ Báo cáo hoạt động Công đoàn nhiệm kỳ 2012 – 2014 và phương hướng, nhiệm vụ Công đoàn năm 2014 – 2016</p>\r\n\r\n<p>+ Bầu cử Ban chấp hành Công đoàn nhiệm kỳ 2014 – 2016</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Ban chấp hành Công đoàn nhiệm kỳ 2012 – 2014, đại diện là Chủ tịch Công đoàn Đặng Thị Minh Tâm đã tổng kết lại hoạt động Công đoàn trong thời gian qua. Nhìn chung, Công đoàn có những hành động thiết thực thể hiện sự quan tâm đời sống của CB-CNV như: việc hiếu - hỉ, thăm hỏi nhân viên khi sinh nở, ốm đau… Bên cạnh đó, Công đoàn còn tổ chức các hoạt động văn hóa – văn nghệ, thể dục thể thao (đội bóng Tân Minh)… Những hoạt động trên tạo ra sự gắn kết, thân thiết giữa các thành viên Công ty.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Toàn thể CB-CNV đã thống nhất bầu ra Ban chấp hành Công đoàn mới cho nhiệm kỳ 2014 – 2016 gồm:</p>\r\n\r\n<p>+ Chủ tịch Công đoàn: Nguyễn Quỳnh Trang</p>\r\n\r\n<p>+ Ủy viên: Nguyễn Thị Nguyên</p>\r\n\r\n<p>+ Ủy viên: Vi Thị Thanh Hà</p>\r\n\r\n<p>+ Ủy viên: Phạm Đăng Hồng</p>\r\n\r\n<p>+ Ủy viên: Lê Văn Long</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Những thành viên Ban chấp hành mới mang đến sự trẻ trung, năng động, nhiệt tình sẽ tiếp tục xây dựng, thực hiện những kế hoạch, hoạt động của Công đoàn trong nhiệm kỳ 2014 – 2016:</p>\r\n\r\n<p>+ Tiếp tục duy trì các hoạt động chăm lo đời sống CB-CNV</p>\r\n\r\n<p>+ Xây dựng thêm các hoạt động mới bổ ích, nâng cao tinh thần, sự đoàn kết cho CB-CNV</p>\r\n\r\n<p>+ Tiếp thu ý kiến đóng góp của CB-CNV để thay đổi, chỉnh sửa các hoạt động thiết thực, hợp lý hơn</p>\r\n\r\n<p>+ Thường xuyên trao đổi với Ban lãnh đạo Công ty để tạo điều kiện, cải thiện môi trường làm việc chung</p>\r\n\r\n<p>+ Đẩy mạnh phong trào tham gia các khóa học, khóa đào tạo nâng cao kiến thức chuyên môn, kỹ năng cho CB-CNV</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Ban lãnh đạo Công ty cùng toàn thể CB-CNV tin tưởng vào phương hướng hoạt động của Ban chấp hành Công đoàn nhiệm kỳ 2014 – 2016.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Đại hội Công đoàn thành công tốt đẹp !</p>\r\n','dai-hoi-cong-doan-nam-2014',1,'','',[site_id],[user_id],'/media/images/65/news/ava/','210_1417167748_74254784384e008d.jpg',[now], [now],[user_id],0,'','',[now]);set @news16 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,0,'Chào mừng đại lễ 30/4 và Quốc tế lao động 1/5','Thông báo lịch nghỉ lễ 30/4 và Quốc tế lao động 1/5 năm 2014 của Công ty Cổ phần Đầu tư Tân Minh','<p>Nhân dịp kỷ niệm ngày giải phóng miền Nam, thống nhất đất nước (30/04/1975 - 30/04/2014) và kỷ niệm ngày Quốc tế lao động (1/5/1886 - 1/5/2014), thay mặt&nbsp; Ban Lãnh đạo Công ty Cổ phần Đầu tư Tân Minh xin gửi tới các CB-CNV lời chúc tốt đẹp nhất. Chúc ﻿ toàn thể﻿ các CB-CNV có những ngày nghỉ vui vẻ và gặp nhiều may mắn. Chúc Tân Minh ngày càng phát triển thịnh vượng và bền vững.</p>\r\n','chao-mung-dai-le-304-va-quoc-te-lao-dong-15',1,'','',[site_id],[user_id],'/media/images/65/news/ava/','475_1417162937_67547830b91234d.jpg',[now], [now],[user_id],0,'','',[now]);set @news15 = LAST_INSERT_ID();
