INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Tổng quan','<p>Chúng tôi với hoạt động duy nhất là nghiên cứu kỹ thuật và thiết kế website. Cho tới nay Web3nhat đã xây dựng website cho nhiều đối tác trong và ngoài nước.</p>\r\n\r\n<p>Bên cạnh đó là hàng loạt website cho những khách hàng tầm cỡ và có thứ hạng truy cập rất cao (Vccorp, Vật giá) dựa trên công nghệ lập trình, Web3nhat cung cấp nhiều giải pháp và công nghệ tối ưu hóa cho việc quản trị – quảng bá website, giảm chi phí nhân sự, giúp cho việc quản trị trở nên đơn giản, dễ hiểu, thậm chí không cần chúng tôi hướng dẫn sử dụng.</p>\r\n\r\n<p>Đội ngũ lập trình viên của Web3nhat có tư duy tốt, kỹ thuật chuyên môn cao, say mê công việc.</p>\r\n\r\n<p>Đội ngũ tư vấn của Web3nhat luôn nhiệt tình, nhiều kinh nghiệm và hiểu rõ giải pháp pr, truyền thông hợp lý sẽ giúp Quý khách đạt được hiệu quả đầu tư cao nhất.</p>\r\n\r\n<p>Đội ngũ thiết kế web của Web3nhat có khả năng tư duy sáng tạo và cảm nhận thẩm mỹ tốt, cùng với nền tảng chuyên môn được đào tạo từ những trung tâm &amp; trường lớp đồ họa chất lượng cao tại Việt Nam.</p>\r\n',[site_id],[user_id],'gioi-thieu',[now],[now],[user_id],'','','','','');
