INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories18,'Bảo trì thang máy những điều cần biết','Để đảm bảo thang máy được vận hành một cách hoàn hảo và an toàn nhất, thang máy cần phải được bảo trì định kỳ hàng tháng. Thông thường, tất cả các thang máy đều được công ty thang máy bảo trì định kỳ miễn phí mỗi tháng một lần trong thời gian bảo hành, đến khi hết thời gian bảo hành khách hàng phải ký hợp đồng bảo trì với một mức phí nhất định.','<p style=\"text-align: justify;\">Vì vậy nắm được các công việc mà bên cung cấp dịch vụ bảo trì thang máy tiến hành mỗi khi qua bảo trì thang máy cho mình cũng là điều cần thiết.<br />\r\n1. Khâu chuẩn bị và khảo sát<br />\r\n- Báo cáo với đơn vị quản lý tòa nhà hoặc người có trách nhiệm về công việc bảo trì sẽ thực hiện.<br />\r\n- Tham khảo ý kiến đơn vị quản lý tòa nhà về các trục trặc của thang máy trong tháng vừa qua.<br />\r\n- Nếu nhân viên bảo trì dự định ngưng hoạt động thang máy để chuẩn bị bảo trì, thì phải phải có thanh chắn hoặc biển báo tạm dừng thang đặt trước cửa thang.<br />\r\n- Kiểm tra thời hạn kiểm định của thang máy. Nếu hết hạn, thông báo cho đơn vị quản lý.<br />\r\n2. Bảo trì khu vực phòng máy. &nbsp;<br />\r\n- Kiểm tra tổng quát điều kiện hoạt động bình thường của relay, contactor,…<br />\r\n- Kiểm tra tổng quát điện áp nguồn chính, nguồn pin, nguồn chiếu sáng khẩn cấp, các điểm nối đất...<br />\r\n- Kiểm tra tổng quát hoạt động bình thường, độ êm, độ ồn, tình trạng cơ học của các chi tiết bên trong động cơ kéo.<br />\r\n- Kiểm tra nhiệt độ khi động cơ kéo và biến tần làm việc bình thường.<br />\r\n- Kiểm tra thông gió khu vực máy kéo, biến tần…<br />\r\n- Kiểm tra thắng.<br />\r\n- Kiểm tra hoạt động bình thường của thắng.<br />\r\n- Kiểm tra hoạt động của các công tắt thắng.<br />\r\n- Ngừng thang, cắt điện; kéo cần nhả thắng để kiểm tra hoạt động của thắng trong trường hợp khẩn cấp.<br />\r\n- Kết hợp, kiểm tra các chức năng phục vụ cứu hộ như: đèn báo chiều di chuyển, đèn bằng tầng, chuông báo quá tốc độ.<br />\r\n- Kiểm tra và vệ sinh các contactor thắng.<br />\r\n- Kiểm tra các đầu ty treo cáp ; các điểm chịu lực.<br />\r\n- Kiểm tra các rãnh puly, thanh chống nhảy cáp.<br />\r\n- Kiểm tra các tấm cao su giảm chấn, các dây khóa chống tuôn bulon.<br />\r\n- Kiểm tra bộ chống vượt tốc governor, vô dầu mở.<br />\r\n- Kiểm tra tổng quát về độ ồn , nhiệt … tất cả các bộ phận quay.<br />\r\n- Kiểm tra bảo đảm tất cả các công tắt an toàn trong khu vực này khi bị tác động thì buộc thang máy ngừng hoạt động.<br />\r\n- Vệ sinh tổng quát.&nbsp;<br />\r\n- Đưa xe thang đi đến từng tầng; kiểm tra độ lệch tầng và điều chỉnh nếu độ lệch vượt quá quy định.<br />\r\n- Kiểm tra chức năng hoạt động của tất cả các nút trên bảng điều khiển trong xe thang; đặc biệt nút báo khẩn cấp ( alarm ); nút intercom và hoạt động của intercom.<br />\r\n- Kiểm tra hoạt động của hiển thị tầng.<br />\r\n- Kiểm tra hoạt động bình thường , độ êm , độ ồn, tình trạng cơ học của các chi tiết quay như puly chuyển hướng cáp, guốc ( shoe) dẫn hướng.<br />\r\n- Kiểm tra các điểm treo xích bù ; các bulon khóa; dạy bảo hiểm.<br />\r\n4.Cửa tầng thang máy<br />\r\n- Kiểm tra tình trạng của các nút gọi tầng và các nút chức năng khác.<br />\r\n- Kiểm tra các đèn hiển thị, chuông báo dừng tầng.<br />\r\n- Kiểm tra hoạt động đóng/mở của từng cửa tầng; các khe hở kỷ thuật.<br />\r\n- Kiểm tra tình trạng hoạt động của guốc ( shoe ) và roller cửa.<br />\r\n- Vệ sinh các rãnh trượt dưới.<br />\r\n- Kiểm tra, vệ sinh, điều chỉnh tiếp điểm cửa.<br />\r\n- Kiểm tra bảo đảm cửa tầng luôn tự đóng được và tự khóa cơ học từ mọi vị trí.<br />\r\n- Kiểm tra độ giãn cáp kéo thông qua khoảng cách giữa đối trọng và bộ giảm chấn.&nbsp;<br />\r\n5. Hố thang máy.<br />\r\n- Kiểm tra độ giảm cáp bảo hiểm thông qua puly căng cáp governo.<br />\r\n- Kiểm tra khoảng cách giữa xích/cáp bù và đáy hố.<br />\r\n- Kiểm tra hoạt động bình thường , độ êm , độ ồn, tình trạng cơ học của các chi tiết quay như ply, rullo, xích bù.<br />\r\n- Kiểm tra bảo đảm tất cả các công tắt an toàn trong khu vực này khi bị tác động buộc thang máy phải ngừng hoạt động.<br />\r\n- Kiểm tra độ ẩm đáy hố thang và vệ sinh.<br />\r\n- Kiểm tra vô dầu cho các guốc dẫn hướng , các vòng bi.<br />\r\n- Kiểm tra hoạt động các bộ cảm biến quang học của cửa.<br />\r\n- Kiểm tra hoạt động của đèn, quạt lúc bình thường và lúc mất điện.<br />\r\n- Kiểm tra tổng quát các liên kết cơ học, bulon nối , trần.<br />\r\n- Kiểm tra các điểm nối đất trên xe thang.<br />\r\n6.titleCửa xe thang:&nbsp;<br />\r\n- Kiểm tra hoạt động bình thường, độ êm, độ ồn, tình trạng cơ học của các chi tiết.<br />\r\n- Kiểm tra độ thẳng của các cánh cửa , khe hở kỷ thuật.<br />\r\n- Kiểm tra chức năng hoạt động, tốc độ đóng mở, tác động cơ học.<br />\r\n- Vệ sinh các rãnh trượt trên dưới.<br />\r\n- Kiểm tra tổng quát và độ căng cần thiết của dây curoa kéo.<br />\r\n- Kiểm tra thời cài đặt đóng mở cửa có phù hợp.<br />\r\n- Kiểm tra , điều chỉnh, vệ sinh tiếp điểm cửa.<br />\r\n- Kiểm tra cơ học, độ chắc chắn của việc lắp đặt cảm biến quang học.<br />\r\n- Kiểm tra tình trạng hoạt động tốt của roller, guốc ( shoe ) của.<br />\r\n- Kiểm tra bảo đảm khi công tắt an toàn cửa khi bị tác động thì buộc thang máy ngừng hoạt động.<br />\r\n7.Hoàn thành: - Ghi lại các kiến nghị với đơn vị quản lý tòa nhà.<br />\r\n- Dọn dẹp các thanh chắn, hoặc biển báo bảo trì, dụng cụ làm việc.<br />\r\n- Cho thang máy hoạt động trở lại bình thường và theo dõi.</p>\r\n\r\n<p style=\"text-align: justify;\"><br />\r\n&nbsp;</p>\r\n','bao-tri-thang-may-nhung-dieu-can-biet',1,'','',[site_id],[user_id],'/media/images/53/news/ava/','621_1415776077_3875463074d726ec.jpg',[now], [now],[user_id],0,'','',[now]);set @news17 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories17,'Thiết kế gian hàng triển lãm','Việc tổ chức các buổi triển lãm, các showroom, gian hàng nhằm giới thiệu sản phẩm, dịch vụ đến với đông đảo khách hàng đang là việc làm không thể thiếu đối với các Công ty, Doanh nghiệp trong nền kinh tế thị trường hiện nay. Để khẳng định được quy mô và tính chuyên nghiệp của Công ty, doanh nghiệp mình thì việc thiết kế showroom – gian hàng sao cho vừa thể hiện được ngành nghề, lĩnh vực kinh doanh cũng như sản phẩm, dịch vụ vừa thu hút được sự chú ý của khách hàng là điều cốt yếu.\r\n ','<p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp;Để lọt vào tầm mắt của khách hàng trong hàng trăm gian hàng trong hội chợ triển lãm thì việc thiết kế gian hàng hội chợ triển lãm phải thật ấn tượng và nổi bật. Cách bài trí sản phẩm của mỗi gian hàng phải thật hợp lý và khoa học trong một không gian nhỏ nhằm giữ chân khách hàng trong gian hàng của mình.<br />\r\n&nbsp; &nbsp; &nbsp; Chính vì những tiêu chí rất cao và những yêu cầu khắt khe trong việc thiết kế gian hàng như vậy, mà các công ty tham gia hội chợ triển lãm cần tìm cho mình một đối tác thiết kế và thi công thật sự chuyên nghiệp.<br />\r\n&nbsp; &nbsp; &nbsp; Với đội ngũ thiết kế và giám sát thi công giàu kinh nghiệm qua hàng trăm dự án, chúng tôi mang đến cho khách hàng những tư vấn chuyên sâu trong việc thiết kế gian hàng hội chợ triển lãm thế nào để đạt hiệu quả tốt nhất choc ho việc quảng bá thương hiệu và sản phẩm của công ty bạn.</p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"http://w3ni0006.web3nhat.net/data/media/53/images/gianhang1.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n<img alt=\"\" src=\"http://w3ni0006.web3nhat.net/data/media/53/images/gianhang2.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n<img alt=\"\" src=\"http://w3ni0006.web3nhat.net/data/media/53/images/gianhang3.jpg\" style=\"width: 602px; height: 377px;\" /></p>\r\n','thiet-ke-gian-hang-trien-lam',1,'','',[site_id],[user_id],'/media/images/53/news/ava/','187_1415776098_15854630762980b8.jpg',[now], [now],[user_id],0,'','',[now]);set @news18 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories16,'Trang trí nội ngoại thất','Với đội ngũ thiết kế sáng tạo, đội ngũ thợ thi công có tay nghề cao, Đông Quang đã có hơn 10 năm kinh nghiệm trong thiết kế và lắp đặt nội thất. ','<p style=\"text-align: justify;\">Đến với Đông Quang quý khách hàng hoàn toàn yên tâm về chất lượng thi công, tiến độ thực hiện, cũng như sự nhiệt tình trong công việc để cho bạn có một không gian thỏa mãn nhất với giác quan của mình và phù hợp với thẩm mỹ đương thời.<br />\r\nDưới đây là một số hình ảnh nội thất theo nhiều phong cách khác nhau, mời quý khách cùng tham khảo.</p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"http://w3ni0006.web3nhat.net/data/media/53/images/7%20noi%20that%20pk%204.jpg\" style=\"width: 800px; height: 604px;\" /><br />\r\n<img alt=\"\" src=\"http://w3ni0006.web3nhat.net/data/media/53/images/noi%20that%20phong%20ngu%203.jpg\" style=\"width: 800px; height: 500px;\" /></p>\r\n','trang-tri-noi-ngoai-that',1,'','',[site_id],[user_id],'/media/images/53/news/ava/','799_1415776123_4555463077b2b7a6.jpg',[now], [now],[user_id],0,'','',[now]);set @news19 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories24,'Xuất nhập khẩu đồ gia dụng','Chuyên xuất nhập khẩu và kinh doanh các sản phẩm hàng gia dụng, thiết bị nhà bếp. Chúng tôi cam kết luôn cung cấp những sản phẩm với chất lượng tốt nhất, đa dạng về mẫu mã, kiểu dáng theo phương châm \"Mang đến nét sang trọng và tinh tế từ Châu Âu\" cho không gian của bạn.','<p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; Là một trong những công ty có uy tín hàng đầu trong lĩnh vực hàng gia dụng ở Việt Nam với nhãn hiệu Eurohome. Với nhiều năm kinh nghiệm, chúng tôi đã dần khẳng định vị trí là nhà nhập khẩu và phân phối hàng gia dụng cao cấp hàng đầu với chất lượng, uy tín và chính sách chăm sóc khách hàng hoàn hảo nhất. Để đem đến cho người tiêu dùng những sản phẩm &nbsp;gia dụng tốt, đẹp, an toàn và kinh tế nhất, Euro Vision., JSC đã nhập khẩu &amp; phân phối các sản phẩm gia dụng: nồi, chảo rán, chảo nướng, chảo nướng đôi, bộ nồi nhôm … Các sản phẩm của chúng tôi được sản xuất trên dây chuyền công nghệ tiên tiến của Đức, với thiết kế, màu sắc và kiểu dáng sang trọng, mang phong cách châu Âu hiện đại, sản phẩm gia dụng Eurohome đã nhiều năm liền được những người nội chợ Đức tin dùng.</p>\r\n\r\n<p>&nbsp;</p>\r\n','xuat-nhap-khau-do-gia-dung',1,'','',[site_id],[user_id],'/media/images/53/news/ava/','560_1415776146_36546307928cead.jpg',[now], [now],[user_id],0,'','',[now]);set @news20 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories22,'Điện Hoa Chúc mừng ngày Phụ nữ Việt Nam 20 - 10','Điện Hoa Chúc mừng ngày Phụ nữ Việt Nam 20 - 10 của Chủ Tịch HĐQT - Tổng Giám đốc DongQuangGroup Anh Trần Anh Tuấn.','<p style=\"text-align: justify;\"><span style=\"color: rgb(20, 24, 35); font-family: Helvetica, Arial, \'lucida grande\', tahoma, verdana, arial, sans-serif; font-size: 14px; line-height: 19.3199996948242px;\">Thưa toàn thể các nữ cán bộ công nhân viên của Tập đoàn!</span><br style=\"color: rgb(20, 24, 35); font-family: Helvetica, Arial, \'lucida grande\', tahoma, verdana, arial, sans-serif; font-size: 14px; line-height: 19.3199996948242px;\" />\r\n<span style=\"color: rgb(20, 24, 35); font-family: Helvetica, Arial, \'lucida grande\', tahoma, verdana, arial, sans-serif; font-size: 14px; line-height: 19.3199996948242px;\">Không ai có thể phủ nhận những hy sinh, đóng góp lớn lao của những người phụ nữ. Trong đấu tranh, họ là những Bà mẹ Việt Nam anh hùng, những nữ thanh niên xung phong kiên cường, bất khuất. Trong thời bình, họ là những nữ doanh nhân thành đạt, những nữ nhân viên, công nhân chăm chỉ. Trong gia đình, họ là n</span><span class=\"text_exposed_show\" style=\"display: inline; color: rgb(20, 24, 35); font-family: Helvetica, Arial, \'lucida grande\', tahoma, verdana, arial, sans-serif; font-size: 14px; line-height: 19.3199996948242px;\">hững người mẹ, người vợ đảm đang.<br />\r\nNhân ngày Phụ nữ Việt Nam 20 – 10 thay mặt cho Ban lãnh đạo tập đoàn, tôi xin gửi tới các CBCNV nữ tình cảm thân thương quý trọng cùng lời chúc mừng tốt đẹp nhất. Chúc chị em luôn luôn mạnh khoẻ, nhiều niềm vui, tràn đầy hạnh phúc và thành công trong công việc.<br />\r\nBan lãnh đạo ghi nhận và đánh giá cao tinh thần làm việc hăng say, nỗ lực phấn đấu không ngừng và những đóng góp quan trọng của chị em cho sự phát triển của Tập đoàn trong thời gian vừa qua. Mọi sự thành công của Chúng tôi không thể thiếu sự cống hiến to lớn của các bạn.<br />\r\nVì sự nghiệp phát triển chúng tôi sẽ làm hết sức mình vì sự tiến bộ của phụ nữ, không ngừng nâng cao đời sống vật chất và tinh thần, tạo mọi điều kiện thuận lợi để toàn thể các nữ cán bộ công nhân viên phát huy hết khả năng của mình, hoàn thành xuất sắc mọi nhiệm vụ được giao, đóng góp nhiều hơn nữa cho sự phát triển chung của Tập đoàn.<br />\r\nTôi rất mong toàn thể CBCNV công ty sẽ đồng tâm, hiệp lực thực hiện sứ mệnh chung đưa DongQuangGroup ngày một lớn mạnh. Một lần nữa, tôi xin trân trọng gửi đến toàn thể chị em phụ nữ lời chúc sức khỏe, hạnh phúc và thành công.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><br />\r\n<span class=\"text_exposed_show\" style=\"display: inline; color: rgb(20, 24, 35); font-family: Helvetica, Arial, \'lucida grande\', tahoma, verdana, arial, sans-serif; font-size: 14px; line-height: 19.3199996948242px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thân ái!<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Trần Anh Tuấn</span></p>\r\n','dien-hoa-chuc-mung-ngay-phu-nu-viet-nam-20---10',1,'','',[site_id],[user_id],'/media/images/53/news/ava/','352_1415508626_100545ef2922e8cf.jpg',[now], [now],[user_id],0,'','',[now]);set @news21 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories15,'Hệ thống bảng biển Mobifone','Để nâng cao khả năng cạnh tranh, khẳng định vị thế thương hiệu, Mobifone đã triển khai dự án lắp đặt biển hiệu quảng cáo đồng bộ cho tất cả các trung tâm, chi nhánh, cửa hàng của mình theo một quy cách thống nhất.Dự án nàyđược triển khai và tổ chức thực hiện hàng năm. Theo dự án này thì biển hiệu của các cửa hàng, trung tâm, đại lý đều giống nhau về mặt hình ảnh. Biển được làm theo 2 dạng: hộp đèn và biển đơn.','<p style=\"text-align: justify;\"><font face=\"Arial\" size=\"3\"><span style=\"color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; background-color: rgb(255, 255, 255); display: inline ! important; float: none\">Chất liệu khung biển được làm từ hợp kim nhôm, hình ảnh được in trên bạt Hiflex xuyên sáng với độ bền về màu sắc cao. Dưới đây là các mẫu biển cho cửa hàng, đại lý, trung tâm của Mobifone.</span> </font></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><font face=\"Arial\" size=\"3\">&nbsp;&nbsp; </font><font face=\"Arial\" size=\"3\"><img alt=\"alt\" height=\"220\" src=\"http://dongquanggroup.vn/images/stories/DUAN/1.png\" width=\"650\" /></font></p>\r\n\r\n<p style=\"text-align: justify;\"><font face=\"Arial\" size=\"3\"><em>Mẫu biển cho đại lý ủy quyền và trung tâm Mobifone </em></font></p>\r\n\r\n<p style=\"text-align: justify;\"><font face=\"Arial\" size=\"3\">&nbsp;&nbsp; <img alt=\"alt\" height=\"155\" src=\"http://dongquanggroup.vn/images/stories/DUAN/2.png\" width=\"650\" /></font></p>\r\n\r\n<p style=\"text-align: justify;\"><font face