INSERT INTO posts (id, category_id, title, sortdesc, description, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,@post_categories17,'Biển tấm lớn trên Quốc lộ 2 Việt Trì','Quốc lộ 2 dài 312 km, chạy qua địa bàn năm tỉnh và thành phố: Hà Nội, Vĩnh Phúc, Phú Thọ, Tuyên Quang và Hà Giang. Chiều dài toàn tuyến là 315 km. Đoạn từ sân bay Nội Bài (Hà Nội) đến thành phố Vĩnh Yên còn được gọi là Quốc lộ 2A. Đoạn từ Vĩnh Yên đến thành phố Tuyên Quang và một ngả rẽ lên thị trấn Tam Đảo còn được gọi là Quốc lộ 2B. Đoạn từ thành phố Tuyên Quang đến cửa khẩu Thanh Thủy còn được gọi là Quốc lộ 2C.\r\n','<p>&nbsp;Biển được xây dựng bên cạnh đường cao tốc quốc lộ số 2 và cạnh tuyến đường sắt, thuộc phường Bạch Hạc, TP. Việt Trì. Bên phải đường theo hướng Hà Nội đi Yên Bái.</p>\r\n\r\n<p><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/viet%20tri.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/viet%20tri.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n','bien-tam-lon-tren-quoc-lo-2-viet-tri',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','557_1415507554_148545eee62abbd2.jpg',[now], [now],[user_id],[now]);set @posts6 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,@post_categories17,'Biển tấm lớn trong bến xe Gia Lâm','Bến xe Gia Lâm là đầu mối giao thông quan trọng của các tỉnh phía bắc. Mật độ giao thông ở đây khoảng từ 10.000 - 13.000 lượt khách/ ngày, những ngày lễ lớn mật độ lên đến 13.000 - 15.000 lượt khách/ ngày. Hệ thống biển của chúng tôi ở đây gồm 2 vị trí, án ngữ hai bên cửa ra vào nhà chờ xe khách của bến. Biển được đặt ở vị trí trên cao, tầm nhìn tốt. \r\n','<p><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%20xe%20gia%20lam.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%20xe%20gia%20lam.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n','bien-tam-lon-trong-ben-xe-gia-lam',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','626_1415506854_247545eeba6d6f24.jpg',[now], [now],[user_id],[now]);set @posts7 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,@post_categories17,'Biển tấm lớn trong bến xe Mỹ Đình','Bến xe Mỹ Đình được xây dựng cách nay hơn 10 năm, bình quân mỗi ngày có trên 11.000 lượt xe khách hoạt động. Nằm cạnh trục đường chính Phạm Hùng, số lượng hành khách ngày càng tăng, các phương tiện đưa đón khách cũng được bổ xng thường xuyên để đáp ứng đầy đủ nhu cầu đi lại của người dân.\r\n','<p style=\"text-align: justify;\">&nbsp;Hệ thông biển quảng cáo của Đông Quang tại đây gồm 11 biển, trong đó có 3 biển được lắp đặt phía trong nhà chờ xe ( biển số 9, 10, 11 ) và 8 biển được lắp đặt phía trước sân đỗ của bến xe, phía bên phải đường Phạm Hùng ( theo hướng Phạm Hùng - Nguyễn Xiển ).&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%2520xe%2520my%2520dinh.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%2520xe%2520my%2520dinh.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n\r\n<p>&nbsp;</p>\r\n','bien-tam-lon-trong-ben-xe-my-dinh',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','517_1415506174_383545ee8fe73e2d.jpg',[now], [now],[user_id],[now]);set @posts8 = LAST_INSERT_ID();
