INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories129,'Government Full Scholarships','Scholarships to study in Singapore is one of the Singapore study information was most interested. For information about scholarships that ASEAN aka Singapore Singapore has always been full of students sought','<p style=\"text-align: justify;\"><span class=\"notranslate\" style=\"background-color: rgb(230, 236, 249);\">Full scholarship from the Singapore government was founded by former Prime Minister of Singapore Goh Chok Tong initiated at the ASEAN Summit 6th was held in Hanoi in December 1998 with the aim of helping students to excellence from ASEAN member countries, is a scholarship program funded by the Government of Singapore for 9 countries in Southeast Asia are Brunei, Cambodia, Indonesia, Laos, Malaysia, Myanmar, Philippines, Thailand and <strong>Vietnam</strong> for Education Secondary School, Middle School and the University have the opportunity to enjoy a quality education in this island nation Lion.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">Every year, students from ASEAN countries Singapore Scholarship is awarded to attend regular university courses (not including Medicine, Dentistry, Architecture and Law) in one of the university\'s Singapore\'s Nanyang Technological University (NTU), National University of Singapore (NUS) or Singapore Management University (SMU).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">The purpose of the <strong>scholarship</strong> program is to enable <strong>ASEAN</strong> to outstanding students ASEAN countries to develop their talents, with a full inventory to move forward into the new millennium.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"http://lightyearlearning.web3nhat.vn/data/media/114/images/2014/grad1e_0_0_5.jpg\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\" style=\"background-color: rgb(230, 236, 249);\">Students will gain ASEAN scholarship to study at the Singapore Ministry of Education school choice.</span> <span class=\"notranslate\" style=\"\"> Ministry of Education and Training of Vietnam will select students Intermediate School has excellent performance for this scholarship program.</span> <span class=\"notranslate\"> Students applying for scholarships ASEAN if not achieved will be considered for Merit scholarships (Student excellent record unsatisfactory ASEAN scholarship programs may be considered for Merit Scholarships - scholarships worth $ 1000 SGD / year. It is an opportunity for outstanding students from member countries of the Association of Southeast Asian Nations.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">Conditions for exam registration scholarships are students, students should be the focal point in their country and browse records sealed.</span> <span class=\"notranslate\"> Scholarships are awarded based on merit and academic qualifications of the candidate.</span> <span class=\"notranslate\"> There were so many students were awarded scholarships Singapore government and successfully completed his course in which the number of students coming from Vietnam occupies no small number</span></p>\r\n\r\n<h3 style=\"text-align: justify;\"><span class=\"notranslate\" style=\"\"><strong>1. University students in the first year and second year terms and meet the following criteria:</strong></span></h3>\r\n\r\n<ul style=\"text-align: justify;\">\r\n	<li><span class=\"notranslate\">As citizens of Vietnam, college freshman or sophomore year regular basis focusing on higher education in Vietnam, with virtue and good health.</span></li>\r\n	<li><span class=\"notranslate\">Achievement High School: 03 years to achieve good behavior, the average point total reaches 03 years of learning from 7.5 upwards, graduated high school with good back up.</span></li>\r\n	<li><span class=\"notranslate\">Are employed directly or university matriculation examination results achieved from 21 points or more (zero coefficients).</span> <span class=\"notranslate\"> Sophomore outside this regulation must have a GPA of learning first-year college or above 7.5.</span></li>\r\n	<li><span class=\"notranslate\">Registration on or close to the disciplines of study are studying in Vietnam and in accordance with the program of study prescribed (Medicine, Dentistry, Architecture and the Law shall not be granted a scholarship under this program).</span></li>\r\n	<li><span class=\"notranslate\">Fluency in English to take the written exam, oral examination and college English.</span> <span class=\"notranslate\"> Candidates who have international certificates TOEFL / IELTS internal or international.</span></li>\r\n</ul>\r\n\r\n<h3 style=\"text-align: justify;\"><span class=\"notranslate\"><strong>2. Students in grade 12 to meet the conditions and criteria:</strong></span></h3>\r\n\r\n<ul style=\"text-align: justify;\">\r\n	<li><span class=\"notranslate\">As citizens of Vietnam, grade 12 secondary schools in Vietnam, with virtue and good health, the school year high school achieve good behavior and good academic grading (grade point average Grades 10, 11 reaches 8.0 or above).</span></li>\r\n	<li><span class=\"notranslate\">Award-winning international, regional or national.</span></li>\r\n	<li><span class=\"notranslate\">Register disciplines relevant to the study of international award-winning, regional or national and fit the program of study prescribed (Medicine, Dentistry, Architecture and the Law shall not be granted scholarship program This process).</span></li>\r\n	<li><span class=\"notranslate\">Fluency in English to take the written exam, oral examination and college English.</span> <span class=\"notranslate\"> Candidates who have international certificates TOEFL / IELTS internal or international.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">The Singapore government encourages candidates on the link below to register for the exam Singapore Scholarship: <a href=\"https://translate.googleusercontent.com/translate_c?depth=1&amp;hl=vi&amp;rurl=translate.google.com.vn&amp;sl=vi&amp;tl=en&amp;u=https://singaporescholarship.scp.gov.sg/&amp;usg=ALkJrhhU2P94C9O0L5qM5k4AtYABRGcZ2A\">https://singaporescholarship.scp.gov.sg</a></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">For more information, please contact:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\"><strong>Consulting firm to study and train in Europe and America (AMEC)</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n','thiet-ke-gian-hang-trien-lam',1,'','',[site_id],[user_id],'/media/images/114/news/ava/','153_1417532872_715547dd5c8a3f60.jpg',[now], [now],[user_id],0,'','',[now]);set @news161 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories128,'Study of management in hospitality','Founded by the corporate governance of Singapore in 2003, ERC Institute is one of the best private universities School was founded with the goal of creating generations of outstanding entrepreneurs to Singapore','<p style=\"text-align: justify;\"><span class=\"notranslate\" style=\"\">Students at the school will experience a modern learning environment for teaching activities with modern facilities.</span> <span class=\"notranslate\"> Not only that, our students have the opportunity to be the largest business network in the ERC to share experiences, lessons learned their business</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\"><img alt=\"\" src=\"http://lightyearlearning.web3nhat.vn/data/media/114/images/ERC.jpg\" /></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\" style=\"background-color: rgb(230, 236, 249);\">ERC Institute is one of the first universities of Singapore honored EDUTRUST- certification certificate for the most prestigious private schools, private in this country.</span> <span class=\"notranslate\"> Not only that, the school also achieved certification SQC S-Class and the quality of teaching services provided by private educational institutions in Singapore Quality Class for Private Schools Government of Singapore</span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align: justify;\"><span class=\"notranslate\" style=\"\"><strong>II.</strong></span> <span class=\"notranslate\"> <strong>The reason you should choose the</strong></span></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">ERC by the Corporate Governance Asia\'s leading founders.</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">The opportunity to meet and interact with successful entrepreneurs and experienced in the field</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Upon graduation students will be guaranteed a job with a starting salary of $ 1,000 SNG / month falls on</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">The business project\'s feasibility study will be funded by Angel Fund in Singapore amounted to S $ 100,000.</span> <span class=\"notranslate\"> This is a good opportunity to help you get more experience in the field of business, opportunity to apply theory to practice</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">The opportunity to receive international qualifications and the opportunity to transfer to the School of English</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Flexible curriculum, student learning conditions do</span></li>\r\n</ul>\r\n\r\n<h3 style=\"text-align: justify;\"><span class=\"notranslate\"><strong>IV.</strong></span> <span class=\"notranslate\"> <strong>Curriculum</strong></span></h3>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\"><em><strong>Fee</strong></em></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Degree programs by ERC: $ 4,000- $ 17.500</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Degree program by the University of Greenwich: $ 14,000- $ 16.500</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Degree program by the University of <strong>Wolverhampton: $ 13,800- $ 15,000</strong></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\"><em><strong>Entry requirements</strong></em></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\"><strong>College: Graduated from high school, IELTS 5.5</strong></span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\"><strong>BA: Graduate from high school, IELTS 6.0</strong></span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\"><strong>Master: Graduated University, IELTS 6.5</strong></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><strong>&nbsp;</strong> <span class=\"notranslate\"> <strong>Program Management Hotel Du lich-</strong></span></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_11773\" style=\"width: 575px\">\r\n<p style=\"text-align: justify;\"><a data-slb-active=\"1\" data-slb-group=\"11771\" data-slb-internal=\"0\" href=\"http://www.amec.com.vn/wp-content/uploads/2014/10/Untitled1.jpg\"><img alt=\"Untitled1 Du học ngành quản trị du lịch khách sạn cùng ERC   Singapore\" class=\"size-full wp-image-11773\" height=\"469\" src=\"http://www.amec.com.vn/wp-content/uploads/2014/10/Untitled1.jpg\" title=\"Study of management in hospitality and ERC Singapore\" width=\"565\" /></a></p>\r\n\r\n<p class=\"wp-caption-text\" style=\"text-align: justify;\"><span class=\"notranslate\">Program administrators hospitality - Singapore</span></p>\r\n</div>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Students studying Hospitality Management Hotel Du lich- the ERC will have the opportunity to receive a degree from the University of Greenwich UK</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Admission time: in January, 3, 5, 7, 9, 11 enabling students enrolled easy</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Where there are hotels to help students with many opportunities to practice</span></li>\r\n	<li style=\"text-align: justify;\"><span class=\"notranslate\">Their students can intern at the hotel facilities, travel agency partner schools</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n','trang-tri-noi-ngoai-that',1,'','',[site_id],[user_id],'/media/images/114/news/ava/','581_1417533202_649547dd71239938.jpg',[now], [now],[user_id],0,'','',[now]);set @news162 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories127,'Institute of Management','I. Introduction\r\n\r\nLocated in central Singapore, China Town area is only a 10 minute walk and immediately adjacent to Clark Quay area, Nanyang Academy of Management is one of the leading establishments in Singapore Lion Island and Southeast Asia in the field of management training. Although newly established since 2001, but the school has built a reputation for quality training and infrastructure. ','<p style=\"text-align: justify;\"><span class=\"notranslate\">.</span> <span class=\"notranslate\" style=\"\"> To facilitate the students a chance to practice, academia has become a partner of more than 200 leading hotel: Fullerton, Intercontinental, World Sentosa Resort, Holiday Inn, Pan Pacific.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><a data-slb-active=\"1\" data-slb-group=\"11478\" data-slb-internal=\"0\" href=\"http://www.amec.com.vn/wp-content/uploads/2014/09/nanyang12.jpg\"><img alt=\"nanyang12 Học viện quản lý Nanyang  hàng đầu Singapore\" class=\"aligncenter  wp-image-11479\" height=\"261\" src=\"http://www.amec.com.vn/wp-content/uploads/2014/09/nanyang12.jpg\" title=\"Nanyang Institute of Management Singapore top\" width=\"685\" /></a></p>\r\n\r\n<ul style=\"text-align: justify;\">\r\n	<li><span class=\"notranslate\"><strong>5 School Academy members include:</strong></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- School of Economics</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- School of Hotel Administration</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- School of Engineering</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- School of Education</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- Language School</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- School of Logistics</span></p>\r\n\r\n<ul style=\"text-align: justify;\">\r\n	<li><span class=\"notranslate\"><strong>Nayang received the top certification for quality:</strong></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- CPE Registration</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- ISO 9001: 2008</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- EduTrust</span></p>\r\n\r\n<h3 style=\"text-align: justify;\"><span class=\"notranslate\"><strong>II.</strong></span> <span class=\"notranslate\"> <strong>Reasons to attend</strong></span></h3>\r\n\r\n<p style=\"text-align: justify;\"><a data-slb-active=\"1\" data-slb-group=\"11478\" data-slb-internal=\"0\" href=\"http://www.amec.com.vn/wp-content/uploads/2014/09/120409HD00_716fe.jpg\"><img alt=\"120409HD00 716fe Học viện quản lý Nanyang  hàng đầu Singapore\" class=\"aligncenter size-full wp-image-11480\" height=\"375\" src=\"http://www.amec.com.vn/wp-content/uploads/2014/09/120409HD00_716fe.jpg\" title=\"Nanyang Institute of Management Singapore top\" width=\"600\" /></a></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- You will have the opportunity to <strong>6 month</strong> paid <strong>internship</strong> at the hotel\'s partner schools with <strong>salary 800-1000 SGD / month.</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- Schools are awarded a certificate spells Singapore (valid for 4 years), certification of ability to maintain a good standard of education for the benefit of students.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- The curriculum of the school is certified by the Council for Private Education Singapore</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- Where in <strong>the top 10 private schools in Singapore are the most popular</strong> according to research by Jobscentral 2011</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- According to the school, students will have the opportunity to experience life in the city center, access to hotels, banks and companies and are more convenient to go back and use the service other necessary services.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span class=\"notranslate\">- In some disciplines, such as hotel and tourism business administration, you will have the opportunity to receive by Australian universities while studying and living in Singapore.</span></p>\r\n','he-thong-bang-bien-mobifone',1,'','',[site_id],[user_id],'/media/images/114/news/ava/','55_1417533486_139547dd82e69fc7.jpg',[now], [now],[user_id],0,'','',[now]);set @news165 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,@,'Biển tấm lớn trên Quốc lộ 2 Việt Trì','Quốc lộ 2 dài 312 km, chạy qua địa bàn năm tỉnh và thành phố: Hà Nội, Vĩnh Phúc, Phú Thọ, Tuyên Quang và Hà Giang. Chiều dài toàn tuyến là 315 km. Đoạn từ sân bay Nội Bài (Hà Nội) đến thành phố Vĩnh Yên còn được gọi là Quốc lộ 2A. Đoạn từ Vĩnh Yên đến thành phố Tuyên Quang và một ngả rẽ lên thị trấn Tam Đảo còn được gọi là Quốc lộ 2B. Đoạn từ thành phố Tuyên Quang đến cửa khẩu Thanh Thủy còn được gọi là Quốc lộ 2C.\r\n','<p>&nbsp;Biển được xây dựng bên cạnh đường cao tốc quốc lộ số 2 và cạnh tuyến đường sắt, thuộc phường Bạch Hạc, TP. Việt Trì. Bên phải đường theo hướng Hà Nội đi Yên Bái.</p>\r\n\r\n<p><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/viet%20tri.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/viet%20tri.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n','bien-tam-lon-tren-quoc-lo-2-viet-tri',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','557_1415507554_148545eee62abbd2.jpg',[now], [now],[user_id],[now]);set @posts28 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,@,'Biển tấm lớn trong bến xe Gia Lâm','Bến xe Gia Lâm là đầu mối giao thông quan trọng của các tỉnh phía bắc. Mật độ giao thông ở đây khoảng từ 10.000 - 13.000 lượt khách/ ngày, những ngày lễ lớn mật độ lên đến 13.000 - 15.000 lượt khách/ ngày. Hệ thống biển của chúng tôi ở đây gồm 2 vị trí, án ngữ hai bên cửa ra vào nhà chờ xe khách của bến. Biển được đặt ở vị trí trên cao, tầm nhìn tốt. \r\n','<p><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%20xe%20gia%20lam.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%20xe%20gia%20lam.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n','bien-tam-lon-trong-ben-xe-gia-lam',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','626_1415506854_247545eeba6d6f24.jpg',[now], [now],[user_id],[now]);set @posts29 = LAST_INSERT_ID();
INSERT INTO posts (id, category_id, title, sortdesc, description, alias, status, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, publicdate) VALUES (null,@,'Biển tấm lớn trong bến xe Mỹ Đình','Bến xe Mỹ Đình được xây dựng cách nay hơn 10 năm, bình quân mỗi ngày có trên 11.000 lượt xe khách hoạt động. Nằm cạnh trục đường chính Phạm Hùng, số lượng hành khách ngày càng tăng, các phương tiện đưa đón khách cũng được bổ xng thường xuyên để đáp ứng đầy đủ nhu cầu đi lại của người dân.\r\n','<p style=\"text-align: justify;\">&nbsp;Hệ thông biển quảng cáo của Đông Quang tại đây gồm 11 biển, trong đó có 3 biển được lắp đặt phía trong nhà chờ xe ( biển số 9, 10, 11 ) và 8 biển được lắp đặt phía trước sân đỗ của bến xe, phía bên phải đường Phạm Hùng ( theo hướng Phạm Hùng - Nguyễn Xiển ).&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"440\" hspace=\"100\" vspace=\"100\" width=\"660\"><param name=\"quality\" value=\"high\" /><param name=\"movie\" value=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%2520xe%2520my%2520dinh.swf\" /><embed height=\"440\" hspace=\"100\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://w3ni0006.web3nhat.net/data/media/53/flash/ben%2520xe%2520my%2520dinh.swf\" type=\"application/x-shockwave-flash\" vspace=\"100\" width=\"660\"></embed></object></p>\r\n\r\n<p>&nbsp;</p>\r\n','bien-tam-lon-trong-ben-xe-my-dinh',1,'','',[site_id],[user_id],'/media/images/53/post/ava/','517_1415506174_383545ee8fe73e2d.jpg',[now], [now],[user_id],[now]);set @posts30 = LAST_INSERT_ID();
