INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'TRIẾT LÝ HOẠT ĐỘNG','Đưa tu nghiệp sinh đi học tập và làm việc tại Nhật Bản trước tiên phải xuất phát từ chữ “tâm”, mọi hoạt động, suy nghĩ của Vitech phải mang đậm tính nhân bản và nỗ lực không ngừng nhằm mang lại sự giúp đỡ, hỗ trợ tốt nhất cho người lao động với khát vọng mở ra một tương lai mới tốt đẹp hơn cho các bạn trẻ Việt Nam, cũng như mang lại sự yên tâm cao nhất cho các Hiệp hội; Xí nghiệp; Công ty tuyển dụng Nhật Bản. ','','/media/images/85/intro/ava/','580_1423213698_66254d484827f53d.jpg','','','',[now],[now]);
