INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories54,'Tuyển 100 Nam thích nghề cơ khí','+ Tuyển 100 Nam/Nữ tham gia chương trình tu nghiệp sinh Nhật Bản.<br />\r\n+ Thời gian tu nghiệp  : 03 năm.<br />\r\n+ Sơ tuyển dự kiến     : 03/2010<br />\r\n+ Phỏng vấn chính thức dự kiến  : Tháng 04-05 năm 2010 với Giám đốc tuyển dụng Nhật Bản.','<p><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Tuyển&nbsp;</span><span style=\"widows: auto; font-family: Arial; font-size: 17px; line-height: normal; color: rgb(255, 0, 0);\"><b><span style=\"font-family: Arial, sans-serif; font-size: 13pt;\">100 Nam/Nữ</span></b>&nbsp;</span><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">tham gia chương trình tu nghiệp sinh Nhật Bản.</span><br style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\" />\r\n<span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Thời gian tu nghiệp&nbsp;&nbsp;:&nbsp;</span><span style=\"widows: auto; font-family: Arial; font-size: 17px; line-height: normal; color: rgb(255, 0, 0);\"><b><span style=\"font-family: Arial, sans-serif; font-size: 13pt;\">03 năm.</span></b></span><br style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\" />\r\n<span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Sơ tuyển dự kiến&nbsp;&nbsp; &nbsp;&nbsp;:&nbsp;</span><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\"><b><span style=\"font-family: Arial, sans-serif; color: red; font-size: 13pt;\">03/2010</span></b></span><br style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\" />\r\n<span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Phỏng vấn chính thức dự kiến&nbsp; :&nbsp;</span><font face=\"Arial\" style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\"><strong><font color=\"#ff0000\" size=\"4\">Tháng 04-05 năm 2010</font></strong></font><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">&nbsp;với Giám đốc tuyển dụng Nhật Bản.</span></p>\r\n','tuyen-100-nam-thich-nghe-co-khi',1,'','',[site_id],[user_id],'','',[now], [now],[user_id],0,'','',[now]);set @news70 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories54,'Tuyển gấp 50 Nam - Phỏng vấn','Kinh tế Nhật Bản đang phục hồi mạnh mẽ sau khủng khoảng toàn cầu. Để đáp ứng cho kế hoạch sản xuất trong thời gian sắp tới, hiện tại các công ty Nhật Bản đang có nhu cầu tuyển dụng nhân lực rất lớn.','<ol style=\"line-height: 20.7999992370605px;\">\r\n	<li value=\"1.1\">&nbsp; &nbsp;Kinh tế Nhật Bản đang phục hồi mạnh mẽ sau khủng khoảng toàn cầu. Để đáp ứng cho kế hoạch sản xuất trong thời gian sắp tới, hiện tại các công ty Nhật Bản đang có nhu cầu tuyển dụng nhân lực rất lớn.</li>\r\n	<li value=\"1.2\">&nbsp;&nbsp; Công ty CP Cung ứng Nhân lực Việt-Nhật (TP.HCM) – Là công ty hoạt động trong lĩnh vực phái cử nhân lực đi làm việc tại Nhật Bản đang có nhu cầu tuyển dụng ứng viên đi làm việc tại Nhật Bản theo Chương trình Thực tập sinh kỹ năng tại Nhật Bản trong tháng 06/2010 :</li>\r\n	<li>Tuyển&nbsp;50 ứng viên làm việc tại Nhật Bản</li>\r\n	<li>Thời gian thực tập 03 năm.</li>\r\n	<li>Phỏng vấn chính thức trong tháng 06/2010</li>\r\n</ol>\r\n','tuyen-gap-50-nam---phong-van',1,'','',[site_id],[user_id],'','',[now], [now],[user_id],0,'','',[now]);set @news69 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories54,'Tuyển 100 Nam/Nữ làm việc tại Nhật Bản','+ Tuyển 100 Nam/Nữ tham gia chương trình tu nghiệp sinh Nhật Bản.<br />\r\n+ Thời gian tu nghiệp  : 03 năm.<br />\r\n+ Sơ tuyển dự kiến     : 03/2010<br />\r\n+ Phỏng vấn chính thức dự kiến  : Tháng 04-05 năm 2010 với Giám đốc tuyển dụng Nhật Bản.','<p><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Tuyển&nbsp;</span><span style=\"widows: auto; font-family: Arial; font-size: 17px; line-height: normal; color: rgb(255, 0, 0);\"><b><span style=\"font-family: Arial, sans-serif; font-size: 13pt;\">100 Nam/Nữ</span></b>&nbsp;</span><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">tham gia chương trình tu nghiệp sinh Nhật Bản.</span><br style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\" />\r\n<span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Thời gian tu nghiệp&nbsp;&nbsp;:&nbsp;</span><span style=\"widows: auto; font-family: Arial; font-size: 17px; line-height: normal; color: rgb(255, 0, 0);\"><b><span style=\"font-family: Arial, sans-serif; font-size: 13pt;\">03 năm.</span></b></span><br style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\" />\r\n<span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Sơ tuyển dự kiến&nbsp;&nbsp; &nbsp;&nbsp;:&nbsp;</span><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\"><b><span style=\"font-family: Arial, sans-serif; color: red; font-size: 13pt;\">03/2010</span></b></span><br style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\" />\r\n<span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">+ Phỏng vấn chính thức dự kiến&nbsp; :&nbsp;</span><font face=\"Arial\" style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\"><strong><font color=\"#ff0000\" size=\"4\">Tháng 04-05 năm 2010</font></strong></font><span style=\"widows: auto; color: rgb(100, 96, 97); font-family: Arial; font-size: 17px; line-height: normal;\">&nbsp;với Giám đốc tuyển dụng Nhật Bản.</span></p>\r\n','tuyen-100-namnu-lam-viec-tai-nhat-ban',1,'','',[site_id],[user_id],'','',[now], [now],[user_id],0,'','',[now]);set @news68 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories53,'Mục tiêu phát triển & Định hướng',' Mở rộng thị trường xuất khẩu lao động, đa dạng hoá các loại hình đào tạo. Xây dựng Công ty cổ phần cung ứng nhân lực VIỆT NHẬT trở thành một trong những công ty hàng đầu của cả nước về đào tạo và phát triển nguồn nhân lực đáp ứng nhu cầu trong và ngoài nước','<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: center;\"><img alt=\"\" src=\"http://vitech.nanoweb.vn/data/media/142/images/2015/muctieu(1).gif\" style=\"cursor: default; width: 617px; height: 210px;\" /></p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Mở rộng thị trường xuất khẩu lao động, đa dạng hoá các loại hình đào tạo. Xây dựng Công ty cổ phần cung ứng nhân lực VIỆT NHẬT trở thành một trong những công ty hàng đầu của cả nước về đào tạo và phát triển nguồn nhân lực đáp ứng nhu cầu trong và ngoài nước</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Đầu tư và phát triển nguồn lực con người đủ về số lượng, có chất lượng cao để đảm bảo hoàn thành tốt kế hoạch đề ra. Không ngừng nâng cao tính chuyên nghiệp từ đội ngũ cán bộ quản lý, điều hành đến CBCNV.</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Tăng trưởng bền vững, tài chính minh bạch, quản trị doanh nghiệp theo chuẩn mực quốc tế.</p>\r\n','muc-tieu-phat-trien--dinh-huong',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','201_1427102591_125550fdb7fe99ad.gif',[now], [now],[user_id],0,'','',[now]);set @news67 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories53,'Lĩnh vực hoạt động','Hoạt động dịch vụ đưa người lao động Việt Nam đi làm việc ở nước ngoài theo Hợp đồng (Giấy phép do Bộ Lao động – TB&XH cấp số: 419/LĐTBXH-GP)','<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><img alt=\"\" src=\"http://vitech.nanoweb.vn/data/media/142/images/2015/6.jpg\" style=\"cursor: default; width: 800px; height: 290px;\" /></p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Hoạt động dịch vụ đưa người lao động Việt Nam đi làm việc ở nước ngoài theo Hợp đồng (Giấy phép do Bộ Lao động – TB&amp;XH cấp số: 419/LĐTBXH-GP);</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Kinh doanh thương mại</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Đào tạo ngoại ngữ, đào tạo nghề trung và ngắn hạn.</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">- Kinh doanh xuất nhập khẩu</p>\r\n','linh-vuc-hoat-dong',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','32_1427102458_669550fdafa282dc.jpg',[now], [now],[user_id],0,'','',[now]);set @news66 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories53,'Thư ngỏ','Công ty CP cung ứng nhân lực Việt Nhật (VITECH.,JSC) được thành lập từ năm 2003 với  lĩnh vực hoạt động chính là “ Dịch vụ đưa Người lao động đi làm việc ở nước ngoài ”. VITECH.,JSC luôn hướng đến mục tiêu vì sự hài lòng các Quý Đối tác, Quý Khách hàng trong và ngoài nước, vì sự phát triển – thịnh vượng chung của đất nước. ','<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: center;\"><img alt=\"\" src=\"http://vitech.vn/data/media/142/images/2015/21.jpg\" style=\"cursor: default; width: 642px; height: 350px;\" /></p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: 18px;\"><span style=\"font-family: \'lucida sans unicode\', \'lucida grande\', sans-serif;\">Kính thưa các Quý đối tác, Quý khách hàng !</span></span></span></p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Công ty CP cung ứng nhân lực Việt Nhật (VITECH.,JSC) được thành lập từ năm 2003 với&nbsp; lĩnh vực hoạt động chính là “ Dịch vụ đưa Người lao động đi làm việc ở nước ngoài ”. VITECH.,JSC luôn hướng đến mục tiêu vì sự hài lòng các Quý Đối tác, Quý Khách hàng trong và ngoài nước, vì sự phát triển – thịnh vượng chung của đất nước.&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><em>Trong lĩnh vực XKLĐ:</em></p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Với đối tác ngoài nước, VITECH.,JSC tự tin về sự chuyên nghiệp của đội ngũ Nhân viên nghiệp vụ của mình. Chúng tôi cam kết tuyển chọn chặt chẽ và đào tạo nguồn lao động trong môi trường đáp ứng đầy đủ các yêu cầu của Chủ sử dụng theo quy trình và kinh nghiệm được dày công vun đắp suốt hơn một thập kỷ qua, nhằm cung ứng các đối tượng lao động có chất lượng tốt nhất đi làm việc tại các thị trường ngoài nước, góp phần quảng bá hình ảnh đất nước, con người Việt Nam chăm chỉ – năng động – thông minh và thân thiện đến bạn bè thế giới.&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Với khách hàng trong nước, chúng tôi lấy phương châm: UY TÍN – CHẤT LƯỢNG – HIỆU QUẢ làm gốc để thực hiện tốt nhất công tác phục vụ khách hàng, phục vụ người lao động. Bên cạnh đó luôn nghiêm túc thực hiện các quy định của luật pháp Việt Nam trong công tác đưa người lao động đi làm việc ở nước ngoài, đảm bảo quyền lợi hợp pháp và chính đáng cho Doanh nghiệp và các đối tượng công việc liên quan.&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Thay mặt CBCNV của Vitech.,JSC, tôi xin gửi lời cám ơn chân thành nhất đến các Quý Đối tác, các Quý Khách hàng đã và đang hợp tác, luôn ủng hộ và giúp đỡ cho Công ty chúng tôi .&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Trân trọng!</p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><strong>Ms . Nguyễn Thúy Nga</strong></p>\r\n\r\n<p style=\"color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, \'Trebuchet MS\'; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><em>&nbsp;Chủ tịch Hội Đồng Quản Trị</em></p>\r\n','thu-ngo',1,'','',[site_id],[user_id],'','',[now], [now],[user_id],0,'','',[now]);set @news65 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories46,'Bỏ mức lương 80 triệu mỗi tháng để khởi nghiệp lần hai','Đang có mức thu nhập khoảng 80 triệu đồng tại Singapore, anh Nguyễn Văn Sự vẫn quyết định quay về Việt Nam khởi nghiệp lần hai. Hiện doanh thu công ty anh đạt gần 6 tỷ đồng chỉ sau một năm vận hành.','<p><span style=\"color: rgb(51, 51, 51); font-family: arial; font-size: 14px; line-height: 18px; background-color: rgb(255, 255, 255);\">ôi năm nay 27 tuổi, sinh ra trong một gia đình thuộc vùng ven Long Khánh, Đồng Nai. Gia đình khó khăn nên tuổi thơ của tôi khá vất vả. Lúc đi học, tôi không có tiền mua sách mà phải mượn từ bạn. Cũng chính vì cuộc sống khó khăn nên trong lòng tôi luôn nung nấu ý định thoát nghèo.</span></p>\r\n\r\n<p><span style=\"color: rgb(51, 51, 51); font-family: arial; font-size: 14px; line-height: 18px; background-color: rgb(255, 255, 255);\">Khi học cấp ba, dù phải đạp xe hàng chục km đến trường nhưng tôi chưa bao giờ thấy nản chí. Lúc nhận giấy báo đậu Đại học Khoa học Tự nhiên TP HCM, niềm hạnh phúc trong tôi dường như vỡ òa.</span></p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">rong thời gian học, tôi cố gắng đi làm thêm, vừa để kiếm thêm thu nhập, vừa để học hỏi kinh nghiệm kinh doanh. Tôi không chừa bất cứ công việc gì từ làm gia sư, phát tơ rơi, giao hàng, cho đến coi tiệm net, bán hàng online... Ngoài tiền làm thêm để trang trải chi phí, suốt 4 năm tôi đều nhận được học bổng nên thoáng cái đã hoàn thành khóa học với tấm bằng loại giỏi.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Sau khi ra trường, khác với những bạn bè có thành tích học tập cao đều muốn ở lại trường giảng dạy hoặc học lên nữa, ước mơ của tôi là sẽ làm chủ một công ty về công nghệ thông tin của riêng mình. Vì vậy, tôi quyết định đi làm để học hỏi, trước là công nghệ, sau là cách vận hành doanh nghiệp.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Vào năm 2012, khi đang ở vị trí trưởng nhóm cho một công ty về công nghệ thông tin, tôi quyết định bỏ công việc khá tốt này và cùng với một người bạn lập công ty riêng.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Chúng tôi đã xây dựng nhiều ý tưởng về công nghệ thông tin cho khách hàng, một trong những dự án đầu tiên lúc bấy giờ là shoplienket. Đây là dự án nhằm tạo ra sự liên kết giữa các shop với nhau, đi kèm với thẻ giảm giá chung có thể sử dụng ở tất cả các shop. Tuy nhiên, sau một thời gian hoạt động, công ty thất bại do thiếu vốn hoạt động.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Tôi nhận ra rằng chỉ ý tưởng và quyết tâm là chưa đủ để giúp mình thành công, vấn đề cốt lõi là cần phải có vốn. Vì vậy, tôi chấp nhận đi làm trở lại để có thể tích lũy vốn, sau đó quay lại khởi nghiệp.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">May mắn tôi sớm tìm được một công việc tại Singapore với mức lương khởi điểm 3.000 USD (tầm hơn 60 triệu đồng). Tôi quyết định sang nước ngoài làm việc với mong muốn có thêm kiến thức về công nghệ thông tin, học hỏi thêm kinh nghiệm ở môi trường quốc tế, đồng thời tích lũy vốn để thành lập doanh nghiệp riêng của mình.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Ngoài thời gian làm việc chính tại công ty, buổi tối tôi luôn dành thời gian nghiên cứu, xây dựng ý tưởng riêng cho doanh nghiệp của mình khi trở về Việt Nam. Tôi nhận thấy rằng bất cứ doanh nghiệp nào cũng có website, nhưng họ rất khó để chủ động liên lạc với những người ghé thăm website của mình. Ý tưởng phần mềm \"PM live chat\" xuất hiện từ đó. Đây là giải pháp giúp cho chủ website tương tác dễ dàng nhất với khách ghé thăm.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Sau vài năm làm việc, tích lũy được một số vốn cùng với những kiến thức và kinh nghiệm cần thiết, tôi quyết định từ bỏ công việc tại Singapore (thời điểm này lương tôi có lúc lên 4.000 USD, tức hơn 80 triệu đồng) để quay về Việt Nam tiếp tục ước mơ của mình. Tôi nghĩ, tuổi đời mình còn quá trẻ nếu có thất bại cũng không sao, vì sau thất bại tôi sẽ học được những kinh nghiệm quý báu.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Nghĩ là làm, sau khi về nước (năm 2013), tôi cùng người bạn thân lúc trước thuê một mặt bằng nhỏ và làm lại doanh nghiệp lần hai với mức vốn 500 triệu đồng và 6 nhân viên. Ngoài phần mềm live chat, công ty tập trung xây dựng giải pháp công nghệ thông tin cho doanh nghiệp Việt với các sản phẩm như thiết kế website, phần mềm hỗ trợ trực tuyến giúp tăng doanh số, mobile hóa website, xây dựng ứng dụng moblie và mạng xã hội cho doanh nghiệp.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Bắt tay vào vận hành công ty, chúng tôi tiếp tục đối mặt với rất nhiều khó khăn. Bởi thị trường công nghệ thông tin là lĩnh vực đầu tư sinh lợi nhuận cao, không tốn chi phí cho nguyên liệu sản xuất như các ngành khác vì chi phí nhân bản sản phẩm bằng 0 (do khi phần mềm đã hoàn thành thì chỉ cần copy là đã có một bản mới để bán cho khách hàng), vì vậy sự cạnh tranh rất khốc liệt.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Ngoài ra, cũng như các doanh nghiệp nhỏ khác khi mới thành lập, chúng tôi vẫn tiếp tục đối mặt với tình trạng thiếu vốn vì giá thuê nhân sự ngành công nghệ thông tin khá cao. Chưa kể trong quá trình triển khai dự án cho khách hàng, họ thường không có yêu cầu rõ ràng, lại thay đổi ý kiến thường xuyên khiến chúng tôi rất khó kết thúc mỗi dự án để mà nghiệm thu và tất toán hợp đồng. Điều này làm cho dòng vốn của chúng tôi càng trở nên eo hẹp.</p>\r\n\r\n<p class=\"Normal\" style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Tuy nhiên, bằng quyết tâm cao của bản thân cũng như những cộng sự, sau một năm đầu khó khăn, hiện nay doanh nghiệp tôi đã bắt đầu dần ổn định. Công ty đã có hơn trăm khách hàng doanh nghiệp trong và ngoài nước sử dụng dịch vụ với những hợp đồng làm việc rõ ràng và chuyên nghiệp hơn (không còn tình trạng chậm nghiệm thu hợp đồng). Doanh thu hiện tại của công ty đạt khoảng 500 triệu đồng mỗi tháng. Đây là con số còn khiêm tốn nhưng tôi tin với chiến lược phát triển hiện nay, kèm sự quyết tâm cao, doanh thu của công ty sẽ tăng nhanh trong thời gian tới.</p>\r\n\r\n<p style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Qua câu chuyện của mình, tôi muốn chia sẻ vài điều, thành công chỉ đến với những người kiên trì theo đuổi ước mơ đến cùng. Và điều quan trọng không kém là bạn cần phải tích lũy kinh nghiệm, vốn trước khi khởi nghiệp.</p>\r\n\r\n<p style=\"margin: 0px 0px 1em; padding: 0px; line-height: 18px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; background-color: rgb(255, 255, 255);\">Bởi chỉ ý tưởng không thì chưa đủ, vì khi đã vận hành công ty, bạn không thể vay ngân hàng nếu không có tài sản thế chấp, và cũng không thể vay các tổ chức đầu tư mạo hiểm nếu công ty của bạn chưa có doanh thu lớn. Ngoài ra, khi bạn dùng nguồn vốn từ chính tiền mô hồi nước mắt mà bạn tiết kiệm được để khởi nghiệp, bạn mới trân trọng nó và sử dụng hiệu quả nhất.</p>\r\n','bo-muc-luong-80-trieu-moi-thang-de-khoi-nghiep-lan-hai',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','117_1423218553_42354d497795d201.png',[now], [now],[user_id],0,'','',[now]);set @news56 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories46,'65.013 lao động được đưa đi làm việc tại nước ngoài','Số liệu trên là kết quả hoạt động xuất khẩu lao động của cả nước trong 9 tháng đầu năm, đạt 76,5% kế hoạch cả năm','<div id=\"intro\">Số liệu trên là kết quả hoạt động xuất khẩu lao động của cả nước trong 9 tháng đầu năm, đạt 76,5% kế hoạch cả năm. Theo cục Quản lý lao động ngoài nước, Bộ lao động – Thương binh và Xã hội, tại 11 thị trường truyền thống như Hàn Quốc, Đài Loan, Nhật Bản, Malaysia, Ma Cao và một số nước Trung Đông… đã tiếp nhận hơn 61.000 lao động, chiếm 95% tổng số lao động đi làm việc tại nước ngoài trong 9 tháng qua.</div>\r\n\r\n<div id=\"detail\">\r\n<div>\r\n<p>Đứng đầu danh sách các thị trường tiếp nhận lao động Việt Nam là Đài Loan với 25.990 lao động, tiếp theo là Hàn Quốc với 11.772 lao động.</p>\r\n\r\n<p>Theo cục Quản lý lao động ngoài nước, trong tương lai gần, với mục tiêu tăng số lượng lao động đi làm việc tại nước ngoài từ 85.000 người trong năm 2008 lên 90.000 người năm 2009 và lên đến 100.000 người vào năm 2010, Bộ sẽ thực hiện một số giải pháp chủ yếu như tư vấn cho người lao động sang Malaysia và mở rộng các thị trường mới, các thị trường có thu nhập cao, khuyến khích lao động có tay nghề và lao động kỹ thuật...</p>\r\n</div>\r\n</div>\r\n','65013-lao-dong-duoc-dua-di-lam-viec-tai-nuoc-ngoai',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','623_1423217524_69554d493749f2d0.jpg',[now], [now],[user_id],0,'','',[now]);set @news55 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories46,'Thủ tướng đồng ý cho phép đưa lao động trở lại Libya','Thủ tướng Chính phủ đồng ý cho phép thí điểm đưa lao động trở lại Libya làm việc. ','<p>Bộ Lao động, Thương binh và Xã hội được giao nhiệm vụ lập hợp đồng lao động với các đối tác tin cậy và hợp tác chặt chẽ với Bộ Ngoại giao cùng các cơ quan có liên quan khác.</p>\r\n\r\n<p>Đầu năm 2011, Bộ LĐTBXH đã phối hợp với các cơ quan nước bạn để sơ tán hơn 10.000 lao động Việt Nam rời khỏi Libya để tránh bạo loạn.</p>\r\n','thu-tuong-dong-y-cho-phep-dua-lao-dong-tro-lai-libya',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','169_1423216998_74154d4916628c57.jpg',[now], [now],[user_id],0,'','',[now]);set @news54 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories46,'Cơ hội cuối cùng cho lao động Việt nam Bất hợp pháp ở nước ngoài tự nguyện đăng ký về nước trước ngà','Thực hiện một trong những biện pháp quyết liệt nhằm chấn chỉnh hoạt động đưa lao động Việt nam đi làm việc ở nước ngoài, ngày 27/01/2014, Chính phủ đã ban hành Nghị quyết số 09/NQ-CP','<p style=\"margin: 0px 0px 10px; color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 20px;\">hực hiện một trong những biện pháp quyết liệt nhằm chấn chỉnh hoạt động đưa lao động Việt nam đi làm việc ở nước ngoài, ngày 27/01/2014, Chính phủ đã ban hành Nghị quyết số 09/NQ-CP. Theo đó, hạn cuối cùng để áp dụng xử phạt tiền đối với lao động Việt Nam làm việc ở nước ngoài bỏ trốn khỏi nơi làm việc theo hợp đồng hoặc ở lại nước ngoài trái phép sau khi hết hạn hợp đồng là sau ngày 10/3/2014. Điều này có nghĩa là những lao động Việt Nam đã vi phạm mà tự nguyện về nước trước ngày 10/3/2014 thì sẽ chưa áp dụng phạt tiền như quy định tại Điều 35 Nghị định số 95/2013/NĐ-CP ngày 22/8/2013.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; color: rgb(34, 34, 34); font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; line-height: 20px;\">Bộ LĐTBXH và Bộ Ngoại giao đã có thông tư liên tịch số 32 ngày 6/12/2013 qui định chi tiết và rõ ràng qui trình xử phạt cả khi phát hiện người lao động vi phạm khi đang ở nước ngoài cũng như khi đã trở về Việt nam. Cơ quan đại diện Việt nam ở nước ngoài sẽ tiến hành lập biên bản vi phạm đối với người lao động vi phạm căn cứ vào thông báo của các cơ quan chức năng nước sở tại hoặc chủ sử dụng lao động và ra quyết định xử phạt đến 100 triệu đồng về hành vi này. Ở trong nước, trách nhiệm này thuộc về Chính quyền địa phương và ngành LĐTBXH. Quyết định xử phạt sẽ được niêm yết ở Cơ quan đại diện ngoại giao Việt nam ở nước ngoài cũng như UBND cấp xã nơi người lao động cư trú trước khi đi, thông báo đến gia đình người lao động và sẽ cưỡng chế thi hành theo pháp luật xử phạt vi phạm hành chính nếu người vi phạm không tự nguyện chấp hành.</p>\r\n','co-hoi-cuoi-cung-cho-lao-dong-viet-nam-bat-hop-phap-o-nuoc-ngoai-tu-nguyen-dang-ky-ve-nuoc-truoc-nga',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','906_1423216854_53054d490d6e41d8.jpg',[now], [now],[user_id],0,'','',[now]);set @news53 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories48,'Thị trường UAE','Dubai (tiếng Ả Rập: دبيّ Dubayy) là một trong các vương quốc của Các Tiểu Vương quốc Ả Rập Thống nhất (Abu Dhabi, Dubai, Sharjah, Ras Al-Khaimah, Umm Al-Qaiwain, Ajman và Fujairah) và cũng là thành phố lớn nhất của quốc gia này (thường được gọi là Thành phố Dubai)','<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><span style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Dubai</span><span style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">&nbsp;(tiếng Ả Rập: دبيّ&nbsp;<i style=\"margin: 0px; padding: 0px; font-size: 16px; border: 0px; outline: 0px; vertical-align: baseline;\">Dubayy</i>) là một trong các vương quốc của Các Tiểu Vương quốc Ả Rập Thống nhất (Abu Dhabi, Dubai, Sharjah, Ras Al-Khaimah, Umm Al-Qaiwain, Ajman và Fujairah) và cũng là thành phố lớn nhất của quốc gia này (thường được gọi là&nbsp;<span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 18px;\">Thành phố Dubai</span>)</span></span></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Dubai đã có lịch sử khoảng 150 năm. Thành phố có sự chia sẻ chức năng luật pháp, chính trị, quân sự, và kinh tế với các tiểu vương quốc khác, trong liên bang. Dubai là thành phố có dân số lớn nhất, và diện tích lớn thứ 2 nhất sau Abu Dhabi. Thành phố được trị vì bởi vua Al Maktoum từ năm 1833. Người lãnh đạo hiện tại của thành phố Mohammed bin Rashid Al Maktoum là thủ tướng đồng thời là phó tổng thống của UAE.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Khác với các thành viên khác trong Các Tiểu Vương quốc Ả Rập Thống nhất, lợi nhuận thu được từ dầu mỏ của Dubai chỉ chiếm khoảng 6% GDP trong nền kinh thế 37 tỷ USD của thành phố. Dubai là một trong những nền kinh tế đang phát triển lớn nhất thế giới. Phần lớn lợi nhuận thu được là nhờ vào các dịch vụ như cảng biển, du lịch, tài chính. Khu tự do Jebel Ali cũng góp phần lớn vào sự phát triển này.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Với những dự án xây dựng khổng lồ và sự phát triển của nhiều nghành công nghiệp, Dubai đang trở thành điểm đến thu hút các nhà đầu tư trên toàn thế giới qua thị trường địa ốc phát triển, các sự kiện thể thao, hội nghị hội thảo và các kỷ lục Guinness như toà nhà cao nhất thế giới (đang được xây dựng), khu mua sắm lớn nhất thế giới, đảo nhân tạo lớn nhất thế giới, khu trượt tuyết trong nhà lớn nhất thế giới hay khách sạn sang trọng nhất thế giới.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Dubai nẳm trên bờ biển phía Bắc UAE và là một trong hai quốc gia lớn nhất. Phần lớn đất đai là đồi núi và được bao quanh bởi các xa mạc.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Dubai được xem như là viên ngọc của vùng Vịnh, điều đó thì dễ dàng nhận thấy tại sao. Chúng ta có thể thấy rõ sự sa hoa và giàu có ở bất cứ nơi đâu tại đây. Dubai cũng là nơi đi đầu trong việc mở cửa và hội nhập ở Trung Đông, với chính sách miễn hoàn toàn thụế thu nhập, thuế hải quan cao nhất chỉ 5%, chính những điều đó đã làm cho Dubai trở thành trung tâm của vùng Vịnh hiện nay.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Sự phát triển kinh tế và những thành tựu mà Dubai đạt được do cả những chính sách thông thoáng phù hợp và cả sự thuận lợi về địa lý mang lại. Dubai là trung tâm, cửa ngõ giữa Châu Á, Châu Âu và Châu Phi. Những suy nghĩ mới và cách làm mới đã giúp cho Dubai có sụ phát triển vượt bậc và thành công như ngày hôm nay. Với rất nhiều những khu mậu dịch tự do được xây dựng, những dự án khổng lồ , độc đáo như : Thành Phố Internet, Thành Phố Thể thao,Đảo Cây Cọ… đã đưa Dubai thành điểm đến hấp dẫn cả về kinh doanh và du lịch. Hầu hết các tập đoàn lớn trên thế giới như: IBM, Intel, Microsoft đều đã có mặt tại đây. Chính những điều đó càng củng cố niềm tin về sự phát triển trong tương lai và con đường mà Dubai lựa chọn là hoàn toản đúng đắn. Đặc bịêt Bất Động Sản đang lả một lĩnh vực kinh doanh thu hút rất nhiều nhà đầu tư đến từ khắp nơi trên thế giới, với giá cà hấp dẫn, tỷ suất sinh lời cao, miễn thuế hoàn toàn…đã làm cho BDS Dubai trở nên hấp dẫn hơn bao giờ hết và đây chính là thời điểm tốt nhất để đẩu tư vào BDS tại Dubai.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Dubai - một thành phố giàu có và phát triển nhanh nhất thế giới hiện nay.</span></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><u style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Lịch sử, văn hóa</span></u></font></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Các tiểu vương quốc Ả Rập thống nhất (UAE) được thành lập vào ngày 2 tháng 12 năm 1971, gồm bảy lãnh địa Emir là thủ đô Abu Dhabi, Dubai, Sharjah, Ajman, Umm Al quwain, Ra Al Khaimah và Fujairah.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\nTrong gần 30 năm, tận dụng tối đa được nguồn lợi tức từ việc khai thác dầu và các nguồn tự nhiên khác cùng với sự lãnh đạo sáng suốt của H.H. Sheikh Zayed bin Sultan Al Nahyan, UAE đã có những tiến bộ đáng kể trên tất cả các mặt. Những bước tiến vượt bậc trong nền kinh tế đã biến vùng đất nghèo nàn, lạc hậu trước đây thành một đất nước có nền kinh tế phát triển, công nghiệp và cơ sở hạ tầng phồn thịnh.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\nXã hội UAE được xây dựng trên nền tảng cơ bản của các bộ tộc. Bắt đầu từ giữa thiên niên kỷ thứ nhất trước công nguyên khi những làn sóng di trú liên tiếp diễn ra đã đưa các bộ lạc Ả Rập tới khu vực này. Địa hình ở đây rất phong phú nhưng lại không ưu đãi cho con người, chính vì thế việc chế ngự được môi trường sống khắt khe qua nhiều thế kỷ đã tạo cho con người ở đây một tinh thần bền bỉ, tháo vát. Cấu trúc xã hội lâu đời đã thành truyền thống là mọi người đều có, bổn phận và nghĩa vụ giúp đỡ những thành viên trong gia đình, anh em họ hàng nói riêng, và bộ lạc nói chung. Trong bộ lạc mọi thành viên đều lấy lòng hiếu khách, đầy vị tha là niềm vinh dự và tự hào. Hồi giáo, quốc giáo, cũng khuyên răn sự gắn bó giúp đỡ lẫn nhau giữa người với người.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\nBộ lạc lớn nhất là Bani Yas, đã từng có cuộc sống du cư trên những vùng cát rộng lớn mà nay là các tiểu vương quốc Abu Dhabi và Dubai. Những bộ tộc khác như Awamir và Manasir cũng có cách thức sống như vậy qua nhiều thế hệ. Hành trình du cư của tất cả các bộ lạc và thị tộc luôn cùng với lạc đà và toàn bộ các gia đình thành viên trong bộ tộc. Hầu hết các gia đình của bộ tộc Bani Yas, trừ những nhóm đánh cá như nhóm Al Rumaitha, sẽ trở về \"nhà\" tại một trong các khu định cư tại ốc đảo vào một thời điểm nhất định nào đó trong năm.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\nTrong những thung lũng lòng chảo của các cồn cát lớn tại Liwa là những vườn chà là rộng lớn, có hiệu quả kinh tế cao nhưng do vị trí và địa hình nên việc lấy nước tưới cũng gặp một số khó khăn vì phải khoan nước từ dưới tầng cát hấp thụ. Những vườn chà là ở Al Ain và các ốc đảo sum suê và tươi tốt hơn nhờ được tưới nước đầy đủ từ hệ thống thủy lợi truyền thống (falai. Ar. Pl aflai) lấy từ tầng chứa nước trong các núi. Trong thung lũng, những dòng suối cạn là những hệ thống thủy lợi nhân tạo mà trước đây thường được sử dụng để tưới nước cho các vườn bậc thang.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\nHầu hết các bộ lạc giành một phần thời gian để câu cá, mò ngọc trai và xay lúa. Ban đầu việc mò ngọc trai chỉ là công việc bình thường của các bộ lạc nhưng ngày càng được chú trọng hơn và số đàn ông khỏe mạnh tham gia khai thác ngọc trai trong bốn tháng mùa hè ngày một tăng. Dần dần, sự bùng nổ khai thác ngọc trai đã dẫn đến hiện tượng đô thị hóa tăng lên do ngày càng có nhiều các bộ tộc ổn định cuộc sống tại các thành phố và các làng ven biển. Quá trình này liên tục tăng đến chóng mặt khi việc khám phá và xuất khẩu dầu lửa phát triển ở nơi này&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\nUAE là một nước giàu văn hóa và truyền thống. Cho đến nay, người dân của UEA vẫn còn giữ nhiều truyền thống và những nét văn hoá đẹp từ xa xưa. Với sự hiểu biết sâu sắc về giá trị các di sản văn hóa dân tộc cũng như việc bảo tồn chúng đã cho người dân ở đây một cầu nối mật thiết giữa quá khứ và hiện tại. Điều đó đã tạo ra một nền tảng vững chắc để đi lên và đối phó với những thách thức của tương lai.</span></font></span></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><font style=\"margin: 0px; padding: 0px; font-size: 13px; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 24px;\">Vị trí : Nằm ở phía Đông bán đảo A-rập , phía Bắc giáp Ca-ta, phía Đông giáp vịnh A-rập , phía Tây giáp A-rập Xê-út (có đường biên giới dài 457km), phía Nam giáp Oman (có đường biên giới dài 410km).&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Diện tích: 77,700 km2&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Dân số: 4,444,011 người và 3 triệu người lao động nước ngoài (2006)&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Dân tộc: 19% gốc UAE, 23% là người gốc A-rập khác và Iran, 50% là dân gốc Nam Á, 8% từ các nước châu Á khác. Lao động người nước ngoài làm thuê ở Emirates chiếm khoảng 90% lực lượng lao động.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Tiền tệ: Dirhams. 1 USD = 3,66 dirhams.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Ngôn ngữ: Tiếng A-rập, ngoài ra còn có tiếng Ba Tư, tiếng Anh, tiếng Hindi và Urdu.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Tôn giáo: Đạo Hồi là quốc đạo (96%), Thiên chúa giáo, Ấn Độ giáo và các giáo phái khác : 4%.&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Tổng thống: Sheikh Khalifa Bin Zayed Bin Sultan Al-Nahyan ( 11/2004)&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Phó Tổng thống kiêm Thủ tướng: Sheikh Mohammed bin Rashid al-Maktoum&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Ngoại trưởng : Sheikh Abdullah bin Zayed Al Nahyan&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Quốc khánh : 2/12/1971 ( Ngày Độc lập).&nbsp;<br style=\"margin: 0px; padding: 0px; font-size: 12px;\" />\r\n- Khí hậu: Khí hậu xa mạc nắng nóng, nhiệt độ mùa hè 45 - 52 độ, mùa mát từ 28-30 độ, vùng núi phía đông mát mẻ hơn.</span></font></span></span></p>\r\n','thi-truong-uae',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','355_1423216716_81554d4904c14446.png',[now], [now],[user_id],0,'','',[now]);set @news52 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories48,'Thị trường Qatar','Qatar là một trong 6 quốc gia vùng Vịnh. Dân số theo thống kê năm 200 khoảng 1,4 triệu người, đứng thứ 148 trên thế giới; Diện tích củaCa-ta là 11.437 km2, đứng thứ 164 trên thế giới','<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Thị trường </span></span>Qatar</p>\r\n\r\n<p style=\"text-align: justify;\">Qatar<span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"> là một trong 6 quốc gia vùng Vịnh. Dân số theo thống kê năm 200 khoảng 1,4 triệu người, đứng thứ 148 trên thế giới; Diện tích củaCa-ta là 11.437 km2, đứng thứ 164 trên thế giới. Năm 2007, GDP bình quân đầungười của Ca ta khoảng 70.000USD. Ca ta có nguồn dầu mỏ phong phú, là nguồn thu ngoại tệ chủ yếu của nền kinh tế. Kinh tế Ca ta đang phát triển với tốc độnhanh, việc đầu tư xây dựng các công trình cơ sở hạ tầng phục vụ việc phát triển các ngành kinh tế được đặc biệt ưu tiên.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;Tôn giáo chính thống là đạo Hồi.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;Đồng tiền:&nbsp;QatarRial. 1USD = 3.65 QAR</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;Khí hậu: Sa mạc, nắng nóng, nhiệt độ cao nhất vào mùa Hè cóthể lên tới trên 500C.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;Dân số và lao động ít trong khi nhu cầu xây dựng và phát triển kinh tế cao, Ca ta đã và đang cần rất nhiều lao động nước ngoài để phục vụ cho công cuộc phát triển đất nước. Lao động nước ngoài chiếm số đông trong lực lượng lao động của Ca ta, chủ yếu là những người đến từ các nước&nbsp;Nam&nbsp;á và các quốc gia A rập khác không có nguồn dầu mỏ phong phú.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;Tuyệt đại bộ phận lao động Việt&nbsp;Nam&nbsp;làm việc tại Ca – ta trong lĩnhvực xây dựng. Mức lương cơ bản vào khoảng 190USD/tháng đối với lao động không nghề và khoảng từ 250USD/tháng trở lên đối với lao động có nghề. Ngoài ra, người lao động đều có giờ làm thêm nên có mức thu nhập đối với lao động phổ thông vào khoảng 250USD/tháng và lao động có nghề khoảng 400USD/tháng.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Để phục vụ nhu cầu phát triển trong nước, Ca-ta cần tiếp nhận nhiều lao động từ nước ngoài, đặc biệt trong lĩnh vực xây dựng.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Hiện nay đã có 1 cán bộ của Cục Quản lý lao động ngoài nước sang làm việc tại Đại sứ quán Việt nam tại Ca ta phụ trách công tác quản lý laođộng.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Thị trường Các Tiểu vương quốc A-rập Thống nhất (UAE)</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">UAE là một trong 6 quốc gia vùng Vịnh. Dân số theo thống kê năm 2008 khoảng 5,4 triệu người. Diện tích của UAE là 83.600 km2, đứng thứ 116 trên thế giới. Năm 2007, GDP bình quân đầu người khoảng 50.000USD. UAE bao gồm7 tiểu vương trong đó thủ đô là Abu Dahbi, thành phố lớn nhất là Du bai. UAE có nguồn dầu mỏ và khí đốt phong phú. Đất nước này bắt đầu trở lên thịnh vượng từsau khi có nguồn đầu tư nước ngoài từ những năm 1970. Hiện nay UAE là một nướccông nghiệp hoá cao và là một trong những nước phát triển nhất trên thế giới.Bên cạnh nguồn dầu mỏ và khí đốt phong phú vẫn đóng vai trò quan trọng trong nền kinh tế, UAE đã đa dạng hóa nền kinh tế thông qua việc mở rộng ngành xây dựng,sản xuất chế tạo và dịch vụ.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Khí hậu: mang khí hậu đặc trưng của các nước Vùng Vịnh, sa mạc và nắng nóng.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Đồng tiền: UAE Dirham. 1USD = 3.67AED</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">UAE có khoảng 3,11 triệu lao động nước ngoài đến từ 202 quốc gia, trong đó lao động ấn Độ chiếm đa số (khoảng 1,5 triệu người). Các công tyXKLĐ Việt&nbsp;Nam&nbsp;bắt đầu ký hợp đồng cung ứng lao động cho thị trường UAE từ năm 1995.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Đã có 64 doanh nghiệp xuất khẩu lao động đưa lao động sang UAE làm việc. Hiện nay, số lượng&nbsp;&nbsp;lao động Việt&nbsp;Nam&nbsp;đang làm việc theo hợp đồng khoảng 9.500 người, ngành nghề chủ yếu của lao động ta là xây dựng, cơ khí xây dựng, đóng tàu, dịch vụ khách sạn, điện lạnh, thuỷ sản,nhựa,&nbsp;&nbsp;may mặc, salon,...</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Thu nhập của người lao động tại UAE vào khoảng 245 USD/tháng đối với lao động phổ thông và 300USD/tháng đối với lao động có nghề.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Hiện nay, đã có&nbsp; cán bộ của Bộ Lao động – Thưong binh và Xã hội phụ trách công tác quản lý lao động tại UAE được cử sang làm việc tại Đại sứ quán Việt&nbsp;Nam&nbsp;tại UAE.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;Thị trường A rập Xê út</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">A rập Xê út là một nước vùng Vịnh có diện tích 2,1 triệukm2, đứng thứ 14 trên thế giới và dân số khoảng 27,6 triệu người, đứng thứ 46trên thế giới. A rập Xê út là nước xuất khẩu dầu mỏ hàng đầu trên thế giới. Xuất khẩu dầu mỏ là nguồn thu chính của quốc gia. Thu nhập bình quân đầu người là20.700USD/tháng (năm 2007). Là một trong những nền kinh tế phát triển nhanh nhất thế giới.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Khí hậu sa mạc, nắng nóng.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Tôn giáo: Hồi giáo. Các tôn giáo khác bị cấm hoạt động công khai.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Đồng tiền: Saudi Riyal. 1USD = 3,75 SAR</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Với số lượng dân số ít và nhu cầu phát triển kinh tế cao nên A rập Xê út cần nhiều nhân công nước ngoài. Người nước ngoài làm việc tại A rậpXê út đến từ các nước&nbsp;Nam&nbsp;và Đông&nbsp;Nam&nbsp;Á, chủ yếu là lao động không nghề hoặc bán nghề. Nhu cầu tiếp nhận lao động giúp việc gia đình rất lớn.</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Ta bắt đầu đưa lao động sang A-rập Xê-út từ tháng 8 năm2003. Hiện tại có khoảng 6.800 lao động đang làm việc tại A-rập Xê-út, với ngành nghề chủ yếu là xây dựng và một số lao động nữ làm việc tại gia đình.</span></span></p>\r\n','thi-truong-qatar',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','992_1423216546_27354d48fa2887bb.gif',[now], [now],[user_id],0,'','',[now]);set @news51 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories48,'Thị trường Lybia','Tên nước Nước Jamahirya Libya Ả Rập Nhân dân Xã hội Chủ nghĩa Vĩ đại','<p>I. GIỚI THIỆU CHUNG</p>\r\n\r\n<p>1. Các thông tin cơ bản</p>\r\n\r\n<p>Tên nước Nước Jamahirya Libya Ả Rập Nhân dân Xã hội Chủ nghĩa Vĩ đại</p>\r\n\r\n<p>Thủ đô Tripoli (Tarabulus)</p>\r\n\r\n<p>Quốc khánh 1/9</p>\r\n\r\n<p>Diện tích 1,759,540 km2</p>\r\n\r\n<p>&nbsp;Dân số 6,733,620 người (tính đến tháng 7/2012)</p>\r\n\r\n<p>Khí hậu Phía Bắc khí hậu Địa trung hải, mùa đông lạnh, ẩm ướt, nhiệt độ khoảng từ</p>\r\n\r\n<p>150C đến 200C. Phía Nam khí hậu sa mạc, mùa hè khô nóng, nhiệt độ lên</p>\r\n\r\n<p>tới 40oC- 45oC. Lượng mưa hàng năm ở Tri-pô-li và các vùng lân cận</p>\r\n\r\n<p>khoảng 400 mm-500 mm, ở các tỉnh khác khoảng 200 mm</p>\r\n\r\n<p>Ngôn ngữ Ả-rập, ngôn ngữ thứ hai : Anh và Ý</p>\r\n\r\n<p>Tôn giáo Đạo Hồi</p>\r\n\r\n<p>Đơn vị tiền tệ Đồng Dinar Libya (LYD), 1USD = 1.257 LYD (năm 2012)</p>\r\n\r\n<p>Múi giờ GMT + 2.00</p>\r\n\r\n<p>Thể chế</p>\r\n\r\n<p>Điều hành đất nước</p>\r\n\r\n<p>Libya theo chế độ Chủ nghĩa xã hội</p>\r\n\r\n<p>Hội đồng Quốc gia chuyển tiếp ( 23/10/2011 )</p>\r\n\r\n<p>Thủ tướng Ali ZAYDAN (từ 4/10/2012)</p>\r\n\r\n<p>2. Lịch sử</p>\r\n\r\n<p>Ý thay thế người Thổ Nhĩ Kỳ Ottoman ở khu vực xung quanh Tripoli năm 1911 và đã không từ bỏ nắm</p>\r\n\r\n<p>giữ của họ cho đến năm 1943 khi đánh bại trong Chiến tranh Thế giới II. Libya sau đó được truyền cho</p>\r\n\r\n<p>chính quyền Liên hợp quốc và được độc lập vào năm 1951. Sau một cuộc đảo chính quân sự năm 1969,</p>\r\n\r\n<p>Đại tá Muammar Abu Minyar al-QADHAFI bắt đầu theo hệ thống chính trị của riêng mình, học thuyết</p>\r\n\r\n<p>về vũ trụ thứ ba.&nbsp; Ban Quan hệ Quốc tế Hồ sơ thị trường Libya</p>\r\n\r\n<p>&nbsp;Cập nhật ngày 29/02/2011 Trang 2</p>\r\n\r\n<p>Hệ thống này là sự kết hợp của chủ nghĩa xã hội và Hồi giáo bắt nguồn một phần từ thực tiễn bộ lạc và</p>\r\n\r\n<p>được thực hiện bởi những người Libya mình trong một hình thức độc đáo của \"dân chủ trực tiếp.\"</p>\r\n\r\n<p>QADHAFI đã luôn luôn thấy mình là một nhà lãnh đạo cách mạng và nhìn xa trông rộng. Ông đã sử</p>\r\n\r\n<p>dụng các quỹ dầu trong thập niên 1970 và thập niên 1980 để quảng bá hệ tư tưởng của ông bên ngoài</p>\r\n\r\n<p>Libya, hỗ trợ và khủng bố ở nước ngoài để đẩy nhanh kết thúc của chủ nghĩa Mác và chủ nghĩa tư bản.</p>\r\n\r\n<p>Ngoài ra, bắt đầu vào năm 1973, ông tham gia vào các hoạt động quân sự ở miền bắc Chad của Aozou</p>\r\n\r\n<p>Strip - để được truy cập vào khoáng sản và sử dụng như là một căn cứ ảnh hưởng trong nền chính trị</p>\r\n\r\n<p>Chad - nhưng buộc phải rút lui vào năm 1987. LHQ trừng phạt vào năm 1992 cô lập chính trị sau</p>\r\n\r\n<p>QADHAFI bắn rơi máy bay của Pan AM 103 trên Lockerbie, Scotland. Trong những năm 1990,</p>\r\n\r\n<p>QADHAFI bắt đầu xây dựng lại mối quan hệ của ông với châu Âu.</p>\r\n\r\n<p>Trừng phạt của LHQ đã bị đình chỉ trong tháng 4 năm 1999 và cuối cùng nâng lên trong tháng 9 năm</p>\r\n\r\n<p>2003 sau khi Libya chấp nhận trách nhiệm về vụ đánh bom Lockerbie. Trong tháng 12 năm 2003,</p>\r\n\r\n<p>Libya tuyên bố họ đã đồng ý để lộ ra và kết thúc chương trình của mình để phát triển vũ khí hủy diệt</p>\r\n\r\n<p>hàng loạt và từ bỏ khủng bố. QADHAFI có những bước tiến đáng kể về bình thường hóa quan hệ với</p>\r\n\r\n<p>các nước phương Tây từ đó.</p>\r\n\r\n<p>Ông đã gặp nhiều nhà lãnh đạo Tây Âu cũng như làm việc nhiều cấp và các đoàn thương mại, và thực</p>\r\n\r\n<p>hiện chuyến đi đầu tiên của ông tới Tây Âu trong 15 năm khi ông đi du lịch đến Brussels trong tháng 4</p>\r\n\r\n<p>năm 2004. Mỹ bãi bỏ tên Libya như là một nhà nước tài trợ khủng bố trong tháng 6 năm 2006. Trong</p>\r\n\r\n<p>tháng 1 năm 2008, Libya thừa nhận một ghế vào Hội đồng Bảo an nhiệm kỳ 2008-2009.</p>\r\n\r\n<p>Trong tháng 8 năm 2008, Hoa Kỳ và Libya đã ký một thỏa thuận song phương toàn diện giải quyết</p>\r\n\r\n<p>khiếu nại bồi thường người nộp đơn ở cả hai nước đã cáo buộc bị thương tích hay chết trong tay của</p>\r\n\r\n<p>nước khác, bao gồm vụ đánh bom Lockerbie, vụ đánh bom disco LaBelle, và UTA 772 vụ đánh bom.</p>\r\n\r\n<p>Trong Tháng 10- 2008, Chính phủ Mỹ nhận được 1,5 tỷ đô la theo thỏa thuận để phân phối cho người</p>\r\n\r\n<p>nộp đơn quốc gia Mỹ, và kết quả là bình thường hóa quan hệ song phương có hiệu quả của nó với</p>\r\n\r\n<p>Libya.</p>\r\n\r\n<p>Hai nước sau đó trao đổi Đại sứ cho lần đầu tiên kể từ năm 1973 trong tháng 1 năm 2009. QADHAFI</p>\r\n\r\n<p>trong tháng 2 năm 2009 đã qua là Chủ tịch của Liên minh Châu Phi trong thời hạn 2.009-10, trong</p>\r\n\r\n<p>tháng 9 năm 2009, Libya đã tiếp nhận chức tổng thống năm dài của Đại hội đồng LHQ.</p>\r\n\r\n<p>&nbsp;Cuối tháng 12/2010, các cuộc biểu tình chống chính phủ đã lan từ một số nước Bắc Phi sang Li-bi.</p>\r\n\r\n<p>Tháng 3/2011, Hội đồng dân tộc chuyển tiếp Li-bi (NTC) được thành lập tại thành phố Benghazi nhằm</p>\r\n\r\n<p>lật đổ chế độ của Nhà lãnh đạo Ca-đa-phi. Sau gần 8 tháng xung đột, được sự hậu thuẫn về mọi mặt,</p>\r\n\r\n<p>đặc biệt về quân sự của Mỹ và đồng minh, ngày 20/10/2011, NTC chiếm thành phố Sirte và sát hại ông</p>\r\n\r\n<p>Ca-đa-phi, nắm quyền kiểm soát toàn bộ đất nước.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>II. TÌNH HÌNH KINH TẾ</p>\r\n\r\n<p>1. Tổng quan</p>\r\n\r\n<p>Nền kinh tế Libya phụ thuộc chủ yếu khi doanh thu từ khu vực năng lượng quốc gia, trong đó đóng góp</p>\r\n\r\n<p>khoảng 95% doanh thu xuất khẩu, 80% của GDP, và 99% doanh thu của chính phủ. Điểm yếu của giá</p>\r\n\r\n<p>dầu khí thế giới trong năm 2009 giảm thuế thu nhập Libya chính phủ và hạn chế tăng trưởng kinh tế.</p>\r\n\r\n<p>doanh thu đáng kể từ ngành năng lượng kết hợp với một dân số nhỏ cung cấp cho Libya là một trong</p>\r\n\r\n<p>những cao nhất cho mỗi GDP đầu người ở Châu Phi, nhưng ít thu nhập này chảy xuống các đơn đặt</p>\r\n\r\n<p>hàng thấp của xã hội.</p>\r\n\r\n<p>Các quan chức Libya trong năm năm qua đã đạt được tiến bộ về cải cách kinh tế như một phần của một</p>\r\n\r\n<p>chiến dịch rộng lớn hơn để tái hòa nhập đất nước vào gấp quốc tế. Nỗ lực này nhặt hơi nước sau khi&nbsp; Ban Quan hệ Quốc tế Hồ sơ thị trường Libya</p>\r\n\r\n<p>&nbsp;Cập nhật ngày 29/02/2011 Trang 3</p>\r\n\r\n<p>lệnh trừng phạt của LHQ đã được dỡ bỏ trong tháng chín năm 2003 và là Libya công bố vào Tháng 12</p>\r\n\r\n<p>năm 2003 rằng họ sẽ từ bỏ chương trình để xây dựng các loại vũ khí hủy diệt hàng loạt.</p>\r\n\r\n<p>Quá trình nâng trừng phạt đơn phương của Mỹ đã bắt đầu vào mùa xuân năm 2004, tất cả các lệnh</p>\r\n\r\n<p>trừng phạt đã được gỡ bỏ bởi.Tháng Sáu năm 2006, giúp Libya thu hút đầu tư trực tiếp nước ngoài lớn</p>\r\n\r\n<p>hơn, đặc biệt là trong lĩnh vực năng lượng. Libya dầu và khí đạn cấp giấy phép tiếp tục thu hút sự quan</p>\r\n\r\n<p>tâm cao quốc tế; các Tổng công ty Dầu Quốc gia (NOC) đặt ra mục tiêu sản xuất gần gấp đôi dầu đến</p>\r\n\r\n<p>3triệubbl / ngày vào năm 2012. Trong tháng 11 năm 2009, các NOC tuyên bố rằng mục tiêu có thể</p>\r\n\r\n<p>trượt để đến cuối năm 2017.</p>\r\n\r\n<p>Libya đối mặt với một con đường dài phía trước trong tự do hóa nền kinh tế theo định hướng xã hội</p>\r\n\r\n<p>chủ nghĩa, nhưng những bước đầu tiên - bao gồm xin gia nhập WTO, giảm một số khoản trợ cấp, và kế</p>\r\n\r\n<p>hoạch thông báo cho tư nhân - được đặt nền tảng cho một sự chuyển tiếp sang nền kinh tế dựa trên thị</p>\r\n\r\n<p>trường nhiều hơn nữa.</p>\r\n\r\n<p>Các ngành sản xuất phi dầu mỏ và xây dựng, trong đó chiếm trên 20% GDP, đã mở rộng từ chế biến</p>\r\n\r\n<p>sản phẩm nông nghiệp chủ yếu bao gồm việc sản xuất các chất hóa dầu, sắt, thép, nhôm. Điều kiện khí</p>\r\n\r\n<p>hậu và nghèo đất bị giới hạn sản lượng nông nghiệp, và Libya nhập khẩu khoảng 75% trên thức ăn của</p>\r\n\r\n<p>nó.</p>\r\n\r\n<p>Nguồn nước nông nghiệp nhỏ của Libya vẫn duy trì dự án sông nhân tạo rộng lớn, nhưng nguồn tài</p>\r\n\r\n<p>nguyên quan trọng đang được đầu tư nghiên cứu khử muối để đáp ứng nhu cầu về nước đang tăng.</p>\r\n\r\n<p>Sau khi Kadafi lên nắm quyền cho tới năm 1987: Libya áp dụng mô hình kinh tế theo kiểu Liên Xô,</p>\r\n\r\n<p>Đông Âu; chủ trương xây dựng nền kinh tế tự cấp, tự túc, đóng cửa với bên ngoài, hạn chế nhập khẩu...</p>\r\n\r\n<p>Từ năm 1988, Libya đã tiến hành điều chỉnh chính sách kinh tế, cải cách theo hướng tự do và tư nhân</p>\r\n\r\n<p>hoá, giảm dần bao cấp và trợ giá, từng bước chuyển nền kinh tế sang cơ chế thị trường, mở cửa với bên</p>\r\n\r\n<p>ngoài, trong đó ưu tiên quan hệ với các nước láng giềng và khu vực, do đó sản xuất được phát triển,</p>\r\n\r\n<p>hoạt động kinh tế trở nên năng động và có hiệu quả hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Libya chủ trương quản lý và sử dụng nguồn thu nhập dầu lửa một cách chặt chẽ, hợp lý, ưu tiên phát</p>\r\n\r\n<p>tiển các ngành mũi nhọn như nông nghiệp, hải sản, năng lượng tiến tới tự túc về lương thực. Bạn đang</p>\r\n\r\n<p>thực hiện kế hoạch phát triển kinh tế xã hội phấn đấu trong 15-20 năm tới xây dựng một nền kinh tế</p>\r\n\r\n<p>phát triển đồng bộ, vững mạnh.</p>\r\n\r\n<p>Gần đây, Libya thực hiện cải cách kinh tế theo hướng tư nhân hoá và tự do hoá, đặt mục tiêu thu hút 50</p>\r\n\r\n<p>tỷ USD vào lĩnh vực dầu lửa để đưa sản lượng lên 3 triệu thùng/ngày vào năm 2012. Tuy nhiên, sản</p>\r\n\r\n<p>xuất dầu thô của Libya chỉ đạt khoảng 500.000 thùng / ngày năm 2012, thấp hơn nhiều so với mục tiêu</p>\r\n\r\n<p>trước đó do Tổng công ty dầu khí quốc gia (NOC) đề ra. Libya đối mặt với một con đường dài phía</p>\r\n\r\n<p>trước trong việc tự do hóa nền kinh tế của nó chủ yếu là xã hội chủ nghĩa. Các lĩnh vực dịch vụ và xây</p>\r\n\r\n<p>dựng, chiếm khoảng 20% GDP đã phát triển trong 5 năm qua và có thể phát triển thêm nữa nếu Tripoli</p>\r\n\r\n<p>ưu tiên chi tiêu vốn vào các dự án phát triển hơn là giảm bất ổn chính trị. Điều kiện khí hậu và đất đai</p>\r\n\r\n<p>nghèo nàn làm hạn chế sản lượng nông nghiệp, và Libya nhập khẩu khoảng 80% lượng thức ăn của</p>\r\n\r\n<p>mình.</p>\r\n\r\n<p>Năm 2006 Libya đã mời các giáo sư kinh tế của trường Harvard soạn thảo ‘Chiến lược kinh tề quốc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>dân’.&nbsp;</p>\r\n','thi-truong-lybia',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','842_1423216406_90754d48f16a922a.jpg',[now], [now],[user_id],0,'','',[now]);set @news50 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories48,'Thị trường Nhật bản','Nhật Bản là một đất nước gồm nhiều hòn đảo, nằm ở Đông á, có diện tích là 337.923 km2, đứng thứ 61 trên thế giới. Dân số Nhật Bản khoảng 130 triêu người, đứng thứ 10 trên thế giới','<p><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/mapnhatban.gif\" style=\"width: 659px; height: 739px;\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Nhật Bản là một đất nước gồm nhiều hòn đảo, nằm ở Đông á, có diện tích là 337.923 km2, đứng thứ 61 trên thế giới. Dân số Nhật Bản khoảng 130 triêu người, đứng thứ 10 trên thế giới. Nhật Bản có 4 mùa rõ rệt là Xuân, Hạ,Thu, Đông với khí hậu thay đổi, khí hậu cận nhiệt đới ở phía Nam đến khí hậu lạnhở phía Bắc. Về mùa Đông ở Nhật Bản, nhiệt độ ở nhiều vùng xuống dưới 0oC.&nbsp;</span></span><span style=\"font-family: Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Đồng tiền quốc gia của Nhật Bản là Yên; 1 USD tương đương 98,46 Yên Nhật.</span></span><u1:p></u1:p></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><o:p></o:p></span></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><u1:p>&nbsp;Chính sách của Nhật Bản không cho phép tiếp nhận lao động nước ngoài trình độ thấp hoặc không có tay nghề vào làm việc. Đối với lao động nước ngoài có tay nghề, lao động kỹ thuật cao, Chính phủ Nhật Bản khuyến khích và tạo điều kiện thuận lợi cho họ vào làm việc ở Nhật Bản.</u1:p></span></span></p>\r\n\r\n<p><span style=\"font-family: Tahoma;\"><u1:p></u1:p></span></p>\r\n\r\n<p><u1:p></u1:p></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><o:p></o:p></span></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><u1:p></u1:p>&nbsp;Tuy nhiên, lao động phổ thông (lao động không có tay nghề hoặc tay nghề thấp) nước ngoài có thể vào Nhật Bản làm việc theo chương trình tu nghiệp tại Nhật Bản với thời gian không quá 1 năm. Chương trình này được khởi điểm từ những năm cuối thập kỷ 60 của thế kỷ trước. Đến năm 1992, để bù đắp sự thiếu hụt nguồn nhân lực do dân số già hoá và tỷ lệ sinh thấp, Chính phủ Nhật Bản mở rộng thêm chương trình thực tập kỹ thuật với thời gian tối đa là 2 năm, nâng tổng số thời gian tu nghiệp và thực tập kỹ thuật lên tối đa là 3 năm. Lao động nước ngoài tu nghiệp, làm việc tại Nhật Bản chủ yếu trong lĩnh vực cơ khí, dệt may, chế biến lương thực, xây dựng, chế biến hải sản, nông nghiệp và ngư nghiệp.Mục tiêu chính của chương trình này là nhằm chuyển giao công nghệ, kỹ thuật tiên tiến, hỗ trợ phát triển nguồn nhân lực cho các nước đang phát triển.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family: Tahoma;\">&nbsp;<span style=\"font-size: 14px;\">Những năm gần đây, sự thiếu hụt nhân lực trầm trọng do sự già hoá dân số và tỷ lệ sinh thấp đã khiến số lượng tu nghiệp sinh nước ngoài vào Nhật Bản liên tục tăng lên. Bình quân, mỗi năm Nhật Bản tiếp nhận trên70.000 người vào làm việc với tư cách tu nghiệp sinh.</span></span></p>\r\n\r\n<p><u1:p></u1:p></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><o:p></o:p></span></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><u1:p>&nbsp;Tính đến hết tháng 10 năm 2008, đã có trên 35.000 tu nghiệp sinh Việt Nam được đưa sang tu nghiệp tại các xí nghiệp vừa và nhỏ của Nhật Bản,chủ yếu trong các nghề các ngành dệt, may công nghiệp, lắp ráp điện tử, gia công cơ khí, chế biến, xây dựng và thuỷ sản.</u1:p></span></span></p>\r\n\r\n<p><span style=\"font-family: Tahoma;\"><u1:p></u1:p></span></p>\r\n\r\n<p><u1:p></u1:p></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><o:p></o:p></span></span></p>\r\n\r\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\"><u1:p></u1:p>Tu nghiệp sinh Việt Nam có mặt trên hầu khắp các tỉnh của Nhật Bản, trừ Hokkaido, nhưng tập trung chủ yếu tại các vùng như Gifu, Kanto, Ai-chi và Hiroshima. Số lượng tu nghiệp sinh Việt Nam sang Nhật Bản có thể nói tăng dần theo theo từng năm. Hiện nay, ta có khoảng 20.000 lao động và tu nghiệp sinh đang làm việc và tu nghiệp tại Nhật Bản.</span></span></p>\r\n\r\n<p><span style=\"font-family: Tahoma;\">&nbsp;<span style=\"font-size: 14px;\">Hiện có khoảng 139 doanh nghiệp xuất khẩu lao động Việt Nam được phép đưa tu nghiệp sinh Việt Nam sang tu nghiệp và thực tập kỹ thuật tại Nhật Bản,chủ yếu trong các nghề may công nghiệp, lắp ráp điện tử, gia công cơ khí và xây dựng.</span></span></p>\r\n\r\n<p><span style=\"font-family: Tahoma;\"><span style=\"font-size: 14px; font-family: Arial, Verdana, sans-serif;\">Hiện nay, tại Nhật Bản có Ban Quản lý lao động và chuyên gia Việt&nbsp;</span><span style=\"font-size: 12px; font-family: Tahoma, sans-serif;\"><st1:country-region u2:st=\"on\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Nam</span></span></st1:country-region><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;trực thuộc Cơ quan Đại sứ quán Việt&nbsp;</span></span><st1:country-region u2:st=\"on\"><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">Nam</span></span></st1:country-region><span style=\"font-size: 14px;\"><span style=\"font-family: Tahoma;\">&nbsp;tại Nhật Bản..</span></span></span></span></p>\r\n','thi-truong-nhat-ban',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','898_1423216240_82654d48e701b852.gif',[now], [now],[user_id],0,'','',[now]);set @news49 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories47,'Hệ thống trường đào tạo nghề','Với hệ thống trường đào tạo nghề, Công ty Cổ phần Vitech luôn luôn sẵn sàng hỗ trợ trong công tác tuyển dụng và đào tạo nghề cho công nhân theo yêu cầu của nhà tuyển dụng','<p>Với hệ thống trường đào tạo nghề, Công ty Cổ phần Vitech&nbsp;luôn luôn sẵn sàng hỗ trợ trong công tác tuyển dụng và đào tạo nghề cho công nhân theo yêu cầu của nhà tuyển dụng. Công tác đào tạo dưới sự giám sát trực tiếp của Công ty hoặc các trường đào tạo đã được mở rộng với chức năng chính là đào tạo ngoại ngữ, kỹ năng cho người lao động trước khi xuất cảnh để đảm bảo chất lượng của người lao động đáp ứng với yêu cầu của chủ sử dụng từ Đài Loan, Malaysia, Libya, Nhật Bản, Hàn Quốc, UAE, Qatar, Algeria, Saudi Arabia… Trung tâm đã đào tạo, giáo dục định hướng cho hơn 10.000 lao động và lao động được đào tạo luôn được chủ sử dụng đánh giá cao về tay nghề, ngoại ngữ, phong cách làm việc, kỷ luật lao động, thể chất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống trường đào tạo :</strong></p>\r\n\r\n<p>1/ Trường dạy nghề Vitech tại Phú Minh – Sóc Sơn – Hà Nội. Được thiết kế sử dụng cho mục đích dạy nghề với diện tích 12.000m<sup>2</sup> bao gồm 12 phòng học, ký túc xá, nhà bếp, sân bóng chuyền vv… Có khả năng đào tạo cho 250 học viên mỗi tháng với nhiều loại nghề khác nhau như : Công nhân xây lắp công trình, công nhân cơ khí…</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>2/ Trung tâm dạy nghề hàn và cơ khí công nghệ cao tại Đông Anh - Sóc Sơn - Hà Nội</strong></p>\r\n\r\n<p>Trung tâm được xây dựng trên diện tích 10.000m<sup>2</sup> với hơn 100 máy hàn và nhiều giáo viên giàu kinh nghiệm, có thể đào tạo thợ hàn TIG và ARC có trình độ đáp ứng yêu cầu của chủ sử dụng lao động</p>\r\n\r\n<p>Bên cạnh đào tạo thợ hàn, trung tâm cũng đã đào tạo được hàng trăm thợ lắp ráp đường ống mỗi năm.</p>\r\n\r\n<p><strong>3/ Trường đào tạo tiếng Nhật</strong></p>\r\n\r\n<p>Với diện tích 2.000m<sup>2</sup>, trường hiện đào tạo ngôn ngữ, văn hóa, kỷ luật làm việc cho học viên. Cơ sở vật chất của trường bao gồm :</p>\r\n\r\n<p>Chỗ ở : 200 – 300 học viên</p>\r\n\r\n<p>Lớp học : 12 phòng</p>\r\n\r\n<p>Giáo viên :&nbsp;&nbsp;&nbsp;&nbsp; Giáo viên Việt Nam :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10&nbsp; người</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giáo viên Nhật :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2 người</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Quản lý đào tạo :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3 người</p>\r\n\r\n<p>Ngoài các trường đào tạo Vitech, chúng tôi còn kết hợp với một số trường dạy nghề của Vitech&nbsp;và Bộ Lao động với mục tiêu tìm nguồn cung ứng lao động đủ tiêu chuẩn cho các đối tác nước ngoài.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Một số trường đào tạo Công ty đang liên kết :</strong></p>\r\n\r\n<p><em><strong>1/ Trường Cao đẳng Kỹ thuật xây dựng và dạy nghề tại phường Đồng Xuân – thị xã Phúc Yên – tỉnh Vĩnh Phúc.</strong></em></p>\r\n\r\n<p>Chức năng và năng lực đào tạo của trường :</p>\r\n\r\n<p>Đào tạo nghề cơ bản và cao cấp trong các lĩnh vực sau :</p>\r\n\r\n<p>Đào tạo nghề cho xuất khẩu lao động và các lĩnh vực khác liên quan.</p>\r\n\r\n<p>Kết hợp đào tạo với các trường khác.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>2/ Trường Cao đẳng xây dựng tại Đường Lê Lợi – Thị xã Bỉm Sơn – tỉnh Thanh Hóa</strong></em></p>\r\n\r\n<p>Trường hiện đào tạo nghề cơ bản và cao cấp của các ngành nghề sau :</p>\r\n\r\n<p>Cao đẳng chuyên nghiệp</p>\r\n\r\n<p>Giáo dục định hướng cho xuất khẩu lao động</p>\r\n\r\n<p>Tổ chức đào tạo và nâng cao kỹ thuật, kỹ năng để đáp ứng cho yêu cầu về nguồn nhân lực của Tổng công ty Vitech và các đơn vị khác, sản xuất và dịch vụ liên quan đến xuất khẩu lao động.</p>\r\n\r\n<p>Xây dựng kế hoạch đào tạo dựa theo khung hướng dẫn của Bộ Xây dựng, Bộ Giáo dục &amp; Đào tạo và Tổng cục Dạy nghề.</p>\r\n\r\n<p>Tổ chức, nghiên cứu, phiên dịch, biên tập chương trình đào tạo phù hợp với chỉ đạo của Bộ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Liên kết với các trường Cao đẳng, Đại học, Daonh nghiệp, các ngành, các địa phương để triển khai đào tạo cho phù hợp với năng lực của các trường Cao đẳng mục tiêu đào tạo ra nguồn nhân lực có kiến thức và kinh nghiệm.</p>\r\n','he-thong-truong-dao-tao-nghe',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','687_1423215978_23854d48d6a5e49d.jpg',[now], [now],[user_id],0,'','',[now]);set @news48 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories47,'Trung tâm ĐT công nhân xây dựng','Trung tâm ĐT công nhân xây dựng','<p><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_1.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_2.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_4.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_cogioi_xecau.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_cogioi_xenang.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_dien1.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_dobetong2.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_lamsat2.jpg\" style=\"width: 575px; height: 431px;\" /><img alt=\"\" src=\"http://w3ni134.web3nhat.net/data/media/85/images/2015/daotaoxaydung/xaydung_mayxuc.jpg\" style=\"width: 575px; height: 431px;\" /></p>\r\n','trung-tam-dt-cong-nhan-xay-dung',1,'','',[site_id],[user_id],'/media/images/85/news/ava/','406_1423215682_83954d48c42231bc.jpg',[now], [now],[user_id],0,'','',[now]);set @news47 = LAST_INSERT_ID();
