INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product104,'Nước hoa Dior J’adore là sản phẩm tiếp nối sự thành công tuyệt đối của sản phẩm nước hoa J\'adore dành riêng cho những người yêu mến Dior J’adore cũng như nhãn hiệu DIOR. Rực rỡ, gợi cảm, tinh tế, nước hoa Dior J’adore tôn vinh vẻ đẹp nữ tính tuyệt đối (tên gọi sản phẩm là cách chơi chữ của từ ‘absolute’ có nghĩa là tuyệt đối) và sức mạnh của những xúc cảm bột phát. Sản phẩm được sản xuất bởi chuyên gia Francois Demachy. Nước hoa Dior J’adore mang mùi hương của một bó hoa lan thơm ngát, cảm giác êm ái như nhung của mận xứ Đamat và vị ngọt đậm của gỗ Amarante, cùng hương thơm xạ hương phảng phất.','<h2 class=\"product-description__title\" style=\"margin: 0px 0px 17px; padding: 0px; font-weight: bold; font-size: 16px; line-height: 2.4rem; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif;\">Chi tiết sản phẩm</h2>\r\n\r\n<p><span style=\"color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\">&nbsp;</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\">Nước hoa&nbsp;<b style=\"margin: 0px; padding: 0px;\">Dior J’adore</b>&nbsp;là sản phẩm tiếp nối sự thành công tuyệt đối của sản phẩm nước hoa J\'adore dành riêng cho những người yêu mến&nbsp;<b style=\"margin: 0px; padding: 0px;\">Dior J’adore</b>&nbsp;cũng như nhãn hiệu DIOR. Rực rỡ, gợi cảm, tinh tế, nước hoa&nbsp;<b style=\"margin: 0px; padding: 0px;\">Dior J’adore</b>&nbsp;tôn vinh vẻ đẹp nữ tính tuyệt đối (tên gọi sản phẩm là cách chơi chữ của từ ‘absolute’ có nghĩa là tuyệt đối) và sức mạnh của những xúc cảm bột phát. Sản phẩm được sản xuất bởi chuyên gia Francois Demachy. Nước hoa&nbsp;<b style=\"margin: 0px; padding: 0px;\">Dior J’adore</