INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product105,'CHRISTIAN BERTON CB Him với hương cây cỏ và trái cây mang phong cách sang trọng, lịch lãm. Sản phẩm mang phong cách ấm áp, dễ chịu phù hợp sử dụng trong mọi hoàn cảnh. Chai nước hoa tinh tế này sẽ đem lại cho bạn nét nam tính đầy cuốn hút........','<h2 class=\"product-description__title\" style=\"margin: 0px 0px 17px; padding: 0px; font-weight: bold; font-size: 16px; line-height: 2.4rem; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif;\">Chi tiết sản phẩm</h2>\r\n\r\n<p><b style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\">CHRISTIAN BERTON CB Him&nbsp;</b><span style=\"color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\">với hương cây cỏ và trái cây mang phong cách sang trọng, lịch lãm. Sản phẩm mang phong cách ấm áp, dễ chịu phù hợp sử dụng trong mọi hoàn cảnh. Chai nước hoa tinh tế này sẽ đem lại cho bạn nét nam tính đầy cuốn hút.</span></p>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">ĐẶC ĐIỂM NỔI BẬT</b></div>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Nhóm hương</b><br style=\"margin: 0px; padding: 0px;\" />\r\nSản phẩm thuộc nhóm hương hoa cỏ.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Phong cách</b><br style=\"margin: 0px; padding: 0px;\" />\r\n<b style=\"margin: 0px; padding: 0px;\">CHRISTIAN BERTON CB Him</b>&nbsp;mang phong cách ấm áp, sang trọng và quý phái.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Các lớp hương</b></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Lớp hương đầu</b><br style=\"margin: 0px; padding: 0px;\" />\r\nHương thơm từ trái cây nhiệt đới.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Lớp hương giữa</b><br style=\"margin: 0px; padding: 0px;\" />\r\nMùi cây cỏ và trái cây, hoa violet, hoa loa kèn.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Lớp hương cuối</b><br style=\"margin: 0px; padding: 0px;\" />\r\nGỗ xạ hương, hổ phách.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: 22.7999992370605px;\"><b style=\"margin: 0px; padding: 0px;\">Cách dùng</b><br style=\"margin: 0px; padding: 0px;\" />\r\nXịt sản phẩm lên những vùng da có nhiều mạch máu như gáy, sau tai, cổ tay. Có thể xịt ra khoảng không trước mặt và bước tới trước để mùi hương tỏa đều và lưu lại lâu hơn. Chú ý không sử dụng quá nhiều.</p>\r\n',CONCAT('[{"id":"',@product_attribute33,'","name":"Xuất xứ","code":"xuat-xu","index_key":"',@product_attribute_option60,'","value":"',@product_attribute_option60,'"}]'),'','','','',[site_id]);
