INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product192,' Không gian sống của bạn sẽ thay đổi hoàn toàn khi có sự hiện diện của chiếc tủ rượu này, với thiết kế nội thất theo phong cách hiện đại của không gian sống hiện nay vai trò của tủ rượu lại càng có vai trò quan trọng hơn nữa. Một chiếc tủ rượu đẹp, có kích thước gọn nhẹ và tinh tế sẽ làm tăng lên sự tiện nghi và sang trọng cho căn hộ của bạn.\r\n','','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product193,' Ghế sofa nỉ mã NI03 với kiểu dáng chữ L sang trọng, tiết kiệm không gian diện tích, các chất liệu từ khung xương, đến vải bọc , đệm mút đều được nhập khẩu chính hãng, tạo nên một sản phẩm không chỉ đẹp, sang trọng mà còn bền nữa.','','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product194,'Bộ sofa gỗ với phần khung gỗ tự nhiên được sơn đen ấn tượng, kết hợp với da màu be tạo nên một tổng thể hết sức hài hòa và không kém phần sống động. Bộ ghế sofa gỗ này sẽ là nơi lí tưởng để gia chủ iếp đón các vị khách quý đến chơi nhà.','','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product195,'Nếu bạn là người yêu thích sự đơn giản, nhẹ nhàng mà lại sang trọng quý phái thì Kệ tivi gỗ phòng khách mã KTV84 là lựa chọn hoàn hảo nhất. Sản phẩm được sử dụng chất liệu gỗ HDF phủ veneer có ưu điểm là cách âm, có khả năng chịu nhiệt cao đặc biệt là có khả năng chống ẩm mốc tốt hơn gỗ MDF nên khách hàng hoàn toàn yên tâm khi lựa chọn.','','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product196,'Chiếc bàn trà này được ví như một\" cô nàng yểu điệu\" bởi nó có thiết kế vô cùng tinh tế. Với những đường nét trang trí mềm mại và có tính thẩm mỹ cao. Chất liệu gỗ được phủ Venner và phủ PU bóng mờ tạo ra vẻ đẹp ấn tượng cho bàn trà. Những vân gỗ sẽ là những chi tiết làm nổi bật và tạo điểm nhấn cho chiếc bàn trà đẹp này. Đây là món đồ nội thất đẹp và phù hợp với nhiều không gian và phong cách thiết kế nội thất khác nhau.\r\n','','','','','','',[site_id]);
