INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product444,'Tính năng sản phẩm\r\nMàn hình: Full HD, 5.5 inches\r\nHĐH: Android 5.0\r\nCPU: Octa-core 1.7 GHz\r\nCamera: 13 MP\r\nPin: Li-Ion 2600 mAh','<div class=\"post_title\" style=\"border: 0px; font-size: 12px; margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; line-height: 24px;\">\r\n<h2 style=\"border: 0px; margin: 0px; padding: 0px; font-size: 20px !important;\"><a href=\"http://www.nguyenkim.com/dien-thoai-di-dong-sony/\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; line-height: 40px; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !important;\">ĐIỆN THOẠI SONY</a>&nbsp;XPERIA C4 DUAL MÀU TRẮNG</h2>\r\n</div>\r\n\r\n<div class=\"post_content\" style=\"border: 0px; font-size: 12px; margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; line-height: 24px;\">\r\n<p style=\"border: 0px; margin: 0px; padding: 6px 0px;\">Nếu tiêu chí khi chọn mua một chiếc&nbsp;<a href=\"http://www.nguyenkim.com/dien-thoai-di-dong/\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !important;\">điện thoại thông minh</a>&nbsp;của bạn là phải chụp hình đẹp đặc biệt là khi chụp ảnh \"selfie\", cấu hình mạnh mẽ, màn hình Full HD, kiểu dáng tinh tế thì&nbsp;<strong style=\"border: 0px; margin: 0px; padding: 0px;\">ĐTDĐ</strong><strong style=\"border: 0px; margin: 0px; padding: 0px;\">&nbsp;Sony Xperia C4 Dual</strong>&nbsp;sẽ là một lựa chọn rất hoàn hảo cho bạn.&nbsp;<a href=\"http://www.nguyenkim.com/sony-xperia-c4-dual-e5333.html\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !