INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product445,'Tính năng sản phẩm\r\nMàn hình: Full HD, 5.0 inches\r\nHĐH: Android 5.0\r\nCPU: Quad-Core 2.5 GHz\r\nCamera: 13 MP\r\nPin: Li-Po 2600 mAh','<div class=\"post_title\" style=\"border: 0px; font-size: 12px; margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; line-height: 24px;\">\r\n<h2 style=\"border: 0px; margin: 0px; padding: 0px; font-size: 20px !important;\"><a href=\"http://www.nguyenkim.com/htc-one-e8-dual-sim.html\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; line-height: 40px; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !important;\">HTC ONE E8 DUAL SIM</a>&nbsp;- Chiếc smartphone hoàn hảo trong tầm giá</h2>\r\n</div>\r\n\r\n<div class=\"post_content\" style=\"border: 0px; font-size: 12px; margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; line-height: 24px;\">\r\n<p style=\"border: 0px; margin: 0px; padding: 6px 0px;\">Thật tuyệt vời khi bạn được sở hữu một phiên bản&nbsp;<strong style=\"border: 0px; margin: 0px; padding: 0px;\">HTC One E8 Dual sim</strong>&nbsp;với cấu hình tương đương chiếc&nbsp;<em style=\"border: 0px; margin: 0px; padding: 0px;\">HTC One E8</em>&nbsp;cao cấp ra mắt hồi năm ngoái với giá chỉ bằng một nửa.&nbsp;<strong style=\"border: 0px; margin: 0px; padding: 0px;\">HTC One E8 Dual sim</strong>&nbsp;là phiên bản được cải biên với cấu hình vẫn là chip&nbsp;<em style=\"border: 0px; margin: 0px; padding: 0px;\">Qualcomm Snapdragon 801, 4 nhân, 2.5 Ghz, Ram 2GB, camera 13 MP</em>, camera trước góc rộng thoải mái selfie 5 MP. Điểm khác biệt giữa phiên bản mới và cũ chính là ở chiếc vỏ nhựa và 2 sim được trang bị ở bản&nbsp;<strong style=\"border: 0px; margin: 0px; padding: 0px;\">One E8 Dual sim</strong>.<br />\r\n<strong style=\"border: 0px; margin: 0px; padding: 0px;\">HTC One E8 2 sim</strong>&nbsp;được xem là đối thủ nặng ký nhất so với các sản phẩm khác trong cùng phân khúc giá cũng đang được nhắc nhiều đến như&nbsp;<a href=\"http://www.nguyenkim.com/sony-xperia-c4-dual-e5333.html\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !important;\">Sony Xperia C4 Dual&nbsp;</a>hay&nbsp;<a href=\"http://www.nguyenkim.com/asus-zenfone-2-ze551ml-4gb-32gb-vn.html\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !important;\">Asus Zenfone 2</a>. Hãy&nbsp;tìm hiểu thiết kế cũng như hiệu suất hoạt động của&nbsp;<strong style=\"border: 0px; margin: 0px; padding: 0px;\">One E8 Dual sim</strong>&nbsp;để xem tại sao chiếc&nbsp;<a href=\"http://www.nguyenkim.com/dien-thoai-di-dong-htc/\" style=\"border: 0px; margin: 0px; padding: 0px; cursor: pointer; text-decoration: none; font-size: 1em !important; color: rgb(3, 143, 215) !important; outline: none !important;\">smartphone của HTC</a>&nbsp;này lại được đánh giá cao hơn so với những đối thủ trong cùng tầm giá.</p>\r\n</div>\r\n','','','','','',[site_id]);
