INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'CHÀO MỪNG BẠN ĐẾN VỚI \"QUÁN VỊT G\"','Ra đời gắn liền cùng niềm đam mê học hỏi của ông chủ. \"Quán Vịt G\" đã và đang dần lớn mạnh để khẳng định vị \r\nthế của mình trong làng ẩm thực Hà Thành. Ham mê món lạ, chịu khó tìm tòi học hỏi, ông chủ quán đã không quản ngại đường xá cất công \r\nsang nước bạn Trung Quốc tìm thầy học món vịt quay Bắc Kinh, nổi tiếng trên toàn thế giới.','<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni131.web3nhat.net/data/media/81/images/2015/Trust-681x321.jpg\" style=\"width: 681px; height: 321px;\" /></p>\r\n\r\n<p>Trung Tâm Phân Phối Sản Phẩm Sơn Hà Trên Toàn Quốc là đơn vị cung cấp sản phẩm Sơn Hà lớn nhất tại Việt Nam . Hiện nay chúng tôi là nhà phân phối của Tổng công ty Cổ phần Quốc tế Sơn Hà có trụ sở tại Hà Nội hơn 10 năm qua. Chúng tôi đã thấu hiểu quan điểm, chính sách chiến lược của Tổng công ty là làm thế nào để đưa được sản phẩm tới tay người tiêu dùng trên khắp mọi miền Tổ quốc và bạn bè Quốc tế.<br />\r\n<br />\r\nVới mạng lưới phân phối rộng khắp tại 63 tỉnh thành phố của Việt Nam .Sản phẩm chúng tôi cung cấp luôn đảm bảo chất lượng tiêu chuẩn của của nhà sảm xuất , dịch vụ chu đáo trước và sau bán hàng , tư vấn miễn phí tại nhà là nguyên nhân khiến đông đảo khách hàng tìm tới chúng tôi và qua đó ngày càng khẳng định được vị trí số 1 của mình tại thị trường Việt Nam trong lĩnh vực kinh doanh sản phẩm Sơn Hà .<br />\r\nHưởng ứng chương trình khuyến khích \"Người Việt Nam dùng hàng Việt Nam\", sản phẩm của chúng tôi không bị giới hạn bởi&nbsp; khoảng cách. Để đạt được những thành quả trên, đội ngũ cán bộ, công nhân viên chuyên nghiệp cùng với các chi nhánh, các nhà phân phối, các đại lý chính thức của chúng tôi luôn nỗ lực hoàn thiện mình để đưa đến cho khách hàng cung cách, thái độ phục vụ tư vấn tận tình.<br />\r\n<br />\r\nHãy gọi đến TRUNG TÂM của chúng tôi, quý khách sẽ luôn nhận được sự tư vấn miễn phí, tận tình và sử dụng những sản phẩm chất lượng tiêu chuẩn Quốc tế và dịch vụ Chăm sóc khách hàng hoàn hảo.Bạn “cần”&nbsp; chúng tôi “có “&nbsp; rất hân hạnh được phục vụ quý khách hàng.<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px;\">\" Sự hài lòng của quý khách là tiêu chí của chúng tôi \".<br />\r\nTrân trọng được hợp tác !</span></p>\r\n','','','','','',[now],[now]);
