INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories157,'Nâng mũi không phẫu thuật','Nâng mũi không phẫu thuật là cách nâng mũi bằng phương pháp tiêm chất làm đầy Restylane -  Giải pháp hoàn hảo cho phái đẹp. Cùng với sự phát triển vượt bậc của công nghệ thẩm mỹ, các phương pháp nâng mũi ngày càng được cải thiện và mang lại hiệu quả tối ưu nhất.  Nâng mũi không cần phẫu thuật trong ngành thẩm mỹ ngày nay rất phổ biến và được phát triển rộng rãi. Đây cũng là giải pháp tối ưu và hữu hiệu nhất, giúp cho những ai muốn sở hữu một chiếc mũi đẹp tự nhiên hài hoà và phù hợp với khuôn mặ','<p><span style=\"color: rgb(34, 34, 34); font-family: Arial, sans-serif; line-height: 19.5px; text-align: justify;\">Nâng mũi không phẫu thuật sử dụng Restylane, tiến hành nâng mũi bằng phương pháp tiêm chất Restylane vào những vị trí đã đánh dấu. Bởi vậy, khi tiến hành nâng mũi theo phương pháp này sẽ giúp chị em lấy lại chiếc mũi đẹp tự nhiên mà không cần bóc tách, không gây chảy máu, đau hay để lại sẹo.</span></p>\r\n','nang-mui-khong-phau-thuat',1,'','',[site_id],[user_id],'/media/images/127/news/ava/','60_1437649325_31655b0c9ad9ec80.jpg',[now], [now],[user_id],0,'','',[now]);set @news320 = LAST_INSERT_ID();
