INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories157,'Nâng mũi hàn quốc','Nâng mũi Hàn Quốc đang được sự quan tâm của rất nhiều chị em phụ nữ. Khi hình ảnh các diễn viên xinh đẹp Hàn Quốc với chiếc mũi thon, gọn đã phổ biến tại Việt Nam, nhu cầu làm đẹp của chị em cũng từ đó mà tăng cao. Nâng mũi công nghệ Hàn Quốc là cách thẩm mỹ làm cho mũi của bạn được đẹp chuẩn theo công nghệ Hàn Quốc giúp cho khuôn mặt trở nên thanh thoát hơn. Hiện nay, phẫu thuật thẩm mỹ theo công nghệ Hàn Quốc đang được rất nhiều chị em hưởng ứng vì đây là cách làm đẹp không đau, không để lại s','<p><span style=\"color: rgb(34, 34, 34); font-family: Arial, sans-serif; line-height: 19.5px; text-align: justify;\">Công nghệ nâng mũi tại Xuân Hương sử dụng vật liệu đang được ưa chuộng nhất hiện nay là silicon sinh học. Với phương pháp nâng mũi hiện đại cùng đội ngũ bác sĩ tay nghề cao, nâng mũi Hàn Quốc tại&nbsp; Xuân Hương sẽ mang tới kết quả tốt hơn, tránh hiện tượng bóng-đỏ sau khi phẫu thuật, giữ được dáng mũi đẹp tự nhiên và bền lâu hơn.</span></p>\r\n','nang-mui-han-quoc',1,'','',[site_id],[user_id],'/media/images/127/news/ava/','689_1437649149_93755b0c8fd69391.jpg',[now], [now],[user_id],1,'','',[now]);set @news318 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories166,'Phun xăm mí mắt','Phun xăm mí mắt là một hình thức trang điểm vĩnh viễn mí mắt vượt trội hơn hẳn so với vẽ viền mi mắt bằng chỉ kẻ mắt. Kỹ thuật này yêu cầu khéo léo, tỉ mỉ cho đường viền mí mắt mảnh, sắc sảo, đẹp tự nhiên mà hoàn toàn không gây hại cho vùng nhạy cảm của đôi mắt. Bạn đang tìm hiểu về dịch vụ phun xăm mí mắt uy tín ? Với Xuân Hương, dịch vụ phun xăm mí mắt luôn là một trong những dịch vụ được nhiều khách hàng yêu thích và lựa chọn. Hãy khám phá ngay dịch vụ phun xăm mĩ mắt của Xuân Hương ngay tại ','<p>Phun xăm mí mắt là một hình thức trang điểm vĩnh viễn mí mắt vượt trội hơn hẳn so với vẽ viền mi mắt bằng chỉ kẻ mắt. Kỹ thuật này yêu cầu khéo léo, tỉ mỉ cho đường viền mí mắt mảnh, sắc sảo, đẹp tự nhiên mà hoàn toàn không gây hại cho vùng nhạy cảm của đôi mắt. Bạn đang tìm hiểu về dịch vụ phun xăm mí mắt uy tín ? Với Xuân Hương, dịch vụ phun xăm mí mắt luôn là một trong những dịch vụ được nhiều khách hàng yêu thích và lựa chọn. Hãy khám phá ngay dịch vụ phun xăm mĩ mắt của Xuân Hương ngay tại&nbsp;</p>\r\n','phun-xam-mi-mat',1,'','',[site_id],[user_id],'/media/images/127/news/ava/','708_1437649030_26155b0c8866195f.jpg',[now], [now],[user_id],1,'','',[now]);set @news317 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories166,'Phun xăm môi','Bạn đang tìm hiểu về dịch vụ phun xăm môi ? Xăm môi hồng uy tín tại Hà Nội ? Dịch vụ Phun xăm môi nghệ thuật tại Thẩm Mỹ Viện Xuân Hương sẽ cho bạn đôi môi căng mọng, quyến rũ với màu sắc tự nhiên, phù hợp với từng khuôn mặt. Nói đến phun môi đẹp tự nhiên và lên màu một cách nhanh chóng và hồng tự nhiên nhất, thì Thẩm mỹ viện Xuân Hương luôn khiến mọi khách hàng hài lòng. Hãy khám phá dịch vụ phun xăm môi thẩm mỹ của Xuân Hương ngay tại đây !','<p><span style=\"color: rgb(34, 34, 34); font-family: Arial, sans-serif; line-height: 19.5px; text-align: justify;\">Xăm môi nghệ thuật là kỹ thuật khá đơn giản và an toàn tuyệt đối, sử dụng mực xăm cao cấp từ Mỹ và chiết xuất từ thiên nhiên, ngoài việc không gây kích ứng, tác dụng phụ, còn mang đến cho bạn màu môi bền và đẹp tự nhiên.</span></p>\r\n','phun-xam-moi',1,'','',[site_id],[user_id],'/media/images/127/news/ava/','377_1437648866_24355b0c7e27da37.jpg',[now], [now],[user_id],1,'','',[now]);set @news316 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories166,'Phun thêu lông mày','Phun thêu chân mày là giải pháp hoàn hảo giúp chị em sở hữu đôi lông mày thanh tú, hài hòa khuôn mặt. Thẩm mỹ viện Xuân Hương tự hào là địa chỉ phun thêu chân mày hàng đầu tại Việt Nam. Bạn muốn phun thêu chân lông mày đẹp một cách hoàn hảo nhất thì Thẩm mỹ viện Xuân Hương là một điah chỉ rất đáng tin cậy cho sự lựa chọn của bạn.','<p><span style=\"color: rgb(34, 34, 34); font-family: Arial, sans-serif; line-height: 19.5px; text-align: justify;\">Phun thêu chân mày được thực hiện bằng cách chuyên viên sẽ thêu từng sợi lông mày theo chiều xuôi của dáng lông mày nên trông tự nhiên, phù hợp với màu da, màu tóc và gương mặt.</span></p>\r\n','phun-theu-long-may',1,'','',[site_id],[user_id],'/media/images/127/news/ava/','75_1437648795_78255b0c79b9a667.jpg',[now], [now],[user_id],1,'','',[now]);set @news315 = LAST_INSERT_ID();
