INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product560,'Dành cho sản phụ sau sinh, Giúp các vết thương mau lành hơn, Khử mùi và chống viêm sưng ... ','<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">Thảo dược vệ sinh phụ nữ Tanamera</span>&nbsp;là sản phẩm không thể thiếu để chăm sóc cho sản phụ sau sinh.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">Công dụng</span></p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">- Giúp các vết thương mau lành hơn, giúp sát khuẩn và vệ sinh vùng âm đạo trong thời kỳ chảy máu sau khi sinh.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">- Dung dịch có chứa nhiều chất dầu cần thíết giúp giải tỏa những khó chịu ở vùng âm đạo do quá trình sinh nở gây ra.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">- Chống viêm sưng, thúc đẩy quá trình phục hồi vùng âm đạo.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">- Giúp các cơ và mô vùng âm đạo co thắt chặt lại và khử mùi.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">- Sử dụng từ ngày đầu tiên sau khi sinh.</p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product561,'Chất liệu cotton mềm mại, Rất thoáng mát và có độ thấm hút cao., Có thể giặt lại được. ..','<div class=\"detail-description\" id=\"pr-desc\" style=\"margin: 20px 0px 0px; padding: 0px; font-family: open_sans, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; height: 300px; overflow: hidden; box-sizing: content-box !important; -webkit-user-select: text !important;\">\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">Miếng lót thấm sữa giặt lại được BF-632</span>&nbsp;là dòng sản phấm cho các phụ nữ đang trong giai đoạn cho con bú, giúp bạn giữ được vệ sinh, và không bị sữa làm ướt áo. Miếng lót được làm từ chất liệu an toàn, cùng với thiết kế siêu thấm và kháng khuẩn vượt trội sẽ bảo vệ vùng ngực luôn được khô thoáng. Sử dụng&nbsp;<u style=\"margin: 0px; padding: 0px; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">miếng lót thấm sữa giặt lại được BF-632</span></u>&nbsp;để bạn luôn là người phụ nữ năng động tự tin dù trong giai đoạn nuôi con.</p>\r\n\r\n<h3 style=\"margin: 10px 0px; padding: 0px; font-family: Helvetica, sans-serif; font-weight: bold; color: inherit; font-size: 16px; font-stretch: normal; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Điểm nổi bật của miếng lót thấm sữa giặt lại được BF-632</h3>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Miếng thấm được làm bằng chất liệu mềm mại và có độ thấm hút cao, giúp giữ cho ngực luôn khô thoáng và sạch sẽ thoải mái.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Miếng lót có thể sử dụng được nhiều lần sau khi giặt mà không làm giảm hiệu quả thấm sữa.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Được làm từ chất liệu mềm, thoải mái khi sử dụng.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Rất thoáng mát và có độ thấm hút cao.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Rất kinh tế và góp phần bảo vệ môi trường.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Một hộp có 6 miếng.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Nhãn hiệu được đăng ký tại Anh, Mỹ và toàn cầu; ủy nhiệm sản xuất tại Đài Loan.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: center; box-sizing: content-box !important; -webkit-user-select: text !important;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: center; box-sizing: content-box !important; -webkit-user-select: text !important;\">&nbsp;</p>\r\n</div>\r\n','','','','','',[site_id]);
