INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product563,'Không làm khô da bé, Không làm cay mắt bé, Không gây kích ứng cho da và mắt bé','<div class=\"detail-description\" id=\"pr-desc\" style=\"margin: 20px 0px 0px; padding: 0px; font-family: open_sans, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; height: 300px; overflow: hidden; box-sizing: content-box !important; -webkit-user-select: text !important;\">\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">Tắm gội Johnson\'s baby 500ml</span>&nbsp;nhẹ dịu như làn nước trong lành, giúp tắm gội sạch sẽ cả tóc và toàn thân bé thật tiện lợi mà không làm khô da bé. Với công thức độc đáo không cay mắt, sản phẩm nhẹ dịu như nước tinh khiết nên hầu như không gây kích ứng cho da và mắt bé.</p>\r\n\r\n<h2 style=\"margin: 10px 0px; padding: 0px; font-family: Helvetica, sans-serif; font-weight: bold; color: inherit; font-size: 18px; font-stretch: normal; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Đặc điểm nổi bật của tắm gội Johnson\'s baby 500ml</h2>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Sữa tám gội<a href=\"https://shoptretho.com.vn/danh-muc/sua-tam-cho-be\" style=\"margin: 0px; padding: 0px; text-decoration: none; font-size: 13px; outline: 0px; box-sizing: content-box !important; -webkit-user-select: text !important; color: rgb(0, 98, 183) !important; background: transparent;\">&nbsp;</a>toàn thân Johnson 500ml nhẹ nhàng chăm sóc da đầu và toàn thân bé. Với 