INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product578,'Dành cho bé từ 6-12 tuổi, Gồm 73 miếng ghép, Là đồ chơi an toàn nhập khẩu','<p><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Đồ chơi LEGO Creator 30285 - Mô hình hổ Tiger</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">&nbsp;giúp đánh thức tiềm năng tư duy, kỹ năng sáng tạo của các em nhỏ chỉ với 73 mảnh ghép tinh xảo sắc nét cùng với khả năng sáng tạo là không có giới hạn. Bé có thể lắp ráp lên vô số những mô hình thú vị và độc đáo dựa vào những ý tưởng thông minh và đôi tay khéo léo của mình.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product579,'Chất liệu cotton mềm mại, Thiết kế tiện dùng cho mẹ, Thiết kế thoải mái, vừa vặn','<h3 style=\"font-weight: bold; color: inherit; margin: 10px 0px; padding: 0px; font-family: Helvetica, sans-serif; font-size: 16px; font-stretch: normal; box-sizing: content-box !important; -webkit-user-select: text !important;\">Thông tin về sản phẩm áo lót cho con bú Thái Lan</h3>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Việc mang thai và sắp sửa trở thành mẹ là niềm vui vô cùng to lớn và không gì có thể thay thể được. Tuy nhiên, có rất nhiều vấn đề phải lo lắng khi ngày lâm bồn đang gần kề: chọn lựa quần áo cho bé, nôi, cũi và một thứ không thể quên chính là chiếc áo ngực cho con bú. Không nên để tới lúc sinh con, cho con bú rồi bạn mới sực nhớ ra phải mua loại áo ngực “chuyên dụng” này.</p>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Dùng áo ngực loại này sẽ rất thuận tiện cho mẹ trong sinh hoạt hàng ngày và cho con bú một cách dễ dàng. Sản phẩm được thiết kế phù Hợp với vóc dáng và kích thước ngực của mẹ khi mới sinh giúp mẹ thoải mái khi sử dụng. Bạn còn có thể điều chỉnh độ dài ngắn của dây áo sao cho phù hợp và thoải mái nhất.</p>\r\n\r\n<h3 style=\"font-weight: bold; color: inherit; margin: 10px 0px; padding: 0px; font-family: Helvetica, sans-serif; font-size: 16px; font-stretch: normal; box-sizing: content-box !important; -webkit-user-select: text !important;\">Đặc điểm nổi bật của áo lót Thái Lan</h3>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Áo được làm từ chất liệu cotton mềm mại, thoáng mát, độ thấm hút mồ hôi rất cao. Sữa mẹ được tiết ra liên tục trong quãng thời gian cho con bú, vì thế việc chọn lựa áo ngực làm từ chất liệu cotton sẽ giúp bạn tránh được những bệnh viêm nhiễm không đáng có.</p>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Áo lót Thái Lan cho mẹ được thiết kế hoàn hảo nâng đỡ, bao phủ, tiện lợi và dễ điều chỉnh.</p>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Làm từ vải mềm, chất lượng cao, không gây kích ứng da.</p>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Thiết kế đặc biệt có ngăn để miếng lót thấm sữa vào, ngăn sữa thấm chảy ra áo.</p>\r\n\r\n<p style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; margin: 0px 0px 10px; padding: 0px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Chuyên dùng cho bà bầu hoặc sau khi sinh, rất tiện lợi.</p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product580,'Chất liệu cotton mềm mại, Màu sắc tươi sáng , Thiết kế kiểu mở cúc tiện lợi','<p><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">Trong thời gian mới sinh, việc tăng cân và thay đổi kích thước vòng một khiến chị em khó khăn trong việc lựa chọn trang phục phù hợp. Ngoài tầm quan trọng của một chiếc áo ngực tốt giúp bảo vệ và nâng đỡ bầu ngực, chị em còn cần những trang phục mặc nhà được thiết kế đặc biệt cho con bú. Với sự hỗ trợ thêm của loại áo này, các mẹ sẽ thấy thuận tiện, thoải mái khi chăm con và đặc biệt là không bị mất điểm trong mắt ông xã trong giai đoạn mà nhiều chị em đã lơ là, bỏ qua việc chăm sóc bản thân và hình thức của mình.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product581,'An toàn, bền & đẹp, Có nhiều mầu sắc, Bộ gồm 5 sản phẩm','<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Túi xách dành cho mẹ và bé được may từ chất liệu vải dù, có đường may bền đẹp, khóa kéo chắc chắn, nhiều màu sắc.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Bộ sản phẩm gồm 4 túi và 1 tấm lót thay bỉm giúp bạn đựng được nhiều đồ cho bé như quần áo, giày dép, bình sữa, bình uống nước, tã giấy, khăn lau… mà không lo bị đổ, ngã.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Kiểu dáng túi mang đến cho mẹ và bé vẻ thời trang và sành điệu.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Màu sắc: đỏ, nâu, xanh dương, xanh dưa.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Trọng lượng: 1,1kg.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">Kích thước cho các túi bao gồm:</span></p>\r\n\r\n<p style=\"margin: 0px 0px 10px 40px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Túi 1: 40 x 32 x 17cm</p>\r\n\r\n<p style=\"margin: 0px 0px 10px 40px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Túi 2: 30 x 28 x 14cm</p>\r\n\r\n<p style=\"margin: 0px 0px 10px 40px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Túi 3: 12 x 14 x 6cm</p>\r\n\r\n<p style=\"margin: 0px 0px 10px 40px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Túi 4 ( túi đựng bình nước): cao18 x đường kính 7cm</p>\r\n\r\n<p style=\"margin: 0px 0px 10px 40px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">01 tấm lót bỉm chống thấm 2 mặt: 31 x 55cm</p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product582,'Chất liệu thun co giãn, Thiết kế kiểu lỗ thoáng mát, Có nhiều size để mẹ chọn lựa','<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Bé yêu chào đời là một hạnh phúc vô bờ đối với bất kỳ người mẹ nào. Nhưng sau khi sinh bé mỡ thừa ở vòng 2 tăng dần lên&nbsp; làm cho mẹ mất đi vóc dáng cân đối của mình.&nbsp; Kéo theo đó là sự thiếu tự tin trong giao tiếp và diện những trang phục bó sát....Tuy nhiên, với sản phẩm gen bụng Philip giúp cho mẹ cải thiện vóc dáng đáng kể sau khi sinh. Với chất liệu mỏng nhẹ, mềm mại, thoáng khísẽ tạo cho mẹ cảm giác thoải mái khi sử dụng.</p>\r\n\r\n<h2 style=\"font-weight: bold; margin: 10px 0px; padding: 0px; font-family: Helvetica, sans-serif; color: rgb(34, 34, 34); font-size: 18px; font-stretch: normal; box-sizing: content-box !important; -webkit-user-select: text !important;\">Đặc điểm nổi bật của gen bụng Philip</h2>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Gen bụng philip với màu sắc nhẹ nhàng, có nhiều size phù hợp với cỡ bụng của các mẹ. Sản phẩm giúp các mẹ lấy lại vóc dáng thon gọn sau khi sinh.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Sản phẩm được thiết kế theo kiểu gen lỗ nhỏ rất thoáng mát, cho mẹ sử dụng trong mùa hè và mùa đông.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Sản phẩm có nhiều size để mẹ chọn lựa</p>\r\n','','','','','',[site_id]);
