INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product592,'Kiểu áo cộc tay, Có nhiều size cho bé, Xuất xứ: Việt Nam','<p><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">Mùa hè đang đến, mẹ hãy dành tặng cho con&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">bộ quần áo bé trai cộc tay in hình voi</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">&nbsp;- made in Viet Nam - được may bằng chất vải cotton thấm hút mồ hôi, cho bé thoải mái vận động. Kiểu dáng thể thao, áo cộc tay, quần chun với gam màu và họa tiết nổi bật sẽ mang đến phong cách khỏe khoắn, thời trang cho bé yêu của bạn. Sản phẩm có nhiều size cho trẻ trên 1 tuổi.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product593,'Cho bé từ 1 tuổi trở lên, In hình người nhện, Chất vải cotton mềm mại','<p><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Bộ quần áo in hinh người nhện</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">&nbsp;kiểu áo thun kết hợp quần chun cho các bé trai từ 1 tuổi đến 6 tuổi với hình người nhện cho bé yêu. Bộ quần áo được may từ chất vải cotton, đường chỉ may chắc chắn giúp bé yêu của bạn thoải mái vận động, vui chơi. Sản phẩm thích hợp cho bé mặc ở nhà, đi chơi cùng bạn bè, hay lúc đi ngủ.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product594,'Chất liệu thun co giãn, Họa tiết kẻ ngang , Dành cho bé từ 3-4 tuổi','<p><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">Mùa hè luôn gắn liền với những chuyến du lịch biển. Ngay từ lúc này, cả gia đình bạn đã lên kế hoạch cho một chuyến đi chơi để được hòa mình vào làn nước xanh biếc, tận hưởng cảm giác thoải mái bất tận? Chắc chắn tất cả các thành viên đều đang rất thích thú và đặc biệt, các bé yêu nhà bạn đang vô cùng háo hức bởi các bé sẽ có cơ hội thư giãn sau một năm học vất vả.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product595,'Chất liệu cotton, Họa tiết chấm bi, Size cho bé từ 1-5 tuổi .','<p><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Váy hè cho bé gái 1130</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">&nbsp;kiểu dáng, màu sắc thời trang, điệu đà với thiết kế dáng xòe, công chúa nhỏ nhà bạn hẳn sẽ rất đáng yêu và dễ thương với chiếc váy xinh xắn này. Chiếc váy được may bằng chất liệu cotton với khả năng, thấm hút mồ hôi tốt giúp bé yêu thoải mái vận động cả ngày mà không gây khó chịu.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product596,'Chất liệu cotton thấm mồ hôi cực tốt, Nhiều màu sắc, xu hướng thời trang hè cho bé ... ','<p><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">Bộ bé gái chân váy dành cho bé gái từ 1 đến 8 tuổi nằm trong bộ sưu tập thời trang hè 2015 cho bé gái của nhãn hàng Zara Kids. Bộ bé gái chân váy mang phong cách hiện đại, điệu đà. Với chất liệu cotton thấm mồ hôi, bé sẽ thoải mái vận động nhưng không kém phần xinh xắn, đáng yêu.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product597,'Kiểu ba lô xinh xắn cho bé , Thiết kế nhiều ngăn tiện dụng, Ba lô hình thú đáng yêu ','<p><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">Một trong những đồ dùng cần thiết nhất ngoài quần áo, sách vở mà ba mẹ cần chuẩn bị cho bé mỗi khi đến trường hay đi picnic chính là chiếc ba lô – người bạn thân thiết theo chân bé mỗi ngày. Vì thế hãy chọn những chiếc&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">balo Skiphop</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">&nbsp;kiểu dáng mới nhất, thiết kế thời trang&nbsp;để bé hào hứng hơn khi đến trường nhé.&nbsp;</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product598,'Vali kéo tiện dụng, Kích thước: 49*31.5*21 cm, Bằng nhựa cứng ... ','<p><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Vali trẻ em (nhiều hình)</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">&nbsp;với thiết kế tiện dụng, giúp bé có thể đựng quần áo, vật dụng cá nhân một cách gọn gàng và thuận tiện, dễ dàng cho việc di chuyển. Đây sẽ là món quà ý nghĩa dành tặng cho con yêu, để bé tự chuẩn bị đồ cho mình trong những chuyến du lịch hay về quê thăm ông bà cùng bố mẹ đấy nhé.</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product599,'Có nhiều màu sắc và kiểu dáng , Dành cho bé từ 2 tuổi trở lên, Có đính thú bông ngộ nghĩnh ...','<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Thật tuyệt vời làm sao khi các bé đã khôn lớn và bắt đầu đến trường, các mẹ chắc hẳn sẽ dành nhiều thời gian chuẩn bị những món đồ cần thiết nhất cho con mình và một chiếc Ba lô sẽ luôn là hành trang không thể thiếu.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Với thiết kế đơn giản, nhỏ gọn cùng họa tiết là hình<span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">&nbsp;thú&nbsp; bông&nbsp;</span>thân thiện,&nbsp;<span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; box-sizing: content-box !important; -webkit-user-select: text !important;\">Ba lô em bé hình thú bông 1205</span>&nbsp;nhiều mầu và kiểu dáng là sản phẩm phù hợp dành cho các bé đi nhà trẻ. Chắc chắn chiếc ba lô này sẽ trở thành người bạn thân thiết đồng hành đến lớp cùng bé yêu của bạn.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Đặc điểm nổi bật của sản phẩm:</span></p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Ba lô em bé hình thú bông 1205 nhiều mầu và kiểu dáng có kích thước nhỏ nhắn, thích hợp với các bé đi mẫu giáo, đi chơi cùng gia đình, sản phẩm không quá to để bé cảm thấy nặng, trễ vai.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Túi có nhiều ngăn tiện dụng có thể đựng sách vở, bút, bộ quần áo, sữa hộp và đồ chơi.</p>\r\n\r\n<p style=\"margin: 0px 0px 10px; padding: 0px; font-family: open_sans, Arial, Tahoma; text-align: justify; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; box-sizing: content-box !important; -webkit-user-select: text !important;\">Thiết kế bắt mắt với hình nhiều hình thú ngộ nghĩnh &amp; đáng yêu.</p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product600,'Chất liệu cói, thông thoáng, mát mẻ, Thiết kế thời trang, Thích hợp làm quà tặng cho mọi bé... ','<p><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">Bên cạnh quần áo thì những chiếc mũ xinh xắn cũng là phụ kiện không thể thiết bất kể là đối với bé trai hay bé gái. Không chỉ có ý nghĩa về thời trang, trong những ngày nắng nóng của ngày hè, mũ còn bảo vệ bé yêu khỏi tác hại của ánh nắng mặt trời. Shop Trẻ Thơ mang đến cho bé&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-family: open_sans_bold, Arial, Tahoma; color: rgb(34, 34, 34); font-size: 14px; line-height: 24px; text-align: justify; box-sizing: content-box !important; -webkit-user-select: text !important;\">Mũ rộng vành Hàn quốc màu đỏ hồng&nbsp;</span><span style=\"color: rgb(34, 34, 34); font-family: open_sans, Arial, Tahoma; font-size: 14px; line-height: 24px; text-align: justify;\">cực kì thời trang và cá tính. Bố mẹ hãy giúp bé định hình phong cách thời trang ngay từ khi còn nhỏ nhé.</span></p>\r\n','','','','','',[site_id]);
