INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product654,'Hãng sản xuất: Viglacera. Bảo hành: 3 năm. Xuất xứ:Việt Nam','<p>Thiết kế nội thất phòng tắm:</p>\r\n\r\n<p>Thiết kế nội thất phòng tắm cũng quan trọng không kém các căn phòng khác bởi phòng tắm là không gian riêng tư của các thành viên trong gia đình. Buổi sáng nơi đầu tiên bạn bước vào sau khi thức dậy đó chính là phòng tắm. Buổi tối đi làm về, bạn bước vào phòng tắm tắm rửa, thư giãn và rũ bỏ tất cả những căng thẳng mệt mỏi sau một ngày làm việc. Do vậy, bạn cần phải luôn giữ gìn cho phòng tắm thông thoáng sạch sẽ, phong cách thiết kế nội thất đơn giản nhưng sáng sủa và phải đảm bảo điều kiện thông gió tốt.</p>\r\n\r\n<p>Ánh sáng đèn chiếu và tấm gương lớn sẽ giúp bạn nới rộng không gian, đánh lừa thị giác.&nbsp;</p>\r\n','','','','','',[site_id]);
