INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product656,'Làm từ chất liệu Nội thất đẹp gỗ tự nhiên Bộ Bàn ghế ăn gỗ sồi mã BA 29 hướng đến không gian sinh hoạt mở đầy ấn tượng, thiết kế theo gu thẩm mĩ hiện đại, một đời sống tinh thần phong phú. Sản phẩm sản xuất theo dây truyền công nghệ hiện đại, với đội ngũ những người làm việc chuyên nghiệp gửi đến khách hàng những sản phẩm Bàn ăn gỗ chuyên nghiệp nhất.','<p><strong>&nbsp;ĐỒ GỖ TỰ NHIÊN</strong></p>\r\n\r\n<p>&nbsp;Tư Vấn - Thiết Kế - Thi Công&nbsp;Đồ Gỗ Nội Thất Theo Yêu Cầu</p>\r\n\r\n<p>Chuyên tư vấn thiết kế và sản xuất những sản phẩm nội thất&nbsp; gỗ tự nhiên sang trọng và hiện đại cho gia đình, văn phòng, quán cafe , khách sạn .Sản phẩm chính của chúng tôi bao gồm:</p>\r\n\r\n<p>- Phòng khách : Kệ ti vi , Bàn trà , tap , sofa ...<br />\r\n<br />\r\n- Phòng ngủ : giường, tủ, bàn phấn, kệ tivi...<br />\r\n<br />\r\n- Phòng bếp : tủ bếp, bàn ghế , tủ kệ trang trí</p>\r\n','','','','','',[site_id]);
