INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product695,'Tình trạng: sẵn có\r\nNơi sản xuât: Hà Nội','<p style=\"margin: 0px 0px 10px; color: rgb(90, 90, 90); font-family: arial; font-size: 15px; line-height: 20px; background-color: rgb(250, 250, 250);\">Hầu như phòng vệ sinh được xây dựng cùng một vị trí trên tất cả các tầng trong kết cấu xây dựng nhà cao tầng hiện đại. Trong một tòa nhà cao tầng, nếu ngủ trong một phòng ngủ cải tạo từ nhà vệ sinh thì bạn bị kẹp giữa hai môi trường ẩm ướt, với những bồn cầu, thùng rác, ống nước thải… Chắc chắn sức khỏe thể chất và tinh thần của bạn chắc chắn sẽ có ảnh hưởng không nhỏ theo chiều hướng xấu bởi điều kiện môi trường này.</p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product696,'Tình trang: còn hàng\r\nChất liệu:\r\nNơi sản xuất:','<div style=\"text-align: justify;\"><span style=\"font-size:14px;\"><strong>Thiết kế nội thất phòng ăn hiện đại và ấm cúng</strong></span></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div>\r\n<div>\r\n<div style=\"text-align: justify;\">Bữa cơm gia đình không chỉ là nơi ăn uống mà còn là dịp để các thành viên trong gia đình sum họp. Và một bữa ăn ngon không chỉ phụ thuộc vào mùi vị món ăn mà còn bị chi phối bởi không gian ăn uống, tâm trạng người ăn. Chính vì thế, không gian phòng ăn được thiết kế sao cho hiện đại, hợp phong thủy nhưng vẫn toát lên vẻ ấm cúng là điều mà các gia chủ quan tâm. Thấu hiểu được ước muốn đó nên Pokedecor luôn không ngừng phát triển và sáng tạo để mang đến những không gian \"<a href=\"/\">nội thất phòng ăn</a>\" đẹp đẽ biến phòng ăn trở thành căn phòng rộn ắp tiếng cười với những niềm vui của sự đoàn kết.<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\"><a href=\"http://pokedecor.vn/thiet-ke-noi-that-phong-an-hien-dai-va-am-cung-290.htm\"><img alt=\"thiet-ke-noi-that-phong-an-hien-dai-va-am-cung\" src=\"http://pokedecor.vn/images/2015/03/30/0-thiet-ke-noi-that-phong-an-hien-dai-va-am-cung-04.jpg\" title=\"Thiết kế nội thất phòng ăn hiện đại và ấm cúng\" /></a></div>\r\n\r\n<div style=\"text-align: justify;\"><br />\r\nThiết kế nội thất phòng ăn hợp lý<br />\r\n<br />\r\nTùy vào diện tích căn hộ mà bạn có thể thiết kế phòng ăn cho thật phù hợp. Nếu những căn hộ có diện tích rộng lớn thì phòng ăn được thiết kế riêng biệt nhưng nếu diện tích căn hộ có phần eo hẹp thì phòng ăn được gắn liền với phòng bếp hay phòng khách. Theo đó, tùy vào cách bố trí mà ta có thể “<a href=\"/\">thiết kế nội thất phòng ăn</a>” cho thật phù hợp.<br />\r\n<br />\r\nPhòng ăn gắn liền với phòng bếp hay phòng khách thì chúng ta nên biết cách phối hợp đồ cho thật hài hòa, nếu diện tích phòng hẹp quá thì tối giản đồ đạc là điều cần thiết. Trong trường hợp, phòng ăn được thiết kế riêng lẻ thì chúng ta tha hồ trang trí&nbsp; và thiết kế thêm quầy bar, bàn café, tủ rượu,…tùy theo sở thích và ý nguyện của mình nhưng phải đảm bảo tính thẩm mỹ, sự sang trọng và ấm cúng.<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\"><img alt=\"thiet-ke-noi-that-phong-an-hien-dai-va-am-cung\" src=\"http://pokedecor.vn/images/2015/03/30/thiet-ke-noi-that-phong-an-hien-dai-va-am-cung-06.jpg\" title=\"Thiết kế nội thất phòng ăn hiện đại và ấm cúng\" /></div>\r\n\r\n<div style=\"text-align: justify;\"><br />\r\nMột căn phòng ăn đẹp đẽ, hài hòa là sự cấu thành của nhiều yếu tố như cách bố trí, chất liệu đồ dùng, màu sắc chủ đạo hay phong thủy,… Và khi thiết kế nội thất phòng ăn đòi hỏi chúng ta phải am hiểu gu thẩm mỹ kiến trúc, sự tinh tường trong cách bố trí mọi tiện nghi. Thiết kế nội thất phòng ăn với nhiều phong cách khác nhau, có thể là phòng ăn theo phong cách thiết kế cao cấp hay bình dân, phương Đông hay phương Tây, đơn giản hay cầu kỳ. Tùy vào từng phong cách mà chúng ta nên biết cách phối hợp màu sắc và sử dụng chất liệu đồ dùng cho thật hài hòa.</div>\r\n\r\n<div style=\"text-align: justify;\"><img alt=\"thiet-ke-noi-that-phong-an-hien-dai-va-am-cung\" src=\"http://pokedecor.vn/images/2015/03/30/thiet-ke-noi-that-phong-an-hien-dai-va-am-cung-9.jpg\" title=\"Thiết kế nội thất phòng ăn hiện đại và ấm cúng\" /></div>\r\n\r\n<div style=\"text-align: justify;\"><br />\r\nNếu bạn muốn thiết kế một phòng ăn theo phong cách hiện đại thì sự phối hợp nhuần nhuyễn giữa hai gam mày tương phản trắng và đen là rất cần thiết. Bàn ăn làm bằng gỗ sồi màu đen. Ghế Cantono, bọc da Bahia. Bàn ăn màu đen đặt trong căn phòng màu trắng rất ấn tượng. Màu trắng tạo nên sự thoáng mát, rộng rãi trong khi đó màu đen lại làm cho không gian trở nên ấm cúng hơn. Phòng ăn mang gam màu vàng trắng với bộ bàn ghế màu vàng nhạt, tủ rượu màu trắng, màu đèn vàng, lấp lánh…sẽ tạo nên sự thanh nhã nhưng thật ấm cúng làm sao. Phòng ăn với gam màu đỏ trông thật bắt mắt, ấn tượng mà vẫn ấm cúng và hài hòa,..<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\"><img alt=\"thiet-ke-noi-that-phong-an-hien-dai-va-am-cung\" src=\"http://pokedecor.vn/images/2015/03/30/thiet-ke-noi-that-phong-an-hien-dai-va-am-cung-07.jpg\" title=\"Thiết kế nội thất phòng ăn hiện đại và ấm cúng\" /></div>\r\n\r\n<div style=\"text-align: justify;\"><br />\r\nChất liệu đồ nội thất phòng ăn có thể là nhựa, sắt, inox, gỗ,…Nhưng gỗ được đánh giá là chất liệu được nhiều người lựa chọn nhất vì sự đơn giản trong cách thiết kế nhưng vẫn toát lên vẻ sang trọng mà độ bền cao. Hơn nữa, sử dụng đồ gỗ chất lượng sẽ rất đảm bảo được sức khỏe của người sử dụng vì gỗ được lấy từ thiên nhiên.</div>\r\n</div>\r\n</div>\r\n','','','','','',[site_id]);
