INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product698,'Tình trạng: còn hàng\r\nMàu săc: nhiều màu\r\nNơi sản xuất: Hà Nội','<p style=\"padding: 0px; margin: 0px; line-height: 1.6em; color: rgb(0, 0, 0); font-family: Tahoma, Geneva, sans-serif; font-size: 12px;\"><span style=\"font-size: 12pt; font-family: \'Times New Roman\';\">Phòng ngủ là một môi trường gắn liền đến sự phát triển của trẻ em trong suốt thời gian dài. Do đó khi&nbsp;<strong>thiết kế nội thất phòng ngủ trẻ em</strong>&nbsp;cần phải lưu ý đến màu sắc cũng như cách trang trí sao cho trẻ cảm thấy thích thú, thoải mái. Phòng của bé cần phải trang trí tạo hình và kết hợp màu sắc mà trẻ yêu thích. Do đó hãy tham khảo ý kiến của bé để có nhiều ý tưởng hơn trong việc lựa chọn phòng cho con yêu của bạn</span></p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; line-height: 1.6em; color: rgb(0, 0, 0); font-family: Tahoma, Geneva, sans-serif; font-size: 12px;\">&nbsp;</p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; line-height: 1.6em; color: rgb(0, 0, 0); font-family: Tahoma, Geneva, sans-serif; font-size: 12px;\"><img alt=\"trang trí phòng ngủ cho bé\" height=\"474\" src=\"http://xaysuanhatrongoi.com/images/trang-tri-phong-ngu-cho-be-yeu7.jpg\" style=\"display: block; margin-left: auto; margin-right: auto;\" width=\"650\" /></p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; line-height: 1.6em; color: rgb(0, 0, 0); font-family: Tahoma, Geneva, sans-serif; font-size: 12px; text-align: center;\"><em><span style=\"font-size: 12pt; font-family: \'Times New Roman\';\">&nbsp;Phòng ngủ là nơi bé có thể học tập cũng như vui chơi</span></em></p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; line-height: 1.6em; color: rgb(0, 0, 0); font-family: Tahoma, Geneva, sans-serif; font-size: 12px; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"padding: 0px; margin: 0px; line-height: 1.6em; color: rgb(0, 0,