INSERT INTO albums (album_id, album_name, album_description, album_type, photocount, `alias`, site_id, user_id, meta_keywords, meta_description, avatar_path, avatar_name, avatar_id, created_time, modified_time, cat_id) VALUES (null,'Thiết kế biệt thự tây hồ','<p>Dù không thuê kiến trúc sư nội thất, bạn vẫn có thể làm đẹp cho tổ ấm bằng cách sắp xếp đồ đạc gọn gàng, tạo điểm nhấn cho không gian bởi nội thất đẹp mắt, xinh xắn.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-04-hallway-light-blue-staircase.jpg\" style=\"width: 276px; height: 400px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Vì có diện tích 50 m2 nên chủ nhân bố trí ngôi nhà rất gọn gàng, đồ đạc nhỏ nhắn, không rườm rà.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-05-hallway-through-shot-into-workspace.jpg\" style=\"width: 276px; height: 400px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Tầng một có hai phòng: phòng khách và phòng ăn. Để tạo sự thông thoáng, chủ nhà không lắp cửa cho các phòng.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-07-sideboard-with-cats-pictures-on-wall.jpg\" style=\"width: 276px; height: 400px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Phòng khách của ngôi nhà rất thoải mái. Bạn có thể ngồi bất cứ đâu trong căn phòng này.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-08-small-kitchen-ideas_0.jpg\" style=\"width: 276px; height: 400px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Bếp gần cửa sổ tận dùng ánh sáng tự nhiên.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-09-kitchen-storage-ideas-hangers.jpg\" style=\"width: 276px; height: 400px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Ở góc bếp, chủ nhân bố trí một vài chậu cảnh nho nhỏ. Thay vì dùng tủ bếp thông thường, chủ nhà sắp xế chiếc giá thanh mảnh, xinh xắn.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-10-sewing-area-home-workspace.jpg\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Góc làm việc kết hợp nơi may vá cho cô vợ trẻ.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-11-laptop-workstation-white-metal-drawers.jpg\" style=\"width: 276px; height: 400px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Góc làm việc bố trí gần cửa số tạo độ thông thoáng. Ngoài ra, tấm thảm vui mắt cũng làm không gian thêm sinh động.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-12-bedroom-stripey-bed-clothes.jpg\" style=\"width: 276px; height: 400px;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\">Phòng ngủ của hai vợ chồng đơn giản với những gam màu tươi trẻ.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: center;\"><img alt=\"\" src=\"http://w3ni180.web3nhat.net/data/media/135/files/57-13-sitting-nook-grey-swivel-chair.jpg\" style=\"width: 276px; height: 400px;\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,12,'thiet-ke-biet-thu-tay-ho',[site_id],[user_id],'','','/media/images/albums/135/1/','281_1439348554_1955cab74ac5db7.jpg',638,[now],[now],@albums_categories4);
