INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories208,'Du lịch miền Bắc 4 ngày bốn đêm khởi hành từ Sài Gòn','Lịch trình: MIỀN BẮC: Hà Nội - Yên Tử - Hạ Long - Lào Cai - Sapa - Thác Bạc - Đường Cao Tốc<br />\r\nTP. HỒ CHÍ MINH: 15,18,22/7 & 5,12,19,26/8<br />\r\nDu Lịch Miền Bắc - Chùa Ba Vàng được xây dựng triều vua Lê Dụ Tông tức năm 1676. Đây là ngôi chùa nằm ở độ cao 340m so với mặt nước biển. Với trị trí này, ngôi chùa đã có cho mình một nơi chốn đẹp về phong thuỷ cũng như tâm lin','<div id=\"hethonggia\">\r\n<div id=\"dsgiatour\">\r\n<h2>Giá tour cơ bản (đ)</h2>\r\n</div>\r\n</div>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px;\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Độ tuổi</td>\r\n			<td>Người lớn&nbsp;</td>\r\n			<td>Trẻ em&nbsp;</td>\r\n			<td>Người cao tuổi</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Giá</td>\r\n			<td>5.900.000</td>\r\n			<td>2.500.000</td>\r\n			<td>4.000.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Phụ thu</td>\r\n			<td>Người nước ngoài</td>\r\n			<td>Visa tái nhập</td>\r\n			<td>&nbsp;Hàng hóa</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Giá</td>\r\n			<td>600.000</td>\r\n			<td>0</td>\r\n			<td>300.000</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div>\r\n<div id=\"random-tab-id-108\">\r\n<div aria-hidden=\"false\" aria-labelledby=\"ui-id-1\" id=\"Lchtrnh-0\" role=\"tabpanel\">\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<h3>Ngày 1:&nbsp;&nbsp; TP.HCM – HÀ NỘI – HẠ LONG (Ăn sáng, trưa, chiều)</h3>\r\n\r\n<hr />\r\n<p>Sáng:&nbsp;Quý khách có mặt tại ga quốc nội, sân bay Tân Sơn Nhất trước giờ bay ít nhất hai tiếng. Đại diện công ty Du Lịch Việt đón và hỗ trợ Quý Khách làm thủ tục đón chuyến bay đi&nbsp;Hà Nội. Đến sân bay&nbsp;Nội Bài, xe đón đoàn khởi hành đến Hạ Long, trên đường dừng chân tham quan&nbsp;núi rừng Yên Tử,&nbsp;lên núi bằng cáp treo&nbsp;(chi phí cáp treo tự túc),&nbsp;tham quan&nbsp;chùa Hoa Yên, Bảo Tháp, Trúc Lâm Tam Tổ, Hàng Tùng 700 tuổi…xuống núi tham quan&nbsp;Thiền Viện Trúc Lâm, quả cầu Như Ý nặng 6 tấn được xếp kỷ lục guiness Việt Nam.</p>\r\n\r\n<p><img alt=\"http://dulichviet.com.vn/images/2015/03/du-lich-yen-tu_du-lich-viet.jpg\" src=\"http://dulichviet.com.vn/images/2015/03/du-lich-yen-tu_du-lich-viet.jpg\" style=\"width: 800px; height: 531px;\" /></p>\r\n\r\n<p><img alt=\"http://dulichviet.com.vn/images/2014/07/chua-dong-yen-tu_du-lich-viet.jpg\" src=\"http://dulichviet.com.vn/images/2014/07/chua-dong-yen-tu_du-lich-viet.jpg\" style=\"width: 800px; height: 534px;\" /></p>\r\n\r\n<p>Trưa:&nbsp;Ăn trưa tại nhà hàng.</p>\r\n\r\n<p>Chiều:&nbsp;Tiếp tục hành trình đến&nbsp;Hạ Long, nhận phòng KS – Dùng bữa chiều.</p>\r\n\r\n<p>Tối:&nbsp;Tự do dạo phố, mua sắm tại chợ đêm hoặc tham quan KDL&nbsp;Quốc tế Tuần Châu&nbsp;(vé cổng tự túc), thưởng thức các chương trình biểu diễn như:&nbsp;Show nhạc nước màu hoành tráng lớn nhất Đông Nam Á, show sư tử biển, cá heo…(chi phí tự túc). Nghỉ đêm tại&nbsp;Hạ Long.</p>\r\n\r\n<p><img alt=\"http://dulichviet.com.vn/images/2014/09/show-ca-voi_du-lich-viet.jpg\" src=\"http://dulichviet.com.vn/images/2014/09/show-ca-voi_du-lich-viet.jpg\" style=\"width: 800px; height: 600px;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n','du-lich-mien-bac-4-ngay-bon-dem-khoi-hanh-tu-sai-gon',1,'','',[site_id],[user_id],'/media/images/136/news/ava/','489_1438918639_16155c427ef27889.jpg',[now], [now],[user_id],0,'','',[now]);set @news348 = LAST_INSERT_ID();
