INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories301,' Tiệc trà – Tiệc Cocktail – Tiệc Reception','Nếu bạn đang tìm kiếm một buổi tiệc thân mật, gọn nhẹ, sang trọng, ấm cúng mang tính chất giao lưu tại nhà riêng hay tại công ty, chúng tôi có thể gợi ý về một buổi tiệc trà hoặc tiệc cocktail.','<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><font color=\"#000000\" style=\"outline: 0px;\"><span style=\"outline: 0px;\"><span style=\"outline: 0px;\">Nếu bạn đang tìm kiếm một buổi tiệc thân mật, gọn nhẹ, sang trọng, ấm cúng mang tính chất giao lưu tại nhà riêng hay tại công ty, chúng tôi có thể gợi ý về một buổi tiệc trà hoặc tiệc cocktail.</span></span></font><br style=\"outline: 0px;\" />\r\n<font color=\"#000000\" style=\"outline: 0px;\"><span style=\"outline: 0px;\"><span style=\"outline: 0px;\">Nếu bạn cần một không khí trẻ trung và phá cách, bạn hoàn toàn có thể thử hình thức Free – Drink Party.</span></span></font><br style=\"outline: 0px;\" />\r\n<font color=\"#000000\" style=\"outline: 0px;\"><span style=\"outline: 0px;\"><span style=\"outline: 0px;\">Chất lượng dịch vụ như khách sạn 5 sao nhưng với một mức chi phí phải chăng là những tiêu chí hàng đầu của Ẩm thực 24h. Với nhiều sự lựa chọn phong phú trong từng món ăn, bạn cùng chúng tôi hoàn toàn có thể xây dựng một thực đơn phù hợp cho sự kiện của mình.</span></span></font></span></span></p>\r\n\r\n<div class=\"resizewraper\" style=\"outline: 0px; margin: 0px; padding: 0px; display: inline-block; position: relative;\"><font color=\"#000000\" style=\"outline: 0px;\"><span style=\"outline: 0px; font-family: \'Open Sans\';\"><span style=\"outline: 0px; font-family: arial;\"><img alt=\"\" border=\"0\" original=\"http://amthuc24h.vn/wp-content/uploads/2014/08/2.png\" src=\"http://amthuc24h.vn/wp-content/uploads/2014/08/2.png\" style=\"outline: 0px; border: 0px; max-width: 100%; font-size: 14px; line-height: 17.2199993133545px; display: inline;\" /></span></span></font></div>\r\n\r\n<div>\r\n<div class=\"resizewraper\" style=\"outline: 0px; margin: 0px; padding: 0px; display: inline-block; position: relative;\">&nbsp;</div>\r\n</div>\r\n','tiec-tra--tiec-cocktail--tiec-reception',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','410_1440044715_98055d556ab5f448.jpg',[now], [now],[user_id],0,'','',[now]);set @news446 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories301,'Tổ chức, làm tiệc, đặt tiệc buffet tại nhà, cơ quan','Với đội ngũ nhân sự trẻ, giàu ý tưởng và nhiệt huyết, Ẩm Thực 24h chắc chắn sẽ mang lại cho bạn những buổi tiệc Buffet hoàn hảo.Với đội ngũ nhân sự trẻ, giàu ý tưởng và nhiệt huyết, Ẩm Thực 24h chắc chắn sẽ mang lại cho bạn những buổi tiệc Buffet hoàn hảo.','<p><font color=\"#000000\" style=\"outline: 0px; font-family: helvetica; font-size: 14px; line-height: 17.2199993133545px;\"><span style=\"outline: 0px; font-family: \'Open Sans\';\"><span style=\"outline: 0px; font-family: arial;\">Với đội ngũ nhân sự trẻ, giàu ý tưởng và nhiệt huyết,&nbsp;<i style=\"outline: 0px;\"><b style=\"outline: 0px;\">Ẩm Thực 24h</b></i>&nbsp;chắc chắn sẽ mang lại cho bạn những buổi tiệc Buffet hoàn hảo.Tại đây, chúng tôi luôn chú trọng và theo sát từng sở thích, nhu cầu của khách hàng để đảm bảo chất lượng đồ ăn cũng như sự độc đáo cho từng sự kiện. Những phụ kiện trang trí để trang hoàng cho không gian tiệc được chúng tôi sử dụng một cách hợp lý nhất nhằm tạo điểm nhấn cho các món ăn hấp dẫn đang mời gọi các vị khách.</span></span></font></p>\r\n\r\n<div class=\"resizewraper\" style=\"outline: 0px; margin: 0px; padding: 0px; display: inline-block; position: relative;\"><font color=\"#000000\" style=\"outline: 0px; font-family: helvetica; font-size: 14px; line-height: 17.2199993133545px;\"><span style=\"outline: 0px; font-family: \'Open Sans\';\"><span style=\"outline: 0px; font-family: arial;\"><img alt=\"\" border=\"0\" original=\"http://amthuc24h.vn/wp-content/uploads/2014/08/AT1.png\" src=\"http://amthuc24h.vn/wp-content/uploads/2014/08/AT1.png\" style=\"outline: 0px; border: 0px; max-width: 100%; display: inline;\" /></span></span></font></div>\r\n\r\n<p><br style=\"outline: 0px; color: rgb(56, 56, 56); font-family: helvetica; font-size: 14px; line-height: 17.2199993133545px;\" />\r\n<font color=\"#000000\" style=\"outline: 0px; font-family: helvetica; font-size: 14px; line-height: 17.2199993133545px;\"><span style=\"outline: 0px; font-family: \'Open Sans\';\"><span style=\"outline: 0px; font-family: arial;\">Bên cạnh việc cung cấp đồ ăn và đồ uống trọn gói cho tiệc buffet, Ẩm Thực 24h cònnhận thiết kế và bày trí bàn tiệc Buffet theo chủ đề và sự kiện đặc biệt của bạn.</span></span></font></p>\r\n','to-chu-lam-tiec-dat-tiec-buffet-tai-nha-co-quan',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','750_1440044556_43055d5560c676bc.jpg',[now], [now],[user_id],0,'','',[now]);set @news445 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories301,'Tiệc cỗ - tiệc cưới','Ẩm thực 24h cung cấp dịch vụ Nấu Cỗ cưới tại nhà và Tiệc các loại theo yêu cầu với các với các loại hình cỗ đa dạng phong phú. Tất cả các món: rau, thịt, hải sản đều được tuyển chọn từ nguồn nguyên liệu tươi sống, đảm bảo vệ sinh – an toàn thực phẩm. Các món ăn sẽ mang hương vị thật lạ và độc đáo cho thực khách bằng nghệ thuật tẩm ướp gia vị theo bí quyết riêng của các đầu bếp. ','<h1 style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nhận nấu cỗ tiệc cưới tại nhà tại Hà Nội</span></span></h1>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Ẩm thực 24h&nbsp;cung cấp dịch vụ&nbsp;Nấu Cỗ cưới tại nhà<a href=\"http://tieccuoitainha.com/b694/p4/c5760/nau-co-cuoi-tai-nha.html\" title=\"nấu cỗ cưới tại nhà,nấu tiệc cưới tại nhà \">&nbsp;</a>và&nbsp;Tiệc các loại&nbsp;theo yêu cầu với các với các loại hình cỗ chủ yếu như sau:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nấu cỗ cưới tại nhà</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nấu cỗ ăn hỏi tại nhà</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nấu cỗ phục vụ ngày giỗ</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nấu cỗ ngày Lễ mừng thọ</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nấu cỗ phục vụ nhà có đám</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Dịch vụ nấu cỗ chay</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Dịch vụ tiệc đứng Buffet</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nấu cỗ tại nhà theo yêu cầu</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Dịch vụ tiệc Hội Nghị</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Tiệc cho công ty, tổ chức</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Nhà bạt, bàn ghế cho Cỗ -Tiệc</span></span></li>\r\n</ol>\r\n\r\n<div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Đến với dịch vụ&nbsp;Nấu Cỗ cưới tại nhà&nbsp;của Ẩm thực 24 h&nbsp;,&nbsp;Quý khách sẽ cảm nhận được sự thuận tiện bởi dịch vụ của chúng tôi rất linh động cụ thể như:</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Phục vụ tài nhà số lượng 5 mâm trở lên (mâm 6)</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Không ngại xa, phục vụ tất cả các ngày trong tuần</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Phục vụ cho các yêu cầu như: cỗ cưới, cỗ ăn hỏi, cỗ giỗ, cỗ mừng thọ, tiệc buffet, tiệc hội nghị…</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Chúng tôi có kèm theo dịch vụ nhà bạt, bàn ghế để phục vụ Quý khách khi có nhu cầu</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Với danh sách thực đơn rất đa dạng kết hợp giữa các món ăn cổ truyền và hiện đại, Quý khách có thể chọn thực đơn theo từng món sẽ rất linh hoạt</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Đội ngũ đầu bếp và nhân viên phục vụ rất nhiệt tình, chu đáo đảm bảo hài lòng tất cả các quan khách</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\"><img alt=\"nau co, nấu cỗ, tiệc cưới tại nhà, tiec cuoi tai nha\" src=\"http://tiecvietanh.com/upload_images/images/mon-an.jpg\" style=\"width: 600px; height: 404px;\" /></span></span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</div>\r\n','tiec-co---tiec-cuoi',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','296_1440043744_23655d552e06688d.jpg',[now], [now],[user_id],0,'','',[now]);set @news444 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories301,'Tiệc ẩm thực ','Với đội ngũ đầu bếp chuyên nghiệp, chúng tôi mang đến cho sự kiện của bạn phong cách ẩm thực cao cấp và sẵn sàng đáp ứng mọi yêu cầu của thực khách. Từ những yến tiệc sang trọng được tô điểm bởi sự lung linh của băng đăng đĩêu khắc, cho tới những buổi tiếp đón nhẹ nhàng bằng cocktails và đồ nguội. Từ buổi điễm tâm thư thái cho tới các buổi dạ yến thanh lịch theo chủ đề cho số lượng lên đến hơn ba ngàn thực khách. Ẩm thực 24h cam kết mang đến cho khách hàng một đẳng cấp dịch vụ mới.','<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Với đội ngũ đầu bếp chuyên nghiệp, chúng tôi mang đến cho sự kiện của bạn phong cách ẩm thực cao cấp và sẵn sàng đáp ứng mọi yêu cầu của thực khách. Từ những yến tiệc sang trọng được tô điểm bởi sự lung linh của băng đăng đĩêu khắc, cho tới những buổi tiếp đón nhẹ nhàng bằng cocktails và đồ nguội. Từ buổi điễm tâm thư thái cho tới các buổi dạ yến thanh lịch theo chủ đề cho số lượng lên đến hơn ba ngàn thực khách. Ẩm thực 24 h&nbsp;cam kết mang đến cho khách hàng một đẳng cấp dịch vụ mới.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Dịch vụ:</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">•Giải lao phục vụ trà, bánh ngọt.<br />\r\n•Tiệc trưa.<br />\r\n•Tiệc tối liên hoan.<br />\r\n•Tiệc nhẹ cocktail.<br />\r\n•Tiệc theo chủ đề.<br />\r\n•Phục vụ tiệc tại nơi theo yêu cầu.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n','tiec-am-thuc',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','311_1440043568_35255d5523093360.jpg',[now], [now],[user_id],0,'','',[now]);set @news443 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories301,'Dịch vụ nấu ăn đặt tiệc tại nhà, cơ quan','Ẩm thực 24h huyên về lĩnh vực chế biến và phục vụ ăn uống với đội ngũ quản lý là các cử nhân chuyên ngành ăn uống, đầu bếp có tay nghề cao, kinh nghiệm lâu năm và nhân viên phục vụ nhiệt tình, am hiểu công việc, tâm huyết với nghề.<br />\r\n','<p>Ẩm thực 24h&nbsp;huyên về lĩnh vực chế biến và phục vụ ăn uống với đội ngũ quản lý là các cử nhân chuyên ngành ăn uống, đầu bếp có tay nghề cao, kinh nghiệm lâu năm và nhân viên phục vụ nhiệt tình, am hiểu công việc, tâm huyết với nghề.<br />\r\n<br />\r\n<img alt=\"\" src=\"http://4urestaurant.vn/UserFiles/image/gioithieu/anhhang222.JPG\" /></p>\r\n\r\n<div>\r\n<p>Ẩm thực 24 h&nbsp;nhận tổ chức trọn gói các loại hình tiệc: Tiệc cưới, tiệc Buffet, tiệc hội nghị, tiệc gia đình, tiệc cơ quan, tiệc phục vụ cho các sự kiện lớn với số lượng không hạn chế, tại bất kì địa điểm nào: Nhà riêng, cơ quan, hội trường, sân bãi… (phục vụ từ 30 khách trở lên )&nbsp;</p>\r\n\r\n<p>Mỗi sự kiện, tùy theo tính chất, số lượng khách tới dự, địa điểm. hình thức tổ chức, cùng với các yêu cầu đặc biệt và ngân sách của Quý khách chúng tôi sẽ có những tư vấn cụ thể nhằm mang lại sự thành công và ấn tượng.</p>\r\n</div>\r\n','dich-vu-nau-an-dat-tiec-tai-nha-co-quan',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','829_1440043391_31355d5517fcc71f.jpg',[now], [now],[user_id],0,'','',[now]);set @news442 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories301,'Cơm văn phòng - thơm ngon- hấp dẫn','Ẩm thực 24 h cung cấp cơm văn phòng chuyên nghiệp. Với hình thức sử dụng hộp đựng cơm cao cấp tiện lợi, đảm bảo an toàn vệ sinh, kiểu dáng lịch sự và sang trọng. Mỗi một sản phẩm được cung cấp đến tay khách hàng đều phải đạt được các tiêu chí nghiêm ngặt của ẩm thực 24h: An toàn vệ sinh thực phẩm, thức ăn nóng hợp khẩu vị và kịp thời nhanh chóng cho khách hàng.','<p>Ẩm thực 24 h cung cấp cơm văn phòng chuyên nghiệp. Với hình thức sử dụng hộp đựng cơm cao cấp tiện lợi, đảm bảo an toàn vệ sinh, kiểu dáng lịch sự và sang trọng. Mỗi một sản phẩm được cung cấp đến tay khách hàng đều phải đạt được các tiêu chí nghiêm ngặt của ẩm thực 24h: An toàn vệ sinh thực phẩm, thức ăn nóng hợp khẩu vị và kịp thời nhanh chóng cho khách hàng.</p>\r\n\r\n<p>- Thực đơn thứ hai</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://www.mustforseniors.net/wp-content/uploads/2015/03/com-van-phong-5.jpg\" style=\"width: 500px; height: 362px;\" /></p>\r\n\r\n<p>- Thực đơn thứ 3</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://www.neon-verlag.de/wp-content/uploads/2015/03/com-van-phong-3.png\" style=\"width: 500px; height: 353px;\" /></p>\r\n','com-van-phong---thom-ngon--hap-dan',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','72_1440042815_91055d54f3f49df5.png',[now], [now],[user_id],0,'','',[now]);set @news441 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Món ngon cuối tuần - Canh Sườn Non Đậu Ngự, Đệ Nhất','Ẩm thực 24h sẽ hướng dẫn các bạn món canh sườn non đậu ngự. Vị ngon ngọt của sườn non kết hợp với vị thanh mát của đậu ngự sẽ làm cho bữa ăn của các bạn ngon miệng và hấp dẫn, đưa cơm hơn. Món này dễ ăn và cách chế biến cũng rất đơn giản, các bạn chỉ cần bỏ ra một chút thời gian là có nồi canh ngon cho gia đình mình rồi.','<p><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\"><b>Ẩm thực 24h</b>&nbsp;sẽ hướng dẫn các bạn món canh sườn non đậu ngự. Vị ngon ngọt của&nbsp;<b>sườn non</b>&nbsp;kết hợp với vị thanh mát của đậu ngự sẽ làm cho bữa ăn của các bạn ngon miệng và hấp dẫn, đưa cơm hơn. Món này dễ ăn và cách chế biến cũng rất đơn giản, các bạn chỉ cần bỏ ra một chút thời gian là có nồi canh ngon cho gia đình mình rồi.</span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<div class=\"separator\" style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px; clear: both; text-align: center;\"><a href=\"http://3.bp.blogspot.com/-J6hqJpnk5_8/Va9TollZDNI/AAAAAAAAC5g/kmN6t0GmfgQ/s1600/55313944-1279787096-canh-suon-non-dau-ngu%255B1%255D.jpg\" imageanchor=\"1\" style=\"text-decoration: none; color: rgb(207, 39, 75); margin-left: 1em; margin-right: 1em;\"><img border=\"0\" height=\"475\" src=\"http://3.bp.blogspot.com/-J6hqJpnk5_8/Va9TollZDNI/AAAAAAAAC5g/kmN6t0GmfgQ/s640/55313944-1279787096-canh-suon-non-dau-ngu%255B1%255D.jpg\" style=\"max-width: 100%; position: relative;\" width=\"640\" /></a></div>\r\n\r\n<p><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<a name=\"more\" style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(207, 39, 75); line-height: 18.2000007629395px;\"></a><br />\r\n<br />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\"><b><u>1. Nguyên liệu:</u></b></span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<br />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\">- Sườn non: 300g</span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\">- Cà rốt: 1/2 củ</span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\">- Đậu ngự tươi: 200g</span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\">- Hành lá, hành tím, rau mùi</span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\">- Nước mắm, hạt tiêu, hạt nêm</span><br style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\" />\r\n<br />\r\n<span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: 18.2000007629395px;\"><b><u>2. Cách làm:</u></b></span><br style=\"font-family