INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Những món ăn gây thèm trong ngày Tết Đoan Ngọ','Nhắc đến ẩm thực Ấn Độ, hẳn chúng ta sẽ nhắc đến ngay món cà ri đậm đà hương vị. Hôm nay hãy cùng khám phá một loại món ăn ngọt trứ danh ở xứ sở này là món kẹo Mithai nhé!','<p><a href=\"http://kenh14.vn/made-by-me/2-cach-bien-hoa-don-gian-cho-mon-ruou-nep-them-ngon-20130611092547772.chn\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-decoration: none; font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; color: rgb(0, 0, 205);\">Rượu nếp</span></a><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">&nbsp;chính là món ăn phổ biến nhất ngày này. Những hạt cơm nếp chín mọng, mềm mà không nát, đã lên men ngấm vị ngọt chua cay thơm nồng của rượu làm nên món rượu nếp chuẩn. Năm nay, Tết Đoan Ngọ rơi vào ngày 20/6.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-2\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\" Những hạt cơm nếp chín mọng, mềm mà không nát, đã lên men ngấm vị ngọt chua cay thơm nồng của rượu, đó mới là rượu nếp chuẩn. Rượu nếp chính là món ăn đầu tiên mọi người nghĩ đến trong ngày Tết Đoan Ngọ. \" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114925141.jpg\" style=\"max-width: 100%; cursor: none;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Ở miền Bắc có một loại rượu nếp nữa là&nbsp;</span><a href=\"http://kenh14.vn/made-by-me/hoc-cach-lam-ruou-nep-tai-nha-chang-kho-teo-nao-20116423643451.chn\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-decoration: none; font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" target=\"_blank\" title=\"rượu nếp cẩm\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; color: rgb(0, 0, 205);\">rượu nếp cẩm</span></a><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">. Món ăn được làm từ loại gạo nếp cẩm hay còn gọi là gạo nếp than (vì gạo có màu đen). So với rượu nếp trắng thông thường, món ăn dẻo, dễ chinh phục thực khách hơn.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-3\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\"\" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114925703.jpg\" style=\"max-width: 100%;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Cách chế biến rượu nếp cẩm có khác đôi chút so với rượu nếp thường nhưng cũng không quá khó, quan trọng nhất là khâu chọn gạo và chọn men. Không chỉ có trong ngày Tết Đoan Ngọ, rượu nếp cẩm giờ còn trở thành món ăn của giới trẻ khi được kết hợp cùng sữa chua, chè… tạo thành những món thú vị.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-4\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\"\" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114926717.jpg\" style=\"max-width: 100%; cursor: none;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Bánh gio được chế biến từ 2 nguyên liệu chính là gạo nếp và nước tro - loại nước được chế biến từ thảo dược hay một số loài cây - tạo nên màu sắc cũng như hương vị món ăn.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-5\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\"\" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114927325.jpg\" style=\"max-width: 100%;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Vẻ ngoài bánh gio giống như khối ngọc màu hổ phách trong vắt có thể nhìn thấu bên trong. Khi ăn, món có vị hơi ngai ngái, nồng nồng nhưng càng thưởng thức càng thấy thanh mát, dễ chịu.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-6\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\"\" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114930211.jpg\" style=\"max-width: 100%; cursor: none;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Cắt từng miếng nhỏ, rồi chấm cùng nước mật mía đặc sánh, món ăn càng ngọt mát lôi cuốn. Bánh gio thường được tìm thấy ở những gánh hàng rong với giá khoảng 7.000-10.000 đồng.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-7\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\"\" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114930788.jpg\" style=\"max-width: 100%;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Mận là quả chỉ có trong mùa hè. Mận ngon ngọt nhất là vào khoảng tháng 6. Thanh niên thường mê loại mận cơm, ngọt vừa phải nhưng ăn giòn giòn vui miệng. Những người lớn tuổi lại thường thích giống mận hậu to tròn, chín mọng đỏ au. Mận hậu cũng chính là loại quả tuyệt vời, thích hợp trong quan niệm “giết sâu bọ” của các cụ ngày xưa.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</p>\r\n\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-8\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px; line-height: 22px;\"><img alt=\"\" src=\"http://k14.vcmedia.vn/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo_20150620114931521.jpg\" style=\"max-width: 100%; cursor: -webkit-zoom-in;\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Cùi dày, hạt nhỏ, ngọt sắc - đó chính là giống vải thiều nổi tiếng của vùng đất Bắc mà du khách nào lần đầu thưởng thức cũng mê mệt. Người ta vẫn bảo vải có tính nhiệt cao. nhưng vì độ thơm ngọt, vải thiều vẫn có mặt trên mọi nẻo đường và là loại quả không thể không thưởng thức khi hè về.</span></p>\r\n','nhung-mon-an-gay-them-trong-ngay-tet-doan-ngo',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','276_1440041786_17055d54b3a7fa9d.jpg',[now], [now],[user_id],0,'','',[now]);set @news439 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam','Trong tất cả những mặt văn hóa Việt Nam, có lẽ ẩm thực là một trong số những mảng dễ tiếp cận nhất, đồng thời cũng thể hiện những nét văn hóa đặc trưng độc đáo của đất nước ta.','<p style=\"text-align: justify;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px; font-weight: bold;\">Vẻ đẹp của sự cân bằng</span><br style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n<br style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n<span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Thời đại mở cửa cho phép người Việt Nam hiện nay tiếp cận với nhiều nền ẩm thực tên thế giới. Đặc biệt tại các đô thị lớn, những dịch vụ ăn uống chuyên món nước ngoài ngày một phát triển về số lượng, quy mô lẫn độ phong phú. Ta có chợ Campuchia, có phố người Hoa, có khu ẩm thực Nhật “Little Japan”, cùng hàng loại nhà hàng tiêu chuẩn 5 sao phục vụ món Âu cao cấp. Thế nhưng, trước tình hình “tấn công” ồ ạt của các luồn văn hóa ẩm thực bên ngoài, ẩm thực bản địa Việt Nam lại không hề yếu thế. Chúng ta một ngày 3 bữa vẫn không thể thiếu cơm, và những món truyền thống như cá kho hay canh chua luôn đủ sức hấp dẫn bất kì người Việt nào. Vị trí vững chắc của ẩm thực Việt Nam trong làn sóng toàn cầu hóa và pha trộn văn hóa nằm ở bí quyết đơn giản: vừa miệng và hài hòa.</span><br style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n&nbsp;</p>\r\n\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-1\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 1\" lb=\"1\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/1-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 1\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Khi so sánh với những nền ẩm thực nước ngoài, chúng ta mới thấy rõ được giá trị của cái hài hòa vừa phải trong món ăn Việt.&nbsp;</span></p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: justify;\">Ẩm thực Trung Hoa – nền ẩm thực được coi là có ảnh hưởng đến ẩm thực Việt – thực chất vẫn luôn cho cảm giác bị quá nhiều dầu mỡ. Người Hoa ưa dùng dầu mỡ trong các món ăn, không chỉ công thức chiên xào mà cả hầm, nướng cũng có lượng chất béo rất cao. Mặt khác, chế độ phong kiến với lối sinh hoạt xa hoa đã tạo ra đặc trưng sử dụng quá nhiều nguyên liệu quý hiếm, bổ dưỡng vào món ăn, không chỉ gây ngán ngấy cho thực khách mà đôi khi còn phản tác dụng, làm bội thực, ảnh hưởng đến sức khỏe.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-2\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 2\" lb=\"2\" src=\"http://k14.vcmedia.vn/k:kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2014/10/bd97a9246247d496b735c3b51330193c-d3e1e/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 2\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Người Việt không dùng dầu mỡ nhiều, cũng không sử dụng những nguyên liệu quý hiếm như vi cá, bào ngư hay chuột bao tử. Ẩm thực Việt Nam chinh phục người ta bằng sự thanh đạm, vừa phải trong hương vị lẫn lượng dinh dưỡng từ các nguyên liệu.\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">&nbsp;\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-3\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 3\" lb=\"3\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/2-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 3\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\">Lại nói về hương vị và sự gia giảm, nêm nếm trong nấu ăn, ẩm thực Việt cũng thể hiện đặc trưng hài hòa của mình với việc cân bằng rất tốt chua – cay – mặn – ngọt. Không có một vị nào chiếm thế áp đảo trong đĩa thức ăn của người Việt. Canh chua có cái cay của ớt, nhưng cũng có cái ngọt của dứa và chua của me hoặc giấm, mẻ cùng kết hợp, tạo nên sự đồng đều và vừa phải.&nbsp;</p>\r\n\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-4\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 4\" lb=\"4\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/3-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 4\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\">Trong tương quan so sánh với các nước Châu Âu – nơi được xem là ông lớn của ẩm thực thế giới với bề dày lịch sử nấu ăn quý tộc, ẩm thực Việt Nam vẫn có những nét vượt trội của nó. Trong khi món ăn Pháp nổi tiếng với nguyên tắc giữ trọn hương vị tươi ngon của nguyên liệu, việc nêm nếm hầu như dựa trên ba loại gia vị cơ bản là muối – tiêu – đường, thì gia vị Việt Nam lại đậm đà và phong phú hơn nhiều. Miếng bít tết của người Pháp có thể rất hấp dẫn nhờ cái ngọt nhè nhẹ của thịt bò tái, nhưng cũng “kẻ tám lạng, người nửa cân” khi so với bò lá lốt mỡ chài hay bún bò Huế đậm đà của người Việt.</p>\r\n\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-5\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 5\" lb=\"5\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/4-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 5\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold;\">Triết lý phối hợp trong món ăn</span><br />\r\n<br />\r\nKhông nổi tiếng với những nguyên liệu quý hiếm, đắt đỏ hoặc quá “độc”, bữa ăn người Việt được xây dựng trên nền tảng thực phẩm giản dị, bình dân nhưng phong phú. Phối hợp nhiều loại đạm, chất xơ và đặc biệt là gia vị, món ăn Việt Nam trở nên hài hòa và ngon miệng lạ thường.&nbsp;</p>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: justify;\">Đặc biệt ở gia vị, chúng ta có hàng loạt thể loại khác nhau như hành, rau mùi, tía tô, thì là, kinh giới hay sản phẩm lên men với mẻ, dấm, bỗng rượu; từ gia vị thực vật như tỏi, sả, riềng, gừng đến gia vị từ động vật như hàng chục loại mắm và nước chấm. Chính sự đa dạng và kết hợp linh hoạt giữa các loại gia vị trên đã tạo ra hương vị rất vừa phải, không quá ngọt, béo, cay hay mặn cho ẩm thực Việt Nam.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-6\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 6\" lb=\"6\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/5-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 6\" /></div>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</div>\r\n\r\n<p style=\"text-align: justify;\">Ưu điểm hài hòa của ẩm thực Việt còn đến từ tư tưởng truyền thống âm dương cân bằng. Với âm đại diện cho cái tối tăm, mềm mại, thụ động, còn dương là tươi sáng, cứng rắn, chủ động, triết lý này cho rằng mọi sự trên đời đều có âm có dương, và hai yếu tố đó luôn hòa quyện với nhau làm nên bản chất tồn tại của thế giới.&nbsp;</p>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: justify;\">Ăn uống cũng không nằm ngoài nguyên lý này. Một món ăn hay một mâm cơm của người Việt chứa đựng trong mình những giá trị triết học sâu sắc với âm dương phối triển và ngũ hành tương sinh – hai nguyên tắc kết hợp trong nấu nướng nhằm đạt đến vẻ đẹp hài hòa theo đúng tiêu chuẩn mỹ học của người Việt.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-7\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 7\" lb=\"7\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/6-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 7\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\">Cụ thể, một món ăn phải chứa đựng cả hai trạng thái âm dương cho cần bằng. Nguyên liệu chính có tính lạnh (âm) như trứng vịt lộn phải đi cùng với rau thơm có tính nóng (dương) như rau răm, thịt vịt mang tính lạnh (âm) phải có gừng mang tính nóng (dương) mới ngon. Trên bình diện rộng hơn là mâm cơm hàng ngày, quy tắc âm – dương này càng thể hiện rõ nét: món kho, rán, hoặc nướng mang vị mặn, kết cấu sệt, khô (dương) đã có món canh rau thanh mát, dạng nước lỏng (âm) cân bằng lại.&nbsp;</p>\r\n\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-8\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 8\" lb=\"8\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/7-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 8\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\">Đi sâu một chút nữa vào nguyên tắc phối triển nguyên liệu và phương thức nấu nướng của người Việt, ta sẽ bất ngờ khi biết đến luật tương sinh Ngũ hành khá nghiêm ngặt trong nấu ăn. Ngũ hành sinh ra từ Âm Dương, đại diện cho 5 trạng thái luân phiên thay đổi của vũ trụ: Kim – Mộc – Thủy – Hỏa – Thổ. Từng cặp yếu tố trong Ngũ hành nếu kết hợp đúng sẽ tương sinh – hỗ trợ và nâng đỡ lẫn nhau, trái lại sẽ gây ra tương khắc – triệt tiêu lẫn nhau. Trong nấu ăn, người Việt rất chú trọng đến việc lựa chọn từng cặp nguyên liệu và gia vị thích hợp sao cho đạt đến trạng thái Ngũ hành tương sinh, đưa hương vị và tác dụng bổ dưỡng của món ăn đến giá trị cao nhất của nó.</p>\r\n\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-9\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 9\" lb=\"9\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/8-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: none;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 9\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\">Người Việt đã phân loại vị chua, chất bột và phần mật động vật vào Mộc, vị đắng, chất béo và lòng non vào Hỏa, vị ngọt, chất đạm và dạ dày vào Thổ, vị cay, muối khoáng và lòng già vào Kim, cuối cùng vị mặn, chất lỏng và thận thuộc về Thủy. Hảo hợp Thổ, nên những nguyên liệu có vị ngọt tự nhiên như cá lóc đồng rất ăn ý với rau đắng.&nbsp;</p>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: justify;\">Món ngon của người Việt đôi khi còn là sự tổng hòa cả 5 yếu tố của trời đất, vừa truyền tải trọn vẹn mọi sắc độ phong phú của hương vị tự nhiên, vừa cân bằng giá trị dinh dưỡng trong cơ thể. Trong một bát phở, ta thấy có sự kết hợp của mọi chất liệu, mùi, vị, màu sắc: thịt bò nạc (Thổ), nạm gàu béo (Hỏa), nước dùng mặn (Kim), cái cay của dấm ớt va gừng (Thủy), cùng cái chua của chanh tươi vắt trên mặt (Mộc).&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div align=\"center\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-10\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 10\" lb=\"10\" src=\"http://k14.vcmedia.vn/k:oIR8yoccccccccccccZPjUHu5lEQqW/Image/2014/10/9-a946d/dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam.jpg\" style=\"max-width: 640px; cursor: -webkit-zoom-in;\" title=\"Điều tuyệt vời khiến bạn tự hào về ẩm thực Việt Nam 10\" /></div>\r\n<br />\r\n&nbsp;</div>\r\n\r\n<p style=\"text-align: justify;\">Dù không mang những yếu tố hào nhoáng, sang trọng hoặc quá độc đáo kì thú, nhưng ẩm thực Việt Nam vẫn “ghi điểm” nhờ vị ngon hài hòa của những điều giản dị. Ẩn chứa trong mình những giá trị triết học sâu sắc mang đậm tinh thần Á Châu, món ăn Việt ngày nay vẫn luôn là lựa chọn thích hợp nhất cho chúng ta – dân tộc với tính cách ngàn đời vẫn luôn quý trọng cái hòa hợp, bình ổn giữa con người và thiên nhiên.</p>\r\n</div>\r\n</div>\r\n','dieu-tuyet-voi-khien-ban-tu-hao-ve-am-thuc-viet-nam',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','517_1440041574_47555d54a66d9269.jpg',[now], [now],[user_id],0,'','',[now]);set @news438 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Bụng đói cồn cào vì bản đồ các món ăn ngon nhất Hàn Quốc','Mỗi địa danh khác nhau lại mang trong mình những món ăn đặc trưng rất khác biệt đó! Hãy cùng Ẩm thực 24h khám phá những món ăn ngon và lạ miệng này nhé','<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-family: Tahoma; font-weight: bold; color: rgb(128, 0, 0);\">1. Canh xương bò, Seoul:</span></p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Là thủ đô của đất nước Hàn Quốc đồng thời cũng là một trung tâm văn hóa - kinh tế lớn của xứ sở kim chi, không có gì ngạc nhiên khi thành phố Seoul mang trong mình sự giao thoa ảnh hưởng phong cách ẩm thực của các vùng lân cận vào trong các món ăn địa phương.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Song, có một món ăn bạn nhất định phải thử qua khi đến thăm Seoul, đó là Seolleongtang – món canh xương bò hầm với phần nước hầm trắng sữa, nóng hổi và ngọt lừ vị xương thịt được ninh chín mềm.&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Theo truyền thuyết, món canh này là 1 phần trong nghi lễ mùa xuân ở triều đại Joseon để tôn vinh nghề nông. Buổi lễ này thường bắt đầu bằng việc hiến tế 1 con bò cho Dangun (người đầu tiên sáng lập ra vương triều Hàn Quốc) rồi sau đó nấu nó thành món súp với yêu cầu phải tận dụng được hết tất cả con bò và có thể chia cho nhiều người.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-1\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Seolleongtang-Seoul-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/Seolleongtang-Seoul-d3e46.jpg\" style=\"max-width: 100%; cursor: none;\" w=\"600\" /></div>\r\n</div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Món canh này yêu cầu thời gian nấu khá dài, trung bình từ 10 tiếng trở lên để phần xương bò được ninh thật nhừ, nước hầm chuyển từ trong sang trắng sữa và thịt bò tơi ra. Kim chi củ cải và mì trắng luôn là món ăn kèm được dọn ra cùng với canh xương bò hầm.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<hr /></div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-family: Tahoma; font-weight: bold; color: rgb(128, 0, 0);\">2. Canh \"đồ thừa\", tỉnh Gyeonggi:</span></div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Món ăn này có tên là Uijeongbu budaejjigae (món “soup quân đội”) hay “Johnson tang” (“tang” trong tiếng Hàn Quốc có nghĩa là “món canh”, còn Johnson là tên của vị tổng thống Mỹ Lyndon B. Johnson – người đã ca ngợi món canh súp này trong suốt chuyến công du sang Hàn Quốc). Sở dĩ nó có tên như vậy vì món hầm này được ra đời trong thời kỳ chiến tranh Triều Tiên, khi mà đời sống người dân vô cùng khổ cực và nguồn thực phẩm thì rất khan hiếm. Chính vì thế nên “trong cái khó ló cái khôn”, người dân nơi đây đã sáng tạo ra món hầm này bằng cách tận dụng phần thức ăn thừa của các doanh trại Mỹ (xúc xích, thịt nguội, phô mai...) và thêm vào các nguyên liệu địa phương như bánh gạo, mì ramen, rau, kimchi, tương ớt....&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-2\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Uijeongbu-budaejjigae-Gyeonggi-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/Uijeongbu-budaejjigae-Gyeonggi-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n<br />\r\nĐây không chỉ là một món ăn có hương vị kết hợp hài hòa Á – Âu mà còn là một biểu tượng nhắc nhớ đến khoảng thời gian khó khăn, gian khổ cũng như tinh thần mạnh mẽ mà người Hàn Quốc đã trải qua để có được cuộc sống như ngày hôm nay.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<hr /></div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">3. Rượu gạo, tỉnh Gyeonggi:</span></div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Pocheon makgeolli&nbsp;là một loại rượu gạo khá nổi tiếng ở Hàn Quốc với vị chua dịu, ngọt nhẹ và dễ dàng nhận ra bởi màu sắc trắng kem. Ta có thể tìm thấy rượu makgeolli ngon trên khắp đất nước Hàn Quốc, tuy nhiên, mỗi vùng miền sẽ có những phương pháp lên men rược khác nhau.</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-3\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"pocheon-makgeolli-Gyeonggi-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/pocheon-makgeolli-Gyeonggi-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<hr style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">4. Gà xào bánh gạo chua cay và sườn nướng, tỉnh Gangwon:</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Vùng Chuncheon thuộc tỉnh Gangwon, Hàn Quốc được biết đến với 2 món ăn vô cùng nổi tiếng đó là:&nbsp;<b>Dakgalbi</b>&nbsp;và&nbsp;<b>Makguksu</b>.</p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><b>Dakgalbi</b>&nbsp;là một món khá bình dân và có giá thành rẻ, nhưng hương vị lại vô cùng hấp dẫn. Nó bao gồm phần thịt lườn gà nướng trên chảo lớn nóng hổi cùng với sốt gochujang (một loại sốt tương ớt chua cay) cùng với bánh gạo, bắp cải, khoai lang, cà rốt thái lát mỏng và lá tía tô. Hương vị cay nồng, chua ngọt quyện trong từng miếng lườn gà chín mềm của món ăn này quả thật rất tuyệt vời.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-4\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"chuncheon-dakgalbi-makguksu-Gangwon-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/chuncheon-dakgalbi-makguksu-Gangwon-d3e46.jpg\" style=\"max-width: 100%; cursor: none;\" w=\"600\" /></div>\r\n</div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Có thể nói&nbsp;<b>Makguksu</b>&nbsp;là một đặc sản của vùng Chuncheon mà bạn nhất định không thề bỏ qua khi đến thăm nơi đây. Tương tự như món mì lạnh, song trong món Makguksu, người ta sử dụng mì kiều mạch ướp lạnh, ăn kèm với kim chi và có thể trộn thêm đường, mù tạt, giấm hoặc dầu mè để tăng thêm hương vị thay vì ăn cùng với nước dùng thịt bò hay nước dùng nấu từ rau ướp lạnh.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-5\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"Makguksu-d3e46\" h=\"394\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/Makguksu-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">&nbsp;</div>\r\n\r\n<hr /></div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">5. Dồi, phía Nam tỉnh Chungcheong:</span></div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Byeongcheon Sundae này của người Hàn Quốc khá giống với món dồi của người Việt mình, ở những công thức lược giản thì nó được nhồi từ tiết lợn hoặc bò. Trong 1 số công thức đúng chuẩn truyền thống khác thì nó được nhồi dangmyeon, lúa mạch và tiết lợn, thỉnh thoảng có sự góp mặt của lá tía tô, hành lá, doenjang, gạo nếp, kim chi, đậu tương mầm... Sundae thường được ăn kèm với sốt hoặc tiết canh lợn. Byeongcheon sundae của tỉnh Chungcheong nổi danh bởi hương vị thơm ngon, phần tiết nhồi bên trong đặc, mềm, vỏ ngoài dai dai rất hấp dẫn.</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-6\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"byeongcheon-sundae-south-chungcheong-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/byeongcheon-sundae-south-chungcheong-d3e46.jpg\" style=\"max-width: 100%; cursor: none;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<hr style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">6. Cơm trộn, phía Bắc Jeolla:</span></p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Nếu muốn thưởng thức trọn vẹn hương vị của món cơm trộn Bibimbap lừng danh thì bạn nhất định phải tìm đến thành phố Jeonju, quê hương của món ăn này. Với sự sắp xếp hài hòa từ màu sắc đến thành phần nguyên liệu trong thố cơm, món Bibimbap không chỉ chinh phục thực khách bằng hương vị thơm ngon mà còn bởi vẻ ngoài ngăn nắp, rực rỡ màu sắc rất bắt mắt.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-7\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"bibimbap-north-jeolla-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/bibimbap-north-jeolla-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">&nbsp;</div>\r\n\r\n<hr /></div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">7. Cơm nấu ống tre và sườn nướng, phía Nam Jeolla:</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Damyang được biết đến như là xứ xở tre xanh của đất nước Hàn Quốc, thế nên, sẽ là thật thiếu sót khi nhắc đến ẩm thực ở Damyang mà không nhắc đến&nbsp;<b>Deatongbap</b>&nbsp;và&nbsp;<b>Ddeokgalbi</b>.</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><b>Deatongbap</b>&nbsp;là món cơm nấu trong ống tre cùng với các loại hạt như: hạt dẻ, táo tàu, hạt thông, đậu... mang hương vị thơm ngon và khá đặc biệt.</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-8\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"daetongbap-south-jeolla-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/daetongbap-south-jeolla-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<p><br style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n<b style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">Ddeokgalbi</b><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\">&nbsp;là món sườn nướng cực kỳ đặc biệt của xứ Damyang. Thay vì dùng sườn miếng như bình thường, Ddeokgalbi là phần sườn được băm nhỏ, tẩm, ướp gia vị kỹ càng rồi nướng (hoặc rán) thành miếng nhỏ như miếng chả.</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-9\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"tteokgalbi-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/tteokgalbi-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px; text-align: center;\">&nbsp;</div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</p>\r\n\r\n<hr style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 20px;\" />\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">8. Cháo bào ngư, đảo Jeju:</span></p>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Jeonbokjuk là một món cháo khá đặc biệt được nấu từ ruột bào ngư thái lát, dầu mè và gạo tẻ. Món cháo này có màu hơi xanh lơ, khá đắt tiền nhưng lại đặc biệt giàu dinh dưỡng và rất tốt cho sức khỏe.</p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-10\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"jeonbokjuk-Jeju-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/jeonbokjuk-Jeju-d3e46.jpg\" style=\"max-width: 100%;\" w=\"600\" /></div>\r\n</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">&nbsp;</div>\r\n\r\n<hr /></div>\r\n\r\n<p style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\"><span style=\"outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-family: Tahoma; color: rgb(128, 0, 0);\">9. Tôm xào cay, phía Nam Gyeongsang:</span></p>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">Masan agujjim&nbsp;là món tôm xào cay với tương ớt và giá đỗ đặc sản của vùng Masan. Có 1 điều thú vị là vào khoảng thời gian những năm trước 1940, tôm tươi không được coi là 1 loại hải sản có giá trị cao và thường bị người dân nơi đây bỏ đi đấy. Mãi sau này, khi nguồn hải sản bắt đầu khan hiềm hơn, người dân nơi đây mới sử dụng tôm như một nguyên liệu chính khi chế biến món ăn và cho ra món agujjim đặc biệt ngon lành này.</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">&nbsp;</div>\r\n\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: \'Times New Roman\'; font-size: 16px;\">\r\n<div style=\"outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;\">\r\n<div class=\"warpt-share-fbPhoto\" id=\"btn-share-img-11\" style=\"outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block; width: 642px;\"><img alt=\"agujjim-d3e46\" h=\"336\" src=\"http://k14.vcmedia.vn/thumb_w/600/71bbe627e7/2015/08/14/agujjim-d3e46.jpg\" style=\"max-width: 100%; cursor: -webkit-zoom-in;\" w=\"600\" /></div>\r\n</div>\r\n</div>\r\n','bung-doi-con-cao-vi-ban-do-cac-mon-an-ngon-nhat-han-quoc',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','297_1440041062_41955d548669d08f.jpg',[now], [now],[user_id],0,'','',[now]);set @news437 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories298,'Tôm rang me thơm ngon - thưởng thức là nghiền','Món tôm rang me có màu sắc vô cùng ngon mắt, cách làm lại đơn giản và rất nhanh; không chỉ vậy nó còn chinh phục bạn bởi vị ngọt tự nhiên của tôm và vị chua ngọt dịu nhẹ của lớp xốt bám bên ngoài cùng mùi thơm hấp dẫn của rau ngò.','<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Món tôm rang me có màu sắc vô cùng ngon mắt, cách làm lại đơn giản và rất nhanh; không chỉ vậy nó còn chinh phục bạn bởi vị ngọt tự nhiên của tôm và vị chua ngọt dịu nhẹ của lớp xốt bám bên ngoài cùng mùi thơm hấp dẫn của rau ngò.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-14_60153.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<div class=\"ads-ct\" style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular;\">\r\n<center style=\"margin: 0px; padding: 0px;\">\r\n<div style=\"margin: 0px; padding: 7px 0px;\">&nbsp;</div>\r\n</center>\r\n</div>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Nguyên liệu:</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-1_6868b.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">300gr tôm - rửa sạch với nước muối pha loãng rồi&nbsp;bóc vỏ, rút chỉ đen trên lưng tôm, để dành lại đuôi tôm cho đẹp.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">1 vắt me khô hoặc 1 quả me tươi</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Hành lá và rau ngò (rau thơm)&nbsp;rửa sạch, thái nhỏ</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Nước mắm, đường, muối, tiêu, hạt điều tạo màu và tỏi.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-2_325ef.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Bước 1:</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Ướp tôm với&nbsp;nửa thìa cà phê muối cùng chút&nbsp;hạt tiêu trong khoảng 15 phút.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-3_b7aab.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Bước 2:</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Me đổ ra bát, dầm với ba thìa súp nước nóng cho me tan thì&nbsp;gạn lấy nước cốt me, bỏ hột me đi.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-4_e24fb.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Tỏi bóc vỏ, thái nhỏ hoặc giã nhuyễn.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-5_685de.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Bước 3:</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Làm&nbsp;nóng hai thìa cà phê dầu ăn trong nồi hoặc chảo, đổ ít hạt điều vào đảo khoảng 2 phút để tạo màu cho đẹp.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-6_b0f81.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Vớt hạt điều bỏ đi, cho&nbsp;tỏi vào phi&nbsp;thơm.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-7_99ecd.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Đổ tôm vào xào chín rồi cho ra đĩa, để riêng.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-8_44b46.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Bước 4:</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Vẫn dùng chảo đã xào tôm, bạn&nbsp;đổ nước cốt me vào, thêm vào khoảng hai thìa cà phê nước mắm và&nbsp;hai thìa cà phê đường.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-9_85dcf.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Đun sôi đến khi nước xốt me sánh đặc thì bạn nêm nếm lại tùy theo khẩu vị, nêm hơi ngọt ngọt, chua chua; thêm ớt bột hoặc&nbsp;ớt tươi xắt lát&nbsp;nếu bạn thích&nbsp;ăn cay.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-10_0e605.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Bước 5:</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Nhanh tay đổ tôm vào đảo cho nước xốt bám đều lên&nbsp;tôm.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-11_52ba9.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Thêm hành lá và rau ngò đã thái nhỏ vào đảo đều rồi&nbsp;tắt bếp.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\"><img alt=\"\" src=\"http://afamily1.vcmedia.vn/Qalypm8xccccccccccccW2vZ1VroR/Image/2012/03/120328afamily-AN-tom-rang-me-12_4e3c2.JPG\" style=\"padding: 0px; border: none; outline: none; display: block; max-width: 100%; margin: 10px auto !important; width: auto !important;\" /></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Lấy tôm ra đĩa, dùng nóng với cơm.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Món tôm rang me có màu sắc vô cùng ngon mắt, cách làm lại đơn giản và rất nhanh; không chỉ vậy nó còn chinh phục bạn bởi vị ngọt tự nhiên của tôm và vị chua ngọt dịu nhẹ của lớp xốt bám bên ngoài cùng mùi thơm hấp dẫn&nbsp;của rau ngò.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Chừng đó có lẽ đã quá đủ lý do để giải thích vì sao đây là món ăn thường xuyên xuất hiện trong thực đơn gia đình mình - mình thì thích vì nấu nó rất nhanh, còn các thành viên trong nhà thì dĩ nhiên là bởi vị ngon khó chối từ của nó, nhất là khi ăn với cơm.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; font-family: HelveticaRegular; color: rgb(34, 34, 34); line-height: 19px; text-align: justify; font-size: 15px !important;\">Bé nhà mình bình thường khá kén ăn, vậy mà mỗi lần mẹ làm món này thì bé ăn ngoan và tự giác hơn rất nhiều, không cần mẹ phải dỗ dành nữa.</p>\r\n','tom-rang-me-thom-ngon---thuong-thuc-la-nghien',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','336_1439967034_96455d4273a5200f.jpg',[now], [now],[user_id],0,'','',[now]);set @news432 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories298,'Tôm nướng sốt thì là ','Tôm nướng sốt thì là là món ăn thơm ngon, bổ dưỡng, cung cấp nhiều đạm, protein cho cơ thể, đem lại cảm giác ngon miệng cho người ăn. Đây là món ăn thường hay có trong thực đơn của các nhà hàng lớn. Hãy cùng nhau học cách chế biến món ăn này tại nhà nhé.','<p style=\"border: 0px; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; margin: 0px 0px 1.5em; outline: 0px; padding: 0px; vertical-align: baseline; line-height: 21px; background-color: rgb(254, 255, 248);\"><span style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-decoration: underline;\">Nguyên liệu:</span>:</p>\r\n\r\n<ul style=\"border: 0px; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; margin: 0px 0px 1.5em 1.5em; outline: 0px; padding-right: 0px; padding-left: 0px; vertical-align: baseline; list-style: square; line-height: 21px; background-color: rgb(254, 255, 248);\">\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Tôm to</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Thì là</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Hành tây</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Bơ</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Kem tươi</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Sữa tươi</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Rượu vang trắng</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Măng tây</li>\r\n</ul>\r\n\r\n<p style=\"border: 0px; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; margin: 0px 0px 1.5em; outline: 0px; padding: 0px; vertical-align: baseline; line-height: 21px; background-color: rgb(254, 255, 248);\"><span style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-decoration: underline;\">Cách làm:</span></p>\r\n\r\n<ol style=\"border: 0px; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; margin: 0px 0px 1.5em 1.5em; outline: 0px; padding-right: 0px; padding-left: 0px; vertical-align: baseline; line-height: 21px; background-color: rgb(254, 255, 248);\">\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Tôm bóc vỏ để đầu và đuôi, rạch lưng ướp muối tiêu</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Rán tôm&nbsp;áp chảo với bơ</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Nước sốt: cho bơ đun chảy, cho 1/2 củ&nbsp;hành tây&nbsp;băm nhỏ&nbsp;vào phi thơm, cho ½ muỗng canh bột mì vào xào qua không để vàng, cho nước dùng, sữa tươi, 1 muỗng canh vang trắng, 1 muỗng canh kem tươi, nêm vừa gia vị. Cho thì là thái nhỏ vào sốt</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Cho tôm vào sốt đun khoảng 1 phút bắc ra</li>\r\n	<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">Măng tây tước vỏ&nbsp;luộc qua với chút muối sôi khoảng 1 phút, bày ra đĩa, đổ tôm lên trên.</li>\r\n</ol>\r\n','tom-nuong-sot-thi-la',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','487_1439966457_69855d424f9a6c3a.jpg',[now], [now],[user_id],0,'','',[now]);set @news431 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories298,'Dồi ếch đồng - thơm ngon - chống ngán','Với hương vị thơm ngon, các món ăn chế biến từ ếch đồng là sở thích của nhiều người. Từ ếch có thể chế biến thành rất nhiều món ăn, tuy nhiên, vào những ngày nắng nóng, việc kết hợp thịt ếch với những nguyên liệu khác nhau để chế biến sẽ làm cho món ăn này trở nên hấp dẫn hơn. Dồi ếch đồng là một trong những món ăn được nhiều người ưa thích và lựa chọn.','<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Với hương vị thơm ngon, các món ăn chế biến từ ếch đồng là sở thích của nhiều người. Từ ếch có thể chế biến thành rất nhiều món ăn, tuy nhiên, vào những ngày nắng nóng, việc kết hợp thịt ếch với những nguyên liệu khác nhau để chế biến sẽ làm cho món ăn này trở nên hấp dẫn hơn. Dồi ếch đồng là một trong những món ăn được nhiều người ưa thích và lựa chọn.<br />\r\n————————————————-<br />\r\nTrung tâm Ẩm Thực 24h<br />\r\n9A3 ngõ 7 Kim Mã Thượng, Ba Đình, Hà Nội<br />\r\nHotline: 0913 344 441</span></span></p>\r\n','doi-ech-dong---thom-ngon---chong-ngan',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','97_1439966256_58155d424305bc06.jpg',[now], [now],[user_id],0,'','',[now]);set @news426 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories298,'Đổi vị với món kem mận cơm','Vào những ngày nắng nóng, thì kem là món khoái khẩu của rất nhiều người. Việc sáng tạo, kết hợp các nguyên liệu khác nhau khi làm kem tại nhà sẽ làm cho món ăn trở nên ngon miệng, hấp dẫn và độc đáo hơn – đó là món kem mận cơm. Kem mận cơm – với nguyên liệu chính là mận còn rất giàu vitamin và khoáng chất. ','<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Vào những ngày nắng nóng, thì kem là món khoái khẩu của rất nhiều người. Ngoài việc mua kem bán sẵn, hiện nay, nhiều bà nội trợ có xu hướng tự làm kem ở nhà. Việc sáng tạo, kết hợp các nguyên liệu khác nhau khi làm kem tại nhà sẽ làm cho món ăn trở nên ngon miệng, hấp dẫn và độc đáo hơn – đó là món kem mận cơm. Không chỉ là món ăn lạ miệng, hấp dẫn, kem mận cơm – với nguyên liệu chính là mận còn rất giàu vitamin và khoáng chất.&nbsp;<br />\r\n————————————————-<br />\r\nTrung tâm Ẩm Thực 24h<br />\r\n9A3 ngõ 7 Kim Mã Thượng, Ba Đình, Hà Nội<br />\r\nHotline: 0913 344 441</span></span></p>\r\n','doi-vi-voi-mon-kem-man-com',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','241_1439965763_31955d42243e4e93.jpg',[now], [now],[user_id],0,'','',[now]);set @news425 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories298,'Tự làm kem chuối giải khát ngày nóng','Món kem mát lạnh là lựa chọn hàng đầu của nhiều người khi thời tiết nắng nóng. Không chỉ giải nhiệt, xua tan nóng bức, kem cũng là món ăn cung cấp năng lượng cho cơ thể. Ngoài việc mua sẵn kem, bằng những nguyên liệu và dụng cụ đơn giản, người nội trợ có thể tự làm kem tại nhà.<br />\r\n','<p>Món kem mát lạnh là lựa chọn hàng đầu của nhiều người khi thời tiết nắng nóng. Không chỉ giải nhiệt, xua tan nóng bức, kem cũng là món ăn cung cấp năng lượng cho cơ thể. Ngoài việc mua sẵn kem, bằng những nguyên liệu và dụng cụ đơn giản, người nội trợ có thể tự làm kem tại nhà.<br />\r\n—————————————-­———<br />\r\nTrung tâm Ẩm Thực 24h<br />\r\n9A3 ngõ 7 Kim Mã Thượng, Ba Đình, Hà Nội<br />\r\nHotline: 0913 344 441</p>\r\n','tu-lam-kem-chuoi-giai-khat-ngay-nong',1,'','',[site_id],[user_id],'/media/images/138/news/ava/','62_1439965527_56555d42157149c1.png',[now], [now],[user_id],0,'','',[now]);set @news424 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Gia sư tại quận Cầu Giấy - Hà Nội','Nhằm đáp ứng nhu cầu về học tập phát triển kiến thức cho các em học sinh Công ty giáo dục và đào tạo Đức Minh cung cấp đội ngũ gia sư giỏi, uy tín, chuyên nghiệp tại quận Cầu Giấy.','<p>Nhằm đáp ứng nhu cầu về học tập phát triển kiến thức cho các em học sinh Công ty giáo dục và đào tạo Đức Minh cung cấp đội ngũ gia sư giỏi, uy tín, chuyên nghiệp tại quận Cầu Giấy.<br />\r\n&nbsp;<br />\r\nGia sư tại quận Cầu Giấy – Cầu Giấy là trung tâm kinh tế, văn hóa, chính trị của thành phố, trình độ dân trí và giáo dục cao nơi &nbsp;tập trung các trường đại học hàng đầu trong cả nước như đại học quốc &nbsp;gia, đại học ngoại ngữ, đại học sư phạm, báo chí tuyên truyền. Chính vì thế công tác giáo dục và đào tạo ở đây luôn được đặt lên vị trí hàng đầu, các dịch vụ về giáo dục, tư vấn giáo dục, lớp học tập trung, dày kèm, gia su tại nhà rất phát triển, ngày càng có nhu cầu cao.<br />\r\nNhằm đáp ứng nhu cầu về học tập phát triển kiến thức cho các em học sinh Công ty giáo dục và đào tạo Đức Minh cung cấp đội ngũ gia sư giỏi, uy tín, chuyên nghiệp tại quận Cầu Giấy. Gia sư của chúng tôi phần lớn là các bạn sinh viên của trường sư phạm hà nội, ngoài ra còn có các bạn sinh viên của các trường đại học hàng đầu cả nước như ngoại thương, kinh tế quốc dân, đặc biệt đội ngũ gia sư chất lượng &nbsp;cao nhất bao gồm các bạn thạc sĩ ngành sư phạm, các giáo viên nhiều năm kinh nghiệm. Tuy theo nhu cầu của từng gia đình chúng tôi sẽ tư vấn những giáo viên phù hợp nhất về kiến thức chuyên môn cũng như khả năng sư phạm phù hợp giúp các em tiến bộ nhanh về kiến thức, đạt được kết quả học tập như mong muốn .<br />\r\n&nbsp;<br />\r\nTại sao dịch vụ gia sư của công ty Đức Minh lại uy tín, chuyên nghiệp?<br />\r\n&nbsp;<br />\r\nTất cả các giáo viên, gia sư của Trung tâm gia sư Đức Minh khi giới thiệu đến gia đình đều có hồ sơ giới thiệu đi kèm có đầy đủ các thông tin về giáo viên dạy kèm bao gồm nơi đang học tập, công tác, trình độ chuyên môn, các giải thưởng cá nhân đạt được như các giải quốc gia, giải giáo viên dạy giỏi...<br />\r\nCác giáo viên 100% đều có lý lịch rõ ràng có đạo đức tốt giúp các em phát triển đồng đều cả về kiến thức, đạo đức con người, cách sống, ứng xử với mọi người.<br />\r\n&nbsp;<br />\r\nDịch vụ gia sư của Đức Minh đa dạng về các môn học:<br />\r\n&nbsp;<br />\r\nDạy kèm từ lớp 1 đến lớp 12 và luyện thi đại học tất cả các môn tại khu vực quận Cầu Giấy<br />\r\nLuyện thi cấp tốc các chứng chỉ tiếng anh: Toiec, Ielts, toefl<br />\r\nDạy kèm anh văn căn bản và nâng cao, anh văn thiếu nhi, AV cho người nước ngoài<br />\r\nKèm bai cho các em chuẩn bị vào lớp 1, rèn viết chữ đẹp<br />\r\nCác môn : Vẽ, Đàn, Nhạc...<br />\r\nCác ngoại ngữ : Hoa, Hàn, Nhật, Pháp..<br />\r\nLuyện thi cấp tốc các chứng chỉ tiếng anh: Toiec, Ielts, toefl<br />\r\nDạy kèm anh văn căn bản và nâng cao, anh văn thiếu nhi<br />\r\nKèm bai cho các em chuẩn bị vào lớp 1, rèn viết chữ đẹp<br />\r\nCác môn : Vẽ, Đàn, Nhạc...<br />\r\nCác ngoại ngữ : Hoa, Hàn, Nhật, Pháp..<br />\r\nCác lớp đặc biết: Dạy Tiếng Việt cho người nước ngoài, dạy các môn học bằng tiếng Anh..<br />\r\nMọi nhu cầu tim gia su tại khu vực quận Cầu Giấy quí phụ huynh và các bạn hãy liên lạc với chúng tôi &nbsp;tại &nbsp;đây<br />\r\nNếu các bạn sinh viên có nhu cầu làm gia su, giáo viên dạy kèm hãy liên hệ với chúng tôi tại &nbsp;đây để nhận lịch phỏng vấn.</p>\r\n','gia-su-tai-quan-cau-giay---ha-noi',1,'','',[site_id],[user_id],'/media/images/111/news/ava/','436_1433241044_484556d85d42ba14.jpg',[now], [now],[user_id],0,'','',[now]);set @news393 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Gia sư tại quận Đống Đa - Hà Nội','Tất cả các bậc phụ huynh tại khu vực quận Đống Đa có nhu cầu tim gia su giỏi hay các bạn sinh viên đang sinh sống và học tập tại khu vực quận Đống Đa – Hà nội có nhu cầu làm gia sư, dạy kèm hãy liên hệ với chúng tôi ','<p>Tất cả các bậc phụ huynh tại khu vực quận Đống Đa có nhu cầu tim gia su giỏi hay các bạn sinh viên đang sinh sống và học tập tại khu vực quận Đống Đa – Hà nội có nhu cầu làm gia sư, dạy kèm hãy liên hệ với chúng tôi :<br />\r\n&nbsp;<br />\r\nGia sư quận Đống Đa – Thành phố Hà Nội . Công ty giáo dục và đào tạo Đức Minh cung cấp các&nbsp; dịch vụ về giáo dục đào tạo, tư vấn giáo dục cho học sinh tiểu học và phổ thông. Hiện nay công ty đã triển khai&nbsp; dịch vụ gia sư trên tất cả các quận thuộc địa bàn thành phố hà nội. Đội ngũ gia sư quận Đống Đa được tuyển chọn kĩ lưỡng từ các bạn sinh viên và thầy cô có kinh nghiệm giảng dạy, có trình độ chuyên môn và khả năng sư phạm rất tốt. Chúng tôi dám cam kết chất lượng dạy kèm của đội ngũ gia sư của chúng tôi là tốt nhất (Dạy kèm 3 buổi miễn phí, nếu các bậc phụ huynh không ưng ý chúng tôi sẽ không thu bất kì một khoản phí nào)<br />\r\n&nbsp;<br />\r\nDịch vụ gia sư quận Đống Đa :<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Toán - Lý - Hóa - Sinh - Văn...từ lớp 1 đến lớp 12<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Anh văn cơ bản - IELTS - TOELF - TOEIC...<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Các môn Vẽ - Đàn - Nhạc...<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Luyện viết chữ đẹp...<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Các ngoại ngữ: Hoa - Hàn - Nhật - Pháp...<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Và một số lớp đặc biệt dạy Tiếng Việt cho người nước ngoài, dạy các môn học bằng tiếng Anh ...<br />\r\n&nbsp;<br />\r\nTất cả các bậc phụ huynh tại khu vực quận Đống Đa có nhu cầu tìm gia sư giỏi hay các bạn sinh viên đang sinh sống và học tập tại khu vực quận Đống Đa – Hà nội có nhu cầu làm gia sư, dạy kèm hãy liên hệ với trung tâm gia sư của chúng tôi :</p>\r\n','gia-su-tai-quan-dong-da---ha-noi',1,'','',[site_id],[user_id],'/media/images/111/news/ava/','412_1433068039_726556ae2079bd99.png',[now], [now],[user_id],0,'','',[now]);set @news394 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Gia sư tại quận Hoàng Mai - Hà Nội','Trung tâm gia sư Đức Minh cung cấp dịch vụ gia sư uy tín, chuyên nghiệp tại Quận Hoàng Mai. Chúng tôi không ngừng phấn đấu trở thành một Trung tâm chuyên Giáo Dục và Đào Tạo tốt nhất tại Hà Nội','<p style=\"margin: 0px; padding: 0px; text-align: justify; line-height: 22px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: larger;\"><span lang=\"VI\" style=\"margin: 0px; padding: 0px; line-height: 19.3199996948242px;\">Trung tâm gia sư </span><span style=\"margin: 0px; padding: 0px; line-height: 19.3199996948242px;\">Đức Minh</span><span lang=\"VI\" style=\"margin: 0px; padding: 0px; line-height: 19.3199996948242px;\"> cung cấp dịch vụ gia sư uy tín, chuyên nghiệp tại</span><span style=\"margin: 0px; padding: 0px; line-height: 19.3199996948242px;\">Quận Hoàng Mai</span><span lang=\"VI\" style=\"margin: 0px; padding: 0px; line-height: 19.3199996948242px;\">. Chúng tôi không ngừng phấn đấu trở thành một Trung tâm chuyên Giáo Dục và Đào Tạo tốt nhất tại Hà Nội</span></span></strong></p>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Với đội ngũ gia sư giỏi, kiến thức sâu có kinh nghiệm trong nghề đến từ các trường ĐH như ĐH sư phạm hà nội, ĐH ngoại thương, ĐH kinh tế quốc dân … Trung tâm gia sư Đức Minh nhận được nhiều sự tin tưởng từ phía cha mẹ học sinh &nbsp;không chỉ bởi chất lượng dịch vụ mà hơn hết là chất lượng dạy học và hiệu quả học tập của học sinh.</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Nếu các bạn quá bạn rộn vì công việc ngoài xã hội không có đủ thời gian lo lắng việc học hành của con cái thì việc tốt nhất là bạn nên tìm ngay cho con bạn một gia sư để giúp bạn lo lắng chuyện học hành của con các bạn. Việc thành lập trung tâm gia sư với mong muốn kết nối quý phụ huynh và gia sư lại với nhau cùng nhau giáo dục cho các em học sinh.</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Chúng tôi luôn đặt chất lượng giảng dạy và niềm tin của phụ huynh &nbsp;học sinh lên hàng đầu chính vì vậy chúng tôi đưa ra các lựa chọn phù hợp nhất dành cho con em và gia đình bạn.</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"><strong style=\"margin: 0px; padding: 0px;\">Trung tâm gia sư hà nội chúng tôi chuyên cung cấp các dịch vụ:</strong></div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Nhận dạy tại nhà các lớp từ 1-12 các môn Toán – Lý – Hóa – Sinh – Văn – Sử – Địa – Ngoại Ngữ, &nbsp;Tin Học …. Hệ thống lại kiến thức cho học sinh yếu kém và từng bước cải thiện kết quả học tâp trong thời gian sớm nhất, bên cạnh đó trung tâm sẽ tập trung nâng cao kiến thức cho học sinh khá giỏi, bảo đảm những kết quả vượt bậc..</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Ôn luyện thi vào lớp 6, lớp 10 các trường chuyên lớp chọn theo khả năng và yêu cầu của phụ huynh, đồng thời ôn thi tốt nghiệp cấp III . Luyện thi Đại Học các khối A,B,C,D….khối năng khiếu…</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Không chỉ có vậy, Trung tâm còn nhận bồi dưỡng, luyện thi học sinh giỏi cấp quận huyện, thành phố và cấp quốc gia.</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">&nbsp;</div>\r\n\r\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Hãy liên hệ ngay với chúng tôi bằng cách gọi điện thoại trực tiếp hoặc truy cập vào website: http://giasuducminh.com/ để được tư vấn lựa chọn gia sư tại nhà phù hợp nhất cho con em bạn.</div>\r\n','gia-su-tai-quan-hoang-mai---ha-noi',1,'','',[site_id],[user_id],'/media/images/111/news/ava/','549_1433068084_519556ae234e2c28.jpg',[now], [now],[user_id],1,'','',[now]);set @news395 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Gia sư tại quận Ba Đình - Hà Nội','Hiện tại chúng tôi có khá nhiều hợp đồng dạy gia sư tại nhà từ lớp 1 đến lớp 12 ở địa bàn quận Ba Đình với đầy đủ các môn học như:<br /><br />\r\nĐối với sinh viên việc vừa học vừa làm đã trở thành bình thường, nhất là các bạn sinh viên từ tỉnh lẻ lên các thành phố lớn để học; công việc phổ biến nhất mà các bạn lựa chọn là đi dạy thêm tại nhà, đigia sư tại nhà cho các em học sinh cấp 1 đến cấp 3.','<p>Hiện tại chúng tôi có khá nhiều hợp đồng dạy gia sư tại nhà từ lớp 1 đến lớp 12 ở địa bàn quận Ba Đình với đầy đủ các môn học như:</p>\r\n\r\n<p>Đối với sinh viên việc vừa học vừa làm đã trở thành bình thường, nhất là các bạn sinh viên từ tỉnh lẻ lên các thành phố lớn để học; công việc phổ biến nhất mà các bạn lựa chọn là đi dạy thêm tại nhà, đigia sư tại nhà cho các em học sinh cấp 1 đến cấp 3.</p>\r\n\r\n<p><br />\r\nLàm thêm bằng công việc gia sư có lẽ là phù hợp nhất với các bạn sinh viên, ngoài việc giúp bản thân ôn luyện lại kiến thức còn kiếm được những khoản thu nhập tương đối có thể tự phục vụ quá trình học tập tập của mình, giảm bớt gánh nặng “tiền ăn học” cho gia đình. Không những thế khi đi làm thêm, dạy thêm các bạn sẽ có điều kiện để va vấp và học hỏi kinh nghiệm sống, khả năng ứng xử giao tiếp cho bản thân, đó là những kĩ năng rất cần thiết để phục vụ cho công việc sau này của các bạn. Tuy nhiên để tìm được các trung tâm gia sư uy tín, tránh được các trung tâm môi giới lừa đảo là điều không hề đơn giản.</p>\r\n\r\n<p><br />\r\nCác bạn sinh viên sẽ thực sự yên tâm khi đến với Trung Tâm Gia Sư Đức Minh trực thuộc công ty giáo dục và đào tạo Đức Minh, chúng tôi là đơn vị chuyên cung cấp các dịch vụ giáo dục, tư vấn giáo dục, giới thiệu gia sư , lớp dạy kèm rất có uy tín và đáng tin cậy tại quận Ba Đình nói riêng và hiện nay rất nổi tiếng tại Thủ đô Hà Nội, đang được các bậc phụ huynh và học sinh rất tin tưởng về chất lượng dạy và học. Đó cũng là cái gốc cho sự phát triển lâu dài của công ty.</p>\r\n\r\n<p><br />\r\nHiện tại chúng tôi có khá nhiều hợp đồng dạy gia sư tại nhà từ lớp 1 đến lớp 12 ở địa bàn quận Ba Đình với đầy đủ các môn học như:</p>\r\n\r\n<p><br />\r\nToán - Lý - Hóa - Sinh - Văn...từ lớp 1 đến lớp 12</p>\r\n\r\n<p>Anh văn cơ bản - IELTS - TOELF - TOEIC...</p>\r\n\r\n<p>Các môn Vẽ - Đàn - Nhạc...</p>\r\n\r\n<p>Luyện viết chữ đẹp...</p>\r\n\r\n<p>Các ngoại ngữ: Hoa - Hàn - Nhật - Pháp...</p>\r\n\r\n<p>Và một số lớp đặc biệt dạy Tiếng Việt cho người nước ngoài, dạy các môn học bằng tiếng Anh ...</p>\r\n\r\n<p><br />\r\nĐến với Đức Minh, các bạn sinh viên được nhận lớp dạy phù hợp với trình độ chuyên môn, được chúng tôi hướng dẫn những kỹ năng, chia sẽ kinh nghiệm làm gia sư để các bạn dể dàng được nhận lớp dạy, làm việc dựa trên hợp đồng lao động minh bạch, rõ ràng tránh thiệt thòi cho các bạn khi có sự cố phát sinh. Các bạn sinh viên cần tìm lớp dạy kèm hãy đến với trung tâm gia sư Đức Minh, đảm bảo sẽ hài lòng, tránh mất tiền với những trung tâm lừa đảo làm ăn thiếu uy tín và chuyên nghiệp, làm việc không rõ ràng, không có sự ràng buộc bằng hồ sơ, hợp đồng lao động.</p>\r\n','gia-su-tai-quan-ba-dinh---ha-noi',1,'','',[site_id],[user_id],'/media/images/111/news/ava/','267_1433067954_558556ae1b20d093.png',[now], [now],[user_id],0,'','',[now]);set @news396 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories302,'Cha mẹ có nên can thiệp chuyện học tập của con với gia sư','Ngày nay rất nhiều ông bố bà mẹ vì bận chuyện cơ quan, ngoài chợ hay thậm chí có người chỉ ở nhà nội trợ nhưng vẫn cho trẻ học với gia sư dù chỉ là lớp 1 hay cấp bậc tiểu học.','<p>Ngày nay rất nhiều ông bố bà mẹ vì bận chuyện cơ quan, ngoài chợ hay thậm chí có người chỉ ở nhà nội trợ nhưng vẫn cho trẻ học với gia sư dù chỉ là lớp 1 hay cấp bậc tiểu học.<br />\r\n<br />\r\nCha mẹ cứ nghĩ thương con là lo cho con đầy đủ từ tiền ăn, học, vui chơi, sinh hoạt là đủ và muốn con học giỏi hơn thì cứ thuê gia sư về day kem tai nha cho con trẻ là xong chuyện và mọi chuyện học của con trẻ cứ phó mặc cho gia sư.<br />\r\n<br />\r\n<br />\r\nVà hậu quả là trẻ em ngày này rất thụ động và có tính uỷ lại, thay vì tự giải bài tập, hoặc có bị điểm kém để các em có thể tự rút ra bài học cho mình thì nay đã có cô, thầy gia sư rồi có bài khó ,dể thì thì cứ đưa cho gia sư giải, học trò cứ chép y nguyên vào vở, chẳng khác gì con vẹt và lâu dần sẽ mất dần kiến thức căn bản lẫn nâng cao.<br />\r\n<br />\r\n<br />\r\nVì thế để việc học của các em tốt hơn, và giúp các em ngày càng chăm ngoan hơn thì phải làm thế nào?<br />\r\n<br />\r\n<br />\r\nTrong vấn đề này thì vai trò của cha mẹ là rất quan trọng, ngoài việc bỏ tiền thuê gia sư về thì cha mẹ có nhiệm vụ theo dõi việc học của con trong lúc học kèm lẫn bài vở trên lớp có thực sự tiến bộ hay không.<br />\r\n<br />\r\n<br />\r\n<a href=\"http://www.giasubachkhoa.com.vn/upload/gia-su-quan-9.jpg\" target=\"_blank\"><img border=\"0\" src=\"http://www.giasubachkhoa.com.vn/upload/gia-su-quan-9.jpg\" /></a><br />\r\n<br />\r\nNếu như trẻ học không tiến bộ mà hằng tháng phải trả tiền cho gia sư thì thật uổng phí vì thế nhiệm vụ của cha mẹ là:<br />\r\n<br />\r\n-Tìm trung tâm gia sư uy tín.<br />\r\n-Nên chọn gia sư hiện đang là sinh viên trường đại học sư phạm hoặc giáo viên.<br />\r\n-Là người thật thà, siêng năng, chịu khó.<br />\r\n-Theo dõi bài kiểm tra định kỳ của con ở trường.<br />\r\n-Nếu học thức cha mẹ không cao có thể nhờ con cháu, hàng xóm, bạn bè học giỏi lớp con mình kiểm tra kiến thức cho bé.<br />\r\n-Khi con trẻ là con gái đặc biệt tới tuổi dậy thì, cần quan tâm đặc biệt tới tình cảm, tâm lý của bé , nếu không may cô bé thích luôn gia sư của mình thì khổ.<br />\r\n-Người gia sư đó không những có kiến thức chuyên môn, kinh nghiệm truyền đạt mà còn là người có đạo đức tốt, gương mẫu và chững chạc.<br />\r\n-Là người gia sư có trách nhiệm với việc mình làm và trách nhiệm với học sinh.<br />\r\n-Nếu bạn chọn kỹ thì có thể mức lương sẽ cao hơn, nhưng vì tương lai con em chúng ta thì việc chọn kỹ không là quá thừa<br />\r\n<br />\r\nChúc quý bậc phụ huynh có thể chọn được gia sư tốt cho con em mình.</p>\r\n','cha-me-co-nen-can-thiep-chuyen-hoc-tap-cua-con-voi-gia-su',1,'','',[site_id],[user_id],'/media/images/111/news/ava/','712_1433067611_761556ae05b31c21.jpg',[now], [now],[user_id],1,'','',[now]);set @news397 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,0,'Làm gia sư cho trẻ tự kỷ khó hay dể','Xã hội ngày một có nhiều trẻ khi sinh ra đã mắc chứng tự kỷ.Đây là một căn bệnh liên tưởng đến thần kinh.Người ta có liệt kê rằng nếu trẻ tự mình làm lấy mà trong khoảng từ 1-4 tuổi có những phương pháp trợ giúp kỹ năng  tốt thì khả năng tiếp xúc với nhau với người khác sẽ vẫn tốt. ','<p><span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Xã hội ngày một có nhiều trẻ khi sinh ra đã mắc chứng tự kỷ.Đây là một căn bệnh liên tưởng đến thần kinh.Người ta có liệt kê rằng nếu trẻ tự mình làm lấy mà trong khoảng từ 1-4 tuổi có những phương pháp trợ giúp kỹ năng &nbsp;tốt thì khả năng tiếp xúc với nhau với người khác sẽ vẫn tốt.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Cần một gia sư có chuyên trị bên cạnh ?&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Ngay lần đầu gặp , thay vì được giới thiệu tên học trò thì cô giáo phải đi tìm học trò. Đó là&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; font-weight: bold; background-color: rgb(255, 255, 255);\"><a href=\"http://giasutainangviet.com/\" style=\"color: rgb(44, 121, 179); text-decoration: none;\" target=\"_blank\">trung tam gia su</a></span><span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">của trẻ tự mình làm lấy. Trẻ tự mình làm lấy thấy người lạ thường chui dưới gầm bàn , trùm chăn hoặc tự lấy tay đập đầu , khóc thét... Dù có nhận thù lao cao gấp 2 lần so với &nbsp;gia sư &nbsp;cho trẻ thường nhật nhưng không phải ai cũng dám nhận hoặc làm gia su cho trẻ tự kỷ.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">\\\"Tôi đã tốt nghiệp khoa Giáo dục Đặc biệt - Trường Đại học Sư phạm Hà Nội , chuyên ngành Giáo dục trẻ chậm phát triển trí óc. Tôi nhận gia sư cho trẻ tự mình làm lấy , trẻ chậm phát triển trí óc , trẻ chậm nói. Quý phụ huynh nào có nhu cầu giao thông qua email...\\\". Những thông tin người tìm việc như vậy không hiếm trên các trang rao vặt. &nbsp;Không chỉ các bạn sinh viên mới ra trường đăng tải các thông tin cá nhân chủ nghĩa tìm đến các gia đình có nhu cầu mà các trọng tâm gia sư có danh tiếng tốt cũng có những service phục vụ người ốm riêng đối tượng học trò đặc biệt này.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Theo chân chị Nguyễn Thị Linh - cô giáo dạy phát triển tiếng nói cho trẻ tự mình làm lấy , tôi hiểu , để có được gần 200.000 đồng/buổi dạy cho học trò tự mình làm lấy , kém phát triển trí tuệ... là không dễ. Học trò của chị Linh là cậu bé 7 tuổi , mắc chứng tự mình làm lấy. Bố cậu bé làm nghề xây dựng , mẹ là cán bộ truyền thông của Bộ TN - MT.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Đầu giờ học , Linh dạy học trò chào và đóng cửa phòng. Sau bài phát động với vài trò chơi sức khỏe như xếp ghế chồng lên nhau , Linh dạy học trò tập tô màu và dễ dàng cảm nhận các tấm đề can , phân biệt các chữ cái khác nhau nhưng mắt cậu học trò bé nhỏ chỉ chú ý đến những con vật trong hộp đồ chơi gần xó nhà. Lần nào cũng vậy , đòi đồ chơi không được , học trò của Linh lại khóc , lăn đùng ra sàn , đấm thuộc cấp vào tường nhà , thậm chí đánh cả cô giáo. Cô giáo không dỗ bé mà lôi từ tủ ra những tấm ghép hình đủ màu sắc , chơi một mình thể hiện sự thích thú. Thấy vậy , học trò nín khóc và mon men tới gần cô giáo , rồi đòi chơi chung đồ chơi. Sau động thái này của học trò , Linh tập cho trẻ tự mình làm lấy bó tay và nói: \\\"Xin lỗi cô!\\\".&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Chị Nguyễn Thị Phương , Viện tiếng nói , gia sư cho trẻ tự mình làm lấy , tâm sự: \\\"Cám cảnh nhất là những gia đình nghèo có con mắc bệnh tự mình làm lấy. Như trường hợp của bé Hoàng Văn D ( Hà Nội ). Bé đã 5 tuổi nhưng chưa nói được và chỉ phát ra thanh âm bất nghĩa. Lúc tôi làm gia sư cho cháu thì cũng khá muộn. Từ lúc 24 tháng tuổi , các bác sỹ đã phát hiện cháu bị tự mình làm lấy. Tuy nhiên , do hoàn cảnh gia đình có nhiều trở ngại hoặc thiếu thốn , bác mẹ cháu là cần lao phổ thông , nghề nghiệp không yên ổn nên phải đến 5 tuổi gia đình mới can thiệp\\\". Chị Phương còn nhớ như in ngày đầu tiên gặp mặt với D , cháu &nbsp;chạy trốn vào một góc , trùm chăn; khi giận hờn , cháu chỉ biết tự đập tay vào đầu và khóc thét.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">một buổi dạy của gia sư , Hình chỉ mang tính minh họa&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Gia sư không... giáo án&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Không có giáo án hay phương pháp cụt ngủn dạy cho trẻ mắc dạng bệnh này , phần lớn , các cô phải tùy vào tình trạng của từng trẻ để dạy , \\\"điều tiết\\\" cách dạy và chữa bệnh riêng. Kinh nghiệm chính là \\\"kim chỉ nam\\\" cho các giáo viên dạy trẻ tự kỷ.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Với tất cả gia sư cho trẻ tự mình làm lấy , ngoài kỹ năng sư phạm thì sự xem xét kỹ lưỡng , sự mến yêu , cảm thông với số phận các em mắc bệnh là điều quan yếu nhất. Theo lời chị Phương thì sau một tháng chị phải nhớ hầu như tất cả mọi thói quen của trẻ mà mình nhận dạy.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">\\\"Chúng tôi sợ nhất là trẻ tự mình làm lấy có xu hướng xâm hại ( đánh giáo viên ) hoặc tự xâm hại ( tự đánh ) bản thân. Lúc đó , một cái ôm chặt , vỗ về nhẹ nhõm của người dạy sẽ xoa dịu tính tình thất thường của trẻ. Chúng ta phải thuộc lòng thói quen ăn , nghỉ của trẻ. Đặc biệt nữa của trẻ mắc chứng bệnh tự mình làm lấy là rất khó ăn , hoặc chỉ ăn một số thức ăn nhất mực mà thói quen trong sinh hoạt thì rất khó thay đổi. Phần lớn , trẻ tự mình làm lấy không thích ăn rau , rèn cho trẻ biết ăn rau , cũng phải mất cả tháng rồi\\\" , chị Phương tâm sự.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">dòm từng thói quen ăn ngủ , nghỉ , màu sắc yêu thích của trẻ , chứng kiến sự khóc lóc , thậm chí tự làm đau bản thân làm cho không ít gia sư trẻ dù có yêu nghề đến mấy cũng phải chùn bước.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Sau mỗi buổi dạy , cô giáo dạy trẻ tự mình làm lấy phải dặn dò xuân huyên học trò cách duy trì hiệu quả bài học. \\\"Mình sợ nhất những gia đình , xuân huyên phó thác hết cho gia sư. Ví như vậy , trẻ tự mình làm lấy sẽ không tốt hơn trước được. Cần có sự kết hợp của xuân huyên , người nhà trong gia đình hiệu quả mới lâu dài\\\" , cô giáo Linh chia sẻ.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Chị Đỗ Thị Minh Phương , thành viên của tổ chức trợ giúp kỹ năng cho trẻ dị tật trí óc chia sẻ: \\\"Dạy học trò thường đã khó , dạy học trò tự mình làm lấy khó hơn ngàn lần. Hưng thịnh xuân huyên có con tự mình làm lấy , ngay sau khi con nói , học được điều gì , xuân huyên muốn kiểm chứng thường bắt con làm lại nhiều lần , làm con sợ. Giá như vậy , sẽ càng làm cho con không thích học hơn. Gây cảm giác không vui , đòi hỏi đó diễn ra ở nhiều xuân huyên có trẻ tự mình làm lấy. Xuân huyên trẻ tự mình làm lấy đừng bắt con nói đi nói lại một câu. Thay vào đó , xuân huyên hãy cho các hoàn cảnh , môi trường khác nhau để con tự sử dụng những từ đã được học\\\".&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">san sớt từ bậc phụ huynh&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(0, 0, 0); font-family: Arial, \'Lucida Grande\', sans-serif; font-size: small; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20.7999992370605px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(255, 255, 255);\">Anh Văn Ngọc ( Hoàng Mai , Hà Nội ) , một người cha có con tự mình làm lấy phải thuê gia sư chia sẻ: \\\"Chúng tôi hiểu được sự có nhiều trở ngại hoặc thiếu thốn của các thầy cô , bởi chính chúng ta cũng hiện ra tâm lý nản với hoàn cảnh của con. Con tôi bị chứng tăng động , không bao giờ ngồi yên một chỗ. Ngay cả xuân huyên khi nói , con không cùng quan điểm hoặc suy nghĩ với ai đó cũng quay lại đánh hoặc tát vào mặt xuân huyên ngay. Gia đình tôi đã mời không dưới 10 gia sư là sinh viên có thừa sự nồng nhiệt và bổn phận , cô giáo có bằng cấp… cũng bỏ cuộc vì cháu không chịu chơi với cô.</span></p>\r\n','lam-gia-su-cho-tre-tu-ky-kho-hay-de',1,'','',[site_id],[user_id],'/media/images/111/news/ava/','977_1433067511_877556adff74d0f1.jpg',[now], [now],[user_id],1,'','',[now]);set @news398 = LAST_INSERT_ID();
