INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],' Nguyễn Anh Sơn',1439830800,1,'Bếp Á - Âu','Tiến sĩ','/media/images/138/lecturer/ava/','377_1439893669_30755d308a531c8d.png','Thầy Nguyễn Anh Sơn có 15 năm kinh nghiệm làm việc cho đại sứ quán New Zealand và trước đó là những môi trường rất chuyên nghiệp nhứ Hilton, Diva café, Ana Mandara Resort Nha Trang… Là một trong những tên tuổi quen thuộc trong giới đầu bếp tại Hà Nội nhưng anh không thích nói nhiều về bản thân nhưng những kinh nghiệm anh có đã chứng minh tài năng của anh trong nghề bếp. ','<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Thầy Nguyễn Anh Sơn có 15 năm kinh nghiệm làm việc cho đại sứ quán New Zealand và trước đó là những môi trường rất chuyên nghiệp nhứ Hilton, Diva café, Ana Mandara Resort Nha Trang… Là một trong những tên tuổi quen thuộc trong giới đầu bếp tại Hà Nội nhưng anh không thích nói nhiều về bản thân nhưng những kinh nghiệm anh có đã chứng minh tài năng của anh trong nghề bếp. Qua nhiều năm giảng dạy anh đúc kết được rằng “một người thầy tốt thì sẽ có nhiều thế hệ trò tốt”. Trở thành một người thầy tốt đối với các thế hệ học viên là mong muốn và trách nhiệm mà các giáo viên Ẩm thực 24h luôn đặt ra cho mình. Thực hiên sứ mệnh đào tạo các Đầu bếp chuyên nghiệp tốt cung cấp cho các nhà hàng, khách sạn; nâng cao chất lượng món ăn gia đình, các kĩ năng cơ bản trong bếp núc để mọi người tự tin sáng tạo các món ăn, anh Nguyễn Anh Sơn đã xây dựng Ẩm thực 24h với thực đơn giáo trình sát sao thiết thực nhất.</span></span></p>\r\n',1,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','0913 344 441',4,'','garyson@amthuc24h.vn','Giám đốc ','Ẩm thực 24h');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],'Trần Viết Tuấn',1439830800,1,'Món Âu','Thạc sĩ','/media/images/138/lecturer/ava/','35_1439895473_18655d30fb1d712e.jpg','Sinh ra và lớn lên trong một gia đình gốc Hà Nội, cả bố và mẹ đều là giáo viên nhưng ngay từ nhỏ anh đã đam mê với việc chế biến các món ăn. Ngay khi tốt nghiệp phổ thông, anh đã tham gia khóa đào tạo bếp và thực hiện niềm đam mê của mình suốt 5 năm để từ vị trí nhân viên rồi lên tổ trưởng Bếp ở Press Club.','<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Sinh ra và lớn lên trong một gia đình gốc Hà Nội, cả bố và mẹ đều là giáo viên nhưng ngay từ nhỏ anh đã đam mê với việc chế biến các món ăn. Ngay khi tốt nghiệp phổ thông, anh đã tham gia khóa đào tạo bếp và thực hiện niềm đam mê của mình suốt 5 năm để từ vị trí nhân viên rồi lên tổ trưởng Bếp ở Press Club.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Không ngừng học hỏi và cống hiến, anh luôn giữ vị trí bếp trưởng ở các nhà hàng khách sạn trên cả nước như trên Du thuyền Potomax, Sunspa Resort Quảng Bình, Đại sứ quán Angieri, khách sạn Lecanort, khách sạn Sheraton… Và bây giờ thầy đang truyền đạt lại những gì mình có, mình học hỏi được cho các thế hệ học viên khi đứng lớp giảng dạy tại Ẩm thực 24h.</span></span></p>\r\n\r\n<div>\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Nhiệt tình, tận tâm, khả năng chuyên môn cao và yêu nghề là những từ học viên luôn nhắc đến khi nói về thầy. Khi được hỏi bí quyết nào làm nên thành công hiện nay, thầy chia sẻ: “ Muốn có được những món ăn ngon thì phải hiểu biết về gia vị”. Đó chính là kinh nghiệm từ bản thân thầy muốn nhắn nhủ tới thế hệ học viên của mình muốn theo nghề bếp.</span></span></p>\r\n</div>\r\n',1,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','0913 344 441',0,'','garyson@amthuc24h.vn','Giảng viên','Ẩm thực ');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],'Trịnh Ngọc Long',1439830800,1,'Món Á','Thạc sĩ','/media/images/138/lecturer/ava/','196_1439895009_11655d30de11ae60.jpg','Chef Long – thầy giáo tham gia cùng Ẩm thực 24h từ những ngày đầu thành lập.','<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Với niềm đam mê ẩm thực từ khi còn ngồi trên ghế nhà trường, thầy đã không ngừng tìm tòi, học hỏi nâng cao tay nghề của mình qua trường lớp, qua các công việc làm thêm. Thầy đã có kinh nghiệm làm bếp gần 20 năm với những vị trí cao trong các nhà hàng, khách sạn nổi tiếng miền Bắc như Paradise Đại Lải Resort, Emeralda Resort Ninh Bình…Chef Long cũng đã từng giữ vị trí Bếp trưởng Á tại các nhà hàng Á của các khách sạn và tập đoàn lớn nhứ Daewoo, Nikko, Red Sun hay ANB company.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Yêu nghề, không ngừng học hỏi và cống hiến là những từ có thể miêu tả về thầy. Thầy luôn nhắn nhủ tới các học viên của mình: “ Học, học nữa, học mãi, học từ thầy cô, từ đồng nghiệp và rút kinh nghiệm cho bản thân mình”.</span></span></p>\r\n',1,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','0934567609',6,'','ngoclong@gmail.com','Giảng viên ','Ẩm thực 24 h');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],'Nguyễn Hoài Hương',1439830800,1,'Ẩm thực Việt','Tiến sĩ','/media/images/138/lecturer/ava/','445_1439895089_76155d30e3180e68.jpg','Cắm hoa là một nghệ thuật và người cắm hoa là một nghệ sĩ và để truyền đạt được nghệ thuật đó cho học viên là cả một tài năng. Đó là những gì Ẩm thực 24h nói về cô giáo Nguyễn Hoài Hương – một trong những Giảng viên tâm huyết, yêu nghề và có chuyên môn cao.','<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Cắm hoa là một nghệ thuật và người cắm hoa là một nghệ sĩ và để truyền đạt được nghệ thuật đó cho học viên là cả một tài năng. Đó là những gì Ẩm thực 24h nói về cô giáo Nguyễn Hoài Hương – một trong những Giảng viên tâm huyết, yêu nghề và có chuyên môn cao.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Bản thân đã từng được đào tạo bài bản về cắm hoa theo phong cách châu Âu, cô đã từng làm việc tại nhiều shop cắm hoa nổi tiếng tại Hà Nội trong đó có shop hoa Calvin Phương – Hai Bà Trưng.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Sau thời gian tìm tòi, học hỏi nâng cao kỹ năng, cô giáo Nguyễn Hoài Hương đã tự tay mở một shop hoa online để phục vụ niềm đam mê của mình. Được sự tin tưởng cũng như ủng hộ của khách hàng, chị đã mở cửa hàng hoa riêng của mình có tên Blue Sun ở Hồ Đắc Di kết hợp với các dịch vụ thiệp handmade, hoa gấu bông… và các món quà nghệ thuật khác. Cho đến nay, shop hoa của chị đã được rất nhiều người biết đến và lựa chọn để đặt những lẵng hoa nghệ thuật phục vụ cho các sự kiện của cá nhân, công ty.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Với kinh nghiệm 8 năm trong lĩnh vực cắm hoa nghệ thuật, chị luôn không ngừng học hỏi, cống hiến nâng cao tay nghề của mình. Hiện nay, vừa làm chủ cửa hàng cắm hoa, chị vừa giảng dạy tại Trung tâm Ẩm thực 24h. Với chị việc truyền đạt kinh nghiệm của mình cho các học viên là niềm vui mà không phải ai cũng có cơ hội.</span></span></p>\r\n',0,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','0969 968 655',7,'','hoaihuong@gmail.com','Giảng viên','Ẩm thực 24h');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],' Phạm Thị Hà',1439830800,1,'Ẩm thực nước ngoài','Thạc sĩ','/media/images/138/lecturer/ava/','802_1439895612_73155d3103cb86e1.jpg','Một trong những giảng viên trẻ nhất đồng hành với Trung tâm Ẩm thực 24h từ những ngày đầu: Giảng viên Phạm Thị Hà. Tuy còn khá trẻ nhưng chị đã có nhiều năm kinh nghiệm và được đánh giá cao trong công việc.','<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Hôm nay, Ẩm thực 24h xin được giới thiệu đến mọi người một trong những giảng viên trẻ nhất&nbsp;đồng hành với Trung tâm Ẩm thực 24h từ những ngày đầu: Giảng viên Phạm Thị Hà. Xuất phát&nbsp;từ niềm đam mê ẩm thực, thích đồ ngọt, cô đã theo học những khóa học Bánh và Chè đầu tiên tại&nbsp;trường đào tạo nghề. Sau đó cô đã làm việc tại các nhà hàng lớn nhỏ trong thành phố như nhà&nbsp;hàng Sen, nhà hàng buffet Thùy Dương, nhà hàng Rosso… trong suốt nhiều năm. Tuy còn khá&nbsp;trẻ nhưng chị đã có nhiều năm kinh nghiệm và được đánh giá cao trong công việc.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Không ngừng học hỏi và cống hiến, hiện nay cô đang làm việc tại nhà hàng Topchef – một trong&nbsp;những địa điểm hấp dẫn thu hút thực khách.&nbsp;Nhiệt tình, tận tâm, khả năng chuyên môn cao và yêu nghề là những từ học viên luôn nhắc đến khi nói về cô. Khi được hỏi bí quyết nào làm nên thành công hiện nay, cô chia sẻ: “ Muốn nấu&nbsp;được chè ngon thì phải hiểu biết về món chè mình nấu”. Đó chính là kinh nghiệm từ bản thân cô&nbsp;giáo trẻ muốn nhắn nhủ tới thế hệ học viên của mình.</span></span></p>\r\n',0,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','0949 864 734',8,'','hapham@gmail.com','Giảng viên ','Ẩm thực 24h');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],' Nguyễn Hồng Lê',1439917200,1,'Món ngon ngày Tết','Tiến sĩ','/media/images/138/lecturer/ava/','969_1439955384_67855d3f9b8277a5.jpg','Cô là môt trong những giảng viên có tuổi đời cũng như tuổi nghề cao nhất và cũng có thời gian gắn bó với Ẩm thực 24h lâu nhất. Cô được nhắc đến với sự nhiệt tình, điềm đạm và tâm huyết dù trong nấu ăn hay trong bất kì công việc gì.','<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Cô là môt trong những giảng viên có tuổi đời cũng như tuổi nghề cao nhất và cũng có thời gian gắn bó với Ẩm thực 24h lâu nhất. Cô được nhắc đến với sự nhiệt tình, điềm đạm và tâm huyết dù trong nấu ăn hay trong bất kì công việc gì.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:14px;\">Cô đã từng công tác tại &nbsp;khách sạn Mỹ Kinh, trợ giảng tại trường Cao đẳng du lịch Hà Nội và cuối cùng gắn bó với vị trí Bếp trưởng tại Viện khoa học sở hữu trí tuệ thuộc Cục sở hữu trí tuệ. Là một đầu bếp có nhiều năm kinh nghiệm trong nghề, cô luôn muốn mình truyền lại hết được những kinh nghiệm của mình cho các thế hệ sau này. Vì thế cô tiếp tục tham gia giảng dạy nghề bếp tại Ẩm thực 24h với mong muốn cung cấp được nhiều đầu bếp tài năng cho nền ẩm thực. Cô tâm niệm: “Nấu ăn là một nghệ thuật và người đầu bếp là một nghệ sĩ thực sự”.</span></span></p>\r\n',0,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','0966 968 645',7,'','hongle@gamail.com','Giảng viên','Ẩm thực 24h');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],'Lê Trần Kiên ',1439917200,1,'Pha chế','Thạc sĩ','/media/images/138/lecturer/ava/','749_1439955537_57255d3fa51a13bf.jpg','Anh là một trong những giảng viên chuyên Á tại Ẩm thực 24h. . Với hơn 15 năm kinh nghiệm làm việc tại khách sạn Melia Hà Nội, song anh vẫn luôn phấn đấu từng ngày bỡi lẽ anh quan niệm rằng, niềm đam mê chính là tiền đề của sự thành công.','<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Anh là một trong những giảng viên chuyên Á tại Ẩm thực 24h. Từ nhỏ chàng trai nhỏ bé này đã có niềm đam mê ẩm thực rất lớn, anh đã đi qua rất nhiều các quốc gia trên thế giới, trau dồi cho mình một vốn kiến thức vững chắc về ẩm thực bốn phương. Anh chú ý tới từng chi tiết nhỏ nhất trong mỗi món ăn. Mỗi món ăn với anh là cả một tác phẩm nghệ thuật từ hương sắc đến mùi vị. Với hơn 15 năm kinh nghiệm làm việc tại khách sạn Melia Hà Nội, song anh vẫn luôn phấn đấu từng ngày bỡi lẽ anh quan niệm rằng, niềm đam mê chính là tiền đề của sự thành công.</span></span></p>\r\n',1,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','01648 631 574',1,'','kienletran@gmail.com','Giảng viên','Ẩm thực 24h');
INSERT INTO edu_lecturer (`id`, `site_id`, `name`, `bod`, `status`, `subject`, `level_of_education`, `avatar_path`, `avatar_name`, `sort_description`, `description`, `gender`, `add`, `phone`, `experience`, `facebook`, `email`, `job_title`, `company`) VALUES (null,[site_id],' Nguyễn Xuân Dũng',1439917200,1,'Fast food','Thạc sĩ','/media/images/138/lecturer/ava/','794_1439957504_6155d4020082f65.jpg','Anh vừa giữ cương vị bếp trưởng tại khách sạn Santa Barbara, vừa tham gia giảng dạy tại Ẩm thực 24h. Với anh, việc giảng dạy là niềm vui, cơ hội để truyền nhiệt huyết với nghề bếp cho các thế hệ trẻ tương lai','<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Với đam mê ẩm thực từ nhỏ, anh Nguyễn Xuân Dũng đã quyết tâm học Nấu ăn tại trường Cao đẳng du lịch để thực hiện giấc mơ của mình. Trong suốt thời gian học tập tại trường, anh đã tranh thủ làm thêm tại bộ phận Bếp của 1 số khách sạn để có cơ hội học tập, rèn luyện như khách sạn Dân chủ, khách sạn Thúy Nga. Sau khi ra trường, anh tiếp tục làm việc tại bộ phận Bếp của khách sạn Sheraton trong suốt 7 năm, tham gia rất nhiều kháo đào tạo kỹ năng chuyên môn. Với những cố gắng và nỗ lực của mình, anh đã khẳng định được vị trí của mình trong nghề bếp. Anh liên tục giữ vị trí bếp phó, bếp trưởng ở các nhà hàng, khách sạn lớn như khách sạn De L’Opera, nhà hàng Topaz, khách sạn Santa Barbara …</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Hiện nay anh vừa giữ cương vị bếp trưởng tại khách sạn Santa Barbara, vừa tham gia giảng dạy tại Ẩm thực 24h. Với anh, việc giảng dạy là niềm vui, cơ hội để truyền nhiệt huyết với nghề bếp cho các thế hệ trẻ tương lai</span></span></p>\r\n',1,'9A3 Ngõ 7 Kim Mã Thượng - Ba Đình - Hà Nội','01234567678',7,'','dungnguyenxuan@gmail.com','Giảng viên','Ẩm thực 24h');
