INSERT INTO jobs (id, user_id, site_id, `position`, degree, trade_id, typeofwork, location, quantity, salaryrange, currency, salary_min, salary_max, description, experience, `level`, includes, requirement, benefit, expirydate, publicdate, created_time, modified_time, `alias`, contact_username, contact_email, contact_phone, contact_address,image_path,image_name) VALUES (null,[user_id],[site_id],'Tuyển nhân viên thiết kế website',2,1,1,'01',5,1,1,0,0,'<ul style=\"box-sizing: border-box; margin: 0px 0px 10px; padding-right: 0px; padding-left: 0px; list-style-position: inside; font-family: Roboto, Arial, Helvetica, sans-serif; line-height: 18.5714282989502px;\">\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Tiếp nhận và phân tích yêu cầu thiết kế, đưa ra ý tưởng phù hợp nhất, và hoàn thành yêu cầu trong thời gian theo kế hoạch;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Lập kế hoạch, nêu ý tưởng và trực tiếp thiết kế giao diện Website, Flash, Banner, Logo...,và các công việc liên quan đến Thiết kế khác;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Kết hợp với bộ phận kinh doanh và các bộ phận khác để đảm bảo thiết kế đáp ứng được nhu cầu của khách hàng;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Thực hiện tốt các công việc khác khi được giao phó.</li>\r\n</ul>\r\n',0,1,'<p><span style=\"font-family: Roboto, Arial, Helvetica, sans-serif; line-height: 18.5714282989502px;\">Đính kèm CV và các sản phẩm tiêu biểu đã thực hiện vào mail buithanhdung@gmail.com</span></p>\r\n','<ul style=\"box-sizing: border-box; margin: 0px 0px 10px; padding-right: 0px; padding-left: 0px; list-style-position: inside; font-family: Roboto, Arial, Helvetica, sans-serif; line-height: 18.5714282989502px;\">\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Có kinh nghiệm thiết kế website nhiều lĩnh vực;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Sử dụng thành thạo các công cụ đồ họa: Photoshop, Illustrator, Corel Draw…;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Thành thạo HTML, CSS;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Sử dụng tốt Flash;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Thành thạo Javascripts là một lợi thế</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Có tư duy sáng tạo và thẩm mỹ tốt;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Có thể chịu được áp lực trong công việc;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Tỉ mỉ, chu đáo, năng động, nhiệt tình.</li>\r\n</ul>\r\n','<ul style=\"box-sizing: border-box; margin: 0px 0px 10px; padding-right: 0px; padding-left: 0px; list-style-position: inside; font-family: Roboto, Arial, Helvetica, sans-serif; line-height: 18.5714282989502px;\">\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Mức lương cạnh tranh phù hợp với năng lực;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Được hưởng các phụ cấp theo quy định của công ty (phụ cấp xăng xe, phụ cấp ăn trưa, ...);</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Được làm việc trong môi trường chuyên nghiệp, năng động và có khả năng phát triển;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Được hưởng đầy đủ các chế độ theo luật quy định của công ty: Bảo hiểm xã hội, bảo hiểm y tế, nghỉ mát, ngày lễ, tết…;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Được tham gia các khoá đào tạo nâng cao trình độ chuyên môn, kĩ năng mềm...;</li>\r\n	<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Cơ hội sở hữu cổ phần ưu đãi dành cho thành viên có kết quả làm việc xuất sắc.</li>\r\n</ul>\r\n',1430326800,1427370915,[now],[now],'tuyen-nhan-vien-thiet-ke-website','A. Dũng','buithanhdung@gmail.com','0948854888','Phòng 211 - Nhà G1 - Số 79 - Ngõ 82 - Phạm Ngọc Thạch - Đống Đa - Hà Nội','/media/images/99/jobs/','186_1429246293_247553091555e6d2.jpg');
