INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'Về chúng tôi','\r\nVới hàng trăm sản phẩm được giới thiệu trên website và hàng ngàn sản phẩm khác được bán trực tiếp tại nhà thuốc, nhà thuốc Việt đảm bảo cung cấp đầy đủ các mặt hàng dược phẩm như:\r\n  -      Thực phẩm chức năng\r\n  -      Thuốc bổ\r\n  -      Thuốc chữa bệnh.\r\n  -      Dược mỹ phẩm.\r\n  -      Mỹ phẩm','<ul style=\"margin: 10px 0px; padding-right: 0px; padding-left: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-stretch: inherit; line-height: 19.5px; vertical-align: baseline; list-style: none outside; color: rgb(68, 68, 68);\">\r\n	<li class=\"name\" style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-family: inherit; font-size: 15px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; line-height: 18px; vertical-align: baseline; width: 385px; float: left; color: rgb(17, 17, 17); text-transform: uppercase;\">\r\n	<h1 style=\"margin: 0px; padding: 0px; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline;\">GIỚI THIỆU</h1>\r\n	</li>\r\n</ul>\r\n\r\n<p class=\"name\" style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-family: inherit; font-size: 15px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; line-height: 18px; vertical-align: baseline; width: 385px; float: left; color: rgb(17, 17, 17); text-transform: uppercase;\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul style=\"margin: 10px 0px; padding-right: 0px; padding-left: 0px; border: 0px; font-family: Arial, Helvetica, sans-serif; font-stretch: inherit; line-height: 19.5px; vertical-align: baseline; list-style: none outside; color: rgb(68, 68, 68);\">