INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product780,'Số đăng ký: H02-056-00\r\nDạng bào chế: Dung dich tiêm\r\nQuy cách đóng gói: Hộp 100 ống x 1ml\r\nCông ty đăng ký: Công ty cổ phần Dược phẩm trung ương 2\r\nThành phần: Adrenalin','<div class=\"headerinfo\" style=\"font-weight: bold; color: rgb(241, 70, 9); font-size: 12px; margin-bottom: 5px; margin-top: 15px; font-family: Arial; line-height: 20px; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Chỉ định:</span></span></div>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Sốc quá mẫn, tai biến dị ứng khi tiêm penicilin hoặc huyết thanh.&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial; font-size: 12px; line-height: 20px; text-align: justify;\" />\r\n<span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Ngất do bloc nhĩ thất hoàn toàn, hôn mê do giảm glucose huyết.</span></span></span></p>\r\n\r\n<div class=\"headerinfo\" style=\"font-weight: bold; color: rgb(241, 70, 9); font-size: 12px; margin-bottom: 5px; margin-top: 15px; font-family: Arial; line-height: 20px; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Chống chỉ định:</span></span></div>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Cường giáp, suy tim, đau thắt ngực, tăng huyết áp, đái tháo đường, hen nặng hoặc quá mẫn với thuốc</span></span></span></p>\r\n\r\n<div class=\"headerinfo\" style=\"font-weight: bold; color: rgb(241, 70, 9); font-size: 12px; margin-bottom: 5px; margin-top: 15px; font-family: Arial; line-height: 20px; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Chú ý đề phòng:</span></span></div>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Do Adrenalin có tác dụng co mạch ở một số vùng (da, mạch tạng) nhưng lại gây giãn mạch ở một số vùng khác (mạch não, mạch phổi), do đó có thể gây biến chứng đứt mạch máu não hoặc phù phổi cấp</span></span></span></p>\r\n\r\n<div class=\"headerinfo\" style=\"font-weight: bold; color: rgb(241, 70, 9); font-size: 12px; margin-bottom: 5px; margin-top: 15px; font-family: Arial; line-height: 20px; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Tác dụng ngoài ý:</span></span></div>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Lo âu, run rẩy, mạch nhanh, loạn nhịp, khô miệng, lạnh các chi, phản ứng ảo giác có thể xảy ra.</span></span></span></p>\r\n\r\n<div class=\"headerinfo\" style=\"font-weight: bold; color: rgb(241, 70, 9); font-size: 12px; margin-bottom: 5px; margin-top: 15px; font-family: Arial; line-height: 20px; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Liều lượng:</span></span></div>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Tiêm dưới da hoặc bắp thịt&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: Arial; font-size: 12px; line-height: 20px; text-align: justify;\" />\r\n<span style=\"color: rgb(0, 0, 0); line-height: 20px; text-align: justify;\">Theo chỉ dẫn của thầy thuốc điều trị&nbsp;</span><br style=\"color: rgb(0, 0, 0); font-family: A