INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Lịch sử phát triển','<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><strong><span style=\"color:#FF0000;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"line-height: 18px; text-align: justify;\">LỊCH SỬ HÌNH THÀNH VÀ PHÁT TRIỂN</span></span></span></strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"color: rgb(78, 78, 78); line-height: 18px; text-align: justify;\">Được thành lập từ ngày 10/08/2007, Công ty Cổ phần Đầu tư và Phát triển Bất động sản HUDLAND không ngừng phát triển bền vững và khẳng định được vị trí của mình trong lĩnh vực đầu tư bất động sản.</span><br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<span style=\"color: rgb(78, 78, 78); line-height: 18px; text-align: justify;\">Để giữ được sự tăng trưởng ổn định và bứt phát trong thời gian tới, Công ty Cổ phần Đầu tư và Phát triển Bất động sản HUDLAND quan niệm rằng thỏa mãn sự hài lòng của khách hàng là mục tiêu phấn đấu và lấy hiệu quả kinh tế là thước đo cho sự phát triển ổn định và bền vững của Công ty. Ngoài việc duy trì và phát triển ngành nghề truyền thống là đầu tư bất động sản, Công ty HUDLAND tiếp tục mở rộng qui mô đầu tư kinh doanh các dự án nhà ở cao cấp, dự án chung cư cao tầng, dự án nhà ở xã hội, khu đô thị… &nbsp;đảm bảo cho Công ty có tiềm lực kinh tế mạnh, đủ sức để cạnh tranh với các doanh nghiệp trong nước và trong khu vực.</span><br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<span style=\"color: rgb(78, 78, 78); line-height: 18px; text-align: justify;\">Trong thời gian tới, Công ty Cổ phần Đầu tư và Phát triển Bất động sản HUDLAND sẽ tập trung chính vào mục tiêu và nhiệm vụ chủ yếu như: Đổi mới doanh nghiệp, mở rộng đầu tư và thực hiện đầu tư, tăng cường công tác quản lý doanh nghiệp và phát triển nguồn nhân lực.</span><br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<span style=\"color: rgb(78, 78, 78); line-height: 18px; text-align: justify;\">Công ty Cổ phần Đầu tư và Phát triển Bất động sản HUDLAND sẽ phát huy cao độ thành tựu đã đạt được trong những năm qua. Đồng thời khắc phục những tồn tại để duy trì sự phát triển ổn định theo định hướng của Công ty phù hợp với xu hướng chung của đất nước. Phấn đấu đưa Công ty trở thành một trong những doanh nghiệp hàng đầu trong lĩnh vực bất động sản, mang đến những sản phẩm khác biệt cho khách hàng, tối đa hóa lợi nhuận cho cổ đông/nhà đầu tư, vun đắp cuộc sống ấm no, hạnh phúc cho các thành viên trong Công ty và góp phần xây dựng đất nước ngày càng vững mạnh và phát triển.</span></span></span><br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<br style=\"color: rgb(78, 78, 78); font-family: Tahoma; font-size: 12px; line-height: 18px; text-align: justify;\" />\r\n<img alt=\"\" src=\"http://hudland.com.vn/Upload/2014-12-27-Dinh%20huong%20phat%20trien-%201.JPG\" style=\"font-size: 12px; margin: 3px; outline: 0px; padding: 0px; font-family: Tahoma; border: 0px; max-width: 852px; color: rgb(78, 78, 78); line-height: 18px; text-align: justify; height: 420px; width: 850px; background-color: transparent;\" /></p>\r\n',[site_id],[user_id],'lich-su-phat-trien',[now],[now],[user_id],'','','','','');
