INSERT INTO edu_course_info (`course_id`, `meta_keywords`, `meta_description`, `meta_title`, `description`, `site_id`) VALUES (@edu_course13,'','','','<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Thành phần món ăn phần lớn làm từ gạo, rau, thịt và đậu phụ. Các bữa ăn truyền thống thường có nhiều món ăn phụ, như là: (banchan) ăn kèm với cơm, canh, và kim chi (một loại banchan có vị cay, được lên men từ rau củ, nhiều nhất là làm từ cải bắp, củ cải và dưa leo). Mọi bữa ăn đều đi kèm với nhiều loại banchan.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Món ăn Hàn Quốc luôn luôn có các loại gia vị như: dầu mè, toenchang (bột đậu nành lên men), nước tương (xì dầu), muối ăn, tỏi, gừng và koch’uchang (bột ớt đỏ).</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Cách nấu nướng cũng thay đổi theo mùa, đặc biệt là trong mùa đông, thường hay dựa vào kim chi và các loại rau củ ngâm được bảo quản trong những lọ gốm chôn dưới nền đất ở trong sân nhỏ. Việc chuẩn bị món ăn thường rất công phu.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Đến với khóa học này, bạn sẽ được học 30 món ăn tuyệt vời của Hàn Quốc</span></span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" style=\"width:714px;\" width=\"714\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">1. Bánh kim chi rán</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">2. Bánh hải sản rán</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">3. Bạch tuộc hấp</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">4. Thăn lợn tẩm bột chiên</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">5. Gà hấp tỏi</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">6. Thịt thăn bò nướng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">7. Thịt lợn hấp gia vị</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">8. Thịt thăn bò tẩm gia vị nướng</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">9. Lẩu gà</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">10. Gà tẩm bột rán</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">11. Cá rán</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">12. Cơm rang tôm</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">13. Tôm hấp</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">14. Tôm tẩm bột rán</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">15. Tôm nướng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">16. Thịt lợn chỉ nướng</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">17. Thịt lợn trộn gia vị nướng</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">18. Sườn lợn tẩm gia vị nướng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">19. Thịt lợn xào</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">20. Cá hấp lò</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">21. Lẩu kim chi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">22. Cơm rang kim chi</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">23. Trứng cuộn cơm</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">24. Canh hải sản</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">25. Cơm cuốn</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">26. Canh Kim chi</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">27. Mì hải sản</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:211px;height:20px;\">\r\n			<p align=\"center\">28. Mì ăn liền Hàn Quốc</p>\r\n			</td>\r\n			<td style=\"height:20px;\">\r\n			<p align=\"center\">29. Gỏi cá sống</p>\r\n			</td>\r\n			<td style=\"width:230px;height:20px;\">\r\n			<p align=\"center\">30. Kim chi Hàn Quốc</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n',[site_id]);
INSERT INTO edu_course_info (`course_id`, `meta_keywords`, `meta_description`, `meta_title`, `description`, `site_id`) VALUES (@edu_course14,'','','','<p><strong><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Tại sao bạn nên học nấu ăn?</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Bởi vì:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Bạn đam mê ẩm thực&nbsp; và muốn tìm hiểu nhiều hơn nữa?</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Bạn muốn nấu cho gia đình những món thật lạ và ngon miệng?</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Bạn muốn đa dạng thực đơn ngày Tết bằng những món ăn ngon?</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Bạn muốn thể hiện trước gia đình chồng hay người thân, bạn bè đến chơi ngày Tết?</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">….</span></span></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Tại sao bạn nên học nấu ăn tại Ẩm Thực 24h?</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Không gian học gọn gàng, sạch sẽ, được thiết kế theo phong cách từng vùng miền, lãnh thổ giúp bạnhiểu rõ hơn về văn hóa ẩm thực</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Giáo trình được kết tinh từ kinh nghiệm thực tế cùng với hình ảnh trực quan để bạn có thể cảm nhận một</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">cách sâu sắc những món ăn trong khóa học</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Đội ngũ giảng viên uy tín, giàu kinh nghiệm, là đầu bếp “cứng tay” từng làm cho nhiều nhà hàng, khách</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">sạn lớn như: Hồng Ngọc Hotel, Golden Lotus, Sofitel Plaza, Sunway, chuỗi nhà hàng Halia, Press Club,</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Grand Plaza, Crowne&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Chi phí học siêu rẻ – học phí trọn gói (chi phí học, nguyên liệu, giáo trình…) chỉ 1.2 triệu đồng cùng nhiều chương trình ưu đãi khác</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Bạn sẽ học được những gì sau khóa học món ngon ngày Tết?</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Khả năng lựa chọn các loại nguyên liệu nấu ăn và gia theo món.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Cách bảo quản các loại nguyên liệu nấu ăn</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Mẹo và bí quyết nấu những món ngon ngày Tết lạ miệng.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">- Cách chế biến các món ăn chuẩn theo phong vị cổ truyền hay sự phá cách trong ẩm thực thế giới.</span></span></p>\r\n',[site_id]);
