INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'HVC tiếp tục cung cấp và lắp đặt thiết bị thủy cung tại Phú Quốc ','Dự án thủy cung đầu tiên HVC thi công tại dự án khu đô thị Times City Hà Nội đã thành công tốt đẹp.  Tiếp tục phát triển lĩnh vực này HVC đã ký hợp đồng thứ 2 cung cấp và lắp đặt toàn bộ thiết bị xử lý nước Thủy Cung tại Phú Quốc với quy mô tương tự như tại dự án Times City.','<p style=\"text-align: justify;\">Thủy cung Phú Quốc được chia ra làm hai khu riêng biệt với khu thủy cung nước ngọt và thủy cung nước mặn, do vậy thiết bị dùng cho thủy cung cũng tương đối đặc chủng, có loại thiết bị phải vận chuyển rời không thể vận chuyển bằng container. Công nghệ xử lý nước thủy cung cũng được HVC lựa chọn kỹ càng để phù hợp với điều kiện khí hậu tại Việt Nam và giảm giá thành đầu tư. Toàn bộ công nghệ thiết bị HVC sử dụng cho thủy cung Phú Quốc được nhập khẩu từ Tây Ban Nha, Maylaysia và Canada.</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/thuy%20cung.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Thủy cung tại đảo Phú Quốc có quy mô tương tự như thủy cung Times City</em></p>\r\n\r\n<p style=\"text-align: justify;\">Với việc thi công Thủy cung thứ 2 tại Việt Nam HVC đã chứng tỏ năng lực là công ty đứng đầu Việt Nam về xử lý nước giải trí cao cấp. Hoàn thành xong dự án này HVC tin tưởng có đủ năng lực để bước vào cạnh tranh trong thị trường thiết kế và xây dựng thủy cung tại khu vực Đông Nam Á. Đây cũng chính là ước muốn cháy bỏng của lãnh đạo công ty HVC trong việc xây dựng công ty cổ phần đầu tư và công nghệ HVC vươn tầm với ra khỏi lãnh thổ quốc gia Việt Nam.</p>\r\n','hvc-tiep-tuc-cung-cap-va-lap-dat-thiet-bi-thuy-cung-tai-phu-quoc',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','315_1428634508_30155273b8cce397.jpg',[now], [now],[user_id],1,'','',[now]);set @news512 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'HVC hoàn thành và khai trương sân trượt băng nghệ thuật tại tỉnh Quảng Ninh','Sáng ngày 19/10/2014, trung tâm thương mại (TTTM) Vincom Center Hạ Long đã chính thức tổ chức lễ khai trương tại Khu Cột Đồng Hồ, Phường Bạch Đằng, TP. Hạ Long. Đây là công trình trung tâm thương mại đầu tiên theo tiêu chí tất cả trong một của tỉnh Quảng Ninh. Điều đặc biết nhất là sự xuất hiện của sân trượt băng nghệ thuật do HVC đã tham gia cung cấp và lắp đặt.','<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" height=\"334\" src=\"http://www.vingroup.net/Uploads/0_Tintuchoatdong/Khai%20truong%20vc%20HL%202014/L%E1%BB%85%20c%E1%BA%AFt%20b%C4%83ng%20khai%20tr%C6%B0%C6%A1ng%20TTTM%20Vincom%20Center%20H%E1%BA%A1%20Long..JPG\" width=\"496\" /></p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: justify;\">Được thiết kế theo phong cách Hoàng Gia Châu Âu sang trọng, TTTM Vincom Center Hạ Long bao gồm 4 tầng nổi và 1 tầng hầm với tổng diện tích sàn lên tới hơn 37.000m2, bốn mặt tiếp giáp đường lớn và nằm trong khu đông dân cư nhất của Thành phố. Với vị trí đắc địa và uy tín của chủ đầu tư, chỉ sau một thời gian ngắn chào thuê, hơn 95% diện tích mặt sàn của TTTM đã được lấp đầy với hàng trăm thương hiệu nổi tiếng, từ thời trang, phụ kiện đến đồ gia dụng, nội thất, ẩm thực...</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" height=\"330\" src=\"http://www.vingroup.net/Uploads/0_Tintuchoatdong/Khai%20truong%20vc%20HL%202014/KhaitruongVHL13.jpg\" width=\"495\" /></p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: justify;\">Đến với Vincom Center Hạ Long, khách hàng có thể thỏa sức mua sắm cùng các nhãn hiệu thời trang như Giordano, Lee, G2000, Elise, Eash, Easy Spirit, Ak Club, Eash, Kelly Bùi, Valentino Creations, CK, Tissot, PNJ, Narsis, Lare Boss, Rayban, BATA, Narsis, PNJ, TNG, Blue Exchange, GenViet Jeans… Ngoài ra, <strong>siêu thị tiêu dùng Saigon Co.opmart</strong> có quy mô trên 3.500m2 với hàng ngàn sản phẩm đa dạng, hấp dẫn sẽ đáp ứng mọi nhu cầu tiêu dùng thiết yếu. Sức hút của <strong>Thế giới Ẩm thực </strong>tại Vincom Center Hạ Long nằm ở hàng chục nhà hàng nổi tiếng như Master Beef, Gogi House, Thái BBQ, Lotteria, Food To Go, Kodo Café &amp; Fastfood,… với đầy đủ hương vị từ đồ truyền thống của Việt Nam, Trung Quốc, Hàn Quốc, Thái Lan… cho tới Châu Âu, Châu Úc…; hoặc đơn giản là những món bánh ngọt, đồ uống, kem tươi và snack thơm ngon mà giới trẻ yêu thích.</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" height=\"334\" src=\"http://www.vingroup.net/Uploads/0_Tintuchoatdong/Khai%20truong%20vc%20HL%202014/KhaitruongVHL10.jpg\" width=\"500\" /></p>\r\n\r\n<p align=\"center\" style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: center;\"><img border=\"0\" src=\"http://m.f25.img.vnecdn.net/2014/10/19/DSC-2294-JPG-9819-1413705030.jpg\" /></p>\r\n\r\n<p style=\"color: rgb(0, 0, 0); font-family: arial; font-size: 12px; line-height: 18px; widows: auto; text-align: justify;\">Đặc biệt, lần đầu tiên tại thành phố biển, người dân và khách du lịch sẽ có cơ hội trải nghiệm cảm giác lướt nhẹ trên sàn trượt băng tự nhiên <strong>Vinpealland Ice Rink Hạ Long</strong> với quy mô 1.086m2; Đây là sân trượt băng được đánh giá đạt tiêu chuẩn và hiện đại nhất Việt Nam hiện nay. Sân trượt băng được thiết kế và sử dụng công nghệ Canada đã được HVC chuyển giao cho chủ đầu tư. Với thời gian thi công nhanh kỷ lục trong 6 tháng so với thông thường là 1 năm đã cho thấy khả năng vượt trội của HVC trong việc giữ uy tín về tiến độ với chủ đầu tư.</p>\r\n','hvc-hoan-thanh-va-khai-truong-san-truot-bang-nghe-thuat-tai-tinh-quang-ninh',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','343_1428634389_31255273b15e02ea.jpg',[now], [now],[user_id],1,'','',[now]);set @news513 = LAST_INSERT_ID();
