INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'Xe máy va chạm với xe bồn, vợ chết, chồng nguy kịch','Đang lưu thông, chiếc xe máy chở hai vợ chồng bị xe bồn va chạm, người vợ bị tử vong tại chỗ, còn người chồng nguy kịch.','<table cellpadding=\"0\" cellspacing=\"0\" class=\"picture\" style=\"margin: 0px auto; padding: 0px; list-style: none; outline: none; line-height: 27px; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif; font-size: 18px; word-wrap: break-word !important; max-width: 675px !important; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(248, 248, 245);\">\r\n	<tbody style=\"margin: 0px; padding: 0px; list-style: none; outline: none; line-height: 27px !important; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important;\">\r\n		<tr style=\"margin: 0px; padding: 0px; list-style: none; outline: none; line-height: 27px !important; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important;\">\r\n			<td class=\"pic\" style=\"margin: 0px; padding: 0px !important; list-style: none; outline: none; line-height: 27px !important; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important;\">\r\n			<p class=\"full_images\" style=\"margin-top: 0px; margin-right: 0px; margin-bottom: 5px !important; margin-left: 0px; padding: 0px; list-style: none; outline: none; line-height: 27px !important; position: relative; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important;\"><img alt=\"Xe máy va chạm với xe bồn, vợ chết, chồng nguy kịch - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x670x/349/2014/12/8/TIN-TUC-TAI-NAN.jpg\" style=\"margin: auto !important; padding: 0px; list-style: none; outline: none; line-height: 27px !important; border: 1px solid rgb(102, 102, 102) !important; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important; display: block; height: auto !important; max-width: 670px;\" title=\"Xe máy va chạm với xe bồn, vợ chết, chồng nguy kịch - Ảnh 1\" width=\"500\" /></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: center;\">Hiện trường vụ tai nạn thương tâm khiến một người chết.</p>\r\n\r\n<p>Vụ tại nạn thương tâm xảy ra vào khoảng hơn 10h ngày 8/12 tại cầu Tào Xuyên, thị trấn Tào Xuyên (TP Thanh Hóa).</p>\r\n\r\n<p>Theo lời các nhân chứng có mặt tại hiện trường, vào thời gian trên, chiếc xe máy mang BKS 36R5-36701 đang lưu thông theo hướng Hoằng Hóa về TP Thanh Hóa thì bất ngờ chiếc xe bồn mang BKS 38C-04418 phía sau vượt lên, va chạm vào xe máy khiến hai vợ chồng (chưa rõ danh tính) đi trên Xe máy ngã ra đường.</p>\r\n\r\n<p>Vụ tai nạn khiến người vợ ngồi phía sau xe máy bị xe bồn tông chết tại chỗ, người chồng bị trọng thương phải đưa đi cấp cứu trong tình trạng nguy kịch.</p>\r\n\r\n<p>Sau khi vụ tai nạn xảy ra, lực lượng CSGT TP Thanh Hóa đã có mặt tại hiện trường phân luồng giao thông và tiếp tục điều tra, làm rõ nguyên nhân cảu vụ tai nạn.</p>\r\n','xe-may-va-cham-voi-xe-bon-vo-chet-chong-nguy-kich',1,'','',[site_id],[user_id],'/media/images/61/news/ava/','159_1418034459_82954857d1b18e4a.jpg',[now], [now],[user_id],1,'DSPL','',[now]);set @news518 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'Miền Bắc chuẩn bị đón rét đậm, rét hại tăng cường',' Khoảng từ ngày 12/12, sẽ có thêm một đợt không khí lạnh mạnh tràn về gây rét đậm, rét hại ở miền Bắc.','<p class=\"full_images\" style=\"margin-top: 0px; margin-right: 0px; margin-bottom: 5px !important; margin-left: 0px; padding: 0px; list-style: none; outline: none; line-height: 27px !important; position: relative; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important;\"><em style=\"margin: 0px; padding: 0px; list-style: none; outline: none; line-height: 27px; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif; font-size: 18px; word-wrap: break-word !important; font-variant: normal; font-weight: bold; letter-spacing: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(248, 248, 245);\"><img alt=\"Miền Bắc chuẩn bị đón rét đậm, rét hại tăng cường - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x670x/349/2014/12/8/tin-tuc-ret-dam-ret-hai.jpg\" style=\"margin: auto !important; padding: 0px; list-style: none; outline: none; line-height: 27px !important; border: 1px solid rgb(102, 102, 102) !important; color: rgb(17, 17, 17); font-family: \'Times New Roman\', Times, serif !important; font-size: 18px !important; word-wrap: break-word !important; display: block; height: auto !important; max-width: 670px;\" title=\"Miền Bắc chuẩn bị đón rét đậm, rét hại tăng cường - Ảnh 1\" width=\"500\" /></em></p>\r\n\r\n<p>Dự báo, khoảng từ ngày 12/12, miền Bắc sẽ có rét đậm, rét hại (Ảnh minh họa).</p>\r\n\r\n<p>Theo tin tức từ Trung tâm dự báo Khí tượng Thủy văn Trung ương, khoảng ngày 12/12 sẽ có thêm một đợt không khí lạnh mạnh tràn về, đợt không khí lạnh này có thể gây rét đậm rét hại trên diện rộng và kéo dài ở miền Bắc nước ta.</p>\r\n\r\n<p>Phía Tây Bắc Bộ từ ngày 9-10/12, nhiều mây, có mưa vài nơi. Gió nhẹ. Trời rét, có nơi rét đậm.</p>\r\n\r\n<p>Từ ngày 11-17/12, nhiều mây, có mưa vài nơi. Gió nhẹ. Trời rét, từ ngày 12-14/12 và ngày 16, ngày 17 trời rét đậm, có nơi rét hại.</p>\r\n\r\n<p>Phía Đông Bắc Bộ và Thanh Hóa&nbsp;từ ngày 8-10/12, nhiều mây, có mưa vài nơi. Gió đông bắc cấp 3. Trời rét, vùng núi có nơi rét đậm.</p>\r\n\r\n<p>Từ ngày 11-15/12, nhiều mây, có mưa vài nơi, riêng ngày 11 có mưa nhỏ rải rác. Gió đông bắc cấp 3. Trời rét, từ ngày 12-14/12 vùng núi, trung du và một số nơi ở vùng đồng bằng trời rét đậm, có nơi rét hại.</p>\r\n\r\n<p>Ngày 16- 17/12 nhiều mây, có mưa nhỏ rải rác trong ngày 16, sau có mưa vài nơi. Gió đông bắc cấp 3. Trời rét đậm, có nơi rét hại.</p>\r\n\r\n<p>Các tỉnh từ Nghệ An đến Thừa Thiên Huế Từ ngày 8-10/12, nhiều mây, có mưa, phía nam có nơi mưa vừa, mưa to và dông trong ngày 8/12, sau có mưa rào rải rác. Gió bắc đến tây bắc cấp 3. Phía bắc trời rét.</p>\r\n\r\n<p>Từ ngày 11-17/12, nhiều mây, có mưa, phía nam có mưa vừa, có nơi mưa to đến rất to. Gió bắc đến tây bắc cấp 3. Trời rét.</p>\r\n\r\n<p>Các tỉnh từ Đà Nẵng đến Bình Thuận&nbsp;từ ngày 8-10/12, nhiều mây, phía bắc có mưa, có nơi mưa vừa, mưa to và dông trong ngày 8, sau có mưa rào rải rác; Phía nam phổ biến có mưa vài nơi. Gió đông bắc cấp 3.</p>\r\n\r\n<p>Từ ngày 11-17/12, nhiều mây, có mưa vừa, có nơi mưa to đến rất to, riêng Ninh Thuận, Bình Thuận có mưa rào và dông rải rác. Gió đông bắc cấp 3, cấp 4.</p>\r\n\r\n<p>Tây Nguyên từ ngày 8-11/12 và từ ngày 15-17/12, mây thay đổi, có mưa vài nơi, ngày nắng. Gió đông bắc cấp 2, cấp 3.</p>\r\n\r\n<p>Từ ngày 12-14/12, nhiều mây, có mưa rào và dông. Gió đông bắc đến đông cấp 3.</p>\r\n\r\n<p>Nam Bộ&nbsp;từ ngày 8-11/12 và từ ngày 15-17/12, mây thay đổi, có mưa vài nơi, ngày nắng. Gió đông bắc cấp 2, cấp 3.</p>\r\n\r\n<p>Từ ngày 12-14/12, nhiều mây, có mưa rào và dông rải rác. Gió đông bắc đến đông cấp 3.</p>\r\n','mien-bac-chuan-bi-don-ret-dam-ret-hai-tang-cuong',1,'','',[site_id],[user_id],'/media/images/61/news/ava/','839_1418034209_10154857c2194295.jpg',[now], [now],[user_id],1,'DSPL','',[now]);set @news519 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'“Chạy đủ thứ” và chuyện tư lợi trong bổ nhiệm cán bộ','Quy trình bổ nhiệm cán bộ của ta tiếng là chặt chẽ, được làm qua rất nhiều bước nhưng xem ra \"con voi vẫn chui lọt lỗ kim\". Những quyết định bổ nhiệm đầy vụ lợi, sự cả nể, rồi những cái \"tặc lưỡi\" của tập thể đã vô hình trung tạo điều kiện cho tham nhũng, tiêu cực, chạy chọt.','<p>PV <a href=\"http://www.doisongphapluat.com/tag/b%C3%A1o.html\">báo</a> <a href=\"http://www.doisongphapluat.com/doi-song/\">Đời sống</a> và <a href=\"http://www.doisongphapluat.com/phap-luat/\">Pháp luật</a> đã có cuộc trao đổi với PGS. TS Lê Bỉnh, Giảng viên cao cấp khoa Triết học - Học viện Chính trị Khu vực 1 (nguyên Chủ nhiệm bộ môn Duy vật lịch sử, khoa Triết học - Học viện Chính trị Quân sự) để đem đến cái nhìn sâu sắc hơn về hiện tượng này.</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\" style=\"line-height:27px;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"line-height:27px !important;\">\r\n			<p><em><img alt=\"“Chạy đủ thứ” và chuyện tư lợi trong bổ nhiệm cán bộ - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x670x/357/2014/12/6/le-binh.jpg\" style=\"line-height:27px !important;border:1px solid rgb(102, 102, 102) !important;height:auto !important;\" title=\"“Chạy đủ thứ” và chuyện tư lợi trong bổ nhiệm cán bộ - Ảnh 1\" width=\"500\" /></em></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"line-height:17px !important;\">\r\n			<h3><em>Ông Lê Bỉnh.</em></h3>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Bổ nhiệm \"ồ ạt\" vì... tư lợi</strong></p>\r\n\r\n<p><em>Ngoài lùm xùm liên quan đến việc bổ nhiệm 60 cán bộ trước khi về hưu của nguyên Tổng Thanh tra Chính phủ Trần Văn Truyền, dư luận không khỏi lo ngại tình trạng trên cũng xảy ra ở ngành này, ngành kia. PGS nghĩ sao về hiện tượng, lãnh đạo trước khi \"hạ cánh\" đã ký bổ nhiệm hàng loạt cán bộ và có dấu hiệu không minh bạch?</em></p>\r\n\r\n<p>Vừa rồi Thanh tra Chính phủ cũng đã xác nhận nguyên Tổng Thanh tra Chính phủ Trần Văn Truyền trước khi nghỉ hưu có ký quyết định <a href=\"http://www.doisongphapluat.com/xa-hoi/xung-quanh-viec-lanh-dao-bo-nhiem-can-bo-o-at-truoc-khi-nghi-huu-a25936.html\">bổ nhiệm cán bộ</a> chưa đủ năng lực. Thanh tra Chính phủ cũng khẳng định, nhiều cán bộ được bổ nhiệm thiếu tiêu chuẩn, điều kiện và đã phải xử lý bằng cách cho đi đào tạo; hiện nay vẫn đang trong quá trình rà soát, phân công lại. Thế nên, việc bổ nhiệm sai quy trình của cán bộ tiền nhiệm sẽ để lại nhiều hậu quả với đội ngũ lãnh đạo kế cận. Đó là chưa kể những cán bộ không đủ năng lực sẽ làm méo mó nền công vụ nước nhà, ảnh hưởng tiêu cực đến sự phát triển bền vững của chế độ công quyền.</p>\r\n\r\n<p>Trước đây, những người trước khi về hưu cũng có trường hợp bổ nhiệm cán bộ. Nhưng những trường hợp cán bộ được bổ nhiệm này là cần thiết, khách quan, dân chủ nên không có dư âm gì. Gần đây cùng xu thế \"chạy chức\", \"chạy quyền\", lợi dụng sơ hở pháp luật đã xuất hiện tư tưởng tư lợi trong vấn đề đề bạt cán bộ, nên một số lãnh đạo trước khi về tranh thủ bổ nhiệm nhiều cán bộ sai quy định. Trong việc của ông Truyền, các cơ quan chức năng cần phải khẩn trương vào cuộc xác minh rõ có \"chạy chức\", \"chạy quyền\" hay không? Nếu có vi phạm thì phải xử lý nghiêm, không có \"vùng cấm\" cho bất kỳ cán bộ nào cả.</p>\r\n\r\n<p><em>Việc các quan chức tại vị liên tục \"nhét\" con cháu mình vào những vị trí quan trọng để khi về hưu có thể \"hạ cánh an toàn\" được ví như một hình thức \"tham nhũng tương lai\" và thể hiện lối tư duy \"giữ gôn\", PGS nghĩ sao về nhận định này?</em></p>\r\n\r\n<p>Theo tôi, khái niệm \"giữ gôn\" hay \"tham nhũng tương lai\" cần phải bàn thêm, nhưng với những quan chức trước khi về hưu vẫn bổ nhiệm những cán bộ không đúng tiêu chuẩn là lạm dụng quyền lực. Mà cái cốt lõi, lạm dụng quyền lực cũng chính là một hình thức tham nhũng. Anh không gương mẫu thực hiện nghiêm túc các quy định của pháp luật về quy trình bổ nhiệm cán bộ để có căn cứ theo nhu cầu thực tiễn, mà anh cứ bổ nhiệm sai, vì \"cái gì đó\", ồ ạt... đa số là vì vụ lợi thôi. Nếu không vì vụ lợi, anh chẳng bổ nhiệm cán bộ ồ ạt để làm gì. Đặc biệt, việc bổ nhiệm những người có \"dây mơ rễ má\" khiến dư luận càng nghi ngờ.</p>\r\n\r\n<p>Những nghi vấn về việc \"chạy chức\", \"chạy quyền\", chi tiền để được bổ nhiệm cũng từ đó mà ra. Câu chuyện của nguyên Tổng Thanh tra Chính phủ Trần Văn Truyền bổ nhiệm mấy chục cán bộ trước khi về hưu cũng khiến dư luận đặt câu hỏi. Không thể cứ bổ nhiệm xong rồi \"hạ cánh an toàn\" được. Điều đó cũng cho thấy, có một số trường hợp lạm dụng chức quyền, tranh thủ thời gian để chụp giật cơ hội tham nhũng. Thực tế đặt ra yêu cầu cần phải có cơ chế giám sát chặt chẽ đối với những người đứng đầu có quyền hạn trong bổ nhiệm đề bạt cán bộ, làm sao để họ muốn lạm dụng quyền lực (mà thực chất là tham nhũng) cũng không lạm dụng được.</p>\r\n\r\n<p><strong>\"Bốn ệ\" và trách nhiệm người đứng đầu</strong></p>\r\n\r\n<p><em>Vậy việc bổ nhiệm sai, bổ nhiệm thừa cán bộ là do cơ chế chính sách của ta chưa chặt hay do khâu tổ chức thực hiện, thưa PGS?</em></p>\r\n\r\n<p>Việc bổ nhiệm cán bộ ồ ạt không đủ tiêu chuẩn cho thấy, thực tế hiện nay phát sinh khái niệm \"nợ tiêu chuẩn\". Điều đó là vi phạm pháp luật, vi phạm quy định bổ nhiệm cán bộ của Đảng ta. Theo đúng quy định, tiêu chí bổ nhiệm, đề bạt cán bộ phải rõ ràng, minh bạch, đúng tiêu chuẩn chứ không cán bộ nào được \"nợ tiêu chuẩn\". Về nguyên tắc, khi không đạt tiêu chuẩn thì không bổ nhiệm. Việc cho \"nợ tiêu chuẩn\" là do cán bộ quan chức lạm dụng quyền lực, tự ý cho nợ để rồi lại nảy sinh tình trạng bổ sung tiêu chuẩn bằng việc chạy chọt, đút lót. Như vậy là do khâu tổ chức thực hiện sai chứ không phải chủ trương chính sách sai.</p>\r\n\r\n<p><em>PGS nghĩ sao trước nhiều ý kiến cho rằng, việc bổ nhiệm cán bộ hiện nay theo tiêu chí \"bốn ệ\" (hậu duệ, tiền tệ, quan hệ, trí tuệ) dẫn đến tình trạng cán bộ ngồi nhầm chỗ, không đảm bảo được công việc?</em></p>\r\n\r\n<p>Điều đó thật đáng suy ngẫm. Tất nhiên, đó chỉ là dư luận xã hội. Một vấn đề mà xã hội, cử tri lo ngại và nhiều ĐBQH đã phản ánh là việc bổ nhiệm, đề bạt, luân chuyển cán bộ; tuyển dụng công chức, viên chức biểu hiện nhiều tiêu cực. Tình trạng chạy chức, chạy quyền, chạy việc ngày càng lộ diện rõ hơn. Nếu như bộ ngành nào, địa phương nào cũng bổ nhiệm cán bộ theo tiêu chí ưu tiên \"bốn ệ\" như dư luận <a href=\"http://www.doisongphapluat.com/xa-hoi/\">Xã hội</a> đã đề cập thì đội ngũ cán bộ làm được việc, có năng lực, đủ tâm, đủ tầm sẽ bị thui chột. Do đó phải có giải pháp đồng bộ, trước hết, chăm lo xây dựng Đảng thật sự trong sạch vững mạnh, nâng cao năng lực lãnh đạo và sức chiến đấu của Đảng; chăm lo xây dựng đội ngũ cán bộ trước hết là đội ngũ cán bộ chủ chốt.</p>\r\n\r\n<p><em>Để ngăn ngừa việc lạm dụng quyền hạn, ký quyết định bổ nhiệm cán bộ thiếu khách quan, sai quy định như vậy cần những cơ chế giám sát gì, thưa PGS?</em></p>\r\n\r\n<p>Việc phân cấp, phân quyền đã được thực hiện khá tốt trong những năm gần đây, tuy vậy, đối với công tác bổ nhiệm, ngoài đảm bảo đúng quy trình, thủ tục, tiêu chuẩn, điều kiện để bổ nhiệm, phải đảm bảo tính công khai, dân chủ, công bằng. Tất cả phải quy trách nhiệm cho người đứng đầu. Nếu làm tốt, nhất quán, chắc chắn sẽ phòng chống được tiêu cực trong bổ nhiệm, đề bạt cán bộ cũng như thi tuyển công chức, viên chức. Phải thực sự thực hiện tốt nguyên tắc tập trung dân chủ, phát huy vai trò của cấp ủy Đảng và trách nhiệm của người đứng đầu; đặc biệt cần khắc phục thói độc đoán chuyên quyền, gia trưởng của người đứng đầu.</p>\r\n\r\n<p><em>Trân trọng cảm ơn PGS!</em></p>\r\n','chay-du-thu-va-chuyen-tu-loi-trong-bo-nhiem-can-bo',1,'','',[site_id],[user_id],'/media/images/61/news/ava/','112_1418034068_37754857b94d9264.jpg',[now], [now],[user_id],1,'DSPL','',[now]);set @news520 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'Nam thanh niên ném bom xăng đốt ga Vinh bị tâm thần?','Sáng nay (8/12), một nam thanh niên có dấu hiệu bị tâm thần xách theo can xăng 10l đổ vào phòng chờ ga Vinh rồi châm lửa đốt. Vụ việc đã khiến ga Vinh tê liệt, hỗn loạn hơn 1 giờ đồng hồ. ','<p>Theo tin tức từ Dân trí, khoảng 7h40 sáng nay, một nam thanh niên phi Xe máy vào trong ga, bất chấp sự ngăn cản của nhân viên bảo vệ cổng ga, mang theo một can nhựa 10l chứa đầy xăng, chạy vào phòng bán vé và bất ngờ ném can xăng vào tấm kính chắn ô cửa phòng bán vé, đồng thời bật lửa đốt.<br />\r\nNgọn lửa bốc cao ngùn ngụt khiến các nhiên viên nhà ga và hành khách bỏ chạy toán loạn. Lửa bùng cháy dữ dội, lan vào một số bộ phận máy móc, bàn ghế… nhưng rất may không gây thương vong về người.<br />\r\n“Vụ ném \"bom\" xăng đã gây cháy một số máy móc ở phòng bán vé, cháy một bàn, ghế, làm hỏng máy điều hoà, quạt ngắn gió, hỏng camera và đặc biệt là hệ thống bán vé bị tê liệt. Sau hơn 1 giờ đồng hồ nỗ lực, chúng tôi đã khắc phục lại hệ thống bán vé”, ông Nguyễn Đình Thanh, Trưởng ban giám sát ga Vinh&nbsp;cho biết.<br />\r\nThông tin về đối tượng ném \"bom\" xăng,VnExpress đưa tin, Công an TP Vinh đã bắt giữ được kẻ phóng hỏa vào phòng chờ ga Vinh. Đối tượng tên là Hòa, 31 tuổi, trú tại phường Quán Bàu, TP Vinh. Hòa là lái xe ôm, thường đợi khách tại cổng ga Vinh.<br />\r\nThượng tá Mai Chiến Thắng, Phó trưởng công an TP Vinh cho biết, cơ quanđiều tra đang khám nghiệm hiện trường, điều tra vụ việc. \"Thông tin ban đầu, kẻ gây ra vụ đốt có dấu hiệu bệnh tâm thần\", ông Thắng nói.<br />\r\n&nbsp;</p>\r\n','nam-thanh-nien-nem-bom-xang-dot-ga-vinh-bi-tam-than',1,'','',[site_id],[user_id],'/media/images/61/news/ava/','397_1418033888_37154857ae08727c.jpg',[now], [now],[user_id],0,'DSPL','',[now]);set @news521 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'Nguyên Giám đốc Sở Xây dựng Cà Mau bị kỷ luật vì \"ưu ái\" vợ mới','Nguyên Giám đốc Sở Xây dựng Cà Mau Nguyễn Quốc Định vừa bị xử lý kỷ luật cảnh cáo về mặt Đảng và chính quyền.','<p>Ban chấp hành Tỉnh ủy Cà Mau đã thống nhất kỷ luật đối với ông Nguyễn Quốc Định - nguyên Giám đốc Sở Xây dựng Cà Mau (hiện là cán bộ của Ban Tổ chức Tỉnh ủy) bằng hình thức cảnh cáo về mặt Đảng và chính quyền.</p>\r\n\r\n<p>Ông Định bị xử lý kỷ luật do có 4 sai phạm, khuyết điểm trong thời gian đương chức.</p>\r\n\r\n<p>Sai phạm của ông Định có liên quan đến bà Nguyễn Cẩm Nhung, Giám đốc Công ty TNHH Hưng Điền, là vợ mới của ông Định.</p>\r\n\r\n<p>Ông Định đã dùng chức vụ của mình để giúp bà Nhung trúng nhiều gói thầu lớn, trong khi công ty của bà Nhung không đủ năng lực thi công, hay thanh toán vượt khối lượng ở một số dự án cho công ty này dẫn đến thất thoát ngân sách…</p>\r\n\r\n<p>Ngoài ra, ông Định còn vi phạm trong công tác tổ chức cán bộ, để xảy ra tai tiếng không tốt cho cá nhân và đơn vị.</p>\r\n\r\n<p>Trước đó, tháng 10/2012, ông Định cũng đã bị kỷ luật cảnh cáo về mặt Đảng và chính quyền do liên quan đến những sai phạm khác.</p>\r\n','nguyen-giam-doc-so-xay-dung-ca-mau-bi-ky-luat-vi-uu-ai-vo-moi',1,'','',[site_id],[user_id],'/media/images/61/news/ava/','941_1418033673_69454857a09ce7df.jpg',[now], [now],[user_id],0,'DSPL','Văn Dũng',[now]);set @news522 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories348,'Bể bơi tầng 1 nhà R3 khu đô thị Royal City','Bể bơi tầng 1 nhà R3 khu đô thị Royal City','<p><span style=\"color: rgb(48, 48, 48); font-family: Arial, Tahoma, Helvetica, sans-serif; line-height: normal; text-align: center;\">Bể bơi tầng 1 nhà R3 khu đô thị Royal City</span></p>\r\n','be-boi-tang-1-nha-r3-khu-do-thi-royal-city',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','617_1428662248_6155527a7e88caa2.jpg',[now], [now],[user_id],0,'','',[now]);set @news509 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'HVC sẽ là công ty có tốc độ xây dựng bể bơi nhanh nhất Việt Nam','Vừa qua Công ty cổ phần đầu tư và công nghệ HVC đã ký hợp đồng với công ty TNHH Vinpearl về việc cung cấp lắp đặt công nghệ thiết bị 150 bể bơi biệt thự, 2 cụm bể bơi resort 500m3, 1 cụm bể bơi trung tâm 2500m3','<p style=\"text-align: justify;\">Thời gian thực hiện rất ngắn cho cả dự án là 3 tháng. Hoàn thành dự án này HVC sẽ lập kỷ lục là công ty cung cấp lắp đặt thiết bị bể bơi nhanh nhất tại Việt Nam. Thông thường việc nhập khẩu thiết bị cho dự án đã mất khoảng 2 tháng như vậy HVC chỉ còn 1 tháng để hoàn thành việc lắp đặt.</p>\r\n\r\n<p style=\"text-align: justify;\">Dự án Vinpearl resort and Villas được đầu tư tại khu vực Vũng Me đảo Hòn Tre - TP Nha Trang - Khánh Hòa. Với quy mô 900 phòng bao gồm khách sạn và biệt thự nằm ngay sát bờ biển nơi đây hứa hẹn sẽ là thiên đường của du lịch nghỉ dưỡng. Được đầu tư theo tiêu chuẩn 5 sao nên chủ đầu tư đặc biệt chú trọng về kiến trúc, chất lượng xây dựng và chất lượng trang thiết bị.</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/vinpearl%20villas%201.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Dự án bao gồm 1 khu khách sạn lớn và 150 biệt thự nghỉ dưỡng</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/vinpearl%20villas%202.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Mỗi biệt thự sẽ bao gồm 1 bể bơi được đầu tư theo tiêu chuẩn 5 sao</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/vinpearl%20villas%203.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Ngoài ra còn khu bể bơi lớn tại trung tâm khách sạn và 2 bể bơi resort</p>\r\n','hvc-se-la-cong-ty-co-toc-do-xay-dung-be-boi-nhanh-nhat-viet-nam',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','954_1428634717_49455273c5d0c2a3.jpg',[now], [now],[user_id],1,'','',[now]);set @news510 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories347,'Chính thức khai trương công viên nước Vinpearl Phú Quốc','Tiếp theo chuỗi khai trương khách sạn, khu biệt thự, thủy cung, vui chơi trẻ em... của khu nghỉ dưỡng đẳng cấp Vinpearl Phú Quốc. Ngày 15/11/2014 HVC kết hợp cùng Công ty Vinpearl Phú Quốc tiếp tục khai trương công viên nước Vinpearl Phú Quốc','<p style=\"text-align: justify;\">Công viên nước Vinpearl Phú Quốc được đánh giá là một trong những công viên nước quy mô nhất Việt Nam về số lượng trò chơi nước, nhất là các trò chơi cảm giác mạnh. Việc khai trương công viên nước Vinpearl Phú Quốc sẽ góp phần lớn tạo ra sức hút cho khu du lịch Vinpearl Phú Quốc. Hoàn thành công trình công viên nước Vinpearl Phú Quốc trong một thời gian rất ngắn khoảng 5 tháng HVC đã chứng tỏ là công ty số 1 Việt Nam về việc cung cấp thiết bị bể bơi và thiết bị công viên nước hiện nay. Dưới đây là một số hình ảnh sơ bộ về công viên nước Vinpearl Phú Quốc trong ngày khai trương:</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%2012.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Một góc của công viên nước Vinpearl Phú Quốc</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%201.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi dành cho trẻ em mô hình nhà Hawaii</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%202.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Dòng sông lười uốn lượn bao quanh công viên nước</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%204.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%203.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Bể tạo sóng với nhiều chế độ sóng khác nhau</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%205.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò trơi trượt lướt ván nhẹ nhàng</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%206.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%207.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi trượt phao gia đình cho 4 người 1 lượt chơi</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%208.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%209.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trờ chơi hố trượt lốc xoáy với cảm giác mạnh</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvp%2010.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi trượt tốc độ và trượt kín Aqualop</p>\r\n\r\n<p style=\"text-align: center;\"><img border=\"0\" src=\"http://hvcgroup.net/images/stories/kt%20cvnvppq%2011.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Trò chơi tạo cảm giác mạnh nhất là hố đen vũ trụ</p>\r\n','chinh-thuc-khai-truong-cong-vien-nuoc-vinpearl-phu-quoc',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','739_1428634619_26155273bfb5bf63.jpg',[now], [now],[user_id],1,'','',[now]);set @news511 = LAST_INSERT_ID();
