INSERT INTO jobs (id, user_id, site_id, `position`, degree, trade_id, typeofwork, location, quantity, salaryrange, currency, salary_min, salary_max, description, experience, `level`, includes, requirement, benefit, expirydate, publicdate, created_time, modified_time, `alias`, contact_username, contact_email, contact_phone, contact_address,image_path,image_name) VALUES (null,[user_id],[site_id],'Nhân viên văn phòng trực điện thoại',2,61,1,'01',1,1,1,0,0,'<p><span style=\"color: rgb(85, 85, 85); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 23px; background-color: rgb(255, 255, 255);\">Nhân viên văn phòng nghe trực điện thoại tư vấn khách hàng giải đáp thông tin cho khách, làm các công việc văn phòng chung của công ty, công tác trong địa bàn theo lệnh điều hành của văn phòng công ty.</span><br style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; color: rgb(85, 85, 85); line-height: 23px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(85, 85, 85); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 23px; background-color: rgb(255, 255, 255);\">Công ty phân ra hai mảng văn phòng để nhân viên lựa chọn: 1 mảng nghe trực điện thoại tư vấn khách hàng, giải đáp các thông tin từ phía khách hàng; 1 mảng soạn thảo công văn giấy tờ, quản lý công văn đi, công văn đến, theo dõi định mức văn phòng phẩm, bảo hiêm... của văn phòng công ty.</span><br style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; color: rgb(85, 85, 85); line-height: 23px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(85, 85, 85); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 23px; background-color: rgb(255, 255, 255);\">Làm thời gian hành chính, nghỉ thứ bảy, nghỉ chủ nhật, nghỉ ngày lễ tết.&nbsp;</span><br style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; color: rgb(85, 85, 85); line-height: 23px; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(85, 85, 85); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 23px; background-color: rgb(255, 255, 255);\">02 nhân viên làm việc tại Thành Phố Thái Nguyên, 02 nhân viên làm việc tại Thành Phố Vĩnh Yên, Tỉnh Vĩnh Phúc.</span></p>\r\n',0,1,'<p>Sơ yếu lý lịch, giấy khai sinh, giây khám sức khỏe, đơn xin việc, chứng minh nhân dân, bản sao giấy tờ bằng cấp liên quan nếu có.<br />\r\nChấp nhận hồ sơ phô tô, hồ sơ không hoàn lại, nộp hồ sơ trực tiếp tại văn phòng công ty trong giờ hành chính trước 16h chiều hàng ngày, khi đến nộp hồ sơn gọi điện trước để nhận lịch phỏng vấn.<br />\r\nCác ứng viên ngoại tỉnh nộp hồ sơ trực tiếp tại công ty ở Hà Nội tại địa chỉ số nhà 21 ngõ 238 đường Hoàng Quốc Việt, quận Bắc Từ Liêm, Hà Nội. Trên hồ sơ ghi rõ ứng cử về làm tại chi nhánh nào, công ty phỏng vấn trực tiếp khi nộp hồ sơ, khi phỏng vấn đạt sẽ phân về chi nhánh làm việc theo nhu cầu ứng tuyển.</p>\r\n','<p>Nam, nữ tuổi từ 18 đến 50, nhanh nhẹn, trung thực, ốt nghiệp từ phổ thông trung học trở lên, có chí tiến thủ, mong muốn làm việc lâu dài, công ty không yêu cầu kinh nghiệm, được hướng dẫn công việc trong thời gian đầu thử việc.</p>\r\n','<p>Hưởng lương thử việc 5,5 tr/ tháng, sau thử việc tăng 6 tr, hỗ trợ ăn trưa, các chế độ bảo hiểm y tế, bảo hiểm xã hội theo quy định công ty.<br />\r\nCông ty có chỗ ở cho người ngoại tỉnh, Nhân viên được tăng lương theo thâm niên công tác, được tạo điều kiện học tập công tác tại công ty.<br />\r\nDu lịch hàng năm, nghỉ ngày lễ tết theo quy định.</p>\r\n',1430154000,1428636305,[now],[now],'nhan-vien-van-phong-truc-dien-thoai','Anh Minh','','','','/media/images/92/jobs/','985_1429009249_985552cf36187042.png');
