INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories354,'HVC to be the fastest swimming pool constructor in Viet Nam','HVC Investment and Technology Joint Stock Company and Vinpearl Limited Liability company have signed the contract on supplying and installing both technology and equipment for 150 villa pools, 2 resorts’ pool complex (500m3), 1 center pool complex (2500m3). ','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: justify; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">HVC Investment and Technology Joint Stock Company and Vinpearl&nbsp;Limited Liability company&nbsp;have signed the contract on supplying and installing both technology and equipment for 150 villa pools, 2 resorts’ pool complex (500m3), 1 center pool complex (2500m3). The construction time is very short, only in 3 months. Completion of this project will help HVC set a record as the fastest company in terms of swimming pool equipment supply and installation in Vietnam. In most cases, it takes 2 months to import all equipments so HVC has only 1 month to complete installation.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: justify; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">Vinpearl Resort and Villas project has been invested in Vung Me- Hon Tre island- Nha Trang city- Khanh Hoa province. With scale of 900 rooms including hotels and villas located right on the coast, it is expected to be a paradise for relaxing tourists. Moreover, the owner specially focuses not only on architecture and construction quality but also on equipment quality so Vinpearl Resort and Villas project could be in accordance with the five- star standard.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: justify; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">&nbsp;</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><img border=\"0\" height=\"300\" src=\"http://hvcgroup.net/data/media/189/images/2015/en/1901.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle; max-width: 100%;\" width=\"500\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">The project includes a large hotel area and 150 villas</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><img border=\"0\" height=\"300\" src=\"http://hvcgroup.net/data/media/189/images/2015/en/1902.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle; max-width: 100%;\" width=\"500\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">The project includes a large hotel area and 150 villas</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><img border=\"0\" height=\"300\" src=\"http://hvcgroup.net/data/media/189/images/2015/en/1903.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle; max-width: 100%;\" width=\"500\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">The large swimming pool in hotel center and 2 resort swimming pools</p>\r\n','hvc-to-be-the-fastest-swimming-pool-constructor-in-viet-nam',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','895_1428634083_283552739e35d852.jpg',[now], [now],[user_id],0,'','',[now]);set @news542 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories354,'Design consultancy, supply and installation the first artistic skating rink in Quang Ninh','The artistic ice rink is very rare in Vietnam. Viet Nam has invested only two skating rinks in Hanoi and Ho Chi Minh City up to now.','<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: justify; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\">The artistic ice rink is very rare in Vietnam. Viet Nam has invested only two skating rinks in Hanoi and Ho Chi Minh City up to now. Because of a large investment and high operating costs, skating rinks should normally have been invested association with urban areas, enormous commercial center. However; Viet Nam will continue to invest in building more and more artistic skating rink with general development trend of the world economy.&nbsp;HVC technology and investment joint stock company with CTC firm has recently signed consultancy contract to provide and install artistic skating rink equipment in the Ha Long commercial center- Quang Ninh province. It is the commercial center largest scale not only in Quang Ninh provinces but also in the northern plain. This work has 7 floor scale (6 embossed storeys and 1 basement). Ha Long commercial center was built following to the all in one international standard model, with a lot of items such as luxury shopping cente, meeting rooms, conference rooms, entertainment centers, health and education consultant chain, cuisine area, four season water park, restaurant, movie theater. Skating rink will be a main destination of the entire commercial center. The entire skating rink which is used Canada technology have the best quality in comparison with others in Vietnam.</p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><img border=\"0\" src=\"http://baoquangninh.com.vn/dataimages/201405/original/images731097_DSC_25.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle; max-width: 100%;\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Perspective commercial center in Ha Long</em></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><img border=\"0\" height=\"231\" src=\"http://images.travelpod.com/users/ffoutofoffice/1.1275149661.ice-rink.jpg\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle; max-width: 100%;\" width=\"463\" /></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: center; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Skating rink inside commercial centers.</em></p>\r\n\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; text-align: justify; color: rgb(0, 0, 0); line-height: 20px; font-family: tahoma, geneva, sans-serif; font-size: 14px;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\" />\r\nHa Long commercial center and skating rink will be completed and put into use in the end of september, 2014. When finishing, not only Ha Long commercial center will contribute to highlight in the Ha Long Bay but also it really attract tourists to Halong Bay.</p>\r\n','design-consultancy-supply-and-installation-the-first-artistic-skating-rink-in-quang-ninh',1,'','',[site_id],[user_id],'/media/images/92/news/ava/','70_1428634028_852552739acc6fd3.jpg',[now], [now],[user_id],0,'','',[now]);set @news543 = LAST_INSERT_ID();
