INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product884,'Được trang bị động cơ với công suất lên đến 350W, Honda A6 cho người đi có khả năng đạt tốc độ tối đa lên đến 35 km/h, tốc độ này cao hơn mức trung bình của các xe đạp điện khác trên thị trường tới 10 km/h, do đó, được khá nhiều người ưa thích sử dụng.','<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Ngoài ra, động cơ này cũng cho phép xe có khả năng trở<a href=\"http://websosanh.vn/s/v%E1%BA%ADt+n%E1%BA%B7ng.htm\" style=\"box-sizing: border-box; color: rgb(2, 177, 242); text-decoration: none; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; font-style: inherit; font-family: inherit; vertical-align: baseline; cursor: pointer; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; font-style: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 0, 0);\">&nbsp;vật nặng</span></a>&nbsp;lên tới 140 kg, do đó, xe cũng cho khả năng trở thêm một người phía sau khá thoải mái.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Honda A6 có thiết kế khá an toàn và thân thiện với người sử dụng, luôn tạo cảm giác thoải mái nhất cho người điều khiển và cả người ngồi phía sau.</div>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product885,'Giant M133L đưiọc trang bị động cơ 350W cho tốc độ tối đa lên đến 30 km/h. Vận hành hoàn toàn tự động, êm ái với 2 chế độ đi là Power và Eco, giúp người dùng không lo phải luôn giữ tay ga như nhiều loại xe khác.','<p><span style=\"font-family: \'Times New Roman\'; font-size: 16px; line-height: 22.4px;\">Thiết kế của Giant M133L cũng khá thân thiện với người dùng với chỗ ngồi khá rộng rãi và thoải mái, chỗ để chân khá thoải mái cho người đi. Xe đạc biệt phù hợp với các bạn trẻ, nhất là các bạn học sinh, sinh viên, thì Giant M133L quả là một phương tiện di chuyển hữu hiệu</span></p>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product886,'Yamaha Cute Girl 2 là chiếc xe được thiết kế dành riêng cho phái nữ với kiểu dáng hết sức nữ tính, màu sắc trẻ trung, với những họa tiết cute, dành riêng cho các bạn gái dịu dàng, nữ tính. Thiết kế giỏ xe mây độc đáo, khá rộng đáp ứng nhu cầu chứa hàng tá đồ của chị em. Ngoài ra, xe cũng có chỗ ngồi khá êm ái và rộng rãi, vì thế, người đi, đặc biệt là các chị em sẽ hết sức thoải mái khi điều khiển xe.','<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Xe còn có ghế ngồi phía sau với chỗ dựa nên rất tiện nếu muốn đèo thêm môt người. Chiều cao yên 750mm và có thể điều chỉnh độ cao, vì thễ phù hợp với nhiều chiều cao của phụ nữ Việt. Chỗ để chân cũng khá thoải mái, nên rất tiện khi các chị em đi dép cao gót.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Xe có khối lượng 50kg, khá nhẹ nên các chị em cũng dễ dàng khi di chuyển hay dắt xe.</div>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product887,'Nijia 2014 được trang bị động cơ có công suất tới 450W, 3 pha do đó, cho xe có khả năng đạt tốc độ tối đa lên tới 45 km/h, cao hơn mức tốc độ chung của các dòng xe khác trên thị trường tới 20 km/h','<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Bên cạnh đó, với động cơ 450W, xe có khả năng chở vật nặng lên đến 140kg, nên khá cơ động, nhất là khi muốn trở thêm một người phía sau</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Mặc dù có thể đạt được tốc độ cao lên đến 45 km/h, nhưng Nijia 2014 có một thiếu sót là hệ thống phanh không tương xứng. Nijia 2014 vẫn sử dụng hệ thống phanh cơ truyền thống trong các xe đạp điện khác. Hệ thống này nếu như đi ở tốc độ khoảng 20 km/h thì khá an toàn, nhưng nếu đi với tốc độ cao hơn khi phanh sẽ rất nguy hiểm.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Nhìn chung với mức giá 9,100,00 thì Nijia đời 2014 quả là một chiếc xe rất tốt cho sự lựa chọn của các bạn trẻ</div>\r\n','','','','','',[site_id]);
INSERT INTO product_info (product_id, product_sortdesc, product_desc, dynamic_field, meta_title, meta_keywords, meta_description, list_product_relate, site_id) VALUES (@product888,'Xe đạp điện Honda Cool được trang bị động cơ 3fa, với sức mạnh 350W, cho xe có khả năng đạt mức tốc độ 35 km/h, mức tốc độ cao hơn trung bình của các dòng xe đạp điện khác trên thị trường (với chỉ 25 km/h), do đó, với mức tốc độ này, Honda Cool có thể chinh phục được nhiều bạn trẻ yêu thích những chiếc xe đạp điện có tốc độ.','<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; font-style: inherit; font-family: inherit; vertical-align: baseline;\">Mặc dù vậy, khả năng trở vật nặng của xe lại khá kém, vì động cơ 3fa dường như khá yếu trong việc trở vật nặng, với khối lượng tối đa chỉ dừng lại ở mức dưới 90 kg, do vậy chỉ thích hợp với việc 1 người điều khiển xe ngồi (có thể đây là lý do mà xe không có thêm ghế ngồi sau, và cũng không được trang bị cốp xe hay giỏ xe như nhiều loại xe đạp điện khác)</div>\r\n</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; font-family: \'Times New Roman\'; vertical-align: baseline; line-height: 22.4px;\">Mong rằng với những gợi ý trên về chiếc<a href=\"http://websosanh.vn/s/xe+%C4%91%E1%BA%A1p+%C4%91i%E1%BB%87n+gi%C3%A1+r%E1%BA%BB.htm\" style=\"box-sizing: border-box; color: rgb(2, 177, 242); text-decoration: none; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; font-style: inherit; font-family: inherit; vertical-align: baseline; cursor: pointer; background: transparent;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; font-style: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 0, 0);\">&nbsp;xe đạp điện giá rẻ</span></a>&nbsp;chất lượng tốt thì bạn đã có thể cho mình những quyết định mua xe chính xác hơn.</div>\r\n','','','','','',[site_id]);
