INSERT INTO site_introduces (site_id,user_id, title, sortdesc, description, image_path, image_name, meta_keywords, meta_description, meta_title, created_time, modified_time) VALUES ([site_id],[user_id],'Giới thiệu về Joey Roth','','<p>Joey Roth đã chọn mình để mang lại cho la bàn bằng gỗ của mình với thế giới. Và Loa gốm tuyệt đẹp của mình. Và thủ công của mình, tự tưới nước trồng.</p>\r\n\r\n<p>Bằng cách chọn mình và (nhất) bằng cách làm cho công việc mà tiếng vang, anh mang nghệ thuật của mình cho những người quan tâm đến nó, hàng ngày.</p>\r\n\r\n<p>- Seth Godin</p>\r\n\r\n<p>Joey đã được sinh ra tại San Francisco và lớn lên ở New Jersey. Ông tốt nghiệp từ Swarthmore với bằng Cử nhân Thiết kế công nghiệp Theory. Khi nghỉ ngơi từ thiết kế, anh thích trượt ván đường dài, đọc sách và hoàn thiện BBQ của mình.</p>\r\n\r\n<p>Jana lớn lên ở Manhattan, nhưng nguồn cảm hứng của cô đến càng nhiều từ sa mạc như cảnh quan đô thị. Bà học tại Brown và CCA, và bắt đầu cộng tác với Joey ngay sau khi họ gặp nhau. Cô ấy là một nhà thiết kế, nàng thơ, và đứng đầu hoạt động cho các studio.</p>\r\n','','','','','',[now],[now]);
