INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories373,'Ấn tượng loa sứ Joey Roth','Là cục cưng của nhà thiết kế Joey Roth, bộ loa điện tử Ceramic Speakers sử dụng chất liệu sứ trắng ngà đặt trên chân đế bằng gỗ Baltic nhìn thật sang trọng.','<p style=\"margin: 0px 0px 8px; padding: 0px; color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 18px; line-height: 24px; text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px 0px 8px; padding: 0px; color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 18px; line-height: 24px; text-align: center;\"><img alt=\"\" src=\"http://w3ni201.web3nhat.net/data/media/156/images/82a5b4f2c28f79b189b3b4f099d6703a_1.jpg\" style=\"width: 400px; height: 197px;\" /></p>\r\n\r\n<p style=\"margin: 0px 0px 8px; padding: 0px; color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 18px; line-height: 24px; text-align: center;\"><img alt=\"\" src=\"http://w3ni201.web3nhat.net/data/media/156/images/8a0dfa5dd4b17449ffefbb3e356c5338_1.jpg\" style=\"width: 400px; height: 195px;\" /></p>\r\n\r\n<p style=\"margin: 0px 0px 8px; padding: 0px; color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 18px; line-height: 24px; text-align: justify;\"><span class=\"PBody\">Màn loa làm từ giấy giấy nam châm Neodymium dẻo dai, các đầu dây dẫn mạ ánh kim đẹp tuyệt và dây dẫn đều có lõi làm bằng sợi đồng đỏ. Ngoài ra, bộ ampli đi kèm cũng được độ bằng thép không gỉ ấn tượng.</span></p>\r\n\r\n<p style=\"margin: 0px 0px 8px; padding: 0px; color: rgb(0, 0, 0); font-family: \'Times New Roman\', Times, serif; font-size: 18px; line-height: 24px; text-align: justify;\"><span class=\"PBody\">Hiện tại, bộ loa sứ Joey Roth đang rao bán trên mạng với giá 495 USD (khoảng 9,5 triệu đồng).</span></p>\r\n','an-tuong-loa-su-joey-roth',1,'','',[site_id],[user_id],'/media/images/156/news/ava/','323_1441963915_65755f29f8b0493d.jpg',[now], [now],[user_id],0,'','',[now]);set @news579 = LAST_INSERT_ID();
INSERT INTO news (news_id, news_category_id, news_title, news_sortdesc, news_desc, `alias`, `status`, meta_keywords, meta_description, site_id, user_id, image_path, image_name, created_time, modified_time, modified_by, news_hot, news_source, poster, publicdate) VALUES (null,@news_categories373,'Bộ loa sứ trắng muốt của Joey Roth','Bộ loa sứ của hãng Joey Roth là sự pha trộn hài hòa giữa phong cách thiết kế và nhu cầu âm thanh.','<p><span style=\"color: rgb(0, 0, 0); font-family: Baomoi, Arial, Helvetica, sans-serif; font-size: 16px; line-height: 22px;\">Bộ loa sứ của Joey Roth bao gồm hai loa và một ampli 15 Watt đi kèm. Hai loa đều có vỏ ngoài làm bằng sứ, màu trắng muốt, là kiểu loa dài 8\" , được cho là phù hợp với hệ thống âm thanh 2 kênh của các audiophile \"sành điệu\". Nút sau của loa được làm từ gỗ bulô Baltic, cùng chất liệu với chân đế đặt loa. Củ loa có vành giấy nam châm Neodymium, tần số đáp ứng nằm trong khoảng 70 Hz đến 20.000 Hz, độ nhạy 89 dB với điện trở 4 Ohm. Ampli 15 Watt đi kèm là mẫu ampli Tripath 2024 T-amp được độ lại có vỏ ngoài bằng thép không gỉ đặt trên đế bằng gang chắc chắn. Theo HEmagazine, bộ loa sứ của Joey Roth có thể đặt hàng trực tiếp qua mạng với giá 495 USD. Hình ảnh về bộ loa sứ của Joey Roth. Tuấn Anh Ảnh: Joeyroth</span></p>\r\n','bo-loa-su-trang-muot-cua-joey-roth',1,'','',[site_id],[user_id],'/media/images/156/news/ava/','40_1441958934_51255f28c163c6c8.jpg',[now], [now],[user_id],0,'','',[now]);set @news578 = LAST_INSERT_ID();
