INSERT INTO categorypage (id, title, content, site_id, user_id, `alias`, created_time, modified_time, modified_by, meta_keywords, meta_description, meta_title, image_path, image_name) VALUES (null,'Trang dịch vụ','<div clas=\"row\">\r\n<div class=\"col-sm-4\" style=\"box-sizing: border-box; padding: 0px 15px; margin: 0px; position: relative; min-height: 1px; float: left; width: 390px; font-family: Lato, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px;\">\r\n<h2 style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; line-height: 1.1; color: inherit; font-size: 23px;\">Shipping</h2>\r\n\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px;\">Domestic US shipping is free for orders over $100. International and express shipping is available. The cost is calculated at checkout using live rates from UPS and USPS.</p>\r\n\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px;\">Stocked products are shipped within 48 hours from our warehouse in Carlstadt, NJ.</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\" style=\"box-sizing: border-box; padding: 0px 15px; margin: 0px; position: relative; min-height: 1px; float: left; width: 390px; font-family: Lato, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px;\">\r\n<h2 style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; line-height: 1.1; color: inherit; font-size: 23px;\">Warranty and Returns</h2>\r\n\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px;\">All products carry a one year warranty, with lifetime support. Returns are accepted for unused, unopened products within 30 days of purchase. Any product damaged by shipping or with defective parts is eligible for a return or exchange within 30 days of purchase.</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\" style=\"box-sizing: border-box; padding: 0px 15px; margin: 0px; position: relative; min-height: 1px; float: left; width: 390px; font-family: Lato, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px;\">\r\n<h2 style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; line-height: 1.1; color: inherit; font-size: 23px;\">Privacy</h2>\r\n\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px;\">Your credit card information will be transferred securely via 128-bit encryption, and will never be stored. Additionally, we would never think of sharing, renting, or selling your email or personal information. We won’t even email you ourselves, other than to communicate about your order, unless you give us permission.</p>\r\n</div>\r\n</div>\r\n',[site_id],[user_id],'trang-dich-vu',[now],[now],[user_id],'trang dich vu','dich vu cua cong ty','dich vu','','');
